    "Perseus Family": ["Perseus","Cepheus","Andromeda","Cassiopeia","Pegasus"],
    "Hercules Family": ["Hercules","Corona Borealis","Lyra","Aquila"],
    "Orion Family": ["Orion","Canis Major","Canis Minor","Monoceros"],
    "Bayer Group": ["Hydra","Crater","Corvus"],
    "La Caille Family": ["Fornax","Antlia","Pyxis"]
  };

  function allConstellationsList() {
    // unique list of all constellations in our data or family map
    const s = new Set();
    Object.keys(FAMILIES).forEach(f => FAMILIES[f].forEach(c => s.add(c)));
    Object.keys(CONSTELLATIONS).forEach(c => s.add(c));
    return Array.from(s).sort();
  }

  function populateConstellationOptions() {
    const family = familySelect.value;
    constellationSelect.innerHTML = '';

    const noneOpt = document.createElement('option');
    noneOpt.value = 'None';
    noneOpt.textContent = 'None';
    constellationSelect.appendChild(noneOpt);

    let list = [];
    if (family === 'all') {
      list = allConstellationsList();
      list.forEach(name => {
        const opt = document.createElement('option');
        opt.value = name;
        opt.textContent = name;
        constellationSelect.appendChild(opt);
      });
      constellationSelect.value = 'None';
    } else {
      list = (FAMILIES[family] || []).slice();
      list.forEach(name => {
        const opt = document.createElement('option');
        opt.value = name;
        opt.textContent = name;
        constellationSelect.appendChild(opt);
      });

      // Default behavior for specific family: set to the first constellation
      if (family === 'Zodiac') {
        // As required: default value "Cygnus" when "Zodiac" is selected
        if (list.includes('Cygnus')) {
          constellationSelect.value = 'Cygnus';
        } else {
          constellationSelect.selectedIndex = Math.min(1, constellationSelect.options.length - 1);
        }
      } else {
        constellationSelect.selectedIndex = Math.min(1, constellationSelect.options.length - 1);
      }
    }

    onConstellationChange();
  }

  // Build constellation visuals
  function ensureConstellationObjects() {
    if (!constellationLines) {
      constellationLines = new THREE.LineSegments(
        new THREE.BufferGeometry(),
        new THREE.LineBasicMaterial({ color: 0xff4444, linewidth: 2 })
      );
      scene.add(constellationLines);
    }
    if (!constellationStars) {
      constellationStars = new THREE.Points(
        new THREE.BufferGeometry(),
        new THREE.PointsMaterial({ color: 0xffff66, size: 0.04, sizeAttenuation: true })
      );
      scene.add(constellationStars);
    }
  }

  function clearConstellation() {
    ensureConstellationObjects();
    constellationLines.geometry.dispose();
    constellationLines.geometry = new THREE.BufferGeometry(); // empty
    constellationStars.geometry.dispose();
    constellationStars.geometry = new THREE.BufferGeometry(); // empty
  }

  function onConstellationChange() {
    const name = constellationSelect.value;
    ensureConstellationObjects();

    if (name === 'None' || !CONSTELLATIONS[name]) {
      clearConstellation();
      return;
    }

    const data = CONSTELLATIONS[name];
    const starNames = Object.keys(data.stars || {});
    const starPos = {};
    starNames.forEach(s => {
      const { ra, dec } = data.stars[s];
      starPos[s] = raHoursDecDegToCartesian(ra, dec, R);
    });

    // Build line segments
    const linePoints = [];
    (data.lines || []).forEach(pair => {
      const [a, b] = pair;
      if (starPos[a] && starPos[b]) {
        linePoints.push(starPos[a], starPos[b]);
      }
    });
    const lineGeo = new THREE.BufferGeometry().setFromPoints(linePoints);
    constellationLines.geometry.dispose();
    constellationLines.geometry = lineGeo;

    // Build points for stars (highlight)
    const pointsArray = new Float32Array(starNames.length * 3);
    starNames.forEach((s, i) => {
      const p = starPos[s];
      pointsArray[i * 3 + 0] = p.x;
      pointsArray[i * 3 + 1] = p.y;
      pointsArray[i * 3 + 2] = p.z;
    });
    const ptsGeo = new THREE.BufferGeometry();
    ptsGeo.setAttribute('position', new THREE.BufferAttribute(pointsArray, 3));
    constellationStars.geometry.dispose();
    constellationStars.geometry = ptsGeo;
  }

  // Sliders control zenith position
  function updateZenithFromSliders() {
    const raDeg = parseFloat(sliderRA.value);
    const decDeg = parseFloat(sliderDec.value);
    const zenithPos = raDegDecDegToCartesian(raDeg, decDeg, R);

    // Group position is set to zenith, and the line is updated to reach origin
    zenithGroup.position.copy(zenithPos);

    // Update line local coordinates: from (-group.position) to (0,0,0)
    const p0 = zenithPos.clone().multiplyScalar(-1);
    const p1 = new THREE.Vector3(0, 0, 0);
    const arr = new Float32Array([p0.x, p0.y, p0.z, p1.x, p1.y, p1.z]);
    zenithLine.geometry.setAttribute('position', new THREE.BufferAttribute(arr, 3));
    zenithLine.geometry.computeBoundingSphere();
  }

  // Checkbox handlers (toggle visibility)
  function bindUI() {
    familySelect.addEventListener('change', populateConstellationOptions);
    constellationSelect.addEventListener('change', onConstellationChange);

    checkSphere.addEventListener('change', () => { if (sphereMesh) sphereMesh.visible = checkSphere.checked; });
    checkStars.addEventListener('change', () => { if (starsPoints) starsPoints.visible = checkStars.checked; });
    checkEquator.addEventListener('change', () => { if (equatorLine) equatorLine.visible = checkEquator.checked; });
    checkEcliptic.addEventListener('change', () => { if (eclipticLine) eclipticLine.visible = checkEcliptic.checked; });
    checkZenith.addEventListener('change', () => { if (zenithGroup) zenithGroup.visible = checkZenith.checked; });
    checkAries.addEventListener('change', () => { if (ariesPoint) ariesPoint.visible = checkAries.checked; });

    const onSlider = () => updateZenithFromSliders();
    sliderRA.addEventListener('input', onSlider);
    sliderDec.addEventListener('input', onSlider);
  }

  // Init everything
  buildPolarAxis();
  buildCelestialSphere();
  buildGreatCircles();
  buildAriesPoint();
  buildStars();
  bindUI();

  // Defaults for visibility
  sphereMesh.visible = checkSphere.checked;
  starsPoints.visible = checkStars.checked;
  equatorLine.visible = checkEquator.checked;
  eclipticLine.visible = checkEcliptic.checked;
  zenithGroup.visible = checkZenith.checked;
  ariesPoint.visible = checkAries.checked;

  // Populate dropdowns with default family "Zodiac" and default constellation "Cygnus"
  populateConstellationOptions();

  // Set initial zenith
  updateZenithFromSliders();

  // Initial size
  resize();

  // Animation loop
  function animate() {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
    labelRenderer.render(scene, camera);
  }
  animate();
</script>
</body>
</html>
