      <div id="plot-signal"></div>
      <div id="plot-spectrum"></div>
  </div>

</div>

<!-- Plotly.js CDN -->
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<script>
// Globals and constants
const fs = 32000;     // sampling frequency
const N  = 2048;      // number of samples (power of 2)
const f  = 1000;      // input frequency
const A  = 1;         // input amplitude

let normalizedSpectrum = null;  // stored single-sided amplitude spectrum
let currentScale = 'linear';    // 'linear' or 'decibel'
let timeVec = null;             // cache time vector

// DOM references
const sliderC1 = document.getElementById('slider-c1');
const sliderC2 = document.getElementById('slider-c2');
const c1Display = document.getElementById('c1-value-display');
const c2Display = document.getElementById('c2-value-display');
const resetC1 = document.getElementById('reset-c1');
const resetC2 = document.getElementById('reset-c2');

const checkboxClipping = document.getElementById('checkbox-clipping');
const levelGroup = document.getElementById('level-control-group');
const sliderLevel = document.getElementById('slider-level');
const levelDisplay = document.getElementById('level-value-display');

const btnLinear = document.getElementById('btn-linear');
const btnDecibel = document.getElementById('btn-decibel');

const plotSignalDiv = document.getElementById('plot-signal');
const plotSpectrumDiv = document.getElementById('plot-spectrum');

// Utility: log10 fallback
const log10 = Math.log10 ? Math.log10 : (x => Math.log(x) / Math.LN10);

// FFT implementation (in-place, radix-2, iterative)
function fft(real, imag) {
  const n = real.length;
  // Bit-reversal permutation
  let j = 0;
  for (let i = 0; i < n; i++) {
    if (i < j) {
      [real[i], real[j]] = [real[j], real[i]];
      [imag[i], imag[j]] = [imag[j], imag[i]];
    }
    let m = n >> 1;
    while (m >= 1 && j >= m) {
      j -= m;
      m >>= 1;
    }
    j += m;
  }
  // Danielson-Lanczos
  for (let step = 1; step < n; step <<= 1) {
    const jump = step << 1;
    const theta = -Math.PI / step;
    const wtemp_r = Math.sin(0.5 * theta);
    const wpr = -2.0 * wtemp_r * wtemp_r;
    const wpi = Math.sin(theta);
    let wr = 1.0, wi = 0.0;
    for (let m = 0; m < step; m++) {
      for (let i = m; i < n; i += jump) {
        const j = i + step;
        const tr = wr * real[j] - wi * imag[j];
        const ti = wr * imag[j] + wi * real[j];
        real[j] = real[i] - tr;
        imag[j] = imag[i] - ti;
        real[i] += tr;
        imag[i] += ti;
      }
      const wr_next = wr * wpr - wi * wpi + wr;
      const wi_next = wi * wpr + wr * wpi + wi;
      wr = wr_next;
      wi = wi_next;
    }
  }
}

// Generate (and cache) time vector
function getTimeVector() {
  if (timeVec && timeVec.length === N) return timeVec;
  timeVec = new Array(N);
  for (let i = 0; i < N; i++) timeVec[i] = i / fs;
  return timeVec;
}

// Main simulation update
function updateSimulation() {
  // Read control values
  const c1 = parseFloat(sliderC1.value);
  const c2 = parseFloat(sliderC2.value);
  const clippingEnabled = checkboxClipping.checked;
  const level = parseFloat(sliderLevel.value);

  // Update value displays
  c1Display.textContent = c1.toFixed(1);
  c2Display.textContent = c2.toFixed(1);
  levelDisplay.textContent = level.toFixed(2);

  const t = getTimeVector();

  // Generate input x(t) and nonlinear output y(t)
  const y = new Array(N);
  for (let i = 0; i < N; i++) {
    const x = A * Math.sin(2 * Math.PI * f * t[i]);
    let yi = x + c1 * (x * x) + c2 * (x * x * x);
    if (clippingEnabled) {
      yi = Math.max(-level, Math.min(level, yi));
    }
    y[i] = yi;
  }

  // Update Signal Plot (only up to t = 0.005 s)
  const tEnd = 0.005;
  let lastIdx = 0;
  while (lastIdx < N && t[lastIdx] <= tEnd) lastIdx++;
  const tSeg = t.slice(0, lastIdx);
  const ySeg = y.slice(0, lastIdx);

  Plotly.react(plotSignalDiv, [{
    x: tSeg,
    y: ySeg,
    type: 'scatter',
    mode: 'lines',
    line: { color: '#60a5fa', width: 2 }
  }], {
    title: { text: 'signal', font: { color: '#e5e7eb' } },
    xaxis: { title: 'time[s]', range: [0, 0.005], autorange: false, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' },
    yaxis: { title: '', range: [-2, 2], autorange: false, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' },
    paper_bgcolor: '#0b1220',
    plot_bgcolor: '#0b1220',
    margin: { l: 30, r: 20, b: 40, t: 40 }
  }, {displayModeBar: false});

  // Perform DFT via FFT
  const real = y.slice(); // copy
  const imag = new Array(N).fill(0);
  fft(real, imag);

  // Magnitude and single-sided normalization
  const half = N >> 1;
  const mag = new Array(half);
  for (let k = 0; k < half; k++) {
    const m = Math.hypot(real[k], imag[k]);
    mag[k] = (k === 0) ? (m / N) : (2 * m / N);
  }
  normalizedSpectrum = mag;

  // Update spectrum plot with current scale
  updateSpectrumScale();
}

// Update spectrum scale without recomputing FFT
function updateSpectrumScale() {
  if (!normalizedSpectrum) return;

  const half = normalizedSpectrum.length;
  const freq = new Array(half);
  for (let k = 0; k < half; k++) freq[k] = k * fs / N;

  let yData, yAxis;
  if (currentScale === 'linear') {
    yData = normalizedSpectrum.slice();
    const maxY = Math.max(1.1, Math.max(...yData) * 1.1);
    yAxis = { title: '', range: [0, maxY], autorange: false, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' };
  } else {
    const eps = 1e-12;
    yData = normalizedSpectrum.map(v => 20 * log10(Math.max(v, eps)));
    const minDB = -100;
    for (let i = 0; i < yData.length; i++) if (!isFinite(yData[i]) || yData[i] < minDB) yData[i] = minDB;
    yAxis = { title: '', range: [-100, 0], autorange: false, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' };
  }

  Plotly.react(plotSpectrumDiv, [{
    x: freq,
    y: yData,
    type: 'bar',
    marker: { color: '#34d399' },
    width: 5
  }], {
    title: { text: 'magnitude spectrum (DFT)', font: { color: '#e5e7eb' } },
    xaxis: { title: 'frequency[Hz]', range: [0, 8000], autorange: false, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' },
    yaxis: yAxis,
    paper_bgcolor: '#0b1220',
    plot_bgcolor: '#0b1220',
    margin: { l: 30, r: 20, b: 40, t: 40 },
    bargap: 0
  }, {displayModeBar: false});
}

// Event wiring
function wireEvents() {
  sliderC1.oninput = updateSimulation;
  sliderC2.oninput = updateSimulation;

  resetC1.onclick = () => { sliderC1.value = 0; updateSimulation(); };
  resetC2.onclick = () => { sliderC2.value = 0; updateSimulation(); };

  checkboxClipping.onchange = () => {
    levelGroup.style.display = checkboxClipping.checked ? 'flex' : 'none';
    updateSimulation();
  };
  sliderLevel.oninput = updateSimulation;

  btnLinear.onclick = () => {
    currentScale = 'linear';
    btnLinear.classList.add('active');
    btnDecibel.classList.remove('active');
    updateSpectrumScale();
  };
  btnDecibel.onclick = () => {
    currentScale = 'decibel';
    btnDecibel.classList.add('active');
    btnLinear.classList.remove('active');
    updateSpectrumScale();
  };
}

// Initial plot skeletons for faster first paint
function initPlots() {
  Plotly.newPlot(plotSignalDiv, [{
    x: [0], y: [0], type: 'scatter', mode: 'lines', line: { color: '#60a5fa', width: 2 }
  }], {
    title: { text: 'signal', font: { color: '#e5e7eb' } },
    xaxis: { title: 'time[s]', range: [0, 0.005], autorange: false, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' },
    yaxis: { title: '', range: [-2, 2], autorange: false, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' },
    paper_bgcolor: '#0b1220',
    plot_bgcolor: '#0b1220',
    margin: { l: 30, r: 20, b: 40, t: 40 }
  }, {displayModeBar: false});

  Plotly.newPlot(plotSpectrumDiv, [{
    x: [0], y: [0], type: 'bar', marker: { color: '#34d399' }, width: 5
  }], {
    title: { text: 'magnitude spectrum (DFT)', font: { color: '#e5e7eb' } },
    xaxis: { title: 'frequency[Hz]', range: [0, 8000], autorange: false, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' },
    yaxis: { title: '', autorange: true, gridcolor: '#233146', zerolinecolor: '#233146', color: '#cbd5e1' },
    paper_bgcolor: '#0b1220',
    plot_bgcolor: '#0b1220',
    margin: { l: 30, r: 20, b: 40, t: 40 }
  }, {displayModeBar: false});
}

// Initialize defaults and run
function init() {
  // Initial states per spec
  sliderC1.value = 0;
  sliderC2.value = 0;
  c1Display.textContent = '0';
  c2Display.textContent = '0';

  checkboxClipping.checked = false;
  levelGroup.style.display = 'none';

  sliderLevel.value = 0.7;
  levelDisplay.textContent = '0.7';

  btnLinear.classList.add('active');
  btnDecibel.classList.remove('active');
  currentScale = 'linear';

  wireEvents();
  initPlots();
  updateSimulation();
}

// Start
window.addEventListener('DOMContentLoaded', init);
</script>
</body>
</html>
