            <label for="checkbox-beam-top">beam top half</label>
          </div>
          <div class="check-item">
            <input id="checkbox-beam-bottom" type="checkbox" checked />
            <label for="checkbox-beam-bottom">beam bottom half</label>
          </div>
        </div>
        <div class="legend">
          <div><span class="swatch red"></span>injected</div>
          <div><span class="swatch green"></span>refracted</div>
          <div><span class="swatch blue"></span>reflected</div>
          <div><span class="swatch black"></span>ejected</div>
        </div>
      </div>

      <div class="footer-note">
        Tip: Toggle “normals” and “intersections” to inspect Snell’s law and reflection geometry at each interaction point.
      </div>
    </div>

    <div id="canvas-container"></div>
  </div>

  <!-- p5.js CDN -->
  <script src="https://cdn.jsdelivr.net/npm/p5@1.9.2/lib/p5.min.js"></script>

  <script>
    // ---------- UI State ----------
    const ui = {
      sliders: {
        rayCount: document.getElementById('slider-ray-count'),
        beamWidth: document.getElementById('slider-beam-width'),
        incidentAngle: document.getElementById('slider-incident-angle'),
        refractionRatio: document.getElementById('slider-refraction-ratio'),
      },
      sliderVals: {
        rayCount: document.getElementById('val-ray-count'),
        beamWidth: document.getElementById('val-beam-width'),
        incidentAngle: document.getElementById('val-incident-angle'),
        refractionRatio: document.getElementById('val-refraction-ratio'),
      },
      checks: {
        injected: document.getElementById('checkbox-injected'),
        refracted: document.getElementById('checkbox-refracted'),
        reflected: document.getElementById('checkbox-reflected'),
        ejected: document.getElementById('checkbox-ejected'),
        normals: document.getElementById('checkbox-normals'),
        intersections: document.getElementById('checkbox-intersections'),
        beamTop: document.getElementById('checkbox-beam-top'),
        beamBottom: document.getElementById('checkbox-beam-bottom'),
      }
    };

    function updateSliderDisplays() {
      ui.sliderVals.rayCount.textContent = ui.sliders.rayCount.value;
      ui.sliderVals.beamWidth.textContent = (+ui.sliders.beamWidth.value).toFixed(2);
      ui.sliderVals.incidentAngle.textContent = ui.sliders.incidentAngle.value;
      ui.sliderVals.refractionRatio.textContent = (+ui.sliders.refractionRatio.value).toFixed(2);
    }
    Object.values(ui.sliders).forEach(el => el.addEventListener('input', updateSliderDisplays));
    updateSliderDisplays();

    // ---------- p5 Sketch ----------
    let cnv, scaleFactor, worldHalfSpan = 2.5; // world coordinates visible in [-2.5, 2.5]
    function setup() {
      const parent = document.getElementById('canvas-container');
      const {width, height} = parent.getBoundingClientRect();
      cnv = createCanvas(Math.max(200, width), Math.max(200, height));
      cnv.parent('canvas-container');
      cnv.elt.id = 'p5-canvas';
      pixelDensity(1);
      windowResized();
    }

    function windowResized() {
      const parent = document.getElementById('canvas-container');
      const rect = parent.getBoundingClientRect();
      resizeCanvas(Math.max(200, rect.width), Math.max(200, rect.height));
      scaleFactor = Math.min(width, height) / (2 * worldHalfSpan); // pixels per world unit
    }

    // ---------- Vector helpers ----------
    const EPS = 1e-6;
    function V(x,y){ return {x:x,y:y}; }
    function add(a,b){ return {x:a.x+b.x, y:a.y+b.y}; }
    function sub(a,b){ return {x:a.x-b.x, y:a.y-b.y}; }
    function mul(a,s){ return {x:a.x*s, y:a.y*s}; }
    function dot(a,b){ return a.x*b.x + a.y*b.y; }
    function len(a){ return Math.hypot(a.x, a.y); }
    function norm(v){ const l = len(v); return l>0 ? {x:v.x/l, y:v.y/l} : {x:0,y:0}; }
    function reflect(I, N){ // I, N normalized
      const d = dot(I, N);
      return norm(sub(I, mul(N, 2*d)));
    }
    function clamp(x, a, b){ return Math.max(a, Math.min(b, x)); }

    // Vector Snell's law refraction (LearnOpenGL-style)
    function refractVec(I, N, n1, n2) {
      // I, N normalized
      let cosi = clamp(dot(I, N), -1, 1);
      let etai = n1, etat = n2;
      let n = V(N.x, N.y);
      if (cosi > 0) { // inside the surface
        const tmp = etai; etai = etat; etat = tmp;
        n = mul(n, -1);
      }
      const eta = etai / etat;
      const k = 1 - eta*eta * (1 - cosi*cosi);
      if (k < 0) {
        return null; // total internal reflection
      } else {
        const T = add(mul(I, eta), mul(n, eta*cosi - Math.sqrt(k)));
        return norm(T);
      }
    }

    function rayCircleIntersection(P, D, r=1, tMin=EPS) {
      // Solve |P + t D|^2 = r^2 for t >= tMin, D normalized
      const B = 2 * dot(P, D);
      const C = dot(P,P) - r*r;
      const disc = B*B - 4*C;
      if (disc < 0) return null;
      const sq = Math.sqrt(disc);
      const t1 = (-B - sq)/2;
      const t2 = (-B + sq)/2;
      let t = null;
      if (t1 >= tMin && t2 >= tMin) t = Math.min(t1, t2);
      else if (t1 >= tMin) t = t1;
      else if (t2 >= tMin) t = t2;
      return t;
    }

    function lineBoxInterval(P, D, L) {
      // Returns [tmin, tmax] for line P + t D inside the box [-L, L]^2
      let tmin = -Infinity, tmax = Infinity;
      // X slabs
      if (Math.abs(D.x) < EPS) {
        if (Math.abs(P.x) > L) return null;
      } else {
        const tx1 = (-L - P.x) / D.x;
        const tx2 = ( L - P.x) / D.x;
        const tlo = Math.min(tx1, tx2);
        const thi = Math.max(tx1, tx2);
        tmin = Math.max(tmin, tlo);
        tmax = Math.min(tmax, thi);
      }
      // Y slabs
      if (Math.abs(D.y) < EPS) {
        if (Math.abs(P.y) > L) return null;
      } else {
        const ty1 = (-L - P.y) / D.y;
        const ty2 = ( L - P.y) / D.y;
        const tlo = Math.min(ty1, ty2);
        const thi = Math.max(ty1, ty2);
        tmin = Math.max(tmin, tlo);
        tmax = Math.min(tmax, thi);
      }
      if (tmin > tmax) return null;
      return [tmin, tmax];
    }

    function worldToScreen(pt) {
      return {
        x: width/2 + pt.x * scaleFactor,
        y: height/2 - pt.y * scaleFactor
      };
    }

    // ---------- Drawing helpers ----------
    function beginWorld() {
      push();
      translate(width/2, height/2);
      scale(scaleFactor, -scaleFactor); // world units -> pixels, Y up
    }
    function endWorld() { pop(); }

    function drawAxes() {
      beginWorld();
      stroke(180);
      strokeWeight(1);
      // axes
      line(-worldHalfSpan, 0, worldHalfSpan, 0);
      line(0, -worldHalfSpan, 0, worldHalfSpan);
      // ticks at -2, -1, 1, 2
      const ticks = [-2, -1, 1, 2];
      const tl = 0.05;
      for (const t of ticks) {
        // x-axis ticks (vertical tick marks)
        line(t, -tl, t, tl);
        // y-axis ticks (horizontal tick marks)
        line(-tl, t, tl, t);
      }
      endWorld();
      // labels in screen space
      textAlign(CENTER, TOP);
      textSize(12);
      fill(80);
      noStroke();
      for (const t of [-2, -1, 1, 2]) {
        const p = worldToScreen({x: t, y: 0});
        text(t.toString(), p.x, p.y + 6);
      }
      textAlign(LEFT, CENTER);
      for (const t of [-2, -1, 1, 2]) {
        const p = worldToScreen({x: 0, y: t});
        text(t.toString(), p.x + 6, p.y);
      }
    }

    function drawSphere() {
      beginWorld();
      noFill();
      stroke(0);
      strokeWeight(1.2);
      circle(0, 0, 2); // radius 1 -> diameter 2 in world units
      endWorld();
    }

    function drawWorldLine(a, b, col, w=1.5) {
      beginWorld();
      stroke(col);
      strokeWeight(w);
      line(a.x, a.y, b.x, b.y);
      endWorld();
    }

    function drawIntersection(pt, col) {
      beginWorld();
      noStroke();
      fill(col);
      const r = 0.03; // world radius
      circle(pt.x, pt.y, 2*r);
      endWorld();
    }

    function drawNormal(pt) {
      const n = norm(pt); // outward normal on unit circle
      const L = 0.18; // world length of normal
      const a = pt;
      const b = add(pt, mul(n, L));
      beginWorld();
      const ctx = drawingContext;
      ctx.save();
      ctx.setLineDash([6, 6]); // dashed in pixels
      stroke(40, 40, 40, 200);
      strokeWeight(1);
      line(a.x, a.y, b.x, b.y);
      ctx.setLineDash([]);
      ctx.restore();
      endWorld();
    }

    // ---------- Main draw ----------
    function draw() {
      background(255);

      // Read current UI state
      const N = parseInt(ui.sliders.rayCount.value, 10);
      const w = parseFloat(ui.sliders.beamWidth.value);            // beam width
      const angleDeg = parseFloat(ui.sliders.incidentAngle.value); // degrees
      const nRatio = parseFloat(ui.sliders.refractionRatio.value); // n_sphere / n_env

      const showInjected = ui.checks.injected.checked;
      const showRefracted = ui.checks.refracted.checked;
      const showReflected = ui.checks.reflected.checked;
      const showEjected = ui.checks.ejected.checked;
      const showNormals = ui.checks.normals.checked;
      const showIntersections = ui.checks.intersections.checked;
      const showTop = ui.checks.beamTop.checked;
      const showBottom = ui.checks.beamBottom.checked;

      drawAxes();
      drawSphere();

      // Beam direction and its perpendicular (top half is along +perp)
      const a = angleDeg * Math.PI / 180;
      const u = V(Math.cos(a), Math.sin(a));        // direction of travel
      const nperp = V(-Math.sin(a), Math.cos(a));   // +nperp is "top" relative to beam axis

      // Compute offsets s across [-w/2, w/2]
      const offsets = [];
      if (N === 1) {
        offsets.push(0);
      } else {
        for (let i = 0; i < N; i++) {
          const t = i / (N - 1);
          const s = -w/2 + t * w; // from -w/2 to +w/2
          offsets.push(s);
        }
      }

      // Colors
      const COL_RED = color(255, 0, 0);
      const COL_GREEN = color(0, 255, 0);
      const COL_BLUE = color(0, 0, 255);
      const COL_BLACK = color(0, 0, 0);

      // Bounding box for view in world units
      const L = worldHalfSpan;

      for (let i = 0; i < offsets.length; i++) {
        const s = offsets[i];

        // Beam halves filtering
        if (s > EPS && !showTop) continue;
        if (s < -EPS && !showBottom) continue;
        if (Math.abs(s) <= EPS && (!showTop && !showBottom)) continue; // central ray only if either half is shown

        // Initial ray: r(t) = P0 + t * u, P0 = s * nperp
        const P0 = mul(nperp, s);
        const D0 = u; // normalized

        // First intersection with unit circle
        const t1 = rayCircleIntersection(P0, D0, 1, 0);
        if (t1 === null) continue; // shouldn't happen for |s| <= 1

        const I1 = add(P0, mul(D0, t1)); // first hit point on circle
        const N1 = norm(I1);             // outward normal

        // Draw injected segment from box boundary to I1
        if (showInjected) {
          const rng = lineBoxInterval(P0, D0, L);
          if (rng) {
            const tEnter = rng[0]; // entering the box at smaller t
            const tEnd = Math.min(t1, rng[1]);
            if (tEnter < tEnd) {
              const A = add(P0, mul(D0, tEnter));
              const B = add(P0, mul(D0, tEnd));
              drawWorldLine(A, B, COL_RED, 1.4);
            }
          }
        }
        if (showIntersections) drawIntersection(I1, COL_RED);
        if (showNormals) drawNormal(I1);

        // Refract into sphere (n1=1, n2=nRatio)
        const D1 = refractVec(D0, N1, 1.0, nRatio);
        if (!D1) {
          // No refraction (shouldn't occur from low to high index), skip to next ray
          continue;
        }

        // Second intersection (from inside to boundary)
        const t2 = rayCircleIntersection(I1, D1, 1, 1e-5);
        if (t2 === null) continue;
        const I2 = add(I1, mul(D1, t2));
        const N2 = norm(I2);

        // Draw refracted segment inside
        if (showRefracted) {
          drawWorldLine(I1, I2, COL_GREEN, 1.6);
        }
        if (showIntersections) drawIntersection(I2, COL_GREEN);
        if (showNormals) drawNormal(I2);

        // Reflection at second intersection
        const D2 = reflect(D1, N2);

        // Third intersection (reflected ray to boundary)
        const t3 = rayCircleIntersection(I2, D2, 1, 1e-5);
        if (t3 === null) continue;
        const I3 = add(I2, mul(D2, t3));
        const N3 = norm(I3);

        if (showReflected) {
          drawWorldLine(I2, I3, COL_BLUE, 1.6);
        }
        if (showIntersections) drawIntersection(I3, COL_BLUE);
        if (showNormals) drawNormal(I3);

        // Refract out (n1=nRatio, n2=1)
        const D3 = refractVec(D2, N3, nRatio, 1.0);
        if (D3 && showEjected) {
          const rngOut = lineBoxInterval(I3, D3, L);
          if (rngOut) {
            // Start at t=0 (on the boundary) to exiting point (the larger t)
            const tOut = Math.max(rngOut[0], rngOut[1]); // pick the far boundary (ensures non-negative)
            // Ensure positive parameter
            const tEnd = tOut > 0 ? tOut : Math.min(rngOut[0], rngOut[1]);
            if (tEnd > 0) {
              const O = add(I3, mul(D3, tEnd));
              drawWorldLine(I3, O, COL_BLACK, 1.4);
            }
          }
        }
      }

      // Overlay labels for axes
      push();
      noStroke();
      fill(80);
      textAlign(LEFT, TOP);
      textSize(12);
      const xlbl = worldToScreen({x: worldHalfSpan - 0.25, y: 0.08});
      text("x", xlbl.x, xlbl.y);
      const ylbl = worldToScreen({x: 0.08, y: worldHalfSpan - 0.25});
      text("y", ylbl.x, ylbl.y);
      pop();
    }
  </script>
</body>
</html>
