    <div id="step-controls">
      <!-- Step buttons will be generated dynamically -->
    </div>

    <button id="reset-button" title="Generate new graph">(+)</button>
  </div>

  <div id="visualization-container">
    <p id="status-text"></p>
    <div id="canvas-wrapper">
      <canvas id="main-canvas"></canvas>
    </div>
  </div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.2/p5.min.js" integrity="sha512-Qg4PLaQb84aXFa+0vWnDKYRt2lGWZotv0XawMDw8EjcjtqU+G6kUXZMEmxcEOrYdX5De6vZ0lLWQ3AHIY8DdZg==" crossorigin="anonymous" referrerpolicy="no-referrer"></script>
  <script>
    // Global state and data structures
    let settings = {
      size: 18, // total vertices (even), U size = V size = size/2
      maxDegreeLower: 3
    };

    let graph = {
      U: 0, // size of partition U (top)
      V: 0, // size of partition V (bottom)
      adjU: [], // adjU[u] = [v, ...]
      adjV: []  // adjV[v] = [u, ...]
    };

    let pairU = []; // pairU[u] = v or -1
    let pairV = []; // pairV[v] = u or -1

    let history = []; // array of state snapshots
    let currentStep = 0;

    // Utility: set active button in a group
    function setActiveInGroup(containerId, buttonId) {
      const container = document.getElementById(containerId);
      if (!container) return;
      [...container.querySelectorAll('button')].forEach(btn => {
        if (btn.id === buttonId) btn.classList.add('active');
        else btn.classList.remove('active');
      });
    }

    // Build step buttons dynamically
    function buildStepControls() {
      const container = document.getElementById('step-controls');
      container.innerHTML = '';
      for (let i = 0; i < history.length; i++) {
        const btn = document.createElement('button');
        btn.id = `step-${i+1}`;
        btn.textContent = `${i+1}`;
        btn.title = `Go to step ${i+1}`;
        btn.addEventListener('click', () => {
          goToStep(i);
        });
        container.appendChild(btn);
      }
      // Set default active
      if (history.length > 0) {
        setActiveInGroup('step-controls', 'step-1');
      }
    }

    // Go to a specific step index
    function goToStep(index) {
      if (index < 0 || index >= history.length) return;
      currentStep = index;
      setActiveInGroup('step-controls', `step-${index+1}`);
      updateStatusText();
      if (p5instance) p5instance.redraw();
    }

    // Random integer in [min, max]
    function randInt(min, max) {
      return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    // Shuffle array (Fisher-Yates)
    function shuffle(arr) {
      for (let i = arr.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [arr[i], arr[j]] = [arr[j], arr[i]];
      }
      return arr;
    }

    // Generate a random bipartite graph with constraints:
    // - U size = V size = m
    // - Each V (lower partition) has degree in [1, maxDegreeLower]
    function generateGraph(n, maxDegreeLower) {
      const m = Math.floor(n / 2);
      graph.U = m;
      graph.V = m;
      graph.adjU = Array.from({ length: m }, () => []);
      graph.adjV = Array.from({ length: m }, () => []);
      const maxDeg = Math.min(maxDegreeLower, m);

      // For each v in V, choose deg unique neighbors in U
      for (let v = 0; v < m; v++) {
        const deg = randInt(1, maxDeg);
        const candidates = shuffle([...Array(m).keys()]); // 0..m-1
        let chosen = 0;
        let idx = 0;
        const used = new Set();
        while (chosen < deg && idx < candidates.length) {
          const u = candidates[idx++];
          if (used.has(u)) continue;
          used.add(u);
          graph.adjU[u].push(v);
          graph.adjV[v].push(u);
          chosen++;
        }
        // If for some weird reason chosen < deg (shouldn't happen), it's acceptable;
        // we ensured at least 1 neighbor since m>=1.
      }

      // Optional: deduplicate (just in case)
      graph.adjU = graph.adjU.map(arr => [...new Set(arr)]);
      graph.adjV = graph.adjV.map(arr => [...new Set(arr)]);
    }

    // Compute minimum vertex cover using Konig's theorem from current matching
    // Z = vertices reachable from all free U via alternating BFS (unmatched from U, matched from V)
    // min vertex cover = (U \ Z_U) U (V ∩ Z_V)
    function computeVertexCover() {
      const m = graph.U;
      const visitedU = Array(m).fill(false);
      const visitedV = Array(m).fill(false);

      // Start BFS from all free U
      const queue = [];
      for (let u = 0; u < m; u++) {
        if (pairU[u] === -1) {
          visitedU[u] = true;
          queue.push({ part: 'U', idx: u });
        }
      }

      while (queue.length > 0) {
        const node = queue.shift();
        if (node.part === 'U') {
          const u = node.idx;
          for (const v of graph.adjU[u]) {
            // Only traverse unmatched edges U->V
            if (pairU[u] !== v && !visitedV[v]) {
              visitedV[v] = true;
              queue.push({ part: 'V', idx: v });
            }
          }
        } else {
          const v = node.idx;
          if (pairV[v] !== -1) {
            const u2 = pairV[v];
            if (!visitedU[u2]) {
              visitedU[u2] = true;
              queue.push({ part: 'U', idx: u2 });
            }
          }
        }
      }

      const coverU = visitedU.map(x => !x);
      const coverV = visitedV.map(x => x);
      return { U: coverU, V: coverV };
    }

    // Get matching pairs list from pairU
    function getMatchingPairs() {
      const pairs = [];
      for (let u = 0; u < graph.U; u++) {
        const v = pairU[u];
        if (v !== -1) pairs.push([u, v]);
      }
      return pairs;
    }

    function deepCopyBFSTree(tree) {
      // tree: { root, nodes: {key:{part,idx,depth}}, edges: [[parent,child], ...] }
      const nodesCopy = {};
      for (const k in tree.nodes) {
        const n = tree.nodes[k];
        nodesCopy[k] = { part: n.part, idx: n.idx, depth: n.depth };
      }
      const edgesCopy = tree.edges.map(e => [e[0], e[1]]);
      return { root: tree.root, nodes: nodesCopy, edges: edgesCopy };
    }

    function takeSnapshot(note, bfsTreeState = { root: null, nodes: {}, edges: [] }, highlightedPath = []) {
      const cover = computeVertexCover();
      const matchingPairs = getMatchingPairs();
      history.push({
        matching: matchingPairs,
        cover: cover,
        bfsTree: deepCopyBFSTree(bfsTreeState),
        highlightedPath: highlightedPath.map(e => ({ from: { part: e.from.part, idx: e.from.idx }, to: { part: e.to.part, idx: e.to.idx } })),
        rootActive: bfsTreeState.root || null,
        note: note || ''
      });
    }

    // BFS from a specific free U root to find an augmenting path.
    // Records history steps.
    // Returns true if augmented.
    function bfsAugmentFromRoot(uRoot) {
      const m = graph.U;
      const visitedU = Array(m).fill(false);
      const visitedV = Array(m).fill(false);
      const parent = {}; // key -> parentKey
      const queue = [];

      const keyU = (u) => `U${u}`;
      const keyV = (v) => `V${v}`;

      const bfsTree = { root: keyU(uRoot), nodes: {}, edges: [] };
      bfsTree.nodes[keyU(uRoot)] = { part: 'U', idx: uRoot, depth: 0 };

      visitedU[uRoot] = true;
      queue.push({ part: 'U', idx: uRoot, key: keyU(uRoot), depth: 0 });

      takeSnapshot(`Start BFS from U${uRoot+1}`, bfsTree, []);

      let found = false;
      let endKey = null;

      while (queue.length > 0 && !found) {
        const node = queue.shift();
        if (node.part === 'U') {
          const u = node.idx;
          // Explore unmatched edges U->V
          for (const v of graph.adjU[u]) {
            if (pairU[u] !== v && !visitedV[v]) {
              visitedV[v] = true;
              const childKey = keyV(v);
              parent[childKey] = node.key;
              bfsTree.nodes[childKey] = { part: 'V', idx: v, depth: node.depth + 1 };
              bfsTree.edges.push([node.key, childKey]);
              queue.push({ part: 'V', idx: v, key: childKey, depth: node.depth + 1 });

              takeSnapshot(`Discovered V${v+1} from U${u+1} via unmatched edge`, bfsTree, []);

              if (pairV[v] === -1) {
                // Found free V: augmenting path
                found = true;
                endKey = childKey;
                break;
              }
            }
          }
        } else {
          const v = node.idx;
          // If V is matched, follow matched edge back to U
          const matchedU = pairV[v];
          if (matchedU !== -1 && !visitedU[matchedU]) {
            visitedU[matchedU] = true;
            const childKey = keyU(matchedU);
            parent[childKey] = node.key;
            bfsTree.nodes[childKey] = { part: 'U', idx: matchedU, depth: node.depth + 1 };
            bfsTree.edges.push([node.key, childKey]);
            queue.push({ part: 'U', idx: matchedU, key: childKey, depth: node.depth + 1 });

            takeSnapshot(`Follow matched edge to U${matchedU+1} from V${v+1}`, bfsTree, []);
          }
        }
      }

      if (found && endKey) {
        // Reconstruct path (U - V - U - ... - V_free)
        const pathNodes = [];
        let curKey = endKey;
        while (curKey !== undefined) {
          pathNodes.push(curKey);
          curKey = parent[curKey];
        }
        // pathNodes currently [Vfree, ..., Uroot]; reverse to [Uroot, ..., Vfree]
        pathNodes.reverse();

        const highlightedPath = [];
        for (let i = 0; i < pathNodes.length - 1; i++) {
          const a = pathNodes[i];
          const b = pathNodes[i + 1];
          const partA = a[0], partB = b[0];
          const idxA = parseInt(a.slice(1), 10);
          const idxB = parseInt(b.slice(1), 10);
          highlightedPath.push({
            from: { part: partA, idx: idxA },
            to: { part: partB, idx: idxB }
          });
        }

        takeSnapshot(`Augmenting path found (length ${highlightedPath.length} edges)`, bfsTree, highlightedPath);

        // Augment along the path
        // Path starts with U and ends with V. Flip along U-V edges in order.
        for (let i = 0; i < pathNodes.length - 1; i += 2) {
          // Pair (U, V)
          const uKey = pathNodes[i];
          const vKey = pathNodes[i + 1];
          const u = parseInt(uKey.slice(1), 10);
          const v = parseInt(vKey.slice(1), 10);
          pairU[u] = v;
          pairV[v] = u;
        }

        takeSnapshot(`Matching updated by augmentation`, bfsTree, []);
        return true;
      } else {
        takeSnapshot(`No augmenting path from U${uRoot+1}`, bfsTree, []);
        return false;
      }
    }

    // Kuhn's algorithm (repeated BFS-based augmenting paths, one root at a time) with history capture
    function runAugmentingPathsWithHistory() {
      const m = graph.U;
      pairU = Array(m).fill(-1);
      pairV = Array(m).fill(-1);
      history = [];

      takeSnapshot('Initial state (empty matching)', { root: null, nodes: {}, edges: [] }, []);

      let improved = true;
      let guard = 0; // safety to avoid infinite loops
      while (improved && guard < m * m + 50) {
        guard++;
        improved = false;
        for (let u = 0; u < m; u++) {
          if (pairU[u] === -1) {
            const aug = bfsAugmentFromRoot(u);
            if (aug) improved = true;
          }
        }
      }

      takeSnapshot('Finished: no more augmenting paths', { root: null, nodes: {}, edges: [] }, []);
    }

    // Full regeneration (graph + algorithm + UI)
    function regenerateAll() {
      const n = settings.size % 2 === 0 ? settings.size : settings.size - 1;
      settings.size = n; // enforce even
      generateGraph(n, settings.maxDegreeLower);
      runAugmentingPathsWithHistory();
      buildStepControls();
      currentStep = 0;
      setActiveInGroup('step-controls', 'step-1');
      updateStatusText();
      if (p5instance) {
        p5instance.resizeCanvasToParent();
        p5instance.redraw();
      }
    }

    function updateStatusText() {
      if (!history.length) {
        document.getElementById('status-text').innerText = '';
        return;
      }
      const state = history[currentStep];
      const matchSize = state.matching.length;
      const coverUCount = state.cover.U.reduce((a, b) => a + (b ? 1 : 0), 0);
      const coverVCount = state.cover.V.reduce((a, b) => a + (b ? 1 : 0), 0);
      const coverTotal = coverUCount + coverVCount;
      const stepText = `Step ${currentStep + 1} / ${history.length} — Matching size: ${matchSize}; Covered: ${coverTotal} (U: ${coverUCount}, V: ${coverVCount}).`;
      document.getElementById('status-text').innerText = `${stepText} ${state.note}`;
    }

    // UI event setup
    function setupControls() {
      // Size controls
      const sizeButtons = document.querySelectorAll('#size-controls button');
      sizeButtons.forEach(btn => {
        btn.addEventListener('click', () => {
          const val = parseInt(btn.id.split('-')[1], 10);
          settings.size = val;
          setActiveInGroup('size-controls', btn.id);
          regenerateAll();
        });
      });

      // Degree controls
      const degButtons = document.querySelectorAll('#degree-controls button');
      degButtons.forEach(btn => {
        btn.addEventListener('click', () => {
          const val = parseInt(btn.id.split('-')[1], 10);
          settings.maxDegreeLower = val;
          setActiveInGroup('degree-controls', btn.id);
          regenerateAll();
        });
      });

      // Reset button
      document.getElementById('reset-button').addEventListener('click', () => {
        regenerateAll();
      });
    }

    // p5.js sketch
    let p5instance = null;
    function createSketch() {
      const sketch = (p) => {
        let cw = 800, ch = 600;

        p.preload = function() {}

        // Helper to resize canvas to wrapper
        p.resizeCanvasToParent = function() {
          const wrapper = document.getElementById('canvas-wrapper');
          if (!wrapper) return;
          const w = wrapper.clientWidth;
          const h = wrapper.clientHeight;
          cw = Math.max(400, w);
          ch = Math.max(360, h);
          p.resizeCanvas(cw, ch);
        }

        p.setup = function() {
          // Remove existing placeholder canvas if present
          const old = document.getElementById('main-canvas');
          if (old) old.remove();
          const wrapper = document.getElementById('canvas-wrapper');
          const w = wrapper.clientWidth;
          const h = wrapper.clientHeight;
          cw = Math.max(400, w);
          ch = Math.max(360, h);
          const cnv = p.createCanvas(cw, ch);
          cnv.parent('canvas-wrapper');
          cnv.id('main-canvas');
          p.pixelDensity(1);
          p.noLoop();
        };

        p.windowResized = function() {
          p.resizeCanvasToParent();
          p.redraw();
        }

        function drawGridBg() {
          p.push();
          p.noFill();
          p.stroke(240);
          p.strokeWeight(1);
          for (let x = 0; x < cw; x += 20) {
            p.line(x, 0, x, ch);
          }
          for (let y = 0; y < ch; y += 20) {
            p.line(0, y, cw, y);
          }
          p.pop();
        }

        function drawBipartiteGraph(state) {
          const m = graph.U;
          if (m <= 0) return;

          // Areas
          const leftW = Math.floor(cw * 0.4);
          const rightX = leftW;
          const rightW = cw - leftW;
          const topMargin = 60;
          const bottomMargin = 60;

          const yU = topMargin + 40;
          const yV = ch - bottomMargin - 40;

          const gapX = rightW / (m + 1);

          const posU = [];
          const posV = [];
          for (let i = 0; i < m; i++) {
            posU[i] = { x: rightX + gapX * (i + 1), y: yU };
            posV[i] = { x: rightX + gapX * (i + 1), y: yV };
          }

          // Draw all edges (default)
          p.push();
          p.stroke('#808080');
          p.strokeWeight(1.5);
          for (let u = 0; u < m; u++) {
            for (const v of graph.adjU[u]) {
              const a = posU[u], b = posV[v];
              p.line(a.x, a.y, b.x, b.y);
            }
          }
          p.pop();

          // Draw matching edges (thick, light blue)
          p.push();
          p.stroke('#A0A0FF');
          p.strokeWeight(4);
          for (const [u, v] of state.matching) {
            const a = posU[u], b = posV[v];
            p.line(a.x, a.y, b.x, b.y);
          }
          p.pop();

          // Draw highlighted path (medium, red)
          if (state.highlightedPath && state.highlightedPath.length > 0) {
            p.push();
            p.stroke('#FF0000');
            p.strokeWeight(3);
            for (const e of state.highlightedPath) {
              const from = e.from.part === 'U' ? posU[e.from.idx] : posV[e.from.idx];
              const to = e.to.part === 'U' ? posU[e.to.idx] : posV[e.to.idx];
              p.line(from.x, from.y, to.x, to.y);
            }
            p.pop();
          }

          // Draw vertices
          const radius = Math.max(10, Math.min(18, (rightW / (m + 1)) * 0.25));
          const coverU = state.cover.U;
          const coverV = state.cover.V;
          const rootKey = state.rootActive;
          let rootU = -1;
          if (rootKey && rootKey[0] === 'U') rootU = parseInt(rootKey.slice(1), 10);

          // U vertices (top row)
          for (let u = 0; u < m; u++) {
            const pos = posU[u];
            let fillCol = '#333333';
            if (u === rootU) {
              fillCol = '#FF0000'; // root in red
            } else if (coverU[u]) {
              fillCol = '#00C000'; // in cover
            }
            p.push();
            p.stroke('#000000');
            p.fill(fillCol);
            p.ellipse(pos.x, pos.y, radius * 2, radius * 2);
            p.pop();

            // Label (1..m)
            p.push();
            p.noStroke();
            p.fill(255);
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(12);
            p.text(`${u + 1}`, pos.x, pos.y);
            p.pop();
          }

          // V vertices (bottom row)
          for (let v = 0; v < m; v++) {
            const pos = posV[v];
            let fillCol = '#333333';
            if (coverV[v]) {
              fillCol = '#00C000';
            }
            p.push();
            p.stroke('#000000');
            p.fill(fillCol);
            p.ellipse(pos.x, pos.y, radius * 2, radius * 2);
            p.pop();

            // Label (1..m)
            p.push();
            p.noStroke();
            p.fill(255);
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(12);
            p.text(`${v + 1}`, pos.x, pos.y);
            p.pop();
          }

          // Titles for partitions
          p.push();
          p.noStroke();
          p.fill('#333');
          p.textAlign(p.LEFT, p.TOP);
          p.textSize(14);
          p.text('Bipartite Graph — U (top) and V (bottom)', rightX + 10, 10);
          p.pop();
        }

        function drawBFSTree(state) {
          // Layout in left side of canvas
          const areaX = 0;
          const areaW = Math.floor(cw * 0.4);
          const areaH = ch;
          const marginL = 20, marginR = 20, marginT = 40, marginB = 40;
          const innerW = areaW - marginL - marginR;
          const innerH = areaH - marginT - marginB;

          // Background label
          p.push();
          p.noStroke();
          p.fill('#333');
          p.textSize(14);
          p.textAlign(p.LEFT, p.TOP);
          p.text('Augmenting Path BFS Tree', areaX + 10, 10);
          p.pop();

          const tree = state.bfsTree;
          const nodeKeys = Object.keys(tree.nodes);
          if (nodeKeys.length === 0) {
            // draw hint
            p.push();
            p.noStroke();
            p.fill('#999');
            p.textSize(12);
            p.textAlign(p.LEFT, p.TOP);
            p.text('No active BFS tree', areaX + 12, 34);
            p.pop();
            return;
          }

          // Compute depth groups
          const depths = {};
          let maxDepth = 0;
          for (const k of nodeKeys) {
            const d = tree.nodes[k].depth || 0;
            if (!depths[d]) depths[d] = [];
            depths[d].push(k);
            if (d > maxDepth) maxDepth = d;
          }

          // Determine positions
          const pos = {};
          const nodeSize = 24;
          const columns = maxDepth + 1;
          const colStep = columns > 1 ? innerW / (columns - 1) : 0;
          for (let d = 0; d <= maxDepth; d++) {
            const list = depths[d] || [];
            const count = list.length;
            for (let i = 0; i < count; i++) {
              const k = list[i];
              const x = areaX + marginL + (columns === 1 ? innerW / 2 : colStep * d);
              const y = areaH - marginB - innerH + (count === 1 ? innerH / 2 : (innerH * (i + 1)) / (count + 1));
              pos[k] = { x, y };
            }
          }

          // Draw edges (thin, red)
          p.push();
          p.stroke('#FF0000');
          p.strokeWeight(1.5);
          for (const [parent, child] of tree.edges) {
            const a = pos[parent], b = pos[child];
            if (!a || !b) continue;
            p.line(a.x, a.y, b.x, b.y);
          }
          p.pop();

          // Draw nodes (orange squares with black border)
          for (const k of nodeKeys) {
            const node = tree.nodes[k];
            const pt = pos[k];
            if (!pt) continue;
            p.push();
            p.stroke('#000000');
            p.fill('#FFA500');
            p.rectMode(p.CENTER);
            p.rect(pt.x, pt.y, nodeSize, nodeSize, 4);
            p.pop();

            // Label inside (U# or V#)
            const label = `${node.part}${node.idx + 1}`;
            p.push();
            p.noStroke();
            p.fill('#8080C0');
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(12);
            p.text(label, pt.x, pt.y);
            p.pop();
          }
        }

        p.draw = function() {
          p.clear();
          p.background('#ffffff');
          drawGridBg();

          if (!history.length) return;
          const state = history[currentStep];
          drawBFSTree(state);
          drawBipartiteGraph(state);
        };
      };

      p5instance = new p5(sketch);
    }

    document.addEventListener('DOMContentLoaded', () => {
      setupControls();
      createSketch();
      regenerateAll();
    });
  </script>
</body>
</html>
