          <div class="slider-group">
            <input type="range" id="slider-elements" min="1" max="20" step="1" value="10" aria-label="number of elements">
            <span id="display-elements" class="value-pill">+ 10</span>
          </div>
        </div>
      </div>

      <!-- Algorithm selection -->
      <div class="algorithms" aria-label="algorithm selection">
        <div class="alg-group">
          <span class="alg-title">\( O(n^2) \)</span>
          <div class="alg-options">
            <label class="inline" for="check-bubble">
              <input type="checkbox" id="check-bubble" checked>
              bubble
            </label>
            <label class="inline" for="check-selection">
              <input type="checkbox" id="check-selection" checked>
              selection
            </label>
            <label class="inline" for="check-insertion">
              <input type="checkbox" id="check-insertion" checked>
              insertion
            </label>
          </div>
        </div>

        <div class="alg-group">
          <span class="alg-title">\( O(n \log(n)) \)</span>
          <div class="alg-options">
            <label class="inline" for="check-merge">
              <input type="checkbox" id="check-merge">
              merge
            </label>
            <label class="inline" for="check-quick">
              <input type="checkbox" id="check-quick">
              quick
            </label>
            <label class="inline" for="check-heap">
              <input type="checkbox" id="check-heap">
              heap
            </label>
          </div>
        </div>

        <div class="alg-group">
          <span class="alg-title">\( O(n \cdot n!) \)</span>
          <div class="alg-options">
            <label class="inline" for="check-bogo">
              <input type="checkbox" id="check-bogo">
              bogo
            </label>
          </div>
        </div>
      </div>
    </div>

    <!-- Visualization Area -->
    <div id="plot-container" aria-label="time complexity plot"></div>
  </div>

  <script>
    // Helper: factorial
    function factorial(n) {
      let res = 1;
      for (let i = 2; i <= n; i++) res *= i;
      return res;
    }

    function generateX(n) {
      const x = new Array(n);
      for (let i = 0; i < n; i++) x[i] = i + 1;
      return x;
    }

    function computeSeries(x) {
      return {
        bubble: x.map(v => v * v),
        selection: x.map(v => 0.25 * v * v),
        insertion: x.map(v => v * factorial(v)), // also used by bogo
        merge: x.map(v => v * Math.log2(v)),
        quick: x.map(v => 1.2 * v * Math.log2(v)),
        heap: x.map(v => 1.5 * v * Math.log2(v))
      };
    }

    // Build traces according to the current state
    function buildTraces(n, visibility) {
      const x = generateX(n);
      const s = computeSeries(x);

      return [
        {
          type: 'scatter',
          mode: 'lines',
          name: 'bubble',
          x, y: s.bubble,
          line: { color: 'green', width: 5 },
          visible: visibility.bubble
        },
        {
          type: 'scatter',
          mode: 'lines',
          name: 'selection',
          x, y: s.selection,
          line: { color: 'orange', dash: 'dot', width: 3 },
          visible: visibility.selection
        },
        // Insertion/Bogo combined curve: legend shows "insertion"
        {
          type: 'scatter',
          mode: 'lines',
          name: 'insertion',
          x, y: s.insertion,
          line: { color: 'red', width: 2 },
          visible: visibility.insertionOrBogo
        },
        {
          type: 'scatter',
          mode: 'lines',
          name: 'merge',
          x, y: s.merge,
          line: { color: 'blue', width: 2 },
          visible: visibility.merge
        },
        {
          type: 'scatter',
          mode: 'lines',
          name: 'quick',
          x, y: s.quick,
          line: { color: 'purple', width: 2 },
          visible: visibility.quick
        },
        {
          type: 'scatter',
          mode: 'lines',
          name: 'heap',
          x, y: s.heap,
          line: { color: 'cyan', width: 2 },
          visible: visibility.heap
        }
      ];
    }

    function currentVisibility() {
      const bubble = document.getElementById('check-bubble').checked;
      const selection = document.getElementById('check-selection').checked;
      const insertion = document.getElementById('check-insertion').checked;
      const bogo = document.getElementById('check-bogo').checked;
      const merge = document.getElementById('check-merge').checked;
      const quick = document.getElementById('check-quick').checked;
      const heap = document.getElementById('check-heap').checked;
      return {
        bubble,
        selection,
        insertionOrBogo: insertion || bogo, // single red curve controlled by either checkbox
        merge,
        quick,
        heap
      };
    }

    function currentLayout() {
      const isLinear = document.getElementById('radio-scale-linear').checked;
      const isFixed = document.getElementById('radio-range-fixed').checked;

      const yType = isLinear ? 'linear' : 'log';

      // For fixed ranges:
      // - x-axis: [0, 20]
      // - y-axis:
      //   * linear: [0, 500]
      //   * log: in log axes, range is in log10 units and cannot include 0.
      //     Use [0, log10(500)] which corresponds to [1, 500] in data space.
      const xaxis = isFixed
        ? { title: 'number of elements', range: [0, 20], autorange: false }
        : { title: 'number of elements', autorange: true };

      const yaxis = isFixed
        ? (isLinear
          ? { title: 'operations', type: yType, range: [0, 500], autorange: false }
          : { title: 'operations', type: yType, range: [0, Math.log10(500)], autorange: false })
        : { title: 'operations', type: yType, autorange: true };

      return {
        title: { text: 'time complexity', x: 0.02 },
        xaxis,
        yaxis,
        margin: { t: 48, r: 18, b: 80, l: 58 },
        legend: {
          orientation: 'h',
          x: 0.5,
          xanchor: 'center',
          y: -0.2
        }
      };
    }

    function updateDisplay(n) {
      const display = document.getElementById('display-elements');
      display.textContent = `+ ${n}`;
    }

    function updatePlot() {
      const slider = document.getElementById('slider-elements');
      const n = parseInt(slider.value, 10);
      updateDisplay(n);

      const traces = buildTraces(n, currentVisibility());
      const layout = currentLayout();

      Plotly.react('plot-container', traces, layout, { responsive: true });
    }

    // Initialize
    window.addEventListener('DOMContentLoaded', () => {
      // Initial plot with defaults: n=10, fixed range, linear scale; bubble/selection/insertion visible
      updatePlot();

      // Attach events
      document.getElementById('slider-elements').addEventListener('input', updatePlot);

      document.getElementById('radio-scale-linear').addEventListener('change', updatePlot);
      document.getElementById('radio-scale-logarithmic').addEventListener('change', updatePlot);

      document.getElementById('radio-range-fixed').addEventListener('change', updatePlot);
      document.getElementById('radio-range-automatic').addEventListener('change', updatePlot);

      document.getElementById('check-bubble').addEventListener('change', updatePlot);
      document.getElementById('check-selection').addEventListener('change', updatePlot);
      document.getElementById('check-insertion').addEventListener('change', updatePlot);

      document.getElementById('check-merge').addEventListener('change', updatePlot);
      document.getElementById('check-quick').addEventListener('change', updatePlot);
      document.getElementById('check-heap').addEventListener('change', updatePlot);

      // "bogo" controls the same insertion curve; if either is checked, the red curve is visible
      document.getElementById('check-bogo').addEventListener('change', updatePlot);
    });
  </script>
</body>
</html>
