<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <title>Reflux Ratio vs Pinch Composition</title>
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
  <style>
    body {
      margin: 0;
      padding: 20px;
      font-family: Arial, Helvetica, sans-serif;
      background: #fafafa;
      color: #333;
    }
    #main-container {
      width: 550px;
      margin: 0 auto;
      background: #f0f0f0;
      border: 1px solid #ccc;
      border-radius: 6px;
      padding: 14px 14px 10px 14px;
      box-sizing: border-box;
    }
    #control-panel {
      display: flex;
      align-items: center;
      gap: 10px;
      margin-bottom: 10px;
      flex-wrap: wrap;
    }
    #control-panel label {
      font-size: 14px;
      white-space: nowrap;
    }
    #slider-xD {
      flex: 1 1 auto;
      min-width: 220px;
    }
    #label-xD {
      display: inline-block;
      min-width: 42px;
      text-align: right;
      padding: 2px 6px;
      background: #fff;
      border: 1px solid #ccc;
      border-radius: 4px;
    }
    #plot {
      width: 100%;
      height: 380px;
      border: 1px solid #ccc;
      border-radius: 4px;
      background: #fff;
    }
  </style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <label for="slider-xD">distillate composition</label>
      <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84" />
      <span id="label-xD">0.84</span>
    </div>
    <div id="plot"></div>
  </div>

  <script>
    // Core update function
    function updatePlot(xD) {
      const a = 9;
      const b = -0.6;

      const y = (x) => (a * x) / (1 + (a - 1) * x + b * x * (1 - x));
      const rFunc = (x, xD) => {
        const yx = y(x);
        const denom = yx - x;
        if (!isFinite(yx) || Math.abs(denom) < 1e-12) return NaN;
        return (xD - yx) / denom;
      };

      const x_vals = [];
      const r_vals = [];
      const dx = 0.001;
      const xMax = Math.max(Math.min(xD, 0.999), 0.001); // ensure within (0,1)

      for (let x = 0.001; x <= xMax; x += dx) {
        const r = rFunc(x, xD);
        if (isFinite(r) && r >= 0 && r < 5) {
          x_vals.push(x);
          r_vals.push(r);
        }
      }

      // Tangent pinch point detection: search for a local maximum in r(x)
      let pinch_x = [];
      let pinch_y = [];
      for (let i = 1; i < r_vals.length - 1; i++) {
        if (r_vals[i - 1] < r_vals[i] && r_vals[i] > r_vals[i + 1]) {
          pinch_x = [x_vals[i]];
          pinch_y = [r_vals[i]];
          break; // first local maximum
        }
      }

      const curveTrace = {
        x: x_vals,
        y: r_vals,
        mode: 'lines',
        type: 'scatter',
        line: { color: '#8B0000', width: 2.5 },
        hovertemplate: 'x=%{x:.3f}<br>r=%{y:.3f}<extra></extra>',
        name: 'Bifurcation Curve'
      };

      const pinchTrace = {
        x: pinch_x,
        y: pinch_y,
        mode: 'markers',
        type: 'scatter',
        marker: { color: 'royalblue', size: 10 },
        hovertemplate: pinch_x.length ? 'Pinch: x=%{x:.3f}, r=%{y:.3f}<extra></extra>' : '<extra></extra>',
        name: 'Tangent Pinch Point'
      };

      const layout = {
        xaxis: {
          range: [0, 1.0],
          gridcolor: '#e0e0e0',
          tick0: 0,
          dtick: 0.2,
          zeroline: false
        },
        yaxis: {
          range: [0, 3.0],
          gridcolor: '#e0e0e0',
          tick0: 0,
          dtick: 0.5,
          zeroline: false
        },
        showlegend: false,
        margin: { l: 40, r: 20, b: 40, t: 20 },
        plot_bgcolor: 'white',
        paper_bgcolor: 'white'
      };

      Plotly.react(document.getElementById('plot'), [curveTrace, pinchTrace], layout, {
        displayModeBar: false,
        responsive: true
      });
    }

    // Initialization and interactions
    window.addEventListener('DOMContentLoaded', () => {
      const slider = document.getElementById('slider-xD');
      const label = document.getElementById('label-xD');

      const initVal = parseFloat(slider.value);
      label.textContent = initVal.toFixed(2);
      updatePlot(initVal);

      slider.addEventListener('input', () => {
        const xD = parseFloat(slider.value);
        label.textContent = xD.toFixed(2);
        updatePlot(xD);
      });
    });
  </script>
</body>
</html>
