</style>
</head>
<body>
<div id="app">
  <div id="toolbar">
    <!-- Charges -->
    <button id="btn-charge-minus">-</button>
    <button id="btn-charge-plus">+</button>
    <button id="btn-charge-2">2</button>
    <!-- Elements -->
    <button id="btn-element-H">H</button>
    <button id="btn-element-B">B</button>
    <button id="btn-element-C">C</button>
    <button id="btn-element-N">N</button>
    <button id="btn-element-O">O</button>
    <button id="btn-element-F">F</button>
    <button id="btn-element-P">P</button>
    <button id="btn-element-S">S</button>
    <button id="btn-element-Cl">Cl</button>
    <button id="btn-element-Se">Se</button>
    <button id="btn-element-Br">Br</button>
    <button id="btn-element-I">I</button>
    <button id="btn-element-Li">Li</button>
    <button id="btn-element-Na">Na</button>
    <button id="btn-element-K">K</button>
    <button id="btn-element-Be">Be</button>
    <button id="btn-element-Mg">Mg</button>
    <button id="btn-element-Ca">Ca</button>
    <button id="btn-element-Cr">Cr</button>
    <button id="btn-element-Mn">Mn</button>
    <button id="btn-element-Fe">Fe</button>
    <button id="btn-element-Co">Co</button>
    <button id="btn-element-Cu">Cu</button>
    <button id="btn-element-Zn">Zn</button>
    <button id="btn-element-Al">Al</button>
    <button id="btn-element-Si">Si</button>
  </div>

  <div id="canvas-container">
    <!-- p5.js will create a single <canvas id="drawing-canvas"> here -->
  </div>

  <div id="footer-controls">
    <div class="footer-left">
      <button id="btn-undo">undo</button>
    </div>
    <div class="footer-center">
      <select id="select-structure" size="4">
        <option value="bond" selected>—</option>
        <option value="water">Water</option>
        <option value="cyclopropyl_ethane">Cyclopropyl-ethane</option>
        <option value="borazine">Borazine</option>
        <option value="isopropylamine">Isopropylamine</option>
      </select>
    </div>
    <div class="footer-right">
      <button id="btn-add">add</button>
    </div>
  </div>
</div>

<!-- p5.js rendering engine -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.min.js"></script>
<script>
  // Global state
  let sceneObjects = []; // {type, value, x, y, rotation}
  let selectedTool = null; // e.g., {type:'atom', value:'H'} or {type:'charge', value:'+'}
  let lastSelectedBtn = null;

  // Interaction state
  let activeObject = null;
  let interactionMode = null; // 'translate' | 'rotate' | null
  let dragOffset = {dx: 0, dy: 0};
  let initialMouseAngle = 0;
  let initialRotation = 0;

  // Hover/preview state
  let hover = {x: 0, y: 0, inside: false};

  // Initialize toolbar events
  document.addEventListener('DOMContentLoaded', () => {
    const charges = [
      { id: 'btn-charge-minus', type: 'charge', value: '-' },
      { id: 'btn-charge-plus', type: 'charge', value: '+' },
      { id: 'btn-charge-2', type: 'charge', value: '2' },
    ];
    const elements = [
      'H','B','C','N','O','F','P','S','Cl','Se','Br','I','Li','Na','K','Be','Mg','Ca','Cr','Mn','Fe','Co','Cu','Zn','Al','Si'
    ];

    charges.forEach(ch => {
      const btn = document.getElementById(ch.id);
      btn.addEventListener('click', () => {
        selectTool({ type: 'charge', value: ch.value }, btn);
      });
    });

    elements.forEach(sym => {
      const btn = document.getElementById('btn-element-' + sym);
      if (btn) {
        btn.addEventListener('click', () => {
          selectTool({ type: 'atom', value: sym }, btn);
        });
      }
    });

    document.getElementById('btn-undo').addEventListener('click', () => {
      if (sceneObjects.length > 0) {
        sceneObjects.pop();
      }
    });

    document.getElementById('btn-add').addEventListener('click', () => {
      const sel = document.getElementById('select-structure');
      const value = sel.value || 'bond';
      // Add structure at center of canvas
      const center = getCanvasCenter();
      sceneObjects.push({
        type: 'structure',
        value,
        x: center.x,
        y: center.y,
        rotation: 0
      });
    });

    // Allow Escape key to clear selected tool
    document.addEventListener('keydown', (e) => {
      if (e.key === 'Escape') {
        clearSelectedTool();
      }
    });
  });

  function selectTool(tool, buttonElement) {
    selectedTool = tool;
    if (lastSelectedBtn) lastSelectedBtn.classList.remove('selected');
    if (buttonElement) buttonElement.classList.add('selected');
    lastSelectedBtn = buttonElement;
  }

  function clearSelectedTool() {
    selectedTool = null;
    if (lastSelectedBtn) {
      lastSelectedBtn.classList.remove('selected');
      lastSelectedBtn = null;
    }
  }

  function getCanvasCenter() {
    return { x: width / 2, y: height / 2 };
  }

  // p5.js sketch
  function setup() {
    const container = document.getElementById('canvas-container');
    const w = container.clientWidth;
    const h = container.clientHeight;
    const cnv = createCanvas(w, h);
    cnv.parent('canvas-container');
    cnv.id('drawing-canvas');
    textFont('Helvetica, Arial, sans-serif');
    textAlign(CENTER, CENTER);
    angleMode(RADIANS);
  }

  function windowResized() {
    const container = document.getElementById('canvas-container');
    resizeCanvas(container.clientWidth, container.clientHeight);
  }

  function draw() {
    background(255);

    // Draw all scene objects
    for (let i = 0; i < sceneObjects.length; i++) {
      const obj = sceneObjects[i];
      push();
      translate(obj.x, obj.y);
      rotate(obj.rotation || 0);
      drawObject(obj);
      pop();
    }

    // Draw preview (ghost) if a tool is selected and mouse is over canvas
    if (selectedTool && hover.inside) {
      push();
      translate(hover.x, hover.y);
      noStroke();
      fill(0, 0, 0, 150);
      textSize(20);
      text(selectedTool.value, 0, 0);
      pop();
    }
  }

  function drawObject(obj) {
    if (obj.type === 'atom' || obj.type === 'charge') {
      drawAtomOrCharge(obj.value);
    } else if (obj.type === 'structure') {
      drawStructure(obj.value);
    }
  }

  function drawAtomOrCharge(symbol) {
    noStroke();
    fill(0);
    textSize(20);
    text(symbol, 0, 0);
  }

  function drawStructure(name) {
    stroke(0);
    strokeWeight(2);
    fill(0);
    textSize(16);
    if (name === 'water') {
      drawWater();
    } else if (name === 'cyclopropyl_ethane') {
      drawCyclopropylEthane();
    } else if (name === 'borazine') {
      drawBorazine();
    } else if (name === 'isopropylamine') {
      drawIsopropylAmine();
    } else if (name === 'bond') {
      drawBond();
    } else {
      // Fallback: label unknown structure
      noStroke();
      textSize(14);
      text(name, 0, 0);
    }
  }

  // Structure drawing helpers (relative to local 0,0)
  function drawBond() {
    stroke(0);
    strokeWeight(2.5);
    line(-30, 0, 30, 0);
  }

  function drawWater() {
    // O at center, H's above-left and above-right
    const O = {x: 0, y: 0};
    const H1 = {x: -28, y: -22};
    const H2 = {x: 28, y: -22};
    stroke(0);
    strokeWeight(2);
    line(O.x, O.y, H1.x, H1.y);
    line(O.x, O.y, H2.x, H2.y);
    noStroke();
    fill(0);
    textSize(18);
    text('O', O.x, O.y);
    text('H', H1.x, H1.y);
    text('H', H2.x, H2.y);
  }

  function drawCyclopropylEthane() {
    // Cyclopropyl ring (triangle) connected to ethane chain on the right
    const R = 26;
    const verts = [];
    for (let i = 0; i < 3; i++) {
      const ang = -HALF_PI + i * TWO_PI / 3;
      verts.push({ x: R * cos(ang), y: R * sin(ang) });
    }
    // Triangle
    stroke(0);
    strokeWeight(2);
    for (let i = 0; i < 3; i++) {
      const a = verts[i];
      const b = verts[(i + 1) % 3];
      line(a.x, a.y, b.x, b.y);
    }
    // Label the vertices as carbon
    noStroke();
    fill(0);
    textSize(14);
    verts.forEach(v => text('C', v.x, v.y));

    // Find rightmost vertex to attach ethane
    const rightV = verts.reduce((acc, v) => (v.x > acc.x ? v : acc), verts[0]);
    const attachStart = { x: rightV.x + 4, y: rightV.y };
    // Ethane chain: two carbons
    const c1 = { x: attachStart.x + 22, y: attachStart.y };
    const c2 = { x: c1.x + 24, y: c1.y };
    stroke(0);
    strokeWeight(2);
    line(rightV.x, rightV.y, c1.x, c1.y);
    line(c1.x, c1.y, c2.x, c2.y);
    noStroke();
    text('C', c1.x, c1.y);
    text('C', c2.x, c2.y);
  }

  function drawBorazine() {
    // Hexagon with alternating B and N
    const R = 34;
    const verts = [];
    for (let i = 0; i < 6; i++) {
      const ang = -HALF_PI + i * TWO_PI / 6;
      verts.push({ x: R * cos(ang), y: R * sin(ang) });
    }
    // Edges
    stroke(0);
    strokeWeight(2);
    for (let i = 0; i < 6; i++) {
      const a = verts[i];
      const b = verts[(i + 1) % 6];
      line(a.x, a.y, b.x, b.y);
    }
    // Alternate labels
    noStroke();
    fill(0);
    textSize(14);
    for (let i = 0; i < 6; i++) {
      const label = (i % 2 === 0) ? 'B' : 'N';
      text(label, verts[i].x, verts[i].y);
    }
  }

  function drawIsopropylAmine() {
    // Central carbon
    noStroke();
    fill(0);
    textSize(14);
    text('C', 0, 0);

    // Three substituents: left CH3, right CH3, top NH2
    const L = 28;
    const left = { x: -L, y: 0 };
    const right = { x: L, y: 0 };
    const up = { x: 0, y: -L };

    stroke(0);
    strokeWeight(2);
    line(0, 0, left.x, left.y);
    line(0, 0, right.x, right.y);
    line(0, 0, up.x, up.y);

    noStroke();
    text('CH3', left.x, left.y);
    text('CH3', right.x, right.y);
    text('NH2', up.x, up.y - 2);
  }

  // Helpers
  function withinCanvas() {
    return mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height;
  }

  function pickRadius(obj) {
    if (obj.type === 'atom' || obj.type === 'charge') return 20;
    return 50; // larger for structures
  }

  function mouseMoved() {
    hover.x = mouseX;
    hover.y = mouseY;
    hover.inside = withinCanvas();
  }

  function mousePressed() {
    if (!withinCanvas()) return;

    // If a tool is selected, place it and clear selection
    if (selectedTool) {
      sceneObjects.push({
        type: selectedTool.type,
        value: selectedTool.value,
        x: mouseX,
        y: mouseY,
        rotation: 0
      });
      clearSelectedTool();
      return;
    }

    // Otherwise, attempt to select an existing object (top-most first)
    for (let i = sceneObjects.length - 1; i >= 0; i--) {
      const obj = sceneObjects[i];
      const r = pickRadius(obj);
      const d = dist(mouseX, mouseY, obj.x, obj.y);
      if (d <= r) {
        activeObject = obj;
        if (d < 25) {
          // Rotate
          interactionMode = 'rotate';
          initialMouseAngle = atan2(mouseY - obj.y, mouseX - obj.x);
          initialRotation = obj.rotation || 0;
        } else {
          // Translate
          interactionMode = 'translate';
          dragOffset.dx = mouseX - obj.x;
          dragOffset.dy = mouseY - obj.y;
        }
        break;
      }
    }
  }

  function mouseDragged() {
    if (!withinCanvas()) return;
    if (!activeObject) return;

    if (interactionMode === 'translate') {
      activeObject.x = mouseX - dragOffset.dx;
      activeObject.y = mouseY - dragOffset.dy;
    } else if (interactionMode === 'rotate') {
      const currentAngle = atan2(mouseY - activeObject.y, mouseX - activeObject.x);
      activeObject.rotation = initialRotation + (currentAngle - initialMouseAngle);
    }
  }

  function mouseReleased() {
    activeObject = null;
    interactionMode = null;
  }
</script>
</body>
</html>
