      <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5" />
      <span class="value" id="value-time">2.50</span>
    </div>
    <div class="control-row">
      <label for="select-phase">initial phase \(\phi_0\)</label>
      <select id="select-phase">
        <option value="-3.141592653589793">-π</option>
        <option value="-2.0943951023931953">-2π/3</option>
        <option value="-1.5707963267948966" selected>-π/2</option>
        <option value="-1.0471975511965976">-π/3</option>
        <option value="0">0</option>
        <option value="1.0471975511965976">π/3</option>
        <option value="1.5707963267948966">π/2</option>
        <option value="2.0943951023931953">2π/3</option>
        <option value="3.141592653589793">π</option>
      </select>
    </div>
    <div class="legend" aria-hidden="true">
      <span><span class="dot dot-blue"></span>wave</span>
      <span><span class="dot dot-red"></span>velocity vector</span>
      <span><span class="dot dot-black"></span>acceleration vector</span>
    </div>
  </div>

  <!-- Formula Display -->
  <div id="formula-display"></div>

  <!-- Visualization Canvas -->
  <div id="canvas-container">
    <div class="canvas-wrapper">
      <!-- p5.js will insert a <canvas> here -->
    </div>
  </div>

  <script>
    // State variables
    let A = 1.5;       // amplitude
    let f = 0.3;       // frequency
    let lam = 4.5;     // wavelength
    let tVal = 2.5;    // time
    let phi0 = -Math.PI / 2; // initial phase

    let k = 2 * Math.PI / lam; // wavenumber
    let omega = 2 * Math.PI * f; // angular frequency

    // DOM elements
    const elAmp = document.getElementById('slider-amplitude');
    const elFreq = document.getElementById('slider-frequency');
    const elLam = document.getElementById('slider-wavelength');
    const elTime = document.getElementById('slider-time');
    const elPhase = document.getElementById('select-phase');

    const valAmp = document.getElementById('value-amplitude');
    const valFreq = document.getElementById('value-frequency');
    const valLam = document.getElementById('value-wavelength');
    const valTime = document.getElementById('value-time');

    const formulaDiv = document.getElementById('formula-display');

    function fmt(n, d=2) {
      return (+n).toFixed(d);
    }
    function isClose(a, b, eps = 1e-9) {
      return Math.abs(a - b) < eps;
    }

    function updateStateFromControls() {
      A = parseFloat(elAmp.value);
      f = parseFloat(elFreq.value);
      lam = parseFloat(elLam.value);
      tVal = parseFloat(elTime.value);
      phi0 = parseFloat(elPhase.value);

      k = 2 * Math.PI / lam;
      omega = 2 * Math.PI * f;

      // update display values
      valAmp.textContent = fmt(A, 2);
      valFreq.textContent = fmt(f, 2);
      valLam.textContent = fmt(lam, 2);
      valTime.textContent = fmt(tVal, 2);

      updateFormula();
      if (typeof redraw === 'function') {
        redraw(); // trigger p5 draw once
      }
    }

    function updateFormula() {
      const kf = fmt(k, 2);
      const wf = fmt(omega, 2);
      const Af = fmt(A, 2);
      const phif = fmt(phi0, 2);

      let latex = '';
      // Simplification Logic
      if (isClose(phi0, Math.PI/2)) {
        latex = `\\( y(x,t) = ${Af}\\,\\sin(${kf}\\,x - ${wf}\\,t) \\)`;
      } else if (isClose(phi0, -Math.PI/2)) {
        latex = `\\( y(x,t) = -${Af}\\,\\sin(${kf}\\,x - ${wf}\\,t) \\)`;
      } else if (isClose(phi0, Math.PI) || isClose(phi0, -Math.PI)) {
        latex = `\\( y(x,t) = -${Af}\\,\\cos(${kf}\\,x - ${wf}\\,t) \\)`;
      } else if (isClose(phi0, 0)) {
        latex = `\\( y(x,t) = ${Af}\\,\\cos(${kf}\\,x - ${wf}\\,t) \\)`;
      } else {
        latex = `\\( y(x,t) = ${Af}\\,\\cos(${wf}\\,t - ${kf}\\,x + ${phif}) \\)`;
      }

      formulaDiv.innerHTML = latex;
      if (window.MathJax && MathJax.typesetPromise) {
        MathJax.typesetPromise([formulaDiv]);
      }
    }

    // Attach events
    elAmp.addEventListener('input', updateStateFromControls);
    elFreq.addEventListener('input', updateStateFromControls);
    elLam.addEventListener('input', updateStateFromControls);
    elTime.addEventListener('input', updateStateFromControls);
    elPhase.addEventListener('change', updateStateFromControls);

    // p5.js sketch (global mode)
    let unitX = 35; // pixels per x unit
    let unitY = 35; // pixels per y unit
    let originX = 90; // px from left where (x=0)
    let centerY;      // computed from canvas height
    let rightLimitPx; // drawable x extent to the right
    let topYpx;       // drawable y extent upwards from x-axis
    let bottomYpx;    // drawable y extent downwards

    // Helpers to convert "world" relative (transformed space) coordinates to screen coords for text
    function relToScreen(xRel, yRel) {
      // After translate(originX, centerY) and scale(1, -1):
      return { x: originX + xRel, y: centerY - yRel };
    }

    function setup() {
      const container = document.querySelector('#canvas-container .canvas-wrapper');
      const w = Math.min(container.clientWidth - 16, 980);
      const h = Math.max(420, Math.min(640, Math.round(w * 0.55)));
      const cnv = createCanvas(w, h);
      cnv.parent(container);
      pixelDensity(window.devicePixelRatio || 1);

      centerY = height / 2;
      rightLimitPx = width - originX - 20;
      topYpx = (height - 10) - centerY;
      bottomYpx = centerY - 10;

      textFont('system-ui, -apple-system, Segoe UI, Roboto, Ubuntu, Cantarell, Noto Sans, Helvetica, Arial');
      textSize(12);
      noLoop();

      updateStateFromControls(); // initialize UI + first render
    }

    function windowResized() {
      // Make the canvas responsive to container resizing
      const container = document.querySelector('#canvas-container .canvas-wrapper');
      const w = Math.min(container.clientWidth - 16, 980);
      const h = Math.max(420, Math.min(640, Math.round(w * 0.55)));
      resizeCanvas(w, h);
      centerY = height / 2;
      rightLimitPx = width - originX - 20;
      topYpx = (height - 10) - centerY;
      bottomYpx = centerY - 10;
      redraw();
    }

    function drawAxes() {
      push();
      translate(originX, centerY);
      scale(1, -1);
      stroke(0);
      strokeWeight(1.5);
      // x-axis line
      line(0, 0, rightLimitPx, 0);
      // arrowhead for x
      push();
      translate(rightLimitPx, 0);
      fill(0);
      noStroke();
      const ah = 8;
      // triangle pointing right
      triangle(0, 0, -ah, 3.5, -ah, -3.5);
      pop();

      // y-axis line (draw both up and down so it looks centered)
      line(0, -bottomYpx, 0, topYpx);
      // arrowhead for y (up)
      push();
      translate(0, topYpx);
      fill(0);
      noStroke();
      const ahy = 8;
      triangle(0, 0, -3.5, -ahy, 3.5, -ahy);
      pop();
      pop();

      // Axis labels in screen coords
      noStroke();
      fill(0);
      textSize(13);
      // x label
      text('x', originX + rightLimitPx + 6, centerY - 6);
      // y label
      text('y', originX - 10, (height - 10) - 6);
    }

    function drawGridAndTicks() {
      push();
      translate(originX, centerY);
      scale(1, -1);

      for (let i = 1; i <= 16; i++) {
        const xRel = i * unitX;

        // dashed vertical grid line from x-axis up to top
        push();
        stroke(16, 185, 129, 70);
        strokeWeight(1);
        drawingContext.setLineDash([6, 6]);
        line(xRel, 0, xRel, topYpx);
        drawingContext.setLineDash([]);
        pop();

        // tiny tick on x-axis
        stroke(0);
        strokeWeight(1);
        line(xRel, -4, xRel, 4);
      }

      pop();

      // numeric labels just above the x-axis (screen coords)
      fill(0);
      noStroke();
      textSize(11);
      for (let i = 1; i <= 16; i++) {
        const xScr = originX + i * unitX;
        const yScr = centerY - 8; // slightly above axis
        const label = String(i);
        const tw = textWidth(label);
        text(label, xScr - tw / 2, yScr);
      }
    }

    function drawWave() {
      push();
      translate(originX, centerY);
      scale(1, -1);

      noFill();
      stroke('#2563eb');
      strokeWeight(2);

      beginShape();
      for (let xPix = 0; xPix <= rightLimitPx; xPix++) {
        const xWorld = xPix / unitX;
        const yWorld = A * Math.cos(omega * tVal - k * xWorld + phi0);
        const yPix = yWorld * unitY;
        vertex(xPix, yPix);
      }
      endShape();

      pop();
    }

    function drawPoint(xWorld, yWorld) {
      const xRel = xWorld * unitX;
      const yRel = yWorld * unitY;
      push();
      translate(originX, centerY);
      scale(1, -1);
      fill(0);
      noStroke();
      circle(xRel, yRel, 6);
      pop();
    }

    // Helper: draw vertical arrow from (baseX, baseY) with length vectorY (pixels in transformed coordinates)
    function drawArrow(baseXRel, baseYRel, vectorYPx, colorStr) {
      push();
      translate(originX, centerY);
      scale(1, -1);

      stroke(colorStr);
      fill(colorStr);
      strokeWeight(2);

      // main line
      line(baseXRel, baseYRel, baseXRel, baseYRel + vectorYPx);

      // arrowhead
      const ah = 7; // arrowhead size
      const tipX = baseXRel;
      const tipY = baseYRel + vectorYPx;
      noStroke();
      beginShape();
      vertex(tipX, tipY);
      vertex(tipX - 4, tipY - Math.sign(vectorYPx) * ah);
      vertex(tipX + 4, tipY - Math.sign(vectorYPx) * ah);
      endShape(CLOSE);

      pop();
    }

    function drawVectorLabels(xWorld, yWorld, vLenPx, aLenPx) {
      // Place labels near arrow tips using screen coordinates
      const xRel = xWorld * unitX;
      const yRel = yWorld * unitY;

      const vTip = relToScreen(xRel, yRel + vLenPx);
      const aTip = relToScreen(xRel, yRel + aLenPx);

      const offsetX = 6;
      const offsetY = 4;

      noStroke();
      textSize(11);

      fill('#ef4444');
      text('v', vTip.x + offsetX, vTip.y + offsetY);

      fill('#000000');
      text('a', aTip.x + offsetX, aTip.y + offsetY);
    }

    function drawPointsAndVectors() {
      // scaling for velocity and acceleration arrows
      const velScale = 15; // px per unit velocity
      const accScale = 5;  // px per unit acceleration

      for (let ix = 1; ix <= 16; ix++) {
        const xWorld = ix;
        const phase = omega * tVal - k * xWorld + phi0;
        const yWorld = A * Math.cos(phase);
        const v = -A * omega * Math.sin(phase);
        const a = -A * omega * omega * Math.cos(phase);

        // draw point
        drawPoint(xWorld, yWorld);

        // draw velocity arrow (red)
        const vLenPx = v * velScale;
        drawArrow(xWorld * unitX, yWorld * unitY, vLenPx, '#ef4444');

        // draw acceleration arrow (black)
        const aLenPx = a * accScale;
        drawArrow(xWorld * unitX, yWorld * unitY, aLenPx, '#000000');

        // labels for vectors
        drawVectorLabels(xWorld, yWorld, vLenPx, aLenPx);
      }
    }

    function draw() {
      background(255);

      // gray border around drawing area
      push();
      stroke(230);
      noFill();
      rect(6, 6, width - 12, height - 12, 8);
      pop();

      drawAxes();
      drawGridAndTicks();
      drawWave();
      drawPointsAndVectors();
    }
  </script>
</body>
