  <script>
    // Global visualization parameters
    const p = 2; // semi-latus rectum
    const xMin = -15, xMax = 5;
    const yMin = -7, yMax = 7;
    const SCALE = 30; // pixels per unit
    let originX = 0, originY = 0;

    // Helper: map world coords to screen coords without relying on inverted transform
    function worldToScreen(x, y) {
      return {
        x: originX + x * SCALE,
        y: originY - y * SCALE
      };
    }

    // DOM elements
    const slider = document.getElementById('slider-eccentricity');
    const eccValueSpan = document.getElementById('value-eccentricity');
    const energySpan = document.getElementById('energy-value');

    function updateEnergy(eps) {
      const E = -0.25 * (1 - eps * eps);
      if (eps === 0) {
        // Use MathJax for fraction when ε = 0
        energySpan.innerHTML = '\\(-\\frac{1}{4}\\)';
        if (window.MathJax && MathJax.typeset) MathJax.typeset();
      } else {
        energySpan.textContent = E.toFixed(2);
      }
    }

    function onSliderInput() {
      const eps = parseFloat(slider.value);
      eccValueSpan.textContent = eps.toFixed(1);
      updateEnergy(eps);
      // Trigger a redraw of the canvas
      if (typeof redraw === 'function') {
        redraw();
      }
    }

    document.addEventListener('DOMContentLoaded', () => {
      // Initialize display
      eccValueSpan.textContent = parseFloat(slider.value).toFixed(1);
      updateEnergy(parseFloat(slider.value));
      if (window.MathJax && MathJax.typeset) MathJax.typeset();
      slider.addEventListener('input', onSliderInput);
    });

    // p5.js Sketch (global mode)
    function setup() {
      const cnv = createCanvas(700, 500);
      cnv.parent('canvas-container');
      // Place the world-origin into the canvas as specified
      originX = width * 0.7;
      originY = height / 2;
      noLoop(); // Only redraw on slider input
      redraw(); // Initial draw
    }

    function drawAxesAndTicks() {
      push();
      translate(originX, originY);
      scale(1, -1); // positive y upwards
      stroke(0);
      strokeWeight(1);

      // Axes
      line(xMin * SCALE, 0, xMax * SCALE, 0); // x-axis
      line(0, yMin * SCALE, 0, yMax * SCALE); // y-axis

      // Ticks
      const tickPx = 4;
      for (let xi = Math.ceil(xMin); xi <= xMax; xi++) {
        line(xi * SCALE, -tickPx, xi * SCALE, tickPx);
      }
      for (let yi = Math.ceil(yMin); yi <= yMax; yi++) {
        line(-tickPx, yi * SCALE, tickPx, yi * SCALE);
      }
      pop();

      // Axis labels (upright text)
      noStroke();
      fill(0);
      textSize(12);
      textAlign(LEFT, CENTER);
      const labelXPos = worldToScreen(4.6, -0.7);
      text("Global'x", labelXPos.x, labelXPos.y);

      const labelYPos = worldToScreen(0.4, 6.5);
      text("Global'y", labelYPos.x, labelYPos.y);
    }

    function drawCentralBody() {
      push();
      translate(originX, originY);
      scale(1, -1);
      noStroke();
      fill(0);
      circle(0, 0, 6); // diameter approx 6 px
      pop();
    }

    function drawOrbitCurve() {
      const eps = parseFloat(slider.value);
      push();
      translate(originX, originY);
      scale(1, -1);
      noFill();
      stroke('#4a80b5');
      strokeWeight(2);

      if (eps === 0) {
        // Special circle case: radius 2, centered at (-2, 0)
        beginShape();
        const steps = 720;
        for (let i = 0; i <= steps; i++) {
          const t = i / steps;
          const theta = t * TWO_PI;
          const x = 2 * Math.cos(theta) - 2;
          const y = 2 * Math.sin(theta);
          vertex(x * SCALE, y * SCALE);
        }
        endShape();
      } else if (eps > 0 && eps < 1) {
        // Ellipse
        beginShape();
        const steps = 720;
        for (let i = 0; i <= steps; i++) {
          const t = i / steps;
          const theta = t * TWO_PI;
          const denom = 1 + eps * Math.cos(theta);
          const r = p / denom;
          const x = r * Math.cos(theta);
          const y = r * Math.sin(theta);
          vertex(x * SCALE, y * SCALE);
        }
        endShape();
      } else if (Math.abs(eps - 1) < 1e-12 || eps === 1) {
        // Parabola: limit theta range
        const tMin = -2.2, tMax = 2.2;
        const steps = 1000;
        beginShape();
        for (let i = 0; i <= steps; i++) {
          const t = i / steps;
          const theta = tMin + t * (tMax - tMin);
          const denom = 1 + eps * Math.cos(theta);
          const r = p / denom;
          const x = r * Math.cos(theta);
          const y = r * Math.sin(theta);
          vertex(x * SCALE, y * SCALE);
        }
        endShape();
      } else if (eps > 1) {
        // Hyperbola: theta in range where denominator > 0
        const thetaMax = Math.acos(-1 / eps);
        const pad = 1e-3;
        const tMin = -thetaMax + pad, tMax = thetaMax - pad;
        const steps = 1000;
        beginShape();
        for (let i = 0; i <= steps; i++) {
          const t = i / steps;
          const theta = tMin + t * (tMax - tMin);
          const denom = 1 + eps * Math.cos(theta);
          if (denom <= 0) continue; // safety
          const r = p / denom;
          const x = r * Math.cos(theta);
          const y = r * Math.sin(theta);
          vertex(x * SCALE, y * SCALE);
        }
        endShape();
      }
      pop();
    }

    function draw() {
      background('#f0f0f0');

      // Read ε from slider each draw (as specified)
      // Note: other computations happen in helper functions
      drawAxesAndTicks();
      drawCentralBody();
      drawOrbitCurve();
    }
  </script>
</body>
</html>
