  const sliderCurrentStep = document.getElementById('slider-current-step');
  const displayCurrentStep = document.getElementById('display-current-step');
  const labelCurrentStep = document.getElementById('label-current-step');

  const btnConflicts = document.getElementById('btn-conflicts');
  const btnEigenvalues = document.getElementById('btn-eigenvalues');

  // State
  let currentAlgorithm = 'heuristic';
  let totalSteps = 10;
  let currentStep = 0;
  let plotMode = 'conflicts';

  // Problem instance
  let solutions = [];           // array of solution indices
  let conflicts = new Array(N); // C(i) for all i
  let maxConflict = 0;
  let highlightIndex = null;

  // Precomputed basis vectors for Grover analysis
  let wVec = null; // uniform over solutions, normalized
  let rVec = null; // uniform over non-solutions, normalized

  function randInt(max) { return Math.floor(Math.random() * max); }
  function hammingDistance(a, b) {
    let x = a ^ b;
    let c = 0;
    while (x) { x &= (x - 1); c++; } // Kernighan's bit count
    return c;
  }

  function pickSolutions() {
    const s = new Set();
    while (s.size < NUM_SOL) s.add(randInt(N));
    solutions = Array.from(s);
  }

  function computeConflicts() {
    maxConflict = 0;
    for (let i = 0; i < N; i++) {
      if (solutions.includes(i)) {
        conflicts[i] = 0;
        continue;
      }
      let minH = Infinity;
      for (const sol of solutions) {
        const d = hammingDistance(i, sol);
        if (d < minH) minH = d;
      }
      conflicts[i] = minH;
      if (minH > maxConflict) maxConflict = minH;
    }
  }

  function initGroverBasis() {
    // |w> uniform on solutions, |r> uniform on non-solutions
    const M = solutions.length;
    const wAmp = 1 / Math.sqrt(M);
    const rAmp = 1 / Math.sqrt(N - M);
    wVec = new Array(N).fill(math.complex(0,0));
    rVec = new Array(N).fill(math.complex(0,0));
    for (let i = 0; i < N; i++) {
      if (solutions.includes(i)) wVec[i] = math.complex(wAmp, 0);
      else rVec[i] = math.complex(rAmp, 0);
    }
  }

  function initProblem() {
    pickSolutions();
    computeConflicts();
    initGroverBasis();
    // pick one non-solution to highlight at step 0
    const nons = [];
    for (let i = 0; i < N; i++) if (!solutions.includes(i)) nons.push(i);
    highlightIndex = nons[randInt(nons.length)];
  }

  // -----------------------------
  // Linear algebra helpers
  // -----------------------------
  function complexCloneArray(arr) { return arr.map(c => math.complex(c.re, c.im)); }

  function complexNorm2(vec) {
    let s = 0;
    for (const c of vec) s += c.re*c.re + c.im*c.im;
    return Math.sqrt(s);
  }

  function complexNormalize(vec) {
    const nrm = complexNorm2(vec);
    if (nrm === 0) return vec;
    const inv = 1 / nrm;
    for (let k = 0; k < vec.length; k++) {
      vec[k] = math.multiply(vec[k], inv);
    }
    return vec;
  }

  function innerProduct(u, v) { // <u|v>
    let sum = math.complex(0,0);
    for (let k = 0; k < u.length; k++) {
      // conj(u_k) * v_k
      const ukc = math.conj(u[k]);
      sum = math.add(sum, math.multiply(ukc, v[k]));
    }
    return sum;
  }

  function uniformState() {
    const a = 1 / Math.sqrt(N);
    return Array.from({length: N}, () => math.complex(a, 0));
  }

  // H(s) apply
  // H(s) = (1-s)*H0 + s*Hp
  // H0 = -P0, P0 v = (sum(v)/N) * 1
  // Hp = diag(C(i))
  function applyH(s, vec) {
    // compute sum(v)
    let sum = math.complex(0,0);
    for (let i = 0; i < N; i++) sum = math.add(sum, vec[i]);
    const avg = math.multiply(sum, 1/N);
    const part0 = (1 - s);
    const parts = s;

    const out = new Array(N);
    for (let i = 0; i < N; i++) {
      // H0 v contribution: -(avg)
      let term0 = math.multiply(avg, -part0);
      // Hp v contribution: s * C(i) * v_i
      let termp = math.multiply(vec[i], parts * conflicts[i]);
      out[i] = math.add(term0, termp);
    }
    return out;
  }

  // Imaginary time propagation (approximate ground state of H(s))
  function groundStateByImagTime(s, iters = 300, tau = 0.15) {
    let v = uniformState();
    for (let t = 0; t < iters; t++) {
      const Hv = applyH(s, v);
      for (let k = 0; k < N; k++) {
        // v := v - tau * H v
        v[k] = math.subtract(v[k], math.multiply(Hv[k], tau));
      }
      complexNormalize(v);
    }
    return v;
  }

  // Time-dependent Schrödinger evolution using simple Euler step:
  // v_{k+1} = normalize( v_k + (-i * dt) H(s_k) v_k )
  function evolveDiscrete(currentK, totalK) {
    let v = uniformState();
    if (currentK <= 0) return v;
    const T = totalK;
    // choose a stable dt: smaller for larger T
    const dt = Math.min(0.5, 6 / (T + 1)); // heuristic stability clamp
    for (let k = 0; k < currentK; k++) {
      const s = T > 0 ? k / T : 0;
      const Hv = applyH(s, v);
      for (let j = 0; j < N; j++) {
        // delta = (-i * dt) * Hv[j] = (-i)*Hv * dt = iC.neg() * Hv * dt
        const delta = math.multiply(math.multiply(math.unaryMinus(iC), dt), Hv[j]);
        v[j] = math.add(v[j], delta);
      }
      complexNormalize(v);
    }
    return v;
  }

  // Grover analytic state in 2D subspace
  function groverState(k) {
    const M = solutions.length;
    const theta = Math.asin(Math.sqrt(M / N)); // sin θ = √(M/N)
    const a_w = Math.sin((2*k + 1) * theta) / Math.sqrt(M);
    const a_r = Math.cos((2*k + 1) * theta) / Math.sqrt(N - M);
    const state = new Array(N);
    for (let i = 0; i < N; i++) {
      if (solutions.includes(i)) state[i] = math.complex(a_w, 0);
      else state[i] = math.complex(a_r, 0);
    }
    return state;
  }

  // -----------------------------
  // Plot helpers
  // -----------------------------
  function lerp(a,b,t){ return a + (b-a)*t; }
  function lerpColorRGB(c1, c2, t) { // c1, c2 arrays [r,g,b]
    return `rgb(${Math.round(lerp(c1[0], c2[0], t))},${Math.round(lerp(c1[1], c2[1], t))},${Math.round(lerp(c1[2], c2[2], t))})`;
  }
  function conflictColor(c) {
    const low = [43, 131, 186];    // blue-ish
    const high = [253, 174, 97];   // orange-ish
    const t = maxConflict > 0 ? c / maxConflict : 0;
    return lerpColorRGB(low, high, t);
  }

  function makeAmplitudePlot(state) {
    const xs = [], ys = [], colors = [], sizes = [];
    const xsSol = [], ysSol = [], sizesSol = [];
    const xsRed = [], ysRed = [], sizesRed = [];

    for (let i = 0; i < N; i++) {
      const re = state[i].re, im = state[i].im;
      if (solutions.includes(i)) {
        xsSol.push(re); ysSol.push(im); sizesSol.push(14);
      } else {
        xs.push(re); ys.push(im);
        colors.push(conflictColor(conflicts[i]));
        sizes.push(8);
      }
    }

    if (currentStep === 0 && highlightIndex != null) {
      xsRed.push(state[highlightIndex].re);
      ysRed.push(state[highlightIndex].im);
      sizesRed.push(10);
    }

    const allVals = state.map(c => Math.max(Math.abs(c.re), Math.abs(c.im)));
    const maxAbs = Math.max(0.25, ...allVals) * 1.2; // ensure visible

    const traces = [
      {
        type: 'scattergl',
        mode: 'markers',
        x: xs, y: ys,
        marker: { color: colors, size: sizes, line: { width: 0.5, color: '#333' } },
        name: 'states',
        hoverinfo: 'skip'
      },
      {
        type: 'scattergl',
        mode: 'markers',
        x: xsSol, y: ysSol,
        marker: { color: 'black', size: sizesSol, symbol: 'circle', line: {width: 1, color: '#111'} },
        name: 'solutions',
        hoverinfo: 'skip'
      }
    ];

    if (xsRed.length > 0) {
      traces.push({
        type: 'scattergl',
        mode: 'markers',
        x: xsRed, y: ysRed,
        marker: { color: 'red', size: sizesRed, line: { width: 0.5, color: '#700' } },
        name: 'highlight',
        hoverinfo: 'skip'
      });
    }

    const layout = {
      margin: {l: 45, r: 10, t: 10, b: 40},
      xaxis: { title: 'Re', range: [-maxAbs, maxAbs], zeroline: true, zerolinewidth: 1, zerolinecolor: '#aaa' },
      yaxis: { title: 'Im', range: [-maxAbs, maxAbs], zeroline: true, zerolinewidth: 1, zerolinecolor: '#aaa' },
      showlegend: false
    };

    Plotly.react('plot-amplitudes', traces, layout, {displayModeBar: false, responsive: true});
  }

  function probabilityByConflicts(state) {
    const probs = new Array(maxConflict+1).fill(0);
    for (let i = 0; i < N; i++) {
      const p = state[i].re*state[i].re + state[i].im*state[i].im;
      const c = conflicts[i];
      probs[c] += p;
    }
    return probs;
  }

  function makeProbabilityPlot(state) {
    let traces, layout;
    const totalProb = state.reduce((s,c)=> s + (c.re*c.re + c.im*c.im), 0);
    const ensure1 = Math.abs(totalProb - 1) < 1e-6 ? 1 : totalProb; // for display

    if (plotMode === 'conflicts' || (plotMode === 'eigenvalues' && currentAlgorithm !== 'unstructured')) {
      const probs = probabilityByConflicts(state);
      const xs = probs.map((_,i)=> i);
      const ys = probs.map(v => v / ensure1);

      traces = [{
        type: 'bar',
        x: xs,
        y: ys,
        marker: { color: xs.map(x => conflictColor(x)) },
        width: 0.6,
        hovertemplate: 'conflicts: %{x}<br>prob: %{y:.3f}<extra></extra>',
        name: 'probabilities'
      }];

      const xTitle = (plotMode === 'conflicts') ? 'number of conflicts' : 'eigenvalue';
      layout = {
        margin: {l: 50, r: 10, t: 10, b: 40},
        xaxis: { title: xTitle },
        yaxis: { title: 'probability', range: [0, 1] },
        showlegend: false
      };
    } else {
      // eigenvalues mode for unstructured (Grover)
      const M = solutions.length;
      const theta = Math.asin(Math.sqrt(M / N));
      const angs = [-2*theta, 2*theta];

      // Compute projections onto eigenvectors in 2D subspace:
      // v_+ = (|w> + i |r>)/√2, v_- = (|w> - i |r>)/√2
      const alpha_w = innerProduct(wVec, state); // <w|ψ>
      const alpha_r = innerProduct(rVec, state); // <r|ψ>
      const p_plus = math.divide(math.add(alpha_w, math.multiply(math.unaryMinus(iC), alpha_r)), Math.SQRT2);
      const p_minus = math.divide(math.add(alpha_w, math.multiply(iC, alpha_r)), Math.SQRT2);

      const yPlus = math.abs(p_plus) ** 2;
      const yMinus = math.abs(p_minus) ** 2;

      traces = [{
        type: 'bar',
        x: [angs[0], angs[1]],
        y: [yMinus, yPlus],
        marker: { color: ['#6a3d9a', '#1b9e77'] },
        width: 0.2,
        hovertemplate: 'arg(eigenvalue): %{x:.3f}<br>weight: %{y:.3f}<extra></extra>',
        name: 'weights'
      }];

      layout = {
        margin: {l: 50, r: 10, t: 10, b: 40},
        xaxis: { title: 'arg(eigenvalue)', tickformat: '.2f' },
        yaxis: { title: 'probability', range: [0, 1] },
        showlegend: false
      };
    }

    Plotly.react('plot-probability', traces, layout, {displayModeBar: false, responsive: true});
  }

  // -----------------------------
  // Main update
  // -----------------------------
  function update() {
    // read controls
    totalSteps = parseInt(sliderTotalSteps.value, 10);
    currentStep = parseInt(sliderCurrentStep.value, 10);
    displayTotalSteps.textContent = '' + totalSteps;
    displayCurrentStep.textContent = '' + currentStep;
    labelCurrentStep.textContent = `current step (0 to ${totalSteps})`;

    const s = totalSteps > 0 ? currentStep / totalSteps : 0;

    // compute state
    let state;
    if (currentAlgorithm === 'unstructured') {
      state = groverState(currentStep);
    } else if (currentAlgorithm === 'adiabatic') {
      state = groundStateByImagTime(s, 320, 0.14);
    } else if (currentAlgorithm === 'discrete-adiabatic') {
      state = evolveDiscrete(currentStep, Math.max(totalSteps, 1));
    } else { // heuristic
      state = evolveDiscrete(currentStep, Math.max(totalSteps, 1));
    }

    // plots
    makeAmplitudePlot(state);
    makeProbabilityPlot(state);
  }

  // -----------------------------
  // UI handlers
  // -----------------------------
  const algoDefaults = {
    'unstructured': 5,
    'adiabatic': 200,
    'discrete-adiabatic': 200,
    'heuristic': 10
  };

  function setActiveButton(group, key) {
    Object.entries(group).forEach(([name, btn]) => {
      if (name === key) btn.classList.add('active');
      else btn.classList.remove('active');
    });
  }

  Object.entries(btnsAlgo).forEach(([name, btn]) => {
    btn.addEventListener('click', () => {
      currentAlgorithm = name;
      setActiveButton(btnsAlgo, name);

      const defSteps = algoDefaults[name] || 10;
      sliderTotalSteps.value = defSteps;
      sliderCurrentStep.max = defSteps;
      sliderCurrentStep.value = 0;
      displayTotalSteps.textContent = '' + defSteps;
      displayCurrentStep.textContent = '0';
      labelCurrentStep.textContent = `current step (0 to ${defSteps})`;

      update();
    });
  });

  sliderTotalSteps.addEventListener('input', () => {
    const val = parseInt(sliderTotalSteps.value, 10);
    displayTotalSteps.textContent = '' + val;
    sliderCurrentStep.max = val;
    if (parseInt(sliderCurrentStep.value, 10) > val) sliderCurrentStep.value = val;
    displayCurrentStep.textContent = sliderCurrentStep.value;
    labelCurrentStep.textContent = `current step (0 to ${val})`;
    update();
  });

  sliderCurrentStep.addEventListener('input', () => {
    displayCurrentStep.textContent = sliderCurrentStep.value;
    update();
  });

  btnConflicts.addEventListener('click', () => {
    plotMode = 'conflicts';
    btnConflicts.classList.add('active');
    btnEigenvalues.classList.remove('active');
    update();
  });

  btnEigenvalues.addEventListener('click', () => {
    plotMode = 'eigenvalues';
    btnEigenvalues.classList.add('active');
    btnConflicts.classList.remove('active');
    update();
  });

  // -----------------------------
  // Initialize on load
  // -----------------------------
  function initApp() {
    // defaults already set (heuristic, 10, 0, conflicts)
    initProblem();

    // ensure sliders consistent with defaults
    sliderTotalSteps.value = 10;
    sliderCurrentStep.max = 10;
    sliderCurrentStep.value = 0;
    displayTotalSteps.textContent = '10';
    displayCurrentStep.textContent = '0';
    labelCurrentStep.textContent = 'current step (0 to 10)';

    update();
  }

  initApp();
})();
</script>
</body>
