<script>
(() => {
    // Geometry and state (normalized units for vectors; drawing uses scale R)
    const state = {
        // Parameters
        theta: 0.44879, // default π/7 approx
        R: 150,         // radius in pixels for the semicircle
        canvasW: 460,
        canvasH: 420,
        marginBottom: 30,
        // Vectors (normalized coordinates, radius 1)
        a: {x: 0, y: 1}, // will be set by theta
        b: {x: 0, y: 1}, // will be set by theta
        c: {x: 0.3, y: 0.7}, // draggable vector, clamped to unit semicircle
        dOpt: {x: 0, y: 1},  // computed optimal d for current c
        // Optional "optimal pair" (distinct display)
        c_opt: {x: 0, y: 0},
        d_opt: {x: 0, y: 0},
        // UI flags
        showMetricCircle: false,
        showMetricRegion: false,
        showFirstOrderVec: false,
        showFirstOrderRegion: false,
        showOptimalPair: false,
        showOptimalRegion: false,
        // Plot cache
        D_CA: 0,
        D_DB: 0,
        D_opt_pt: 0, // (sin(2θ)/2)^2 for green point
        // Drag control
        draggingC: false
    };

    // Utility functions
    function clamp(val, min, max) { return Math.max(min, Math.min(max, val)); }
    function len(v) { return Math.hypot(v.x, v.y); }
    function add(u, v) { return {x: u.x + v.x, y: u.y + v.y}; }
    function sub(u, v) { return {x: u.x - v.x, y: u.y - v.y}; }
    function scale(v, s) { return {x: v.x * s, y: v.y * s}; }
    function dot(u, v) { return u.x * v.x + u.y * v.y; }
    function unit(v) { const L = len(v); return L > 1e-12 ? {x: v.x / L, y: v.y / L} : {x: 0, y: 0}; }
    function perpCCW(v) { return {x: -v.y, y: v.x}; }

    // Update a and b from theta
    function updateAB() {
        const s = Math.sin(state.theta);
        const c = Math.cos(state.theta);
        state.a = {x: s, y: c};
        state.b = {x: -s, y: c};
    }

    // Clamp c into the upper unit semicircle
    function clampToSemicircle(v) {
        let x = v.x, y = v.y;
        if (y < 0) y = 0;
        const L = Math.hypot(x, y);
        if (L > 1) {
            x /= L; y /= L;
        }
        return {x, y};
    }

    // Given current c, compute optimal d (closest to b) constrained to ellipse:
    // (d_parallel / sqrt(1 - ||c||^2))^2 + (d_perp / 1)^2 = 1
    function compute_d_opt_for_c() {
        const c = state.c;
        const b = state.b;
        const cn = len(c);
        let u, v;
        if (cn > 1e-9) {
            u = {x: c.x / cn, y: c.y / cn}; // along c
            v = perpCCW(u); // perpendicular in the plane
        } else {
            // If c ~ 0, choose a fixed frame: u along y, v along x
            u = {x: 0, y: 1};
            v = {x: 1, y: 0};
        }
        const aAxis = Math.sqrt(Math.max(0, 1 - cn*cn)); // semi-axis along u
        // Represent target b in (u,v) basis
        const bx = dot(b, u);
        const by = dot(b, v);

        let dx, dy;

        if (aAxis < 1e-9) {
            // Degenerate ellipse: x = 0, y^2 = 1
            dy = (by >= 0 ? 1 : -1);
            dx = 0;
        } else {
            // Solve for t using bisection on:
            // F(t) = (bx^2)/(a^2 (1 + t/a^2)^2) + (by^2)/((1 + t)^2) - 1 = 0
            const a2 = aAxis * aAxis;
            function F(t) {
                const denomX = (1 + t / a2);
                const denomY = (1 + t);
                return (bx*bx) / (a2 * denomX * denomX) + (by*by) / (denomY * denomY) - 1;
            }
            let lo = 0, hi = 1;
            // Ensure hi large enough
            while (F(hi) > 0 && hi < 1e8) hi *= 2;
            // Bisection
            for (let i = 0; i < 80; i++) {
                const mid = 0.5 * (lo + hi);
                const val = F(mid);
                if (val > 0) lo = mid; else hi = mid;
            }
            const t = 0.5 * (lo + hi);
            dx = bx / (1 + t / a2);
            dy = by / (1 + t);
        }
        // Back to Cartesian: d = dx*u + dy*v
        state.dOpt = {x: dx*u.x + dy*v.x, y: dx*u.y + dy*v.y};
    }

    // Errors
    function computeErrors() {
        // D(C, A) = 0.5 * ||c - a||^2
        const dCA = 0.5 * ( (state.c.x - state.a.x)**2 + (state.c.y - state.a.y)**2 );
        // D(D, B) = 0.5 * ||d_opt - b||^2
        const dDB = 0.5 * ( (state.dOpt.x - state.b.x)**2 + (state.dOpt.y - state.b.y)**2 );
        state.D_CA = dCA;
        state.D_DB = dDB;
    }

    // Optimal pair for display when checkbox-optimal-pair is checked.
    // Use a simple symmetric construction that matches the green dot value:
    // Choose c_opt = α a, d_opt = α b with α = 1 - |sin(2θ)| / sqrt(2)
    function computeOptimalPair() {
        const s2t = Math.sin(2 * state.theta);
        const alpha = clamp(1 - Math.abs(s2t) / Math.SQRT2, 0, 1);
        state.c_opt = scale(state.a, alpha);
        state.d_opt = scale(state.b, alpha);
        // Green dot coordinate in plot:
        state.D_opt_pt = Math.pow(s2t / 2, 2);
    }

    // Plotly: initialize and update
    let plotInitialized = false;
    function getTradeoffCurve(theta) {
        const s2t = Math.sin(2 * theta);
        const xmax = s2t*s2t;
        const N = 200;
        const xs = [];
        const ys = [];
        if (xmax <= 1e-12) {
            xs.push(0); ys.push(s2t*s2t);
            return {x: xs, y: ys};
        }
        for (let i = 0; i < N; i++) {
            const x = xmax * (i / (N - 1));
            const y = Math.pow(s2t - Math.sqrt(Math.max(0, x)), 2);
            xs.push(x);
            ys.push(y);
        }
        return {x: xs, y: ys};
    }

    function initPlot() {
        const curve = getTradeoffCurve(state.theta);
        const data = [
            {
                x: curve.x,
                y: curve.y,
                mode: 'lines',
                name: 'boundary',
                line: {color: 'rgb(0, 102, 204)', width: 2}
            },
            {
                x: [state.D_CA],
                y: [state.D_DB],
                mode: 'markers',
                name: 'current',
                marker: {color: 'rgb(220, 0, 0)', size: 10, symbol: 'circle'}
            },
            {
                x: [state.D_opt_pt],
                y: [state.D_opt_pt],
                mode: 'markers',
                name: 'optimal',
                marker: {color: 'rgb(0, 160, 80)', size: 10, symbol: 'circle'},
                visible: state.showOptimalPair ? true : 'legendonly'
            }
        ];
        const layout = {
            margin: {l: 50, r: 10, t: 10, b: 40},
            xaxis: {title: 'D(C, A)', range: [0, 1.5], zeroline: false},
            yaxis: {title: 'D(D, B)', range: [0, 1.5], zeroline: false},
            showlegend: true
        };
        const config = {responsive: true};
        Plotly.newPlot('plot-error', data, layout, config);
        plotInitialized = true;
    }

    function updatePlot() {
        const curve = getTradeoffCurve(state.theta);
        const visOptimal = state.showOptimalPair ? true : 'legendonly';
        if (!plotInitialized) {
            initPlot();
            return;
        }
        // Update all traces efficiently
        Plotly.restyle('plot-error', {
            x: [curve.x],
            y: [curve.y]
        }, [0]);
        Plotly.restyle('plot-error', {
            x: [[state.D_CA]],
            y: [[state.D_DB]]
        }, [1]);
        Plotly.restyle('plot-error', {
            x: [[state.D_opt_pt]],
            y: [[state.D_opt_pt]],
            visible: [visOptimal]
        }, [2]);
