        document.getElementById('span-sinsq-theta-value').textContent = (sin2*sin2).toFixed(6);
    }

    // p5 sketch
    let p5Instance = null;
    function startP5() {
        // Remove placeholder canvas (we'll recreate with p5 while keeping the same id)
        const placeholder = document.getElementById('canvas-bloch');
        if (placeholder && placeholder.parentNode) placeholder.parentNode.removeChild(placeholder);

        const sketch = (p) => {
            let originPx = {x: 0, y: 0}; // pixel coordinates for origin
            function toPx(pt) {
                return {
                    x: originPx.x + pt.x * state.R,
                    y: originPx.y - pt.y * state.R
                };
            }
            function fromMouseToNorm(mx, my) {
                return {
                    x: (mx - originPx.x) / state.R,
                    y: (originPx.y - my) / state.R
                };
            }
            function drawArrowFromOrigin(v, strokeCol = '#000', weight = 2, headSize = 8, dashed = false) {
                const p0 = {x: originPx.x, y: originPx.y};
                const p1 = toPx(v);
                p.push();
                p.stroke(strokeCol);
                p.strokeWeight(weight);
                p.noFill();
                if (dashed) p.drawingContext.setLineDash([6, 6]);
                p.line(p0.x, p0.y, p1.x, p1.y);
                p.drawingContext.setLineDash([]);
                // Arrowhead
                const dx = p1.x - p0.x, dy = p1.y - p0.y;
                const ang = Math.atan2(dy, dx);
                p.fill(strokeCol);
                p.noStroke();
                p.push();
                p.translate(p1.x, p1.y);
                p.rotate(ang);
                p.triangle(0, 0, -headSize, headSize * 0.5, -headSize, -headSize * 0.5);
                p.pop();
                p.pop();
            }
            function drawDashedArc(radiusNorm, col = '#2b8a3e') {
                const rPx = radiusNorm * state.R * 2;
                p.push();
                p.noFill();
                p.stroke(col);
                p.strokeWeight(1.5);
                p.drawingContext.setLineDash([6, 6]);
                p.arc(originPx.x, originPx.y, rPx, rPx, Math.PI, Math.PI * 2);
                p.drawingContext.setLineDash([]);
                p.pop();
            }
            function drawMetricCircle(centerVec, radiusNorm, col = '#1e88e5') {
                const center = toPx(centerVec);
                const rPx = radiusNorm * state.R;
                p.push();
                p.noFill();
                p.stroke(col);
                p.strokeWeight(1.5);
                p.drawingContext.setLineDash([6, 6]);
                p.ellipse(center.x, center.y, rPx * 2, rPx * 2);
                p.drawingContext.setLineDash([]);
                p.pop();
            }
            function labelAtTip(vec, label, col = '#000') {
                const pt = toPx(vec);
                p.push();
                p.noStroke();
                p.fill(col);
                // text is drawn in screen coordinates (downward y), so we can just offset visually upward with negative y
                p.textSize(12);
                p.text(label, pt.x + 6, pt.y - 6);
                p.pop();
            }

            p.setup = () => {
                const cnv = p.createCanvas(state.canvasW, state.canvasH);
                cnv.parent('canvas-container');
                // give it the required id
                cnv.id('canvas-bloch');
                p.pixelDensity(1);
                p.noLoop();
            };

            p.windowResized = () => {
                // Keep fixed size in this demo
            };

            p.draw = () => {
                p.clear();
                p.background('#fafafa');

                // establish origin (bottom-center)
                originPx = {x: p.width / 2, y: p.height - state.marginBottom};

                // Draw baseline axis
                p.push();
                p.stroke(0);
                p.strokeWeight(1.5);
                p.line(originPx.x - state.R - 10, originPx.y, originPx.x + state.R + 10, originPx.y);
