                p.noFill();
                p.arc(originPx.x, originPx.y, state.R * 2, state.R * 2, Math.PI, Math.PI * 2);
                p.pop();

                // Draw vectors a (blue), b (red), c (green)
                drawArrowFromOrigin(state.a, '#1565c0', 2.5, 9, false);
                drawArrowFromOrigin(state.b, '#c62828', 2.5, 9, false);
                drawArrowFromOrigin(state.c, '#2e7d32', 2.5, 9, false);

                labelAtTip(state.a, 'a', '#1565c0');
                labelAtTip(state.b, 'b', '#c62828');
                labelAtTip(state.c, 'c', '#2e7d32');

                // Optional: metric circle centered at tip of a with radius ||c - a||
                if (state.showMetricCircle) {
                    const distCA = Math.hypot(state.c.x - state.a.x, state.c.y - state.a.y);
                    drawMetricCircle(state.a, distCA, '#1565c0');
                }

                // Compute d_opt for current c already done; optionally draw regions
                const showRegion = state.showMetricRegion || state.showFirstOrderRegion;
                if (showRegion) {
                    // green dashed arc for |c|
                    drawDashedArc(len(state.c), '#2e7d32'); // green
                    // orange/brown dashed arc for |d_opt|
                    drawDashedArc(len(state.dOpt), '#bf6d00'); // brownish
                }

                // First-order optimizing vector: dashed orange vector from tip of b to tip of d_opt
                if (state.showFirstOrderVec) {
                    p.push();
                    p.stroke('#ef6c00');
                    p.strokeWeight(2);
                    p.drawingContext.setLineDash([6, 6]);
                    const pb = toPx(state.b);
                    const pd = toPx(state.dOpt);
                    p.line(pb.x, pb.y, pd.x, pd.y);
                    p.drawingContext.setLineDash([]);
                    p.pop();
                    labelAtTip(state.dOpt, 'd_opt', '#ef6c00');
                }

                // Draw d_opt arrow from origin faintly to indicate current optimizing point
                drawArrowFromOrigin(state.dOpt, '#ef6c00', 1.8, 8, true);

                // Optimal pair visualization (independent of current c)
                if (state.showOptimalPair) {
                    // c_opt and d_opt in distinct colors
                    drawArrowFromOrigin(state.c_opt, '#00bcd4', 2.5, 9, false); // cyan
                    drawArrowFromOrigin(state.d_opt, '#8e24aa', 2.5, 9, false); // purple
                    labelAtTip(state.c_opt, 'c_opt', '#00bcd4');
                    labelAtTip(state.d_opt, 'd_opt*', '#8e24aa');
                }

                if (state.showOptimalPair && state.showOptimalRegion) {
                    drawDashedArc(len(state.c_opt), '#00bcd4');
                    drawDashedArc(len(state.d_opt), '#8e24aa');
                }

                // Draw a small draggable handle at tip of c
                const tipC = toPx(state.c);
                p.push();
                p.noStroke();
                p.fill('#2e7d32');
                p.circle(tipC.x, tipC.y, 8);
                p.pop();
            };

            p.mousePressed = () => {
                // Detect if near tip of c
                const tip = {
                    x: (p.width / 2) + state.c.x * state.R,
                    y: (p.height - state.marginBottom) - state.c.y * state.R
                };
                const d = Math.hypot(p.mouseX - tip.x, p.mouseY - tip.y);
                if (d <= 10) {
                    state.draggingC = true;
                }
            };

            p.mouseDragged = () => {
                if (!state.draggingC) return;
                // Convert mouse to normalized coords and clamp to semicircle
                const pt = fromMouseToNorm(p.mouseX, p.mouseY);
                state.c = clampToSemicircle(pt);
                // Update d_opt, errors, plot, and redraw
                compute_d_opt_for_c();
                computeErrors();
                updatePlot();
                p.redraw();
            };

            p.mouseReleased = () => {
                state.draggingC = false;
            };
        };

        p5Instance = new p5(sketch);
    }

    // Update everything for a new theta
    function applyThetaChange(newTheta) {
        state.theta = newTheta;
        updateThetaLabels();
        updateAB();
        // Recompute d_opt for current c
        compute_d_opt_for_c();
        computeErrors();
        // Recompute optimal pair (for green point and optional vectors)
        computeOptimalPair();
        updatePlot();
        if (p5Instance) p5Instance.redraw();
    }

    // Initialize UI bindings
    function initUI() {
        const slider = document.getElementById('slider-theta');
        slider.addEventListener('input', (e) => {
            const val = parseFloat(e.target.value);
            applyThetaChange(val);
        });

        document.getElementById('checkbox-metric-circle').addEventListener('change', (e) => {
            state.showMetricCircle = e.target.checked;
            if (p5Instance) p5Instance.redraw();
        });
        document.getElementById('checkbox-metric-region').addEventListener('change', (e) => {
            state.showMetricRegion = e.target.checked;
            if (p5Instance) p5Instance.redraw();
        });
        document.getElementById('checkbox-first-order-vec').addEventListener('change', (e) => {
            state.showFirstOrderVec = e.target.checked;
            if (p5Instance) p5Instance.redraw();
        });
        document.getElementById('checkbox-first-order-region').addEventListener('change', (e) => {
            state.showFirstOrderRegion = e.target.checked;
            if (p5Instance) p5Instance.redraw();
        });
        document.getElementById('checkbox-optimal-pair').addEventListener('change', (e) => {
            state.showOptimalPair = e.target.checked;
            updatePlot();
            if (p5Instance) p5Instance.redraw();
        });
        document.getElementById('checkbox-optimal-region').addEventListener('change', (e) => {
            state.showOptimalRegion = e.target.checked;
            if (p5Instance) p5Instance.redraw();
        });
    }

    // Initialize everything
    function init() {
        // Ensure c starts within semicircle
        state.c = clampToSemicircle(state.c);
        updateAB();
        compute_d_opt_for_c();
        computeErrors();
        computeOptimalPair();
        updateThetaLabels();
        initPlot();
        startP5();
    }

    // Kick off after DOM is ready
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', () => { initUI(); init(); });
    } else {
        initUI();
        init();
    }
})();
</script>
</body>
</html>
