      text-align: right;
      font-variant-numeric: tabular-nums;
      padding: 2px 6px;
      background: #fff;
      border: 1px solid var(--border);
      border-radius: 6px;
    }
    #plot-container {
      margin-top: 16px;
      border: 1px solid var(--border);
      border-radius: 8px;
      height: 520px;
    }
  </style>
</head>
<body>
  <div id="app-container">
    <div id="controls-container">
      <div class="control-row">
        <label for="slider-points">collocation points</label>
        <input type="range" id="slider-points" min="5" max="30" step="1" value="20" />
        <span id="label-points">20</span>
      </div>
      <div class="control-row">
        <label for="slider-time">t</label>
        <input type="range" id="slider-time" min="0.001" max="0.2" step="0.001" value="0.05" />
        <span id="label-time">0.050</span>
      </div>
    </div>
    <div id="plot-container"></div>
  </div>

  <script>
    (function() {
      const plotDivId = 'plot-container';
      const sliderPoints = document.getElementById('slider-points');
      const sliderTime = document.getElementById('slider-time');
      const labelPoints = document.getElementById('label-points');
      const labelTime = document.getElementById('label-time');

      const PI = Math.PI;
      const PI2 = PI * PI;
      const A = 8 / PI2; // 8 / π^2
      const nTerms = 100; // Number of terms in Fourier series for convergence

      // Compute T(x, t) using the Fourier series:
      // T(x, t) = (8/π²) * Σ[n=1..nTerms] (1/n²) * sin(nπ/2) * sin(nπx) * exp(-2 n² π² t)
      function T(x, t) {
        let sum = 0;
        for (let n = 1; n <= nTerms; n++) {
          const s_half = Math.sin(n * PI / 2);
          if (s_half === 0) continue; // Even n contributes 0 exactly
          const coeff = s_half / (n * n);
          const sin_npix = Math.sin(n * PI * x);
          const decay = Math.exp(-2 * n * n * PI2 * t);
          sum += coeff * sin_npix * decay;
        }
        return A * sum;
      }

      // Generate analytical curve with ~200 x points in [0, 1]
      function generateAnalyticalData(t) {
        const x = [];
        const y = [];
        const nPoints = 200;
        for (let i = 0; i < nPoints; i++) {
          const xi = i / (nPoints - 1);
          x.push(xi);
          y.push(T(xi, t));
        }
        return { x, y };
      }

      // Generate Chebyshev-Gauss-Lobatto collocation points in [0,1] and values
      function generateChebyshevData(numPoints, t) {
        const x = [];
        const y = [];
        const N = numPoints - 1; // Highest index
        for (let j = 0; j <= N; j++) {
          const xj = (Math.cos((j * PI) / N) + 1) / 2;
          x.push(xj);
          y.push(T(xj, t));
        }
        return { x, y };
      }

      const layout = {
        margin: { t: 20, r: 20, b: 40, l: 40 },
        showlegend: false,
        paper_bgcolor: '#ffffff',
        plot_bgcolor: '#ffffff',
        xaxis: {
          title: 'x',
          range: [0, 1],
          tickmode: 'linear',
          dtick: 0.2,
          showgrid: true,
          gridcolor: '#dddddd',
          zeroline: false
        },
        yaxis: {
          title: 'T',
          range: [-0.1, 1.1],
          tickmode: 'linear',
          dtick: 0.2,
          showgrid: true,
          gridcolor: '#dddddd',
          zeroline: false
        }
      };

      function getTraces(numPoints, t) {
        const analytical = generateAnalyticalData(t);
        const cheb = generateChebyshevData(numPoints, t);
        return [
          {
            x: analytical.x,
            y: analytical.y,
            type: 'scatter',
            mode: 'lines',
            line: { color: 'blue', width: 3 }
          },
          {
            x: cheb.x,
            y: cheb.y,
            type: 'scatter',
            mode: 'markers',
            marker: { color: 'red', size: 8 }
          }
        ];
      }

      function newPlot() {
        const numPoints = parseInt(sliderPoints.value, 10);
        const t = parseFloat(sliderTime.value);
        const traces = getTraces(numPoints, t);
        Plotly.newPlot(plotDivId, traces, layout, { responsive: true });
      }

      function updatePlot() {
        const numPoints = parseInt(sliderPoints.value, 10);
        const t = parseFloat(sliderTime.value);
        const traces = getTraces(numPoints, t);
        Plotly.react(plotDivId, traces, layout);
      }

      // Initialize labels and plot on load
      function init() {
        labelPoints.textContent = String(parseInt(sliderPoints.value, 10));
        labelTime.textContent = parseFloat(sliderTime.value).toFixed(3);
        newPlot();

        sliderPoints.addEventListener('input', () => {
          labelPoints.textContent = String(parseInt(sliderPoints.value, 10));
          updatePlot();
        });

        sliderTime.addEventListener('input', () => {
          labelTime.textContent = parseFloat(sliderTime.value).toFixed(3);
          updatePlot();
        });
      }

      if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', init);
      } else {
        init();
      }
    })();
  </script>
</body>
</html>
