  <script>
    // Global application state shared with the p5 sketch and DOM
    const App = {
      numRegularPoints: 4,
      numSteinerPoints: 2,
      pointMode: 'steiner',
      showAngles: true,

      regularPoints: [],
      steinerPoints: [],
      mstEdges: [],        // [{i, j, w}]
      totalLength: 0,

      // Geometry and drawing flags
      needsUpdate: true
    };

    // Utility: Euclidean distance
    function dist2D(a, b) {
      const dx = a.x - b.x, dy = a.y - b.y;
      return Math.hypot(dx, dy);
    }

    // Utility: Union-Find (Disjoint Set Union) for Kruskal
    class DSU {
      constructor(n) {
        this.parent = Array.from({length: n}, (_, i) => i);
        this.rank = Array.from({length: n}, () => 0);
      }
      find(x) {
        if (this.parent[x] !== x) this.parent[x] = this.find(this.parent[x]);
        return this.parent[x];
      }
      union(a, b) {
        let ra = this.find(a), rb = this.find(b);
        if (ra === rb) return false;
        if (this.rank[ra] < this.rank[rb]) [ra, rb] = [rb, ra];
        this.parent[rb] = ra;
        if (this.rank[ra] === this.rank[rb]) this.rank[ra]++;
        return true;
      }
    }

    // Recompute MST and total length
    function recomputeMST() {
      const allPts = App.regularPoints.concat(App.steinerPoints);
      const n = allPts.length;
      const edges = [];
      for (let i = 0; i < n; i++) {
        for (let j = i + 1; j < n; j++) {
          edges.push({i, j, w: dist2D(allPts[i], allPts[j])});
        }
      }
      edges.sort((a, b) => a.w - b.w);
      const dsu = new DSU(n);
      const mst = [];
      let total = 0;
      for (const e of edges) {
        if (dsu.union(e.i, e.j)) {
          mst.push(e);
          total += e.w;
          if (mst.length === n - 1) break;
        }
      }
      App.mstEdges = mst;
      App.totalLength = total;
      const lenText = document.getElementById('text-total-length');
      if (lenText) lenText.textContent = total.toFixed(5);
      App.needsUpdate = false;
    }

    // Place regular points on a regular N-gon
    function placeRegularPolygon(n, width, height) {
      const cx = width / 2;
      const cy = height / 2;
      const r = Math.min(width, height) * 0.38; // 38% of min dimension
      const pts = [];
      const startAngle = -Math.PI / 2; // start pointing upwards
      for (let k = 0; k < n; k++) {
        const ang = startAngle + 2 * Math.PI * (k / n);
        pts.push({ x: cx + r * Math.cos(ang), y: cy + r * Math.sin(ang) });
      }
      App.regularPoints = pts;
    }

    // Ensure Steiner points array length equals target; place new at center
    function setSteinerCount(n, width, height) {
      const cx = width / 2;
      const cy = height / 2;
      const cur = App.steinerPoints.length;
      if (n > cur) {
        for (let i = cur; i < n; i++) App.steinerPoints.push({ x: cx, y: cy });
      } else if (n < cur) {
        App.steinerPoints.splice(n);
      }
    }

    // UI helpers
    function setActiveButton(groupId, buttonId) {
      const container = document.getElementById(groupId);
      if (!container) return;
      const buttons = container.querySelectorAll('button');
      buttons.forEach(btn => btn.classList.remove('active'));
      const active = document.getElementById(buttonId);
      if (active) active.classList.add('active');
    }

    // Attach event listeners to controls
    function bindControls(p5Instance) {
