        App.numRegularPoints = 4;
        setActiveButton('regular-points-selector', 'btn-regular-4');
        placeRegularPolygon(App.numRegularPoints, p5Instance.width, p5Instance.height);
        App.steinerPoints = [];
        setSteinerCount(App.numSteinerPoints, p5Instance.width, p5Instance.height);
        for (let i = 0; i < App.steinerPoints.length; i++) {
          App.steinerPoints[i].x = p5Instance.width / 2;
          App.steinerPoints[i].y = p5Instance.height / 2;
        }
        App.needsUpdate = true;
      });
      document.getElementById('btn-regular-5').addEventListener('click', () => {
        App.numRegularPoints = 5;
        setActiveButton('regular-points-selector', 'btn-regular-5');
        placeRegularPolygon(App.numRegularPoints, p5Instance.width, p5Instance.height);
        App.steinerPoints = [];
        setSteinerCount(App.numSteinerPoints, p5Instance.width, p5Instance.height);
        for (let i = 0; i < App.steinerPoints.length; i++) {
          App.steinerPoints[i].x = p5Instance.width / 2;
          App.steinerPoints[i].y = p5Instance.height / 2;
        }
        App.needsUpdate = true;
      });

      // Steiner count buttons
      document.getElementById('btn-steiner-0').addEventListener('click', () => {
        App.numSteinerPoints = 0;
        setActiveButton('steiner-points-selector', 'btn-steiner-0');
        setSteinerCount(0, p5Instance.width, p5Instance.height);
        App.needsUpdate = true;
      });
      document.getElementById('btn-steiner-1').addEventListener('click', () => {
        App.numSteinerPoints = 1;
        setActiveButton('steiner-points-selector', 'btn-steiner-1');
        setSteinerCount(1, p5Instance.width, p5Instance.height);
        App.needsUpdate = true;
      });
      document.getElementById('btn-steiner-2').addEventListener('click', () => {
        App.numSteinerPoints = 2;
        setActiveButton('steiner-points-selector', 'btn-steiner-2');
        setSteinerCount(2, p5Instance.width, p5Instance.height);
        App.needsUpdate = true;
      });
      document.getElementById('btn-steiner-3').addEventListener('click', () => {
        App.numSteinerPoints = 3;
        setActiveButton('steiner-points-selector', 'btn-steiner-3');
        setSteinerCount(3, p5Instance.width, p5Instance.height);
        App.needsUpdate = true;
      });

      // Point mode radios
      document.getElementById('radio-regular').addEventListener('change', (e) => {
        if (e.target.checked) App.pointMode = 'regular';
      });
      document.getElementById('radio-steiner').addEventListener('change', (e) => {
        if (e.target.checked) App.pointMode = 'steiner';
      });

      // Angles toggle
      document.getElementById('checkbox-show-angles').addEventListener('change', (e) => {
        App.showAngles = !!e.target.checked;
        App.needsUpdate = true; // triggers a redraw
      });
    }

    // P5 Sketch (instance mode)
    const sketch = (p) => {
      let dragging = null; // {type: 'regular'|'steiner', index: number}
      const hitRadius = 10;

      function getContainerSize() {
        const container = document.getElementById('canvas-container');
        const rect = container.getBoundingClientRect();
        // Subtract small margins to avoid scrollbars
        return { w: Math.max(300, Math.floor(rect.width)), h: Math.max(300, Math.floor(window.innerHeight)) };
      }

      p.setup = function() {
        // Remove placeholder canvas with same id (will recreate with p5 and same id)
        const ph = document.getElementById('main-canvas');
        if (ph && ph.parentNode) ph.parentNode.removeChild(ph);

        const { w, h } = getContainerSize();
        const cnv = p.createCanvas(w, h);
        cnv.parent('canvas-wrapper');
        cnv.id('main-canvas');

        // Initial points
        placeRegularPolygon(App.numRegularPoints, p.width, p.height);
        setSteinerCount(App.numSteinerPoints, p.width, p.height);
        App.needsUpdate = true;
        recomputeMST();

        bindControls(p);
      };

      p.windowResized = function() {
        const { w, h } = getContainerSize();
        p.resizeCanvas(w, h);
        // When canvas resizes, we keep current relative positions.
        // Optionally recenter steiner points if outside bounds:
        App.regularPoints.forEach(pt => {
          pt.x = Math.min(Math.max(pt.x, 0), p.width);
          pt.y = Math.min(Math.max(pt.y, 0), p.height);
        });
        App.steinerPoints.forEach(pt => {
          pt.x = Math.min(Math.max(pt.x, 0), p.width);
          pt.y = Math.min(Math.max(pt.y, 0), p.height);
        });
        App.needsUpdate = true;
      };

      function drawCompleteGraph(allPts) {
        p.push();
        p.stroke(190, 160, 130, 128); // rgba(190,160,130,0.5)
        p.strokeWeight(1);
        for (let i = 0; i < allPts.length; i++) {
          for (let j = i + 1; j < allPts.length; j++) {
            p.line(allPts[i].x, allPts[i].y, allPts[j].x, allPts[j].y);
          }
        }
        p.pop();
      }

      function drawMST(allPts) {
        p.push();
        p.stroke(51); // #333333
        p.strokeWeight(3);
        App.mstEdges.forEach(e => {
          const a = allPts[e.i], b = allPts[e.j];
          p.line(a.x, a.y, b.x, b.y);
        });
        p.pop();
      }

      function drawPoints() {
        // Regular points: white fill, blue stroke, radius ~8
        p.push();
        p.stroke(0, 0, 255); // blue
        p.strokeWeight(2);
        p.fill(255);
        App.regularPoints.forEach(pt => {
          p.circle(pt.x, pt.y, 16);
        });
        p.pop();

        // Steiner points: double circle, white fill, red stroke
        p.push();
        p.stroke(255, 0, 0);
        p.strokeWeight(2);
        p.fill(255);
        App.steinerPoints.forEach(pt => {
          p.circle(pt.x, pt.y, 16);
          p.circle(pt.x, pt.y, 8);
        });
        p.pop();
      }

      function neighborsFromMST(nAll) {
        const adj = Array.from({length: nAll}, () => []);
        App.mstEdges.forEach(e => {
          adj[e.i].push(e.j);
          adj[e.j].push(e.i);
        });
        return adj;
      }

      function angleNormalize(a) {
        let t = a % (2*Math.PI);
        if (t < 0) t += 2*Math.PI;
        return t;
      }

      function drawAngles(allPts) {
        if (!App.showAngles) return;
        const nReg = App.regularPoints.length;
        const nAll = allPts.length;
        const adj = neighborsFromMST(nAll);

        p.push();
        p.fill(0);
        p.noStroke();
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(12);

        for (let s = 0; s < App.steinerPoints.length; s++) {
          const idx = nReg + s; // index in combined list
          const center = allPts[idx];
          const neigh = adj[idx];
          if (!neigh || neigh.length < 2) continue;

          // angles to neighbors
          const withAngles = neigh.map(j => {
            const v = { x: allPts[j].x - center.x, y: allPts[j].y - center.y };
            const ang = Math.atan2(v.y, v.x);
            return { j, ang: angleNormalize(ang) };
          }).sort((a, b) => a.ang - b.ang);

          // compute consecutive angle differences including wrap-around
          for (let k = 0; k < withAngles.length; k++) {
            const a = withAngles[k].ang;
            const b = withAngles[(k + 1) % withAngles.length].ang;
            let delta = b - a;
            if (delta < 0) delta += 2 * Math.PI;
            const mid = angleNormalize(a + delta / 2);
            const deg = Math.round(delta * 180 / Math.PI);

            // position text along the angle bisector
            const r = 22; // distance from center for label
            const tx = center.x + r * Math.cos(mid);
            const ty = center.y + r * Math.sin(mid);

            // draw a subtle background for readability
            p.push();
            p.fill(255, 240);
            p.noStroke();
            const tw = p.textWidth(deg + "°") + 6;
            const th = 16;
            p.rectMode(p.CENTER);
            p.rect(tx, ty, tw, th, 3);
            p.pop();

            p.fill(0);
            p.text(deg + "°", tx, ty);
          }
        }
        p.pop();
      }

      function currentMouseOverDraggable() {
        const isRegMode = App.pointMode === 'regular';
        const pts = isRegMode ? App.regularPoints : App.steinerPoints;
        const baseX = 0, baseY = 0; // coordinates already in canvas space
        let bestIdx = -1, bestD = 1e9;
        for (let i = 0; i < pts.length; i++) {
          const dx = p.mouseX - (baseX + pts[i].x);
          const dy = p.mouseY - (baseY + pts[i].y);
          const d = Math.hypot(dx, dy);
          if (d < hitRadius && d < bestD) { bestD = d; bestIdx = i; }
        }
        if (bestIdx >= 0) return { type: isRegMode ? 'regular' : 'steiner', index: bestIdx };
        return null;
      }

      p.mousePressed = function() {
        // Only start drag if inside canvas
        if (p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) return;
        const hit = currentMouseOverDraggable();
        if (hit) dragging = hit;
      };

      p.mouseDragged = function() {
        if (!dragging) return;
        const clamp = (val, min, max) => Math.min(Math.max(val, min), max);
        if (dragging.type === 'regular') {
          const pt = App.regularPoints[dragging.index];
          pt.x = clamp(p.mouseX, 0, p.width);
          pt.y = clamp(p.mouseY, 0, p.height);
        } else {
          const pt = App.steinerPoints[dragging.index];
          pt.x = clamp(p.mouseX, 0, p.width);
          pt.y = clamp(p.mouseY, 0, p.height);
        }
        App.needsUpdate = true;
      };

      p.mouseReleased = function() {
        dragging = null;
      };

      p.draw = function() {
        p.background('#EEEEEE');

        // Cursor hint
        const hover = currentMouseOverDraggable();
        if (hover) {
          p.cursor('pointer');
        } else if (dragging) {
          p.cursor('grabbing');
        } else {
          p.cursor('default');
        }

        // Recompute when needed
        if (App.needsUpdate) {
          recomputeMST();
        }

        const allPts = App.regularPoints.concat(App.steinerPoints);

        // Draw complete graph (light)
        drawCompleteGraph(allPts);

        // Draw MST on top
        drawMST(allPts);

        // Draw points on top
        drawPoints();

        // Optional angles
        drawAngles(allPts);
      };
    };

    // Initialize p5 instance
    new p5(sketch);
  </script>
</body>
</html>
