<script src="https://cdn.jsdelivr.net/npm/p5@1.9.2/lib/p5.min.js"></script>
<script>
  // App state
  const state = {
    k: 4,
    n: 10,
    names: ['W','X','Y','Z'],
    means: [0,0,0,0],
    sds: [1,1,1,1],
    GM: 0,
    SS_between: 0,
    SS_within: 0,
    MS_between: 0,
    MS_within: 0,
    F: 0
  };

  // Utility formatting
  function formatOneDecimal(x) {
    return (+x).toFixed(1);
  }
  function formatF(x) {
    if (!isFinite(x)) return '∞';
    const ax = Math.abs(x);
    if (ax < 0.01) return x.toFixed(4);
    if (ax < 10) return x.toFixed(3);
    return x.toFixed(2);
  }

  // Read sliders, update spans, compute ANOVA, update display, and redraw
  function updateFromUI() {
    // Read means
    const mW = parseFloat(document.getElementById('slider-mean-w').value);
    const mX = parseFloat(document.getElementById('slider-mean-x').value);
    const mY = parseFloat(document.getElementById('slider-mean-y').value);
    const mZ = parseFloat(document.getElementById('slider-mean-z').value);
    state.means = [mW, mX, mY, mZ];

    // Read sds
    const sW = parseFloat(document.getElementById('slider-sd-w').value);
    const sX = parseFloat(document.getElementById('slider-sd-x').value);
    const sY = parseFloat(document.getElementById('slider-sd-y').value);
    const sZ = parseFloat(document.getElementById('slider-sd-z').value);
    state.sds = [sW, sX, sY, sZ];

    // Update value displays
    document.getElementById('value-mean-w').textContent = formatOneDecimal(mW);
    document.getElementById('value-mean-x').textContent = formatOneDecimal(mX);
    document.getElementById('value-mean-y').textContent = formatOneDecimal(mY);
    document.getElementById('value-mean-z').textContent = formatOneDecimal(mZ);

    document.getElementById('value-sd-w').textContent = formatOneDecimal(sW);
    document.getElementById('value-sd-x').textContent = formatOneDecimal(sX);
    document.getElementById('value-sd-y').textContent = formatOneDecimal(sY);
    document.getElementById('value-sd-z').textContent = formatOneDecimal(sZ);

    // ANOVA calculations
    const k = state.k, n = state.n;
    // Grand mean: average of group means
    const GM = (state.means.reduce((a,b)=>a+b,0)) / k;
    state.GM = GM;

    // SS_between = n * sum (mi - GM)^2
    let ssb = 0;
    for (let i=0;i<k;i++){
      const d = state.means[i] - GM;
      ssb += d*d;
    }
    ssb *= n;
    state.SS_between = ssb;

    // MS_between = SS_between / (k - 1)
    state.MS_between = ssb / (k - 1);

    // SS_within = sum (sd_i^2 * (n - 1))
    let ssw = 0;
    for (let i=0;i<k;i++){
      ssw += (state.sds[i] * state.sds[i]) * (n - 1);
    }
    state.SS_within = ssw;

    // MS_within = SS_within / (n*k - k) = SS_within / (k*(n-1))
    const df_within = (n * k - k);
    const msw = ssw / df_within;
    state.MS_within = msw;

    // F = MS_between / MS_within
    state.F = (msw === 0) ? Infinity : (state.MS_between / msw);

    // Update F-ratio DOM display
    const fDisplay = document.querySelector('#visualization-panel .f-ratio-display');
    if (fDisplay) {
      fDisplay.textContent = 'F ratio = ' + formatF(state.F);
    }

    // Trigger p5 redraw
    if (typeof redraw === 'function') {
      redraw();
    }
  }

  // Attach event listeners to all sliders
  function connectSliders() {
    const ids = [
      'slider-mean-w','slider-mean-x','slider-mean-y','slider-mean-z',
      'slider-sd-w','slider-sd-x','slider-sd-y','slider-sd-z'
    ];
    ids.forEach(id => {
      const el = document.getElementById(id);
      if (el) {
        el.addEventListener('input', updateFromUI);
      }
    });
  }

  // p5.js sketch (global mode)
  let canvas;
  function setup() {
    const wrapper = document.getElementById('canvas-wrapper');
    const panel = document.getElementById('visualization-panel');
    const header = panel.querySelector('.f-ratio-display');
    // Compute available canvas size
    const w = wrapper.clientWidth;
    const h = wrapper.clientHeight;
    canvas = createCanvas(w, h);
    canvas.parent('canvas-wrapper');
    noLoop();
    connectSliders();
    updateFromUI(); // initial render
  }

  function windowResized() {
    const wrapper = document.getElementById('canvas-wrapper');
    if (!wrapper) return;
    resizeCanvas(wrapper.clientWidth, wrapper.clientHeight);
    redraw();
  }

  function draw() {
    background(255);

    // Layout parameters
    const pad = 16;

    // Split canvas: top half for group data, bottom half for ANOVA bars
    const topH = height * 0.55;
    const bottomH = height - topH;

    // Top plot area (Group Data)
    const topTitleSpace = 20; // for on-canvas F text
    const topPlotLeft = pad + 44; // space for y-axis labels
    const topPlotRight = width - pad;
    const topPlotTop = pad + topTitleSpace;
    const topPlotBottom = topH - pad - 32; // space for x labels
    const topPlotW = topPlotRight - topPlotLeft;
    const topPlotH = topPlotBottom - topPlotTop;

    // Draw F-ratio text above the group plot (on the canvas)
    noStroke();
    fill(0);
    textSize(14);
    textAlign(LEFT, CENTER);
    text('F ratio = ' + formatF(state.F), pad, pad + 8);

    // Axes for top plot
    stroke(0);
    strokeWeight(1);
    // y-axis
    line(topPlotLeft, topPlotTop, topPlotLeft, topPlotBottom);
    // x-axis baseline (just a line at bottom of plot area)
    line(topPlotLeft, topPlotBottom, topPlotRight, topPlotBottom);

    // y-axis ticks and labels at -10, -5, 0, 5, 10
    const yMin = -10, yMax = 10;
    const yTicks = [-10,-5,0,5,10];
    textSize(12);
    fill(0);
    yTicks.forEach(v => {
      const y = map(v, yMin, yMax, topPlotBottom, topPlotTop);
      stroke(0);
      line(topPlotLeft - 6, y, topPlotLeft, y);
      noStroke();
      textAlign(RIGHT, CENTER);
      fill(60);
      text(v.toString(), topPlotLeft - 8, y);
      // optional faint grid line
      stroke(230);
      line(topPlotLeft, y, topPlotRight, y);
    });

    // Grand Mean dashed line
    const gmY = map(state.GM, yMin, yMax, topPlotBottom, topPlotTop);
    stroke(0);
    strokeWeight(1);
    drawingContext.setLineDash([6, 6]);
    line(topPlotLeft, gmY, topPlotRight, gmY);
    drawingContext.setLineDash([]); // reset dash

    // Group bars (means ± sd)
    const groups = state.names;
    const nGroups = groups.length;
    const slotW = topPlotW / nGroups;
    const barW = Math.min(40, slotW * 0.45);

    for (let i=0; i<nGroups; i++) {
      const cx = topPlotLeft + slotW*(i + 0.5);
      const mean = state.means[i];
      const sd = state.sds[i];

      const topVal = mean + sd;
      const botVal = mean - sd;

      const yTop = map(topVal, yMin, yMax, topPlotBottom, topPlotTop);
      const yBot = map(botVal, yMin, yMax, topPlotBottom, topPlotTop);
      const yMean = map(mean, yMin, yMax, topPlotBottom, topPlotTop);

      // Draw bar (rectangle) centered at mean with height = 2*sd
      noStroke();
      fill('#F57F29');
      const rectX = cx - barW/2;
      const rectY = Math.min(yTop, yBot);
      const rectH = Math.abs(yBot - yTop);
      rect(rectX, rectY, barW, rectH);

      // Mean line across the rectangle
      stroke(0);
      strokeWeight(2);
      line(rectX, yMean, rectX + barW, yMean);

      // X-axis labels under the plot
      noStroke();
      fill(0);
      textAlign(CENTER, TOP);
      textSize(12);
      text(groups[i], cx, topPlotBottom + 6);
    }

    // Bottom plot area (ANOVA Results)
    const bottomTop = topH + 6;
    const bottomLeft = pad + 60; // leave space for y-axis
    const bottomRight = width - pad;
    const bottomBottom = height - pad;
    const bottomW = bottomRight - bottomLeft;
    const bottomHInner = bottomBottom - bottomTop;

    // Title
    noStroke();
    fill(0);
    textSize(14);
    textAlign(LEFT, TOP);
    text('Analysis of the F-Ratio', bottomLeft, bottomTop);

    // Axis config: fixed value domain [0, 200]
    const axisDomainMax = 200;
    const barAreaTop = bottomTop + 26;
    const barAreaBottom = bottomBottom - 34; // reserve space for axis ticks
    const barAreaH = Math.max(60, barAreaBottom - barAreaTop);
    const axisX = bottomLeft;
    const axisY = barAreaBottom + 16;
    const axisWidth = bottomW - 10;

    // Draw x-axis with ticks 0, 50, 100, 150, 200
    stroke(0);
    strokeWeight(1);
    line(axisX, axisY, axisX + axisWidth, axisY);
    const axisTicks = [0, 50, 100, 150, 200];
    textSize(12);
    fill(60);
    axisTicks.forEach(tv => {
      const tx = map(tv, 0, axisDomainMax, axisX, axisX + axisWidth);
      stroke(0);
      line(tx, axisY - 4, tx, axisY + 4);
      noStroke();
      textAlign(CENTER, TOP);
      text(tv.toString(), tx, axisY + 6);
    });

    // Y-axis line (at x=axisX) to indicate origin
    stroke(0);
    line(axisX, barAreaTop, axisX, barAreaBottom);

    // Bars data
    const stats = [
      { label: 'MS Between', value: state.MS_between, color: '#4691A5' },
      { label: 'MS Within',  value: state.MS_within,  color: '#D49D3C' },
      { label: 'F-Ratio',    value: state.F,          color: '#8C3A8D' }
    ];

    // positions for 3 bars
    const rows = stats.length;
    const gap = 20;
    const barHeight = Math.min(28, (barAreaH - gap*(rows-1)) / rows);
    const startY = barAreaTop + (barAreaH - (barHeight*rows + gap*(rows-1)))/2;

    textAlign(LEFT, CENTER);
    textSize(12);
    stats.forEach((s, i) => {
      const y = startY + i*(barHeight + gap);
      const v = s.value;
      const clampedV = Math.max(0, Math.min(axisDomainMax, isFinite(v) ? v : axisDomainMax));
      const w = map(clampedV, 0, axisDomainMax, 0, axisWidth);

      // Draw bar
      noStroke();
      fill(s.color);
      rect(axisX, y, w, barHeight);

      // Draw label to the right of the y-axis, aligned with the bar
      fill(0);
      text(s.label, axisX + 6, y + barHeight/2);
    });
  }
</script>
</body>
</html>
