    </div>
  </div>

  <script>
    // Utility: format numbers for display
    function fmt(x, digits = 3) {
      return Number(x).toFixed(digits);
    }

    // GEV PDF function
    function pdfGEV(x, mu, sigma, xi) {
      if (sigma <= 0) return 0;
      const z = (x - mu) / sigma;
      if (Math.abs(xi) < 1e-6) {
        const t = Math.exp(-z); // Gumbel case
        return (1 / sigma) * t * Math.exp(-t);
      } else {
        const tArg = 1 + xi * z;
        if (tArg <= 0) return 0; // outside support
        const t = Math.pow(tArg, -1 / xi);
        return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
      }
    }

    // Return level function y_p for return period p
    function returnLevel(p, mu, sigma, xi) {
      if (p <= 1) return NaN;
      const A = -Math.log(1 - 1 / p); // positive
      if (Math.abs(xi) < 1e-6) {
        return mu - sigma * Math.log(-Math.log(1 - 1 / p));
      } else {
        return mu - (sigma / xi) * (1 - Math.pow(A, -xi));
      }
    }

    // Generate x for PDF
    function linspace(a, b, n) {
      const arr = new Array(n);
      const step = (b - a) / (n - 1);
      for (let i = 0; i < n; i++) arr[i] = a + i * step;
      return arr;
    }

    // Compute curves
    function computePDFCurve(mu, sigma, xi, xMin = 2.5, xMax = 7, n = 500) {
      const x = linspace(xMin, xMax, n);
      const y = x.map(v => pdfGEV(v, mu, sigma, xi));
      return { x, y };
    }

    function computeReturnLevelCurve(mu, sigma, xi, pMax, n = 400) {
      const pMin = 1.01;
      const p = linspace(pMin, pMax, n);
      const y = p.map(pp => returnLevel(pp, mu, sigma, xi));
      return { p, y };
    }

    // DOM elements
    const sliders = {
      mu1: document.getElementById('slider-mu1'),
      sigma1: document.getElementById('slider-sigma1'),
      xi1: document.getElementById('slider-xi1'),
      mu2: document.getElementById('slider-mu2'),
      sigma2: document.getElementById('slider-sigma2'),
      xi2: document.getElementById('slider-xi2'),
      pmax: document.getElementById('slider-pmax')
    };

    const displays = {
      mu1: document.getElementById('display-mu1'),
      sigma1: document.getElementById('display-sigma1'),
      xi1: document.getElementById('display-xi1'),
      mu2: document.getElementById('display-mu2'),
      sigma2: document.getElementById('display-sigma2'),
      xi2: document.getElementById('display-xi2'),
      pmax: document.getElementById('display-pmax')
    };

    function readParams() {
      return {
        mu1: parseFloat(sliders.mu1.value),
        sigma1: parseFloat(sliders.sigma1.value),
        xi1: parseFloat(sliders.xi1.value),
        mu2: parseFloat(sliders.mu2.value),
        sigma2: parseFloat(sliders.sigma2.value),
        xi2: parseFloat(sliders.xi2.value),
        pmax: parseFloat(sliders.pmax.value)
      };
    }

    function updateReadouts() {
      displays.mu1.value = fmt(sliders.mu1.value, 3);
      displays.sigma1.value = fmt(sliders.sigma1.value, 3);
      displays.xi1.value = fmt(sliders.xi1.value, 3);

      displays.mu2.value = fmt(sliders.mu2.value, 3);
      displays.sigma2.value = fmt(sliders.sigma2.value, 3);
      displays.xi2.value = fmt(sliders.xi2.value, 3);

      displays.pmax.value = fmt(sliders.pmax.value, 0);
    }

    let plotsInitialized = false;

    function renderPDF(params) {
      const { mu1, sigma1, xi1, mu2, sigma2, xi2 } = params;
      const c1 = computePDFCurve(mu1, sigma1, xi1);
      const c2 = computePDFCurve(mu2, sigma2, xi2);

      const data = [
        {
          x: c1.x, y: c1.y,
          type: 'scatter', mode: 'lines',
          name: 'GEV 1',
          line: { color: 'blue', width: 2 },
          fill: 'tozeroy',
          fillcolor: 'rgba(0, 0, 255, 0.2)'
        },
        {
          x: c2.x, y: c2.y,
          type: 'scatter', mode: 'lines',
          name: 'GEV 2',
          line: { color: 'red', width: 2 },
          fill: 'tozeroy',
          fillcolor: 'rgba(255, 0, 0, 0.2)'
        }
      ];

      const layout = {
        title: 'PDFs of GEV Distributions 1 and 2',
        margin: { t: 40, r: 20, l: 50, b: 40 },
        xaxis: { range: [2.5, 7] },
        yaxis: { autorange: true },
        legend: { orientation: 'h', y: -0.2 }
      };

      if (!plotsInitialized) {
        Plotly.newPlot('plot-pdf', data, layout, { responsive: true, displayModeBar: false });
      } else {
        Plotly.react('plot-pdf', data, layout, { responsive: true, displayModeBar: false });
      }
    }

    function renderReturnLevel(params) {
      const { mu1, sigma1, xi1, mu2, sigma2, xi2, pmax } = params;

      const r1 = computeReturnLevelCurve(mu1, sigma1, xi1, pmax);
      const r2 = computeReturnLevelCurve(mu2, sigma2, xi2, pmax);

      const data = [
        {
          x: r1.p, y: r1.y,
          type: 'scatter', mode: 'lines',
          name: 'GEV 1',
          line: { color: 'blue', width: 2 }
        },
        {
          x: r2.p, y: r2.y,
          type: 'scatter', mode: 'lines',
          name: 'GEV 2',
          line: { color: 'red', width: 2 }
        }
      ];

      const layout = {
        margin: { t: 10, r: 20, l: 60, b: 50 },
        xaxis: { title: 'Return Period (years)', range: [0, pmax] },
        yaxis: { title: 'Maximum Sea Level (meters)', autorange: true },
        showlegend: true,
        legend: { orientation: 'h', y: -0.2 }
      };

      if (!plotsInitialized) {
        Plotly.newPlot('plot-return-level', data, layout, { responsive: true, displayModeBar: false });
      } else {
        Plotly.react('plot-return-level', data, layout, { responsive: true, displayModeBar: false });
      }
    }

    function update(triggerId) {
      updateReadouts();
      const params = readParams();

      // If P_max slider moved, only update return level plot; otherwise update both
      if (triggerId === 'slider-pmax') {
        renderReturnLevel(params);
      } else {
        renderPDF(params);
        renderReturnLevel(params);
      }

      if (!plotsInitialized) plotsInitialized = true;
    }

    // Attach listeners
    document.addEventListener('DOMContentLoaded', () => {
      // Initialize readouts and plots
      updateReadouts();
      const allRanges = document.querySelectorAll('#control-panel input[type="range"]');
      allRanges.forEach(r => {
        r.addEventListener('input', (e) => update(e.target.id));
      });
      // First full render
      update('init');
    });
  </script>
</body>
</html>
