<body>
  <div id="main-container">
    <div id="control-panel">
      <div class="row signal-buttons">
        <label>signal</label>
        <button id="btn-rampdown">RampDown</button>
        <button id="btn-tophat">TopHat</button>
        <button id="btn-stairstep">StairStep</button>
        <button id="btn-triangle" class="active">Triangle</button>
        <button id="btn-uh">UH</button>
      </div>

      <div class="sliders">
        <div class="slider-row">
          <label for="slider-a">a</label>
          <input type="range" id="slider-a" min="0.1" max="4" step="0.1" value="2">
          <span id="slider-a-value" class="value-badge">2.0</span>
        </div>
        <div class="slider-row">
          <label for="slider-b">b</label>
          <input type="range" id="slider-b" min="-10" max="10" step="0.1" value="4">
          <span id="slider-b-value" class="value-badge">4.0</span>
        </div>
        <div class="slider-row">
          <label for="slider-t"><span class="math-label">T = \\(\\frac{b}{a}\\)</span></label>
          <input type="range" id="slider-t" min="-25" max="25" step="0.1" value="2">
          <span id="slider-t-value" class="value-badge">2.0</span>
        </div>
      </div>

      <div class="display-options">
        <label>display:</label>
        <input type="checkbox" id="check-x" checked>
        <label for="check-x">\(x(t)\)</label>

        <input type="checkbox" id="check-y" checked>
        <label for="check-y">\(y(t) = x(at - b) = x(a(t-T))\)</label>

        <input type="checkbox" id="check-z" checked>
        <label for="check-z">\(z(t) = x(t - b)\)</label>

        <input type="checkbox" id="check-w">
        <label for="check-w">\(w(t) = x(at)\)</label>
      </div>
    </div>

    <div id="plot-top"></div>
    <div id="plot-bottom"></div>
  </div>

<script>
(function() {
  // Global state
  const state = {
    signalType: 'triangle',
    a: 2.0,
    b: 4.0,
    get T() { return this.b / this.a; },
    set T(val) { this.b = this.a * val; }
  };

  // Elements
  const btns = {
    rampdown: document.getElementById('btn-rampdown'),
    tophat: document.getElementById('btn-tophat'),
    stairstep: document.getElementById('btn-stairstep'),
    triangle: document.getElementById('btn-triangle'),
    uh: document.getElementById('btn-uh'),
  };

  const sliderA = document.getElementById('slider-a');
  const sliderB = document.getElementById('slider-b');
  const sliderT = document.getElementById('slider-t');
  const valA = document.getElementById('slider-a-value');
  const valB = document.getElementById('slider-b-value');
  const valT = document.getElementById('slider-t-value');

  const checkX = document.getElementById('check-x');
  const checkY = document.getElementById('check-y');
  const checkZ = document.getElementById('check-z');
  const checkW = document.getElementById('check-w');

  const plotTop = document.getElementById('plot-top');
  const plotBottom = document.getElementById('plot-bottom');

  // Utility: clamp value
  const clamp = (v, lo, hi) => Math.max(lo, Math.min(hi, v));

  // Utility: get base segments (array of segments; each segment is array of [t, y])
  function getBaseSegments(type) {
    switch (type) {
      case 'triangle':
        return [
          [ [0,0], [3,4], [6,0] ]
        ];
      case 'tophat':
        return [
          [ [1,0], [1,4], [5,4], [5,0] ]
        ];
      case 'stairstep':
        return [
          [ [1,0], [1,1], [2,1], [2,4], [4,4], [4,1], [5,1], [5,0] ]
        ];
      case 'rampdown':
        return [
          [ [0,4], [6,0] ]
        ];
      case 'uh':
        return [
          [ [1,0], [1,4], [1.5,4], [1.5,1], [2.5,1], [2.5,4], [3,4], [3,0] ],
          [ [4,0], [4,4], [4.5,4], [4.5,2], [5.5,2], [5.5,4], [6,4], [6,0] ]
        ];
      default:
        return [
          [ [0,0], [3,4], [6,0] ]
        ];
    }
  }

  // Utility: transform segments according to type
  // 'id' (identity), 'y' (x(a t - b)), 'z' (x(t - b)), 'w' (x(a t))
  function transformSegments(segments, a, b, kind) {
    const out = [];
    for (let s = 0; s < segments.length; s++) {
      const seg = segments[s];
      const outSeg = [];
      for (let i = 0; i < seg.length; i++) {
        const t0 = seg[i][0], y0 = seg[i][1];
        let t;
        if (kind === 'id') t = t0;
        else if (kind === 'y') t = (t0 + b) / a;
        else if (kind === 'z') t = t0 + b;
        else if (kind === 'w') t = t0 / a;
        else t = t0;
        outSeg.push([t, y0]);
      }
      out.push(outSeg);
    }
    return out;
  }

  // Flatten segments to x,y arrays with null separators to avoid connecting across segments
  function segmentsToXY(segments) {
    const x = [], y = [];
    for (let s = 0; s < segments.length; s++) {
      const seg = segments[s];
      for (let i = 0; i < seg.length; i++) {
        x.push(seg[i][0]);
        y.push(seg[i][1]);
      }
      if (s < segments.length - 1) {
        x.push(null);
        y.push(null);
      }
    }
    return { x, y };
  }

  // Build marker shapes and annotations for given b and T
  function buildMarkers(b, T) {
    const r = 0.12; // data-unit radius
    const shapes = [
      {
        type: 'circle',
        xref: 'x',
        yref: 'y',
        x0: T - r, y0: 0 - r,
        x1: T + r, y1: 0 + r,
        line: { color: 'red', width: 1.5 },
        fillcolor: 'red',
        opacity: 0.9
      },
      {
        type: 'circle',
        xref: 'x',
        yref: 'y',
        x0: b - r, y0: 0 - r,
        x1: b + r, y1: 0 + r,
        line: { color: 'green', width: 1.5 },
        fillcolor: 'green',
        opacity: 0.9
      }
    ];
    const annotations = [
      {
        x: T, y: 0.3,
        xref: 'x', yref: 'y',
        text: 'T',
        showarrow: false,
        font: { color: 'red', size: 14 }
      },
      {
        x: b, y: 0.3,
        xref: 'x', yref: 'y',
        text: 'b',
        showarrow: false,
        font: { color: 'green', size: 14 }
      }
    ];
    return { shapes, annotations };
  }

  // Layout helpers
  function topLayout(b, T) {
    const marks = buildMarkers(b, T);
    return {
      title: 'signal',
      xaxis: { title: 'time (s)', range: [-10, 20], zeroline: true },
      yaxis: { range: [-0.5, 4.5], zeroline: true },
      showlegend: true,
      legend: { x: 0.01, y: 0.75 },
      margin: { l: 50, r: 20, t: 40, b: 40 },
      shapes: marks.shapes,
      annotations: marks.annotations
    };
  }
  function bottomLayout(b, T) {
    const marks = buildMarkers(b, T);
    return {
      xaxis: { title: 'time (s)', range: [-10, 20], zeroline: true },
      yaxis: { range: [-0.5, 4.5], zeroline: true },
      showlegend: true,
      legend: { x: 0.01, y: 0.98 },
      margin: { l: 50, r: 20, t: 10, b: 40 },
      shapes: marks.shapes,
      annotations: marks.annotations
    };
  }

  // Central plot update
  function updatePlots() {
    const a = state.a;
    const b = state.b;
    const T = state.T;

    const base = getBaseSegments(state.signalType);

    // Build traces for top plot (x(t) only)
    const xyTop = segmentsToXY(transformSegments(base, a, b, 'id'));
    const traceTop = {
      type: 'scatter',
      mode: 'lines',
      x: xyTop.x,
      y: xyTop.y,
      line: { color: 'rgb(70, 130, 180)', width: 2.5 },
      name: '\\(x(t)\\)'
    };

    // Build traces for bottom plot (x, y, z, w)
    const xyX = segmentsToXY(transformSegments(base, a, b, 'id'));
    const xyY = segmentsToXY(transformSegments(base, a, b, 'y'));
    const xyZ = segmentsToXY(transformSegments(base, a, b, 'z'));
    const xyW = segmentsToXY(transformSegments(base, a, b, 'w'));

    const traceX = {
      type: 'scatter',
      mode: 'lines',
      x: xyX.x, y: xyX.y,
      line: { color: 'rgb(70, 130, 180)', width: 2.5 },
      name: '\\(x(t)\\)',
      visible: checkX.checked ? true : 'legendonly'
    };
    const traceY = {
      type: 'scatter',
      mode: 'lines',
      x: xyY.x, y: xyY.y,
      line: { color: 'red', width: 2.5 },
      name: '\\(y(t) = x(at-b) = x(a(t-T))\\)',
      visible: checkY.checked ? true : 'legendonly'
    };
    const traceZ = {
      type: 'scatter',
      mode: 'lines',
      x: xyZ.x, y: xyZ.y,
      line: { color: 'olivedrab', width: 2.5 },
      name: '\\(z(t) = x(t-b)\\)',
      visible: checkZ.checked ? true : 'legendonly'
    };
    const traceW = {
      type: 'scatter',
      mode: 'lines',
      x: xyW.x, y: xyW.y,
      line: { color: 'saddlebrown', width: 2.5 },
      name: '\\(w(t) = x(at)\\)',
      visible: checkW.checked ? true : 'legendonly'
    };

    Plotly.react(plotTop, [traceTop], topLayout(b, T), {responsive: true, displayModeBar: false});
    Plotly.react(plotBottom, [traceX, traceY, traceZ, traceW], bottomLayout(b, T), {responsive: true, displayModeBar: false});
  }

  // Update only visibility of bottom traces (no recalculation)
  function updateBottomVisibility() {
    // Trace order: 0=x, 1=y, 2=z, 3=w
    Plotly.restyle(plotBottom, {visible: checkX.checked ? true : 'legendonly'}, [0]);
    Plotly.restyle(plotBottom, {visible: checkY.checked ? true : 'legendonly'}, [1]);
    Plotly.restyle(plotBottom, {visible: checkZ.checked ? true : 'legendonly'}, [2]);
    Plotly.restyle(plotBottom, {visible: checkW.checked ? true : 'legendonly'}, [3]);
  }

  // Set active button styling
  function setActiveButton(key) {
    Object.keys(btns).forEach(k => btns[k].classList.remove('active'));
    btns[key].classList.add('active');
    state.signalType = key;
  }

  // Slider handlers
  function handleSliderA() {
    const aMin = parseFloat(sliderA.min);
    const aMax = parseFloat(sliderA.max);
    state.a = clamp(parseFloat(sliderA.value), aMin, aMax);
    valA.textContent = state.a.toFixed(1);

    // Recompute T = b/a and update T slider
    const tVal = state.T; // computed from getter
    valT.textContent = tVal.toFixed(1);
    // Update T slider position (clamped to its range)
    const tMin = parseFloat(sliderT.min), tMax = parseFloat(sliderT.max);
    sliderT.value = clamp(tVal, tMin, tMax);

    updatePlots();
  }

  function handleSliderB() {
    const bMin = parseFloat(sliderB.min);
    const bMax = parseFloat(sliderB.max);
    state.b = clamp(parseFloat(sliderB.value), bMin, bMax);
    valB.textContent = state.b.toFixed(1);

    // Recompute T = b/a and update T slider readout
    const tVal = state.T;
    valT.textContent = tVal.toFixed(1);
    const tMin = parseFloat(sliderT.min), tMax = parseFloat(sliderT.max);
    sliderT.value = clamp(tVal, tMin, tMax);

    updatePlots();
  }

  function handleSliderT() {
    const tMin = parseFloat(sliderT.min);
    const tMax = parseFloat(sliderT.max);
    let desiredT = clamp(parseFloat(sliderT.value), tMin, tMax);

    // Update state via T, but clamp b to its slider limits to keep UI consistent
    const bMin = parseFloat(sliderB.min);
    const bMax = parseFloat(sliderB.max);
    const desiredB = state.a * desiredT;
    state.b = clamp(desiredB, bMin, bMax);
    // Update displayed b and T (T may change due to clamping)
    valB.textContent = state.b.toFixed(1);
    sliderB.value = state.b.toFixed(1);

    const actualT = state.T; // b/a after clamping
    valT.textContent = actualT.toFixed(1);
    sliderT.value = clamp(actualT, tMin, tMax);

    updatePlots();
  }

  // Button listeners
  btns.rampdown.addEventListener('click', () => { setActiveButton('rampdown'); updatePlots(); });
  btns.tophat.addEventListener('click', () => { setActiveButton('tophat'); updatePlots(); });
  btns.stairstep.addEventListener('click', () => { setActiveButton('stairstep'); updatePlots(); });
  btns.triangle.addEventListener('click', () => { setActiveButton('triangle'); updatePlots(); });
  btns.uh.addEventListener('click', () => { setActiveButton('uh'); updatePlots(); });

  // Slider listeners (use input for live update)
  sliderA.addEventListener('input', handleSliderA);
  sliderB.addEventListener('input', handleSliderB);
  sliderT.addEventListener('input', handleSliderT);

  // Checkbox listeners (only change visibility of bottom traces)
  checkX.addEventListener('change', updateBottomVisibility);
  checkY.addEventListener('change', updateBottomVisibility);
  checkZ.addEventListener('change', updateBottomVisibility);
  checkW.addEventListener('change', updateBottomVisibility);

  // Initial setup
  function init() {
    // Defaults are already set by HTML attributes and state object
    // Ensure MathJax processes static labels
    if (window.MathJax && MathJax.typesetPromise) {
      MathJax.typesetPromise();
    }

    // Initial plots
    updatePlots();
  }

  // Kick off
  document.addEventListener('DOMContentLoaded', init);
})();
</script>
</body>
