      </div>
      <div class="row">
        <label for="slider-total-time">total time</label>
        <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
        <div class="value-badge" id="val-total-time">2.2</div>
      </div>
      <div class="row">
        <label>function</label>
        <div id="function-buttons">
          <button id="btn-f">f</button>
          <button id="btn-g" class="active">g</button>
          <button id="btn-sin">sin</button>
          <button id="btn-cos">cos</button>
          <button id="btn-tan">tan</button>
        </div>
      </div>
      <div class="row">
        <label>encoding method</label>
        <div id="encoding-buttons">
          <button id="btn-asdm" class="active">ASDM</button>
          <button id="btn-iaf">IAF</button>
        </div>
      </div>
    </div>

    <div id="canvas-container"></div>
  </div>

<script>
  (function() {
    // State variables
    let startTime = -1.0;
    let totalTime = 2.2;
    let selectedFunc = 'g';
    let encodingMethod = 'ASDM';

    // Controls
    const infoBtn = document.getElementById('info-button');
    const descPanel = document.getElementById('description-panel');

    const sliderStart = document.getElementById('slider-start-time');
    const sliderTotal = document.getElementById('slider-total-time');
    const valStart = document.getElementById('val-start-time');
    const valTotal = document.getElementById('val-total-time');

    const funcButtons = {
      f: document.getElementById('btn-f'),
      g: document.getElementById('btn-g'),
      sin: document.getElementById('btn-sin'),
      cos: document.getElementById('btn-cos'),
      tan: document.getElementById('btn-tan')
    };

    const encButtons = {
      ASDM: document.getElementById('btn-asdm'),
      IAF: document.getElementById('btn-iaf')
    };

    // Info button toggle
    infoBtn.addEventListener('click', () => {
      if (descPanel.style.display === 'none' || descPanel.style.display === '') {
        descPanel.style.display = 'block';
      } else {
        descPanel.style.display = 'none';
      }
    });

    // Sliders: update state and redraw
    sliderStart.addEventListener('input', () => {
      startTime = parseFloat(sliderStart.value);
      valStart.textContent = startTime.toFixed(1);
      requestRedraw();
    });

    sliderTotal.addEventListener('input', () => {
      totalTime = parseFloat(sliderTotal.value);
      valTotal.textContent = totalTime.toFixed(1);
      requestRedraw();
    });

    // Function buttons
    Object.keys(funcButtons).forEach(k => {
      funcButtons[k].addEventListener('click', () => {
        selectedFunc = k;
        // Update active styles
        Object.values(funcButtons).forEach(btn => btn.classList.remove('active'));
        funcButtons[k].classList.add('active');
        requestRedraw();
      });
    });

    // Encoding buttons
    Object.keys(encButtons).forEach(k => {
      encButtons[k].addEventListener('click', () => {
        encodingMethod = k;
        Object.values(encButtons).forEach(btn => btn.classList.remove('active'));
        encButtons[k].classList.add('active');
        requestRedraw();
      });
    });

    // p5 sketch in instance mode
    const container = document.getElementById('canvas-container');
    let pInst = null;

    const sketch = (p) => {
      // Margins and ranges
      const margin = { left: 64, right: 20, top: 20, bottom: 56 };
      const yMin = -1.2;
      const yMax = 1.2;

      function getCanvasSize() {
        const w = Math.min(container.clientWidth || 800, 1200);
        const h = Math.max(360, Math.min(560, Math.floor(w * 0.55)));
        return { w, h };
      }

      function tMin() { return startTime; }
      function tMax() { return startTime + totalTime; }

      function xPix(t) {
        return p.map(t, tMin(), tMax(), margin.left, p.width - margin.right);
      }
      function yPix(y) {
        return p.map(y, yMin, yMax, p.height - margin.bottom, margin.top);
      }

      function drawAxesAndTicks() {
        // Plot area rectangle
        p.stroke(180);
        p.fill(255);
        p.rect(margin.left, margin.top, p.width - margin.left - margin.right, p.height - margin.top - margin.bottom);

        // Ticks every 0.5
        // X ticks and optional grid
        const xStart = Math.ceil(tMin() / 0.5) * 0.5;
        p.textAlign(p.CENTER, p.TOP);
        p.fill(30);
        p.stroke(220);
        for (let tx = xStart; tx <= tMax() + 1e-9; tx += 0.5) {
          const px = xPix(tx);
          // light grid line
          p.stroke(235);
          p.line(px, margin.top, px, p.height - margin.bottom);
          // bottom tick
          p.stroke(120);
          p.line(px, p.height - margin.bottom, px, p.height - margin.bottom + 6);
          // label
          p.noStroke();
          p.textSize(11);
          p.text(formatTick(tx), px, p.height - margin.bottom + 8);
        }

        // Y ticks and grid
        const yStart = Math.ceil(yMin / 0.5) * 0.5;
        p.textAlign(p.RIGHT, p.CENTER);
        for (let ty = yStart; ty <= yMax + 1e-9; ty += 0.5) {
          const py = yPix(ty);
          // grid
          p.stroke(235);
          p.line(margin.left, py, p.width - margin.right, py);
          // left tick
          p.stroke(120);
          p.line(margin.left - 6, py, margin.left, py);
          // label
          p.noStroke();
          p.fill(30);
          p.textSize(11);
          p.text(formatTick(ty), margin.left - 8, py);
        }

        // Axes lines (y=0 and t=0 if in range)
        p.stroke(0);
        p.strokeWeight(1.2);
        if (0 >= yMin && 0 <= yMax) {
          const py0 = yPix(0);
          p.line(margin.left, py0, p.width - margin.right, py0);
        }
        if (0 >= tMin() && 0 <= tMax()) {
          const px0 = xPix(0);
          p.line(px0, margin.top, px0, p.height - margin.bottom);
        }

        // Axis labels (simple)
        p.noStroke();
        p.fill(20);
        p.textSize(12);
        p.textAlign(p.CENTER, p.TOP);
        p.text('t', (margin.left + p.width - margin.right) / 2, p.height - margin.bottom + 28);
        p.push();
        p.textAlign(p.CENTER, p.BOTTOM);
        p.translate(16, (margin.top + p.height - margin.bottom) / 2);
        p.rotate(-Math.PI / 2);
        p.text('signal', 0, 0);
        p.pop();
      }

      function formatTick(v) {
        // Keep clean values like 0.0 -> 0
        const s = (Math.abs(v) < 1e-9) ? 0 : v;
        const str = s.toFixed(1);
        if (str.endsWith('.0')) return str.slice(0, -2);
        return str;
      }

      function analogAt(t) {
        const twoPI = Math.PI * 2;
        switch (selectedFunc) {
          case 'f':
            return 0.5 * t;
          case 'g':
            return Math.sin(twoPI * t) + 0.3 * Math.cos(3 * twoPI * t);
          case 'sin':
            return Math.sin(twoPI * t);
          case 'cos':
            return Math.cos(twoPI * t);
          case 'tan': {
            const y = Math.tan(Math.PI * t);
            return y;
          }
          default:
            return Math.sin(twoPI * t);
        }
      }

      function drawAnalogCurve() {
        p.stroke(0, 0, 255);
        p.strokeWeight(1.6);
        p.noFill();

        const N = Math.max(600, Math.floor(totalTime * 400));
        const dt = (tMax() - tMin()) / N;

        let prevValid = false;
        let prevX = 0, prevY = 0, prevT = 0, prevVal = 0;

        for (let i = 0; i <= N; i++) {
          const t = tMin() + i * dt;
          let y = analogAt(t);

          // Handle discontinuities and extreme values (esp. tan)
          const invalid = !isFinite(y) || Math.abs(y) > 10 * (yMax - yMin);
          if (!invalid) {
            // We'll clamp only for mapping, but not for connection logic
            const px = xPix(t);
            const py = yPix(y);

            if (prevValid) {
              // Break if the last or current is far outside typical range to avoid asymptote connection
              if (Math.abs(y) > 2.5 || Math.abs(prevVal) > 2.5 || Math.abs(py - prevY) > (p.height * 0.45)) {
                prevValid = false;
              } else {
                p.line(prevX, prevY, px, py);
              }
            }
            prevValid = true;
            prevX = px; prevY = py; prevT = t; prevVal = y;
          } else {
            prevValid = false;
          }
        }
      }

      function simulateASDM(t0, t1) {
        const B = 0.25;
        const delta = 0.05;
        const flips = [];
        let t = t0;
        let outputLevel = B;
        let integral = 0;

        const steps = Math.max(800, Math.floor((t1 - t0) * 1500));
        const dt = (t1 - t0) / steps;

        while (t < t1) {
          const f = analogAt(t);
          integral += (f - outputLevel) * dt;

          if ((outputLevel === B && integral < -delta) ||
              (outputLevel === -B && integral > delta)) {
            // Flip occurs at approx current t
            flips.push(t);
            outputLevel = -outputLevel;
            integral = 0;
          }
          t += dt;
        }
        return { flips, B };
      }

      function drawASDM() {
        const { flips, B } = simulateASDM(tMin(), tMax());

        p.stroke(255, 0, 0);
        p.strokeWeight(1.6);
        p.noFill();

        let level = B;
        let t0 = tMin();

        const drawH = (a, b, yVal) => {
          const ax = xPix(a), bx = xPix(b);
          const py = yPix(yVal);
          p.line(ax, py, bx, py);
        };
        const drawV = (tFlip, fromY, toY) => {
          const px = xPix(tFlip);
          p.line(px, yPix(fromY), px, yPix(toY));
        };

        for (let i = 0; i < flips.length; i++) {
          const tf = Math.min(flips[i], tMax());
          if (tf > t0) {
            drawH(t0, tf, level);
          }
          // vertical at flip
          if (flips[i] >= tMin() && flips[i] <= tMax()) {
            drawV(flips[i], level, -level);
          }
          level = -level;
          t0 = tf;
          if (t0 >= tMax()) break;
        }
        if (t0 < tMax()) {
          drawH(t0, tMax(), level);
        }
      }

      function simulateIAF(t0, t1) {
        const theta = 0.05;
        const spikeY = 0.3;
        const spikes = [];
        let t = t0;
        let integral = 0;

        const steps = Math.max(800, Math.floor((t1 - t0) * 1500));
        const dt = (t1 - t0) / steps;

        while (t < t1) {
          const f = analogAt(t);
          integral += f * dt;
          if (integral >= theta) {
            spikes.push(t);
            integral = 0;
          }
          t += dt;
        }
        return { spikes, spikeY };
      }

      function drawIAF() {
        const { spikes, spikeY } = simulateIAF(tMin(), tMax());
        p.stroke(255, 0, 0);
        p.strokeWeight(1.2);
        p.fill(255, 0, 0);

        for (const ts of spikes) {
          const px = xPix(ts);
          const py0 = yPix(0);
          const pys = yPix(spikeY);
          p.line(px, py0, px, pys);
          p.circle(px, pys, 5);
        }
      }

      p.setup = function() {
        const size = getCanvasSize();
        const cnv = p.createCanvas(size.w, size.h);
        cnv.parent('canvas-container');
        p.noLoop();
        p.pixelDensity(1);
        p.background(255);
        p.redraw();
      };

      p.windowResized = function() {
        const size = getCanvasSize();
        p.resizeCanvas(size.w, size.h);
        p.redraw();
      };

      p.draw = function() {
        p.background(255);
        drawAxesAndTicks();
        drawAnalogCurve();

        // Encoded signal on top
        if (encodingMethod === 'ASDM') {
          drawASDM();
        } else {
          drawIAF();
        }
      };
    };

    function requestRedraw() {
      if (pInst) pInst.redraw();
    }

    // Initialize p5 instance after DOM ready
    document.addEventListener('DOMContentLoaded', () => {
      // Ensure default control values reflect initial state
      sliderStart.value = startTime.toString();
      sliderTotal.value = totalTime.toString();
      valStart.textContent = startTime.toFixed(1);
      valTotal.textContent = totalTime.toFixed(1);

      // Instantiate p5
      pInst = new p5(sketch);
    });
  })();
</script>
</body>
</html>
