        <legend>shear wave</legend>
        <div id="s-wave-type-selector" class="toggle-group">
          <button id="s-wave-sine-button" class="active" title="Sine S-wave">
            <svg class="icon" viewBox="0 0 44 24" xmlns="http://www.w3.org/2000/svg">
              <path d="M2,12 Q 9,0 16,12 T 30,12 T 44,12" stroke="currentColor" stroke-width="2" fill="none"/>
            </svg>
          </button>
          <button id="s-wave-pulse-button" title="Pulse (Gaussian) S-wave">
            <svg class="icon" viewBox="0 0 44 24" xmlns="http://www.w3.org/2000/svg">
              <path d="M2,12 C 10,12 16,2 22,2 C 28,2 34,12 42,12" stroke="currentColor" stroke-width="2" fill="none"/>
            </svg>
          </button>
        </div>
        <label for="s-wave-amplitude-slider">amplitude</label>
        <input type="range" id="s-wave-amplitude-slider" min="0" max="0.3" step="0.01" value="0.1">
        <label for="s-wave-wavelength-slider">wavelength</label>
        <input type="range" id="s-wave-wavelength-slider" min="0.5" max="5.0" step="0.1" value="2.5">
        <label>direction</label>
        <div id="s-wave-direction-selector" class="toggle-group">
          <button id="s-wave-dir-xy-button" class="active">x-y</button>
          <button id="s-wave-dir-z-button">z</button>
        </div>
      </fieldset>

      <fieldset>
        <legend>viewpoint</legend>
        <div id="viewpoint-selector" class="toggle-group">
          <button id="viewpoint-top-button" class="active">Top</button>
          <button id="viewpoint-default-button">Default</button>
        </div>
      </fieldset>
    </div>

    <div id="visualization-container"></div>
  </div>

  <script src="https://cdn.jsdelivr.net/npm/three@0.158.0/build/three.min.js"></script>
  <script>
    // State variables
    let time = 0;
    let isPlaying = false;
    const timeSlider = document.getElementById('time-slider');
    const playPauseButton = document.getElementById('play-pause-button');
    const resetButton = document.getElementById('reset-button');
    const stepButton = document.getElementById('step-button');

    const plotPointsSlider = document.getElementById('plot-points-slider');

    const pWaveSineButton = document.getElementById('p-wave-sine-button');
    const pWavePulseButton = document.getElementById('p-wave-pulse-button');
    const sWaveSineButton = document.getElementById('s-wave-sine-button');
    const sWavePulseButton = document.getElementById('s-wave-pulse-button');

    const pAmpSlider = document.getElementById('p-wave-amplitude-slider');
    const pWlSlider = document.getElementById('p-wave-wavelength-slider');
    const sAmpSlider = document.getElementById('s-wave-amplitude-slider');
    const sWlSlider = document.getElementById('s-wave-wavelength-slider');

    const sDirXYButton = document.getElementById('s-wave-dir-xy-button');
    const sDirZButton = document.getElementById('s-wave-dir-z-button');

    const viewpointTopButton = document.getElementById('viewpoint-top-button');
    const viewpointDefaultButton = document.getElementById('viewpoint-default-button');

    let pType = 'sine';  // 'sine' or 'pulse'
    let sType = 'sine';  // 'sine' or 'pulse'
    let sDirection = 'xy'; // 'xy' or 'z'

    // Three.js setup
    const container = document.getElementById('visualization-container');

    const scene = new THREE.Scene();
    scene.background = new THREE.Color('#E0E0E0');

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    container.appendChild(renderer.domElement);

    // Lights
    const ambient = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambient);
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
    dirLight.position.set(1, 1, 2);
    scene.add(dirLight);

    // Cameras
    let perspectiveCamera, orthoCamera, activeCamera;
    function setupCameras(){
      const width = container.clientWidth;
      const height = container.clientHeight || 1;
      const aspect = width / height;

      if (!perspectiveCamera) {
        perspectiveCamera = new THREE.PerspectiveCamera(45, aspect, 0.1, 1000);
        perspectiveCamera.position.set(10, 10, 10);
        perspectiveCamera.lookAt(0,0,0);
      } else {
        perspectiveCamera.aspect = aspect;
        perspectiveCamera.updateProjectionMatrix();
      }

      const viewSize = 6; // Ortho half-size basis
      const orthoHeight = viewSize * 2;
      const orthoWidth = orthoHeight * aspect;
      if (!orthoCamera) {
        orthoCamera = new THREE.OrthographicCamera(-orthoWidth/2, orthoWidth/2, orthoHeight/2, -orthoHeight/2, 0.1, 1000);
        orthoCamera.position.set(0,0,50);
        orthoCamera.up.set(0,1,0);
        orthoCamera.lookAt(0,0,0);
      } else {
        orthoCamera.left = -orthoWidth/2;
        orthoCamera.right = orthoWidth/2;
        orthoCamera.top = orthoHeight/2;
        orthoCamera.bottom = -orthoHeight/2;
        orthoCamera.updateProjectionMatrix();
      }

      if (!activeCamera) activeCamera = orthoCamera; // Top view default
    }

    function resizeRenderer(){
      const width = container.clientWidth;
      const height = container.clientHeight || 1;
      renderer.setSize(width, height, false);
      setupCameras();
    }
    window.addEventListener('resize', resizeRenderer);

    // Grid (plane) and wireframe
    const gridSize = 10; // world units
    let segments = parseInt(plotPointsSlider.value, 10);
    let planeMesh = null;
    let wireframe = null;
    let basePositions = null; // Float32Array of original positions

    function createGrid(segmentsCount){
      // Clean previous
      if (planeMesh) {
        scene.remove(planeMesh);
        planeMesh.geometry.dispose();
        planeMesh.material.dispose();
        planeMesh = null;
      }
      if (wireframe) {
        scene.remove(wireframe);
        wireframe.geometry.dispose();
        wireframe.material.dispose();
        wireframe = null;
      }

      const geometry = new THREE.PlaneGeometry(gridSize, gridSize, segmentsCount, segmentsCount); // XY plane
      // Save base positions
      basePositions = geometry.attributes.position.array.slice(); // copy

      // Color attribute
      const count = geometry.attributes.position.count;
      const colors = new Float32Array(count * 3);
      geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));

      const material = new THREE.MeshLambertMaterial({
        vertexColors: true,
        side: THREE.DoubleSide
      });

      planeMesh = new THREE.Mesh(geometry, material);
      scene.add(planeMesh);

      // Wireframe overlay
      const wfGeo = new THREE.WireframeGeometry(geometry);
      const wfMat = new THREE.LineBasicMaterial({ color: new THREE.Color('#556B2F'), linewidth: 1 });
      wireframe = new THREE.LineSegments(wfGeo, wfMat);
      scene.add(wireframe);
    }

    // Wave functions
    function waveFunction(type, u){
      if (type === 'sine'){
        return Math.sin(2 * Math.PI * u);
      } else {
        // Gaussian pulse
        return Math.exp(-(u*u));
      }
    }

    // Update geometry per frame
    function updateGrid(){
      if (!planeMesh) return;
      const positions = planeMesh.geometry.attributes.position.array;
      const colors = planeMesh.geometry.attributes.color.array;
      const count = planeMesh.geometry.attributes.position.count;

      const pAmp = parseFloat(pAmpSlider.value);
      const pWl  = parseFloat(pWlSlider.value);
      const sAmp = parseFloat(sAmpSlider.value);
      const sWl  = parseFloat(sWlSlider.value);

      let maxAbsZ = 0.0;

      for (let i=0; i<count; i++){
        const idx = i*3;
        const x0 = basePositions[idx + 0];
        const y0 = basePositions[idx + 1];
        // base z is 0 in plane geometry

        const r = Math.hypot(x0, y0);
        let ux=0, uy=0; // radial unit
        if (r > 1e-8){
          ux = x0 / r;
          uy = y0 / r;
        }

        // P-wave: longitudinal along radial
        const uP = (pWl !== 0 ? (r / pWl) : 0) - time;
        const pMag = pAmp * waveFunction(pType, uP);
        const pdx = pMag * ux;
        const pdy = pMag * uy;
        const pdz = 0;

        // S-wave: transverse
        const uS = (sWl !== 0 ? (r / sWl) : 0) - time;
        const sMag = sAmp * waveFunction(sType, uS);

        let sdx=0, sdy=0, sdz=0;
        if (sDirection === 'z'){
          sdx = 0; sdy = 0; sdz = sMag;
        } else {
          // xy direction perpendicular to radial
          if (r > 1e-8){
            const tx = -y0 / r;
            const ty =  x0 / r;
            sdx = sMag * tx;
            sdy = sMag * ty;
            sdz = 0;
          } else {
            sdx = sdy = sdz = 0;
          }
        }

        const nx = x0 + pdx + sdx;
        const ny = y0 + pdy + sdy;
        const nz = 0  + pdz + sdz;

        positions[idx + 0] = nx;
        positions[idx + 1] = ny;
        positions[idx + 2] = nz;

        const absZ = Math.abs(nz);
        if (absZ > maxAbsZ) maxAbsZ = absZ;
      }

      // Update normals for lighting
      planeMesh.geometry.attributes.position.needsUpdate = true;
      planeMesh.geometry.computeVertexNormals();

      // Color mapping: Red (positive z) -> White (0) -> Light Blue (negative z)
      const white = {r:1, g:1, b:1};
      const red   = {r:1, g:0, b:0};
      const blue  = {r:0.3, g:0.6, b:1.0}; // light blue

      if (maxAbsZ < 1e-12) {
        // All white
        for (let i=0; i<count; i++){
          const cidx = i*3;
          colors[cidx+0] = white.r;
          colors[cidx+1] = white.g;
          colors[cidx+2] = white.b;
        }
      } else {
        for (let i=0; i<count; i++){
          const pidx = i*3;
          const z = positions[pidx + 2];
          const t = Math.max(-1, Math.min(1, z / maxAbsZ));
          let cr, cg, cb;
          if (t >= 0){
            // from white to red
            const f = t;
            cr = white.r + (red.r - white.r) * f;
            cg = white.g + (red.g - white.g) * f;
            cb = white.b + (red.b - white.b) * f;
          } else {
            // from white to blue
            const f = -t;
            cr = white.r + (blue.r - white.r) * f;
            cg = white.g + (blue.g - white.g) * f;
            cb = white.b + (blue.b - white.b) * f;
          }
          colors[pidx + 0] = cr;
          colors[pidx + 1] = cg;
          colors[pidx + 2] = cb;
        }
      }
      planeMesh.geometry.attributes.color.needsUpdate = true;

      // Rebuild wireframe geometry so it follows deformations
      if (wireframe) {
        const newWF = new THREE.WireframeGeometry(planeMesh.geometry);
        wireframe.geometry.dispose();
        wireframe.geometry = newWF;
      }
    }

    // Animation loop
    let lastTS = null;
    const timeSpeed = 0.5; // simulation time units per second
    function animate(ts){
      requestAnimationFrame(animate);

      if (isPlaying){
        if (lastTS == null) lastTS = ts;
        const dt = (ts - lastTS) / 1000; // seconds
        lastTS = ts;
        time += timeSpeed * dt;
        if (time > parseFloat(timeSlider.max)) {
          time = parseFloat(timeSlider.min); // loop to start
        }
        timeSlider.value = time.toFixed(3);
        updateGrid();
      }

      renderer.render(scene, activeCamera);
    }

    // Control handlers
    playPauseButton.addEventListener('click', () => {
      isPlaying = !isPlaying;
      playPauseButton.textContent = isPlaying ? '❚❚' : '►';
      if (isPlaying) {
        lastTS = null;
      }
    });

    resetButton.addEventListener('click', () => {
      isPlaying = false;
      playPauseButton.textContent = '►';
      time = 0;
      timeSlider.value = '0';
      updateGrid();
      renderer.render(scene, activeCamera);
    });

    stepButton.addEventListener('click', () => {
      isPlaying = false;
      playPauseButton.textContent = '►';
      time = Math.min(parseFloat(timeSlider.max), time + 0.1);
      timeSlider.value = time.toFixed(3);
      updateGrid();
      renderer.render(scene, activeCamera);
    });

    timeSlider.addEventListener('input', (e) => {
      time = parseFloat(e.target.value);
      isPlaying = false;
      playPauseButton.textContent = '►';
      updateGrid();
      renderer.render(scene, activeCamera);
    });

    plotPointsSlider.addEventListener('change', (e) => {
      segments = parseInt(e.target.value, 10);
      createGrid(segments);
      updateGrid();
      renderer.render(scene, activeCamera);
    });

    // Wave type buttons
    function setActive(btnA, btnB){
      btnA.classList.add('active');
      btnB.classList.remove('active');
    }
    pWaveSineButton.addEventListener('click', () => {
      pType = 'sine';
      setActive(pWaveSineButton, pWavePulseButton);
      updateGrid();
    });
    pWavePulseButton.addEventListener('click', () => {
      pType = 'pulse';
      setActive(pWavePulseButton, pWaveSineButton);
      updateGrid();
    });
    sWaveSineButton.addEventListener('click', () => {
      sType = 'sine';
      setActive(sWaveSineButton, sWavePulseButton);
      updateGrid();
    });
    sWavePulseButton.addEventListener('click', () => {
      sType = 'pulse';
      setActive(sWavePulseButton, sWaveSineButton);
      updateGrid();
    });

    // Sliders for amplitudes and wavelengths
    [pAmpSlider, pWlSlider, sAmpSlider, sWlSlider].forEach(sl => {
      sl.addEventListener('input', () => {
        updateGrid();
      });
    });

    // Direction buttons
    sDirXYButton.addEventListener('click', () => {
      sDirection = 'xy';
      setActive(sDirXYButton, sDirZButton);
      updateGrid();
    });
    sDirZButton.addEventListener('click', () => {
      sDirection = 'z';
      setActive(sDirZButton, sDirXYButton);
      updateGrid();
    });

    // Viewpoint controls
    viewpointTopButton.addEventListener('click', () => {
      activeCamera = orthoCamera;
      setActive(viewpointTopButton, viewpointDefaultButton);
      renderer.render(scene, activeCamera);
    });
    viewpointDefaultButton.addEventListener('click', () => {
      activeCamera = perspectiveCamera;
      setActive(viewpointDefaultButton, viewpointTopButton);
      renderer.render(scene, activeCamera);
    });

    // Initialize
    resizeRenderer();
    createGrid(segments);
    updateGrid();
    requestAnimationFrame(animate);
  </script>
</body>
