            else parts.push(fmt(a) + 'z^2');
        }
        // b z
        if (b !== 0){
            let s = (b > 0 && parts.length) ? ' + ' : (b < 0 && parts.length ? ' - ' : (b < 0 ? '-' : ''));
            let mag = Math.abs(b);
            let term = (mag === 1) ? 'z' : (fmt(mag)+'z');
            parts.push(s + term);
        }
        // c
        if (c !== 0 || parts.length === 0){
            let s = (c > 0 && parts.length) ? ' + ' : (c < 0 && parts.length ? ' - ' : (c < 0 ? '-' : ''));
            parts.push(s + fmt(Math.abs(c)));
        }
        // Join and clean spaces around leading sign
        let tex = parts.join('').replace(/^\s\+\s/,'').replace(/^\s-\s/,'-');
        return tex;
    }
    function setFormula(a,b,c){
        const tex = polyToTeX(a,b,c);
        // Put inline TeX into the placeholder
        formulaText.textContent = ''; // clear
        formulaText.innerHTML = '\\(' + tex + '\\)';
        if (window.MathJax && MathJax.typesetPromise){
            MathJax.typesetPromise([document.getElementById('formula-display'), document.getElementById('results-table')]).catch(()=>{});
        }
    }

    // Geometry computations
    function computeBaseGeometry(){
        L1.x = a;   L1.y = 0;
        L2.x = a;   L2.y = b;
        L3.x = a - c; L3.y = b;

        // Circle center and radius
        Cc.x = (a - c)/2;
        Cc.y = b/2;
        rC = Math.sqrt((a - c)*(a - c) + b*b)/2;

        // Recenter the view on bounding box of O,L1,L2,L3 and circle extremes
        const xmin = Math.min(0, L1.x, L2.x, L3.x, Cc.x - rC);
        const xmax = Math.max(0, L1.x, L2.x, L3.x, Cc.x + rC);
        const ymin = Math.min(0, L1.y, L2.y, L3.y, Cc.y - rC);
        const ymax = Math.max(0, L1.y, L2.y, L3.y, Cc.y + rC);
        viewCenter.x = 0.5*(xmin + xmax);
        viewCenter.y = 0.5*(ymin + ymax);
        return {xmin, xmax, ymin, ymax};
    }

    function computeTestGeometry(){
        // L1' and L2'
        L1p.x = a;          L1p.y = -z * a;
        L2p.x = a + b*z + a*z*z;
        L2p.y = b;
    }

    function evaluatePz(){
        return a*z*z + b*z + c;
    }

    // p5 rendering
    let unitToPixels = 100; // scaled every frame
    let cnv;

    function worldToScreen(pt){
        const s = unitToPixels;
        const x = (pt.x - viewCenter.x) * s + width/2;
        const y = height/2 - (pt.y - viewCenter.y) * s;
        return {x, y};
    }
    function drawPoint(pt, radiusPx=4, col=[0,0,0]){
        const s = unitToPixels;
        push();
        noStroke();
        fill(col);
        // draw in world space with size in pixels
        const r = radiusPx / s;
        ellipse(pt.x, pt.y, r*2, r*2);
        pop();
    }
    function drawSegment(A,B, col=[0,0,0], wPx=2){
        const s = unitToPixels;
        push();
        stroke(col);
        strokeWeight(wPx / s);
        noFill();
        line(A.x, A.y, B.x, B.y);
        pop();
    }
    function drawCircle(center, radius, col=[0,0,0], wPx=1){
        const s = unitToPixels;
        push();
        noFill();
        stroke(col);
        strokeWeight(wPx / s);
        ellipse(center.x, center.y, 2*radius, 2*radius);
        pop();
    }
    function niceStep(range){
        // range is half-extent (plotRange)
        const targetLines = 10; // across the full width (2*range)
        const ideal = (2*range)/targetLines;
        const pow10 = Math.pow(10, Math.floor(Math.log10(ideal)));
        const choices = [1, 2, 5];
        let best = choices[0]*pow10;
        let minDiff = Math.abs(ideal - best);
        for (let c of choices){
            let v = c*pow10;
            let diff = Math.abs(ideal - v);
            if (diff < minDiff){
                minDiff = diff; best = v;
            }
        }
        return best;
    }

    function drawGridAndAxes(){
        const s = unitToPixels;
        const halfX = plotRange;
        const halfY = plotRange * (height/width);
        const xmin = viewCenter.x - halfX, xmax = viewCenter.x + halfX;
        const ymin = viewCenter.y - halfY, ymax = viewCenter.y + halfY;

        const step = niceStep(plotRange);

        if (showGridLines){
            push();
            stroke(220); // light gray
            strokeWeight(1 / s);
            // vertical lines
            let xStart = Math.ceil(xmin / step) * step;
            for (let x = xStart; x <= xmax + 1e-9; x += step){
                line(x, ymin, x, ymax);
            }
            // horizontal lines
            let yStart = Math.ceil(ymin / step) * step;
            for (let y = yStart; y <= ymax + 1e-9; y += step){
                line(xmin, y, xmax, y);
            }
            pop();
        }

        if (showAxes){
            // Axes lines at x=0 and y=0 if inside view
            push();
            stroke(0);
            strokeWeight(1.8 / s);
            // x-axis
            if (ymin <= 0 && ymax >= 0){
                line(xmin, 0, xmax, 0);
            }
            // y-axis
            if (xmin <= 0 && xmax >= 0){
                line(0, ymin, 0, ymax);
            }
            pop();

            // Numeric labels along axes in screen space for crisp text
            const fontPx = 12;
            // x-axis labels
            if (ymin <= 0 && ymax >= 0){
                let xStart = Math.ceil(xmin / step) * step;
                for (let x = xStart; x <= xmax + 1e-9; x += step){
                    if (Math.abs(x) < 1e-9) continue; // skip origin, handled by y labels
                    const p = worldToScreen({x, y: 0});
                    drawScreenText(fmt(x), p.x, p.y + 14, 'center', 'top', fontPx);
                }
            }
            // y-axis labels
            if (xmin <= 0 && xmax >= 0){
                let yStart = Math.ceil(ymin / step) * step;
                for (let y = yStart; y <= ymax + 1e-9; y += step){
                    if (Math.abs(y) < 1e-9) continue;
                    const p = worldToScreen({x: 0, y});
                    drawScreenText(fmt(y), p.x + 6, p.y - 2, 'left', 'middle', fontPx);
                }
            }
            // Origin label
            if (xmin <= 0 && xmax >= 0 && ymin <= 0 && ymax >= 0){
                const p0 = worldToScreen({x:0,y:0});
                drawScreenText('0', p0.x + 6, p0.y + 10, 'left', 'top', fontPx);
            }
        }
    }

    function drawScreenText(txt, sx, sy, align='left', baseline='alphabetic', size=12){
        // Draw text in screen coordinates (upright)
        push();
        resetMatrix(); // remove world transforms
        textSize(size);
        fill(40);
        noStroke();
        drawingContext.textAlign = align;
        drawingContext.textBaseline = baseline;
        text(txt, sx, sy);
        pop();
    }

    // p5 setup and draw
    window.setup = function(){
        const container = document.getElementById('canvas-container');
        const w = container.clientWidth;
        const h = Math.max(420, container.clientHeight || 520);
        cnv = createCanvas(w, h);
        cnv.parent('canvas-container');
        noLoop();

        initControls();
        computeBaseGeometry();
        computeTestGeometry();
        updateTableAndLabels();
        redraw();
    };

    window.windowResized = function(){
        const container = document.getElementById('canvas-container');
        const w = container.clientWidth;
        const h = Math.max(420, container.clientHeight || 520);
        resizeCanvas(w, h);
        redraw();
    };

    window.draw = function(){
        background(255);

        // Determine scale: plotRange units from center to edge horizontally.
        unitToPixels = (width/2) / plotRange;

        // World transform: center, flip y, scale, translate to view center
        push();
        translate(width/2, height/2);
        scale(1, -1);
        scale(unitToPixels, unitToPixels);
        translate(-viewCenter.x, -viewCenter.y);

        // Optional grid and axes
        drawGridAndAxes();

        // Coefficient path O->L1->L2->L3
        drawSegment(O, L1, [0,0,0], 2);
        drawSegment(L1, L2, [0,0,0], 2);
        drawSegment(L2, L3, [0,0,0], 2);
        drawPoint(O, 4, [0,0,0]);
        drawPoint(L1, 4, [0,0,0]);
        drawPoint(L2, 4, [0,0,0]);
        drawPoint(L3, 4, [0,0,0]);

        // Test path
        drawSegment(O, L1p, [0,0,0], 2);
        drawSegment(L1p, L2p, [192, 38, 211], 3); // magenta
        drawSegment(L2p, L3, [225, 29, 72], 3);   // red
        drawPoint(L1p, 4, [0,0,0]);
        drawPoint(L2p, 4, [0,0,0]);

        // Circle
        if (showCircle){
            drawCircle(Cc, rC, [0,0,0], 1.5);
        }

        // Labels
        if (showLabels){
            const labels = [
                {pt:O,   name:'O'},
                {pt:L1,  name:'L1'},
                {pt:L2,  name:'L2'},
                {pt:L3,  name:"L3"},
                {pt:L1p, name:"L1'"},
                {pt:L2p, name:"L2'"},
                ...(showCircle ? [{pt:Cc, name:'C'}] : [])
            ];
            for (const lab of labels){
                const sp = worldToScreen(lab.pt);
                drawScreenText(lab.name, sp.x + 8, sp.y - 6, 'left', 'middle', 13);
            }
        }

        pop();
    };

    // Update UI table and labels
    function updateTableAndLabels(){
        const Pz = evaluatePz();
        const lengthErr = Math.abs(Pz); // equals |L3-L2'| since y are equal

        labelZ.textContent = fmt(z);
        labelPlot.textContent = fmt(plotRange);

        valuePz.textContent = fmt(Pz);
        valueLerr.textContent = fmt(lengthErr);
        valueZ.textContent = fmt(z);
    }

    // Event handlers
    sliderZ.addEventListener('input', () => {
        z = parseFloat(sliderZ.value);
        computeTestGeometry();
        updateTableAndLabels();
        redraw();
    });

    sliderPlot.addEventListener('input', () => {
        plotRange = parseFloat(sliderPlot.value);
        labelPlot.textContent = fmt(plotRange);
        redraw();
    });

    checkLabels.addEventListener('change', () => {
        showLabels = checkLabels.checked;
        redraw();
    });
    checkCircle.addEventListener('change', () => {
        showCircle = checkCircle.checked;
        redraw();
    });
    checkAxes.addEventListener('change', () => {
        showAxes = checkAxes.checked;
        redraw();
    });
    checkGrid.addEventListener('change', () => {
        showGridLines = checkGrid.checked;
        redraw();
    });

    function randomInt(min, max){
        return Math.floor(Math.random()*(max - min + 1)) + min;
    }
    function newRandomCoefficients(){
        // a in [-3,-1] U [1,3]
        const aChoices = [-3,-2,-1, 1,2,3];
        a = aChoices[Math.floor(Math.random()*aChoices.length)];
        b = randomInt(-5, 5);
        c = randomInt(-5, 5);
    }
    function fitViewAndMaybeZoom(){
        // Compute bbox, then ensure plotRange fits geometry with margin
        const bb = computeBaseGeometry();
        const halfW = 0.5*(bb.xmax - bb.xmin);
        const halfH = 0.5*(bb.ymax - bb.ymin);
        const margin = 1.15; // 15% padding
        // Convert vertical half-extent to horizontal-equivalent using aspect
        // We'll choose range to fit the larger of horizontal and vertical
        const canvasAspect = width / Math.max(1, height);
        // If we use plotRange as horizontal half-width, vertical half visible equals plotRange*height/width
        // Required horizontal half-range to include vertical extent is halfH * (width/height)
        const required = Math.max(halfW, halfH * (width/height));
        const targetRange = Math.max(1, Math.min(10, margin * required));
        plotRange = targetRange;
        sliderPlot.value = plotRange;
        labelPlot.textContent = fmt(plotRange);
    }

    btnNew.addEventListener('click', () => {
        newRandomCoefficients();
        setFormula(a,b,c);
        computeBaseGeometry();
        computeTestGeometry();
        updateTableAndLabels();
        fitViewAndMaybeZoom();
        redraw();
    });

    btnGolden.addEventListener('click', () => {
        a = 1; b = -1; c = -1;
        setFormula(a,b,c);
        computeBaseGeometry();
        computeTestGeometry();
        updateTableAndLabels();
        fitViewAndMaybeZoom();
        redraw();
    });

    // Ensure MathJax renders initial content once it's ready
    document.addEventListener('DOMContentLoaded', () => {
        // After MathJax loads asynchronously, typeset the page
        const checkMJ = setInterval(() => {
            if (window.MathJax && MathJax.typesetPromise){
                setFormula(a,b,c); // sets with TeX delimiters
                MathJax.typesetPromise([document.body]).finally(() => clearInterval(checkMJ));
            }
        }, 100);
    });
})();
</script>

