  // Pre-calculated-like dataset generation (embedded in JS)
  const solutionCurveData = (() => {
    const data = [];
    const N = 900; // resolution of curve
    for (let k = 0; k <= N; k++) {
      const s = k / N; // parameter along curve [0,1]
      // Compose alpha in [0, 0.07] with mild loops
      let alpha = 0.035
                + 0.025 * Math.sin(2 * Math.PI * s)
                + 0.010 * Math.sin(6 * Math.PI * s + 0.8);
      // Clamp numerically to domain
