  })();

  // Compute turning points (where slope dT2/dalpha changes sign). Pick six strongest.
  const turningPointsData = (() => {
    const candidates = [];
    const eps = 1e-9;
    for (let i = 1; i < solutionCurveData.length - 1; i++) {
      const p0 = solutionCurveData[i - 1];
      const p1 = solutionCurveData[i];
      const p2 = solutionCurveData[i + 1];
      const da1 = p1.alpha - p0.alpha;
      const da2 = p2.alpha - p1.alpha;
      if (Math.abs(da1) < eps || Math.abs(da2) < eps) continue;
