      const s2 = (p2.t2 - p1.t2) / da2;
      if (s1 === 0 || s2 === 0) continue;
      if (s1 * s2 < 0) {
        const strength = Math.abs(s2 - s1);
        candidates.push({ alpha: p1.alpha, t2: p1.t2, strength });
      }
    }
    // Sort by strength and pick top 6, enforce spacing
    candidates.sort((a, b) => b.strength - a.strength);
    const picked = [];
    const minAlphaGap = 0.004;
    const minT2Gap = 0.35;
    for (const c of candidates) {
      let ok = true;
      for (const p of picked) {
        if (Math.abs(p.alpha - c.alpha) < minAlphaGap && Math.abs(p.t2 - c.t2) < minT2Gap) {
          ok = false; break;
        }
      }
      if (ok) picked.push({ alpha: c.alpha, t2: c.t2 });
      if (picked.length >= 6) break;
    }
    // If fewer than 6, pad with well-spaced samples
    if (picked.length < 6) {
      const extraNeeded = 6 - picked.length;
      const step = Math.floor(solutionCurveData.length / (extraNeeded + 1));
      for (let j = 1; j <= extraNeeded; j++) {
        const idx = j * step;
        picked.push({ alpha: solutionCurveData[idx].alpha, t2: solutionCurveData[idx].t2 });
      }
    }
