    picked.sort((a, b) => a.alpha - b.alpha);
    return picked;
  })();

  // Utility: linear interpolation
  function lerp(a, b, t) { return a + t * (b - a); }

  // Compute intersections of vertical line alpha0 with the polyline of solutionCurveData
  function computeIntersections(alpha0) {
    const res = [];
    const eps = 1e-9;
    for (let i = 0; i < solutionCurveData.length - 1; i++) {
      const p = solutionCurveData[i];
      const q = solutionCurveData[i + 1];

      const a1 = p.alpha;
      const a2 = q.alpha;
      const da = a2 - a1;

      // Skip nearly vertical segments to avoid degeneracy
      if (Math.abs(da) < 1e-7) continue;

      const minA = Math.min(a1, a2) - 1e-12;
      const maxA = Math.max(a1, a2) + 1e-12;

      if (alpha0 >= minA && alpha0 <= maxA) {
        const t = (alpha0 - a1) / da; // param within segment
        if (t >= -eps && t <= 1 + eps) {
          const x1 = lerp(p.x1, q.x1, t);
          const x2 = lerp(p.x2, q.x2, t);
          const t1 = lerp(p.t1, q.t1, t);
          const t2 = lerp(p.t2, q.t2, t);

          // De-duplicate close solutions (can happen at vertices)
          let duplicate = false;
          for (const r of res) {
            if (Math.abs(r.t2 - t2) < 1e-4) { duplicate = true; break; }
          }
          if (!duplicate) {
            res.push({ alpha: alpha0, x1, x2, t1, t2 });
          }
        }
      }
    }
    // Sort by T2 ascending
    res.sort((u, v) => u.t2 - v.t2);
    return res;
  }

  // --- Table (solution list) ---
  function updateTable() {
    const tbody = document.getElementById('solutions-table-body');
    tbody.innerHTML = '';
    const sols = computeIntersections(currentAlpha);

    if (sols.length === 0) {
      const tr = document.createElement('tr');
      const td = document.createElement('td');
      td.colSpan = 5;
      td.textContent = 'No solutions at this α';
      tr.appendChild(td);
      tbody.appendChild(tr);
      return;
    }

    sols.forEach((s, idx) => {
      const tr = document.createElement('tr');

      const tdRoot = document.createElement('td');
      tdRoot.textContent = (idx + 1).toString();
      tr.appendChild(tdRoot);

      const tdX1 = document.createElement('td');
      tdX1.textContent = s.x1.toFixed(5);
      tr.appendChild(tdX1);

      const tdX2 = document.createElement('td');
      tdX2.textContent = s.x2.toFixed(5);
      tr.appendChild(tdX2);

      const tdT1 = document.createElement('td');
      tdT1.textContent = s.t1.toFixed(5);
      tr.appendChild(tdT1);

      const tdT2 = document.createElement('td');
      tdT2.textContent = s.t2.toFixed(5);
      tr.appendChild(tdT2);

      tbody.appendChild(tr);
    });
  }

  // --- View switching ---
  function setActiveButton(buttonId) {
    const ids = ['btn-plot', 'btn-table', 'btn-turning-points'];
    ids.forEach(id => {
      const btn = document.getElementById(id);
      if (id === buttonId) btn.classList.add('active');
      else btn.classList.remove('active');
    });
  }

  function setView(view) {
    currentView = view;

    const plotView = document.getElementById('plot-view');
    const tableView = document.getElementById('table-view');
    const paramControls = document.getElementById('parameter-controls');

    if (view === 'plot_solutions') {
      plotView.style.display = 'block';
      tableView.style.display = 'none';
      paramControls.classList.remove('hidden');
      setActiveButton('btn-plot');
      // Redraw plot
      if (typeof redraw === 'function') redraw();
    } else if (view === 'table') {
      plotView.style.display = 'none';
      tableView.style.display = 'block';
      paramControls.classList.remove('hidden');
      setActiveButton('btn-table');
      updateTable();
    } else if (view === 'plot_turning_points') {
      plotView.style.display = 'block';
      tableView.style.display = 'none';
      paramControls.classList.add('hidden');
      setActiveButton('btn-turning-points');
      if (typeof redraw === 'function') redraw();
    }
  }

  // Update alpha display with 3 or 4 decimals
  function formatAlpha(val) {
    // Prefer 3 decimals; use 4 when it provides additional significant digit
    const v = Number(val);
    const s3 = v.toFixed(3);
    const s4 = v.toFixed(4);
    return (Math.abs(parseFloat(s3) - v) < 5e-5) ? s3 : s4;
  }

  // --- Event listeners for UI ---
  window.addEventListener('DOMContentLoaded', () => {
    // Initialize slider and display
    const slider = document.getElementById('slider-alpha');
    const display = document.getElementById('alpha-value-display');
    slider.value = String(currentAlpha);
    display.textContent = formatAlpha(currentAlpha);

    slider.addEventListener('input', (e) => {
      currentAlpha = parseFloat(e.target.value);
      display.textContent = formatAlpha(currentAlpha);
      if (currentView === 'plot_solutions') {
        if (typeof redraw === 'function') redraw();
      } else if (currentView === 'table') {
        updateTable();
      }
    });

    // View buttons
    document.getElementById('btn-plot').addEventListener('click', () => setView('plot_solutions'));
    document.getElementById('btn-table').addEventListener('click', () => setView('table'));
    document.getElementById('btn-turning-points').addEventListener('click', () => setView('plot_turning_points'));

    // Info toggle (panel optional)
    document.getElementById('btn-info').addEventListener('click', () => {
      const panel = document.getElementById('info-panel');
      panel.classList.toggle('visible');
    });
  });

  // --- p5.js sketch (global mode) ---
  let canvasW = 640;
  let canvasH = 600;
  const margin = { left: 70, right: 26, top: 26, bottom: 60 };

  function setup() {
    const cnv = createCanvas(canvasW, canvasH);
    cnv.parent('plot-view');
    pixelDensity(1);
    textFont('Arial, Helvetica, sans-serif');
    textSize(12);
    noLoop();
  }

  function draw() {
    background(255);

    // Plot area rectangle
    const x0 = margin.left;
    const y0 = margin.top;
    const x1 = width - margin.right;
    const y1 = height - margin.bottom;

    // Draw grid and axes
    drawGridAndAxes(x0, y0, x1, y1);

    // Draw main solution curve in red
    stroke(255, 0, 0);
    strokeWeight(2);
    noFill();
    beginShape();
    for (const d of solutionCurveData) {
      const px = mapAlphaToX(d.alpha, x0, x1);
      const py = mapT2ToY(d.t2, y0, y1);
      vertex(px, py);
    }
    endShape();

    // Depending on view, draw extras
    if (currentView === 'plot_solutions') {
      // Vertical dashed green line at currentAlpha
      const xAlpha = mapAlphaToX(currentAlpha, x0, x1);
      drawDashedLine(xAlpha, y0, y1, color(0, 180, 0), 6, 6);

      // Intersections: blue dots
      const sols = computeIntersections(currentAlpha);
      noStroke();
      fill(0, 0, 255);
      for (const s of sols) {
        const px = mapAlphaToX(s.alpha, x0, x1);
        const py = mapT2ToY(s.t2, y0, y1);
        circle(px, py, 10);
      }
    } else if (currentView === 'plot_turning_points') {
      // Green dots for turning points
      noStroke();
      fill(0, 180, 0);
      for (const p of turningPointsData) {
        const px = mapAlphaToX(p.alpha, x0, x1);
        const py = mapT2ToY(p.t2, y0, y1);
        circle(px, py, 10);
      }
    }
  }

  // Coordinate transforms
  function mapAlphaToX(alpha, x0, x1) {
    return x0 + (x1 - x0) * (alpha - alphaMin) / (alphaMax - alphaMin);
  }
  function mapT2ToY(t2, y0, y1) {
    // invert Y
    return y1 - (y1 - y0) * (t2 - t2Min) / (t2Max - t2Min);
  }

  // Draw grid, axes, ticks, labels
  function drawGridAndAxes(x0, y0, x1, y1) {
    // Background plot area
    noStroke();
    fill(252);
    rect(x0, y0, x1 - x0, y1 - y0);

    // Grid lines
    stroke(220);
    strokeWeight(1);
    // Vertical grid: alpha ticks every 0.01
    for (let a = alphaMin; a <= alphaMax + 1e-10; a += 0.01) {
      const x = mapAlphaToX(a, x0, x1);
      line(x, y0, x, y1);
    }
    // Additional minor verticals every 0.005
    stroke(235);
    for (let a = alphaMin + 0.005; a < alphaMax; a += 0.01) {
      const x = mapAlphaToX(a, x0, x1);
      line(x, y0, x, y1);
    }
    // Horizontal grid: T2 ticks every 1
    stroke(220);
    for (let t = t2Min; t <= t2Max + 1e-10; t += 1) {
      const y = mapT2ToY(t, y0, y1);
      line(x0, y, x1, y);
    }

    // Axes
    stroke(0);
    strokeWeight(1.5);
    // X-axis (alpha)
    line(x0, y1, x1, y1);
    // Y-axis (T2)
    line(x0, y0, x0, y1);

    // Tick labels
    fill(0);
    noStroke();
    textAlign(CENTER, TOP);
    for (let a = alphaMin; a <= alphaMax + 1e-10; a += 0.01) {
      const x = mapAlphaToX(a, x0, x1);
      const label = a.toFixed(2);
      text(label, x, y1 + 6);
    }
    textAlign(RIGHT, CENTER);
    for (let t = t2Min; t <= t2Max + 1e-10; t += 1) {
      const y = mapT2ToY(t, y0, y1);
      text(t.toFixed(0), x0 - 8, y);
    }

    // Axis labels
    // X label
    textAlign(CENTER, TOP);
    textStyle(BOLD);
    text('α', (x0 + x1) / 2, y1 + 28);
    // Y label (rotated)
    push();
    translate(x0 - 44, (y0 + y1) / 2);
    rotate(-Math.PI / 2);
    text('T₂(α)', 0, 0);
    pop();
    textStyle(NORMAL);
  }

  function drawDashedLine(x, yStart, yEnd, col, dash = 6, gap = 6) {
    stroke(col);
    strokeWeight(2);
    const dir = yEnd > yStart ? 1 : -1;
    let y = yStart;
    while ((dir > 0 && y < yEnd) || (dir < 0 && y > yEnd)) {
      const y2 = y + dir * dash;
      line(x, y, x, Math.abs(y2 - yEnd) < dash ? yEnd : y2);
      y += dir * (dash + gap);
    }
  }
</script>
</body>
</html>
