<script>
  // State variables and defaults
  let spiralType = 'double';        // 'single' or 'double' (default: 'double')
  let itemType   = 'point';         // 'none', 'point', 'fox', 'filled-fox' (default: 'point')
  let spiralGrowth = 0.15;          // default
  const RESET_GROWTH_VALUE = 0.25;  // reset target as specified

  // Locator positions in world coordinates (pixels, origin at canvas center)
  let z0_loc     = { x: 0,    y: 0 };
  let z_plus_loc = { x: -100, y: 0 };
  let z_minus_loc= { x: 100,  y: 0 };

  // Dragging flags
  const dragging = { z0: false, zp: false, zm: false };

  // UI Elements
  const btnTypeSingle = document.getElementById('btn-type-single');
  const btnTypeDouble = document.getElementById('btn-type-double');
  const btnItemNone   = document.getElementById('btn-item-none');
  const btnItemPoint  = document.getElementById('btn-item-point');
  const btnItemFox    = document.getElementById('btn-item-fox');
  const btnItemFoxF   = document.getElementById('btn-item-filled-fox');

  const sliderGrowth  = document.getElementById('slider-growth');
  const displayGrowth = document.getElementById('display-growth');
  const btnResetGrowth= document.getElementById('btn-reset-growth');

  // Attach UI listeners
  function setActive(groupButtons, activeBtn) {
    groupButtons.forEach(b => b.classList.remove('active'));
    activeBtn.classList.add('active');
  }

  btnTypeSingle.addEventListener('click', () => {
    spiralType = 'single';
    setActive([btnTypeSingle, btnTypeDouble], btnTypeSingle);
  });
  btnTypeDouble.addEventListener('click', () => {
    spiralType = 'double';
    setActive([btnTypeSingle, btnTypeDouble], btnTypeDouble);
  });

  btnItemNone.addEventListener('click', () => {
    itemType = 'none';
    setActive([btnItemNone, btnItemPoint, btnItemFox, btnItemFoxF], btnItemNone);
  });
  btnItemPoint.addEventListener('click', () => {
    itemType = 'point';
    setActive([btnItemNone, btnItemPoint, btnItemFox, btnItemFoxF], btnItemPoint);
  });
  btnItemFox.addEventListener('click', () => {
    itemType = 'fox';
    setActive([btnItemNone, btnItemPoint, btnItemFox, btnItemFoxF], btnItemFox);
  });
  btnItemFoxF.addEventListener('click', () => {
    itemType = 'filled-fox';
    setActive([btnItemNone, btnItemPoint, btnItemFox, btnItemFoxF], btnItemFoxF);
  });

  function updateGrowthDisplay() {
    displayGrowth.textContent = parseFloat(spiralGrowth).toFixed(4);
  }
  sliderGrowth.addEventListener('input', (e) => {
    spiralGrowth = parseFloat(e.target.value);
    updateGrowthDisplay();
  });
  btnResetGrowth.addEventListener('click', () => {
    spiralGrowth = RESET_GROWTH_VALUE;
    sliderGrowth.value = String(RESET_GROWTH_VALUE);
    updateGrowthDisplay();
  });

  // Initialize display
  updateGrowthDisplay();

  // Complex helpers (math coordinates: re = x, im = y upward)
  function c(re, im){ return {re, im}; }
  function cAdd(a, b){ return {re: a.re + b.re, im: a.im + b.im}; }
  function cSub(a, b){ return {re: a.re - b.re, im: a.im - b.im}; }
  function cMul(a, b){ return {re: a.re*b.re - a.im*b.im, im: a.re*b.im + a.im*b.re}; }
  function cDiv(a, b){
    const d = b.re*b.re + b.im*b.im || 1e-12;
    return {re: (a.re*b.re + a.im*b.im)/d, im: (a.im*b.re - a.re*b.im)/d};
  }
  function cScale(a, s){ return {re: a.re*s, im: a.im*s}; }
  function cPow2(a){ return cMul(a, a); }
  function cExpKIt(k, t){
    const r = Math.exp(k * t);
    return {re: r * Math.cos(t), im: r * Math.sin(t)};
  }

  // Mapping between world (canvas, y down) and complex (math, y up)
  function worldToComplex(pt){ return { re: pt.x, im: -pt.y }; }
  function complexToWorld(z){ return { x: z.re, y: -z.im }; }

  // p5 sketch
  const sketch = (p) => {
    let cnv;

    const COLORS = {
      curve: '#4A6DA8',
      point: '#CB3335',
      fox:   '#D6292D',
      plus:  'rgba(255,255,0,0.8)', // z_plus/yellow
      minus: 'rgba(0,255,0,0.8)',   // z_minus/green
    };

    const FOX_VERTICES = [
      {x:-15, y:0}, {x:-10, y:-5}, {x:-5, y:-4}, {x:0, y:-5},
      {x:5, y:-5}, {x:8, y:-2}, {x:10, y:-6}, {x:12, y:-6},
      {x:15, y:0}, {x:12, y:6}, {x:10, y:6}, {x:8, y:2},
      {x:5, y:5}, {x:0, y:5}, {x:-5, y:4}, {x:-10, y:5}
    ];

    function initCanvas() {
      const container = document.getElementById('canvas-container');
      const w = container.clientWidth;
      const h = container.clientHeight;
      cnv = p.createCanvas(w, h);

      // Replace placeholder canvas element with the p5 canvas to preserve the required ID
      const placeholder = document.getElementById('p5-canvas');
      if (placeholder && placeholder !== cnv.elt) {
        cnv.id('p5-canvas-temp');
        placeholder.replaceWith(cnv.elt);
        cnv.id('p5-canvas');
      }
      cnv.parent('canvas-container');
    }

    p.setup = function(){
      initCanvas();
      p.noStroke();
      p.textFont('sans-serif');
      p.textSize(12);
    };

    p.windowResized = function() {
      const container = document.getElementById('canvas-container');
      p.resizeCanvas(container.clientWidth, container.clientHeight);
    };

    function isMouseInCanvas() {
      return p.mouseX >= 0 && p.mouseX <= p.width && p.mouseY >= 0 && p.mouseY <= p.height;
    }
    function mouseWorld() {
      return { x: p.mouseX - p.width/2, y: p.mouseY - p.height/2 };
    }
    function dist2(a, b){ const dx=a.x-b.x, dy=a.y-b.y; return dx*dx+dy*dy; }

    p.mousePressed = function() {
      if (!isMouseInCanvas()) return;
      const m = mouseWorld();
      const r = 10; // locator radius
      if (spiralType === 'single') {
        if (dist2(m, z0_loc) <= r*r) dragging.z0 = true;
      } else {
        if (dist2(m, z_plus_loc) <= r*r) dragging.zp = true;
        else if (dist2(m, z_minus_loc) <= r*r) dragging.zm = true;
      }
    };

    p.mouseDragged = function() {
      if (!isMouseInCanvas()) return;
      const m = mouseWorld();
      if (dragging.z0) z0_loc = m;
      if (dragging.zp) z_plus_loc = m;
      if (dragging.zm) z_minus_loc = m;
    };

    p.mouseReleased = function() {
      dragging.z0 = dragging.zp = dragging.zm = false;
    };

    function drawAxes() {
      p.stroke(230);
      p.strokeWeight(1);
      p.line(-p.width/2, 0, p.width/2, 0);
      p.line(0, -p.height/2, 0, p.height/2);
      // light grid
      p.stroke(240);
      const step = 100;
      for (let x = 0; x <= p.width/2; x += step) {
        p.line(x, -p.height/2, x, p.height/2);
        p.line(-x, -p.height/2, -x, p.height/2);
      }
      for (let y = 0; y <= p.height/2; y += step) {
        p.line(-p.width/2, y, p.width/2, y);
        p.line(-p.width/2, -y, p.width/2, -y);
      }
    }

    function drawLocator(pt, colorFill) {
      p.push();
      p.noFill();
      p.stroke(0);
      p.strokeWeight(1);
      p.fill(colorFill);
      const r = 10;
      p.circle(pt.x, pt.y, r*2);
      p.line(pt.x - r, pt.y, pt.x + r, pt.y);
      p.line(pt.x, pt.y - r, pt.x, pt.y + r);
      p.pop();
    }

    function nearAnyLocator() {
      const m = mouseWorld();
      const r2 = 10*10;
      if (spiralType === 'single') return dist2(m, z0_loc) <= r2;
      return dist2(m, z_plus_loc) <= r2 || dist2(m, z_minus_loc) <= r2;
    }

    function drawSpiralSingle() {
      const k = spiralGrowth;
      const T = 8 * Math.PI;
      const dt = 0.05;

      // Draw curve
      p.push();
      p.noFill();
      p.stroke(COLORS.curve);
      p.strokeWeight(2);
      p.beginShape();
      // z(t) = z0 + exp((k + i) t)
      // Work in complex then map to world to respect sign of y
      const z0c = worldToComplex(z0_loc);
      for (let t = -T; t <= T; t += dt) {
        const w = cExpKIt(k, t);
        const z = cAdd(z0c, w);
        const pos = complexToWorld(z);
        p.curveVertex(pos.x, pos.y);
      }
      p.endShape();

      // Items along the curve
      if (itemType !== 'none') {
        const stepMark = Math.PI * 0.75;
        for (let t = -T; t <= T; t += stepMark) {
          const w = cExpKIt(k, t);
          const z = cAdd(z0c, w);
          const pos = complexToWorld(z);
          // derivative z' = (k + i) w
          const kpI = c(k, 1);
          const dz = cMul(kpI, w);
          const dx = dz.re;
          const dy = -dz.im;
          const ang = Math.atan2(dy, dx);
          drawItemAt(pos, ang, Math.min(1.2, 0.9 + 0.15 * Math.exp(0.05 * t)));
        }
      }
      p.pop();
    }

    function drawSpiralDouble() {
      const k = spiralGrowth;
      const T = 8 * Math.PI;
      const dt = 0.05;
      const eps = 0.01;
      const pC = worldToComplex(z_plus_loc);
      const qC = worldToComplex(z_minus_loc);

      function zFromT(t) {
        const w = cExpKIt(k, t);
        const numerator = cSub(cMul(pC, w), qC);
        const denominator = cSub(w, c(1, 0));
        return { z: cDiv(numerator, denominator), w };
      }

      function drawBranch(tStart, tEnd, step) {
        p.beginShape();
        for (let t = tStart; (tStart < tEnd) ? t <= tEnd : t >= tEnd; t += step) {
          if (Math.abs(t) < eps) continue; // avoid pole
          const { z } = zFromT(t);
          const pos = complexToWorld(z);
          p.curveVertex(pos.x, pos.y);
        }
        p.endShape();
      }

      // Draw curve branches
      p.push();
      p.noFill();
      p.stroke(COLORS.curve);
      p.strokeWeight(2);
      drawBranch(-T, -eps, dt);
      drawBranch(eps, T, dt);
      p.pop();

      // Items along both branches
      if (itemType !== 'none') {
        const stepMark = Math.PI * 0.75;
        // Negative branch
        for (let t = -T; t <= -eps; t += stepMark) {
          const { z, w } = zFromT(t);
          const pos = complexToWorld(z);
          // z'(t) = (p - q) * (k + i) * w / (w - 1)^2
          const kpI = c(k, 1);
          const num = cMul(cSub(pC, qC), cMul(kpI, w));
          const den = cPow2(cSub(w, c(1, 0)));
          const dz = cDiv(num, den);
          const dx = dz.re;
          const dy = -dz.im;
          const ang = Math.atan2(dy, dx);
          drawItemAt(pos, ang, 1.0);
        }
        // Positive branch
        for (let t = eps; t <= T; t += stepMark) {
          const { z, w } = zFromT(t);
          const pos = complexToWorld(z);
          const kpI = c(k, 1);
          const num = cMul(cSub(pC, qC), cMul(kpI, w));
          const den = cPow2(cSub(w, c(1, 0)));
          const dz = cDiv(num, den);
          const dx = dz.re;
          const dy = -dz.im;
          const ang = Math.atan2(dy, dx);
          drawItemAt(pos, ang, 1.0);
        }
      }
    }

    function drawItemAt(pos, angle, scaleFactor=1.0) {
      if (itemType === 'point') {
        p.push();
        p.noStroke();
        p.fill(COLORS.point);
        p.circle(pos.x, pos.y, 5);
        p.pop();
      } else if (itemType === 'fox' || itemType === 'filled-fox') {
        p.push();
        p.translate(pos.x, pos.y);
        p.rotate(angle);
        p.scale(scaleFactor);
        if (itemType === 'fox') {
          p.noFill();
          p.stroke(COLORS.fox);
          p.strokeWeight(1);
        } else {
          p.noStroke();
          p.fill(COLORS.fox);
        }
        p.beginShape();
        FOX_VERTICES.forEach(v => p.vertex(v.x, v.y));
        p.endShape(p.CLOSE);
        p.pop();
      }
    }

    function drawLocatorsAndLabels() {
      p.push();
      p.textAlign(p.LEFT, p.BOTTOM);
      p.fill(0);
      p.noStroke();

      if (spiralType === 'single') {
        drawLocator(z0_loc, 'rgba(255,255,0,0.8)');
        p.text('z0Loc', z0_loc.x + 12, z0_loc.y - 12);
      } else {
        drawLocator(z_plus_loc, 'rgba(255,255,0,0.8)');
        drawLocator(z_minus_loc, 'rgba(0,255,0,0.8)');
        const mid = { x: (z_plus_loc.x + z_minus_loc.x)/2, y: (z_plus_loc.y + z_minus_loc.y)/2 };
        p.text('z0Loc', mid.x + 12, mid.y - 12);
      }
      p.pop();
    }

    p.draw = function() {
      p.background(255);
      p.push();
      p.translate(p.width/2, p.height/2);

      // Cursor hints
      if (dragging.z0 || dragging.zp || dragging.zm) {
        p.cursor('grabbing');
      } else if (isMouseInCanvas() && nearAnyLocator()) {
        p.cursor('grab');
      } else {
        p.cursor('default');
      }

      drawAxes();
      if (spiralType === 'single') {
        drawSpiralSingle();
      } else {
        drawSpiralDouble();
      }
      drawLocatorsAndLabels();

      p.pop();
    };
  };

  // Boot p5
  new p5(sketch);
</script>
</body>
</html>
