    display: flex;
    gap: 8px;
  }
</style>
</head>
<body>
<div class="app-root">
  <div id="control-panel">
    <div class="section">
      <button id="start-over-btn">start over</button>
    </div>

    <div class="section">
      <label>initial vertex</label>
      <div class="vertex-grid" id="vertex-grid">
        <button id="btn-m">m</button>
        <button id="btn-n">n</button>
        <button id="btn-o">o</button>
        <button id="btn-p">p</button>
        <button id="btn-i">i</button>
        <button id="btn-j">j</button>
        <button id="btn-k">k</button>
        <button id="btn-l">l</button>
        <button id="btn-e">e</button>
        <button id="btn-f">f</button>
        <button id="btn-g">g</button>
        <button id="btn-h">h</button>
        <button id="btn-a">a</button>
        <button id="btn-b">b</button>
        <button id="btn-c">c</button>
        <button id="btn-d">d</button>
      </div>
    </div>

    <div class="section">
      <div class="row">
        <button id="fixed-example-btn">fixed example</button>
        <button id="random-example-btn">random example</button>
      </div>
    </div>

    <div class="section">
      <label>implement algorithm</label>
      <button id="next-step-btn">next step</button>
    </div>
  </div>

  <div id="viz-container">
    <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
    <div id="canvas-container"></div>
  </div>
</div>

<script>
  // ======== Graph and Algorithm State ========
  const letters = ['m','n','o','p','i','j','k','l','e','f','g','h','a','b','c','d'];
  const rows = [
    ['m','n','o','p'],
    ['i','j','k','l'],
    ['e','f','g','h'],
    ['a','b','c','d']
  ];

  const neighborPairs = []; // unique undirected neighbor edges (24)
  (function buildNeighborPairs(){
    // Horizontal neighbors
    for (let r = 0; r < 4; r++) {
      for (let c = 0; c < 3; c++) {
        neighborPairs.push([rows[r][c], rows[r][c+1]]);
      }
    }
    // Vertical neighbors
    for (let r = 0; r < 3; r++) {
      for (let c = 0; c < 4; c++) {
        neighborPairs.push([rows[r][c], rows[r+1][c]]);
      }
    }
  })();

  const positions = {}; // letter -> {x,y}
  const neighbors = {}; // adjacency list: u -> [{v,w}, ...]
  let weightMap = {};   // 'a-b' (sorted key) -> weight
  let startVertex = 'm';
  let visited = new Set();
  let dist = {};
  let pred = {};
  let p5ctx = null;

  // ======== Utility ========
  function keyFor(u, v) {
    return [u, v].sort().join('-');
  }

  function rebuildNeighborsFromWeights() {
    for (const v of letters) neighbors[v] = [];
    for (const [u, v] of neighborPairs) {
      const w = weightMap[keyFor(u, v)];
      neighbors[u].push({ v, w });
      neighbors[v].push({ v: u, w });
    }
  }

  function setActiveVertexButton(letter) {
    for (const L of letters) {
      const btn = document.getElementById(`btn-${L}`);
      if (btn) btn.classList.toggle('active', L === letter);
    }
  }

  function updateTitle() {
    const titleEl = document.getElementById('title-display');
    titleEl.textContent = `Dijkstra's algorithm starting at vertex ${startVertex}`;
  }

  function enableNextStep(enabled) {
    const btn = document.getElementById('next-step-btn');
    btn.disabled = !enabled;
  }

  function allVisited() {
    return visited.size === letters.length;
  }

  function initAlgorithmState() {
    visited = new Set();
    pred = {};
    dist = {};
    for (const v of letters) dist[v] = Infinity;
    dist[startVertex] = 0;
    enableNextStep(true);
  }

  // ======== Edge Weights ========
  function applyFixedExample() {
    const W = {};
    // Horizontal rows
    W[keyFor('m','n')] = 8;  W[keyFor('n','o')] = 7;  W[keyFor('o','p')] = 7;
    W[keyFor('i','j')] = 9;  W[keyFor('j','k')] = 7;  W[keyFor('k','l')] = 1;
    W[keyFor('e','f')] = 1;  W[keyFor('f','g')] = 7;  W[keyFor('g','h')] = 6;
    W[keyFor('a','b')] = 10; W[keyFor('b','c')] = 4;  W[keyFor('c','d')] = 8;
    // Vertical columns
    W[keyFor('m','i')] = 6;  W[keyFor('i','e')] = 9;  W[keyFor('e','a')] = 9;
    W[keyFor('n','j')] = 7;  W[keyFor('j','f')] = 6;  W[keyFor('f','b')] = 5;
    W[keyFor('o','k')] = 1;  W[keyFor('k','g')] = 8;  W[keyFor('g','c')] = 8;
    W[keyFor('p','l')] = 2;  W[keyFor('l','h')] = 1;  W[keyFor('h','d')] = 7;

    weightMap = W;
    rebuildNeighborsFromWeights();
  }

  function applyRandomExample() {
    const W = {};
    for (const [u, v] of neighborPairs) {
      W[keyFor(u, v)] = 1 + Math.floor(Math.random() * 10);
    }
    weightMap = W;
    rebuildNeighborsFromWeights();
  }

  // ======== Dijkstra Step ========
  function nextStep() {
    // Pick unvisited with smallest tentative distance
    let bestV = null;
    let bestD = Infinity;
    for (const v of letters) {
      if (!visited.has(v) && dist[v] < bestD) {
        bestD = dist[v];
        bestV = v;
      }
    }
    if (bestV === null || bestD === Infinity) {
      enableNextStep(false);
      if (p5ctx) p5ctx.redraw();
      return;
    }

    // Visit bestV
    visited.add(bestV);

    // Relax edges
    for (const { v: nb, w } of neighbors[bestV]) {
      if (visited.has(nb)) continue;
      const newDist = dist[bestV] + w;
      if (newDist < dist[nb]) {
        dist[nb] = newDist;
        pred[nb] = bestV;
      }
    }

    if (allVisited()) enableNextStep(false);
    if (p5ctx) p5ctx.redraw();
  }

  // ======== UI Wiring ========
  function wireUI() {
    // Vertex buttons
    for (const v of letters) {
      const btn = document.getElementById(`btn-${v}`);
      if (!btn) continue;
      btn.addEventListener('click', () => {
        startVertex = v;
        setActiveVertexButton(startVertex);
        updateTitle();
        initAlgorithmState();
        if (p5ctx) p5ctx.redraw();
      });
    }

    // Start over
    document.getElementById('start-over-btn').addEventListener('click', () => {
      startVertex = 'm';
      setActiveVertexButton(startVertex);
      updateTitle();
      applyFixedExample();
      initAlgorithmState();
      if (p5ctx) p5ctx.redraw();
    });

    // Fixed example
    document.getElementById('fixed-example-btn').addEventListener('click', () => {
      applyFixedExample();
      initAlgorithmState();
      if (p5ctx) p5ctx.redraw();
    });

    // Random example
    document.getElementById('random-example-btn').addEventListener('click', () => {
      applyRandomExample();
      initAlgorithmState();
      if (p5ctx) p5ctx.redraw();
    });

    // Next step
    document.getElementById('next-step-btn').addEventListener('click', () => {
      nextStep();
    });
  }

  // ======== p5 Sketch ========
  function sketch(s) {
    p5ctx = s;

    s.setup = function() {
      const cnv = s.createCanvas(600, 600);
      cnv.parent('canvas-container');
      s.noLoop();
      s.textFont('Arial, sans-serif');
      s.textSize(14);
      s.strokeCap(s.ROUND);

      // Compute vertex positions in a 4x4 grid
      const pad = 80;
      const step = (s.width - 2 * pad) / 3;
      for (let r = 0; r < 4; r++) {
        for (let c = 0; c < 4; c++) {
          const v = rows[r][c];
          positions[v] = { x: pad + c * step, y: pad + r * step };
        }
      }

      // Initial state on load
      applyFixedExample();
      startVertex = 'm';
      setActiveVertexButton(startVertex);
      updateTitle();
      initAlgorithmState();
      s.redraw();
    };

    s.draw = function() {
      s.clear();
      s.background(250);

      drawGraph(s);
    };
  }

  function drawGraph(s) {
    // Draw edges (default black thin)
    s.push();
    s.stroke(0);
    s.strokeWeight(1.5);

    s.textAlign(s.CENTER, s.CENTER);
    s.textSize(14);

    for (const [u, v] of neighborPairs) {
      const p1 = positions[u];
      const p2 = positions[v];
      s.stroke(0);
      s.line(p1.x, p1.y, p2.x, p2.y);

      // Weight label centered with background
      const w = weightMap[keyFor(u, v)];
      const mx = (p1.x + p2.x) / 2;
      const my = (p1.y + p2.y) / 2;

      const label = String(w);
      const tw = s.textWidth(label) + 6;
      const th = 18;

      s.noStroke();
      s.fill(255);
      s.rectMode(s.CENTER);
      s.rect(mx, my, tw, th, 4);

      s.fill(0);
      s.text(label, mx, my);
    }
    s.pop();

    // Draw shortest-path tree edges (visited vertices with predecessors) as thick blue
    s.push();
    s.stroke(30, 100, 220);
    s.strokeWeight(5);
    for (const v of letters) {
      if (visited.has(v) && pred[v]) {
        const u = pred[v];
        const p1 = positions[u];
        const p2 = positions[v];
        s.line(p1.x, p1.y, p2.x, p2.y);
      }
    }
    s.pop();

    // Draw vertices
    for (const v of letters) {
      const { x, y } = positions[v];
      const isVisited = visited.has(v);

      // Vertex circle
      s.stroke(0);
      s.strokeWeight(2);
      s.fill(isVisited ? s.color(220, 30, 30) : s.color(255));
      s.circle(x, y, 22);

      // Vertex letter (black)
      s.noStroke();
      s.fill(0);
      s.textAlign(s.LEFT, s.CENTER);
      s.textSize(16);
      s.text(v, x + 14, y);

      // Distance label above vertex
      const label = isFinite(dist[v]) ? `(${dist[v]})` : `(∞)`;
      s.textSize(14);
      s.textAlign(s.CENTER, s.BOTTOM);
      s.fill(isVisited ? s.color(220, 30, 30) : s.color(0));
      s.text(label, x, y - 16);
    }
  }

  // ======== Initialize ========
  window.addEventListener('DOMContentLoaded', () => {
    wireUI();
    // Mark default active button m
    setActiveVertexButton('m');
    enableNextStep(true);
    new p5(sketch);
  });
</script>
</body>
</html>
