  <!-- Application logic -->
  <script>
    (function() {
      // DOM references
      const container = document.getElementById('canvas-container');
      const sliderSphereDiameter = document.getElementById('slider-sphere-diameter');
      const sliderConeHeight = document.getElementById('slider-cone-height');
      const sliderConeBase = document.getElementById('slider-cone-base');

      const btnCube = document.getElementById('btn-cube');
      const btnDodeca = document.getElementById('btn-dodecahedron');
      const btnIcosa = document.getElementById('btn-icosahedron');
      const btnOcta = document.getElementById('btn-octahedron');
      const btnTetra = document.getElementById('btn-tetrahedron');

      // Three.js essentials
      const scene = new THREE.Scene();
      const renderer = new THREE.WebGLRenderer({ antialias: true });
      renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
      renderer.setClearColor(0xf0f0f0);
      container.appendChild(renderer.domElement);

      const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 100);
      camera.position.set(0, 0, 8);

      const controls = new THREE.OrbitControls(camera, renderer.domElement);
      controls.enableDamping = true;
      controls.dampingFactor = 0.08;
      controls.target.set(0, 0, 0);

      // Resize handling
      function onResize() {
        const w = container.clientWidth;
        const h = container.clientHeight;
        renderer.setSize(w, h, false);
        camera.aspect = w / h;
        camera.updateProjectionMatrix();
      }
      window.addEventListener('resize', onResize);

      // Lights
      const ambient = new THREE.AmbientLight(0x404040, 1.0);
      scene.add(ambient);

      const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.9);
      dirLight1.position.set(1, 1, 1);
      scene.add(dirLight1);

      const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
      dirLight2.position.set(-1, -1, -1);
      scene.add(dirLight2);

      // Materials
      const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 50, specular: 0x888888 });
      const coneMaterial = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30, specular: 0x996600 });

      // Sphere
      const defaultSphereDiameter = 1.5; // as specified
      const defaultSphereRadius = defaultSphereDiameter / 2;
      const sphereGeometry = new THREE.SphereGeometry(defaultSphereRadius, 64, 32);
      const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
      scene.add(sphere);

      // Cones store
      let cones = [];
      let currentPolyhedron = 'Octahedron';

      // Helpers: get actual sphere radius from slider
      function getSphereRadius() {
        return parseFloat(sliderSphereDiameter.value) / 2;
      }
      function getConeHeight() {
        return parseFloat(sliderConeHeight.value);
      }
      function getConeBaseRadius() {
        return parseFloat(sliderConeBase.value);
      }

      // Deduplicate vertices and return normalized directions
      function getPlatonicVertices(type) {
        let geom;
        switch (type) {
          case 'Cube': {
            // Box with size 2 -> vertices at ±1
            geom = new THREE.BoxGeometry(2, 2, 2, 1, 1, 1);
            break;
          }
          case 'Dodecahedron': {
            geom = new THREE.DodecahedronGeometry(1, 0);
            break;
          }
          case 'Icosahedron': {
            geom = new THREE.IcosahedronGeometry(1, 0);
            break;
          }
          case 'Octahedron': {
            geom = new THREE.OctahedronGeometry(1, 0);
            break;
          }
          case 'Tetrahedron': {
            geom = new THREE.TetrahedronGeometry(1, 0);
            break;
          }
          default:
            geom = new THREE.OctahedronGeometry(1, 0);
        }

        const pos = geom.attributes.position.array;
        const seen = new Set();
        const verts = [];
        for (let i = 0; i < pos.length; i += 3) {
          const x = pos[i], y = pos[i + 1], z = pos[i + 2];
          const key = `${Math.round(x * 1e5)},${Math.round(y * 1e5)},${Math.round(z * 1e5)}`;
          if (!seen.has(key)) {
            seen.add(key);
            const v = new THREE.Vector3(x, y, z).normalize();
            verts.push(v);
          }
        }
        geom.dispose();
        return verts;
      }

      // Remove all cones
      function removeAllCones() {
        cones.forEach(mesh => {
          scene.remove(mesh);
          if (mesh.geometry) mesh.geometry.dispose();
        });
        cones = [];
      }

      // Create cones for given vertex directions
      function createCones(vertexDirs) {
        const sphereRadius = getSphereRadius();
        const height = getConeHeight();
        const radius = getConeBaseRadius();
        const segments = 32;

        vertexDirs.forEach(dir => {
          const geom = new THREE.ConeGeometry(radius, height, segments, 1, false);
          const cone = new THREE.Mesh(geom, coneMaterial);

          // Position so base lies on sphere surface:
          const centerPos = dir.clone().multiplyScalar(sphereRadius + height / 2);
          cone.position.copy(centerPos);

          // Orient so axis (+Y) aligns with direction vector
          const up = new THREE.Vector3(0, 1, 0);
          cone.quaternion.setFromUnitVectors(up, dir);

          // Store the original direction for future updates
          cone.userData.direction = dir.clone();

          scene.add(cone);
          cones.push(cone);
        });
      }

      // Update cones geometry and optionally position
      function updateConesGeometry({ updateHeight = false, updateRadius = false, updatePositions = false } = {}) {
        const height = getConeHeight();
        const radius = getConeBaseRadius();
        const sphereRadius = getSphereRadius();

        cones.forEach(cone => {
          if (updateHeight || updateRadius) {
            // Replace geometry
            const newGeom = new THREE.ConeGeometry(radius, height, 32, 1, false);
            const oldGeom = cone.geometry;
            cone.geometry = newGeom;
            oldGeom.dispose();
          }
          if (updatePositions || updateHeight) {
            const dir = cone.userData.direction;
            const centerPos = dir.clone().multiplyScalar(sphereRadius + height / 2);
            cone.position.copy(centerPos);
          }
        });
      }

      // Update sphere scaling according to slider (scale relative to default)
      function updateSphereScale() {
        const newDiameter = parseFloat(sliderSphereDiameter.value);
        const scale = newDiameter / defaultSphereDiameter;
        sphere.scale.set(scale, scale, scale);
      }

      // Button state management
      function setActiveButton(activeButton) {
        const buttons = [
          btnCube, btnDodeca, btnIcosa, btnOcta, btnTetra
        ];
        buttons.forEach(btn => {
          const isActive = (btn === activeButton);
          btn.classList.toggle('active', isActive);
          btn.setAttribute('aria-pressed', isActive ? 'true' : 'false');
        });
      }

      // Build initial scene
      function init() {
        onResize();

        // Initial sphere scale (should already match default but ensure)
        updateSphereScale();

        // Initial polyhedron: Octahedron
        const verts = getPlatonicVertices('Octahedron');
        createCones(verts);

        animate();
      }

      // Animation loop
      function animate() {
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
      }

      // Event handlers
      sliderSphereDiameter.addEventListener('input', () => {
        updateSphereScale();
        // Move cones so bases stay on sphere
        updateConesGeometry({ updatePositions: true });
      });

      sliderConeHeight.addEventListener('input', () => {
        // Replace geometry and reposition
        updateConesGeometry({ updateHeight: true, updatePositions: true });
      });

      sliderConeBase.addEventListener('input', () => {
        // Replace geometry only, positions unchanged
        updateConesGeometry({ updateRadius: true });
      });

      btnCube.addEventListener('click', () => {
        currentPolyhedron = 'Cube';
        setActiveButton(btnCube);
        removeAllCones();
        createCones(getPlatonicVertices('Cube'));
      });
      btnDodeca.addEventListener('click', () => {
        currentPolyhedron = 'Dodecahedron';
        setActiveButton(btnDodeca);
        removeAllCones();
        createCones(getPlatonicVertices('Dodecahedron'));
      });
      btnIcosa.addEventListener('click', () => {
        currentPolyhedron = 'Icosahedron';
        setActiveButton(btnIcosa);
        removeAllCones();
        createCones(getPlatonicVertices('Icosahedron'));
      });
      btnOcta.addEventListener('click', () => {
        currentPolyhedron = 'Octahedron';
        setActiveButton(btnOcta);
        removeAllCones();
        createCones(getPlatonicVertices('Octahedron'));
      });
      btnTetra.addEventListener('click', () => {
        currentPolyhedron = 'Tetrahedron';
        setActiveButton(btnTetra);
        removeAllCones();
        createCones(getPlatonicVertices('Tetrahedron'));
      });

      // Kick off
      init();
    })();
  </script>
</body>
</html>
