  <!-- p5.js library -->
  <script src="https://cdn.jsdelivr.net/npm/p5@1.9.3/lib/p5.min.js"></script>

  <script>
    // Predefined Planet Data
    const planetData = {
      "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
      "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
    };

    // DOM elements
    const selectPlanet = document.getElementById('select-planet');
    const checkboxManual = document.getElementById('checkbox-manual');

    const sliderTilt = document.getElementById('slider-tilt');
    const sliderEquinox = document.getElementById('slider-equinox');
    const sliderEcc = document.getElementById('slider-eccentricity');

    const checkboxSnapshots = document.getElementById('checkbox-snapshots');
    const checkboxScales = document.getElementById('checkbox-scales');

    // Helpers
    const deg2rad = d => d * Math.PI / 180;
    const rad2deg = r => r * 180 / Math.PI;
    function wrapAngle(rad) {
      const twoPi = Math.PI * 2;
      let x = rad % twoPi;
      if (x <= -Math.PI) x += twoPi;
      if (x > Math.PI) x -= twoPi;
      return x;
    }

    function setManualEnabled(enabled) {
      sliderTilt.disabled = !enabled;
      sliderEquinox.disabled = !enabled;
      sliderEcc.disabled = !enabled;
    }

    // Interaction logic
    selectPlanet.addEventListener('change', () => {
      const val = selectPlanet.value;
      if (val === 'manual') {
        checkboxManual.checked = true;
        setManualEnabled(true);
      } else {
        const d = planetData[val];
        if (d) {
          sliderTilt.value = d.tilt;
          sliderEcc.value = d.eccentricity;
          sliderEquinox.value = d.equinox;
        }
        checkboxManual.checked = false;
        setManualEnabled(false);
      }
      requestRedraw();
    });

    checkboxManual.addEventListener('change', () => {
      const on = checkboxManual.checked;
      setManualEnabled(on);
      if (on) {
        selectPlanet.value = 'manual';
      }
      requestRedraw();
    });

    // Sliders redraw on input
    sliderTilt.addEventListener('input', requestRedraw);
    sliderEquinox.addEventListener('input', requestRedraw);
    sliderEcc.addEventListener('input', requestRedraw);

    // Display toggles
    checkboxSnapshots.addEventListener('change', requestRedraw);
    checkboxScales.addEventListener('change', requestRedraw);

    // p5 sketch
    let sketch = (p) => {
      let topOrbitArea = 120; // px reserved for orbit diagram at top
      let canvas;

      p.setup = function() {
        const dim = getCanvasSize();
        canvas = p.createCanvas(dim.w, dim.h);
        canvas.id('analemma-canvas');
        canvas.parent('visualization-area');
        p.noLoop();
        p.pixelDensity(1);
        p.textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial');
        p.textSize(12);
        requestRedraw(); // initial draw
      };

      p.windowResized = function() {
        const dim = getCanvasSize();
        p.resizeCanvas(dim.w, dim.h, true);
        requestRedraw();
      };

      function getCanvasSize() {
        const container = document.getElementById('visualization-area');
        const styles = getComputedStyle(container);
        const padX = parseFloat(styles.paddingLeft) + parseFloat(styles.paddingRight);
        const padY = parseFloat(styles.paddingTop) + parseFloat(styles.paddingBottom);
        const w = container.clientWidth - padX;
        const h = container.clientHeight - padY;
        return { w: Math.max(300, w), h: Math.max(300, h) };
      }

      function readState() {
        const tiltDeg = parseFloat(sliderTilt.value);
        const equinoxDeg = parseFloat(sliderEquinox.value);
        const ecc = parseFloat(sliderEcc.value);
        const showSnapshots = checkboxSnapshots.checked;
        const showScales = checkboxScales.checked;
        return { tiltDeg, equinoxDeg, ecc, showSnapshots, showScales };
      }

      function solveKepler(M, e) {
        // Normalize M to [-pi, pi]
        M = wrapAngle(M);
        let E = M; // initial guess
        // Newton-Raphson
        for (let i = 0; i < 10; i++) {
          const f = E - e * Math.sin(E) - M;
          const fp = 1 - e * Math.cos(E);
          const dE = f / fp;
          E -= dE;
          if (Math.abs(dE) < 1e-10) break;
        }
        return E;
      }

      function computeAnalemmaPoints(tiltRad, ecc, equinoxRad) {
        const pts = [];
        // One year worth of days
        const N = 366; // include end
        for (let d = 0; d < N; d++) {
          const M = 2 * Math.PI * d / 365.25; // mean anomaly
          const E = solveKepler(M, ecc); // eccentric anomaly
          const sinE2 = Math.sin(E / 2);
          const cosE2 = Math.cos(E / 2);
          const v = 2 * Math.atan2(Math.sqrt(1 + ecc) * sinE2, Math.sqrt(1 - ecc) * cosE2); // true anomaly
          const lambda = wrapAngle(v + equinoxRad); // ecliptic longitude from vernal equinox

          const sinLambda = Math.sin(lambda);
          const cosLambda = Math.cos(lambda);

          // Declination
          const y = Math.asin(Math.sin(tiltRad) * sinLambda);

          // Right ascension
          const alpha = Math.atan2(Math.cos(tiltRad) * sinLambda, cosLambda);

          // Equation of Time (simplified per plan)
          let EoT = wrapAngle(M - alpha);

          // Convert to degrees for plotting consistency
          const xDeg = rad2deg(EoT);
          const yDeg = rad2deg(y);

          pts.push({ x: xDeg, y: yDeg });
        }
        return pts;
      }

      function drawOrbitDiagram(x, y, ecc) {
        p.push();
        p.translate(x, y);
        p.noFill();
        p.strokeWeight(2);
        p.stroke(p.color(139, 90, 43)); // brown
        const base = 45; // base semi-axis
        const rx = base * (1 + ecc); // horizontal radius grows with eccentricity
        const ry = base * Math.sqrt(Math.max(0, 1 - ecc * ecc)); // vertical shrinks with eccentricity
        p.ellipse(0, 0, rx * 2, ry * 2);

        // Perihelion/aphelion markers for context (optional small markers)
        p.noStroke();
        p.fill(p.color(139, 90, 43));
        p.circle(-rx, 0, 4); // perihelion (left)
        p.circle(rx, 0, 4);  // aphelion (right)
        p.pop();
      }

      function drawScales(cx, cy, scalePxPerDeg) {
        p.push();
        p.translate(cx, cy);
        p.stroke(200);
        p.strokeWeight(1.5);
        // Axes
        p.line(-p.width, 0, p.width, 0);
        p.line(0, -p.height, 0, p.height);

        // Ticks every 10 degrees
        const maxXDeg = Math.floor((p.width * 0.45) / scalePxPerDeg);
        const maxYDeg = Math.floor(((p.height - topOrbitArea) * 0.45) / scalePxPerDeg);

        p.stroke(180);
        p.fill(100);
        p.textAlign(p.CENTER, p.TOP);

        // X-axis ticks and labels
        for (let x = -maxXDeg; x <= maxXDeg; x += 10) {
          const px = x * scalePxPerDeg;
          p.line(px, -5, px, 5);
          if (x % 20 === 0) {
            p.text(x.toString(), px, 8);
          }
        }
        // Y-axis ticks and labels
        p.textAlign(p.RIGHT, p.CENTER);
        for (let y = -maxYDeg; y <= maxYDeg; y += 10) {
          const py = y * scalePxPerDeg;
          p.line(-5, py, 5, py);
          if (y % 20 === 0) {
            // Avoid overlapping with x-axis labels at origin
            if (y !== 0) p.text(y.toString(), -8, py);
          }
        }
        p.pop();
      }

      function drawAnalemma(cx, cy, scalePxPerDeg, pts, showCurve=true, showDots=true) {
        p.push();
        p.translate(cx, cy);

        if (showCurve) {
          p.noFill();
          p.stroke(p.color(255, 140, 0));
          p.strokeWeight(2);
          p.beginShape();
          for (let i = 0; i < pts.length; i++) {
            p.vertex(pts[i].x * scalePxPerDeg, -pts[i].y * scalePxPerDeg);
          }
          p.endShape();
        }

        if (showDots) {
          p.noStroke();
          p.fill(p.color(255, 140, 0));
          for (let i = 0; i < pts.length; i += 20) {
            p.circle(pts[i].x * scalePxPerDeg, -pts[i].y * scalePxPerDeg, 8);
          }
        }
        p.pop();
      }

      p.draw = function() {
        const { tiltDeg, equinoxDeg, ecc, showSnapshots, showScales } = readState();
        p.background(255);

        // Layout
        const cx = p.width / 2;
        const cy = (topOrbitArea + (p.height - topOrbitArea) / 2);

        // Orbit diagram at top
        drawOrbitDiagram(cx, topOrbitArea / 2, ecc);

        // Compute scaling: aim to show +/- 50 deg nicely
        const targetHalfSpanDeg = 50; // degrees for both axes
        const availableW = p.width * 0.8;
        const availableH = (p.height - topOrbitArea) * 0.8;
        const scalePxPerDeg = Math.min(availableW, availableH) / (2 * targetHalfSpanDeg);

        // Draw scales if enabled
        if (showScales) {
          drawScales(cx, cy, scalePxPerDeg);
        }

        // Compute analemma points
        const tiltRad = deg2rad(tiltDeg);
        const equinoxRad = deg2rad(equinoxDeg);
        const pts = computeAnalemmaPoints(tiltRad, ecc, equinoxRad);

        // Draw curve and snapshots
        drawAnalemma(cx, cy, scalePxPerDeg, pts, true, showSnapshots);
      };
    };

    // Initialize p5 instance
    let p5Instance = new p5(sketch);

    // Function to request redraw (respect p5 noLoop)
    function requestRedraw() {
      if (p5Instance) p5Instance.redraw();
    }
  </script>
</body>
</html>
