    background: var(--accent);
    border-color: var(--accent);
    color: #fff;
  }
  button:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
  #visualization-panel {
    flex: 1;
    padding: 16px;
    overflow: auto;
  }
  #visualization-container {
    display: flex;
    gap: 16px;
    align-items: flex-start;
  }
  #algo-result-container, #optimal-result-container {
    background: var(--panel);
    border: 1px solid var(--border);
    border-radius: 10px;
    padding: 12px;
    min-width: 480px;
  }
  #algo-result-container h3, #optimal-result-container h3 {
    margin: 6px 0 8px 0;
  }
  #canvas-algo-wrapper, #canvas-optimal-wrapper {
    width: 460px;
    height: 460px;
    border: 1px solid var(--border);
    border-radius: 8px;
    background: white;
    overflow: hidden;
  }
  #table-container {
    margin-top: 10px;
  }
  #table-title { margin: 0 0 6px 0; }
  table {
    border-collapse: collapse;
    width: 100%;
    font-size: 14px;
  }
  th, td {
    border: 1px solid var(--border);
    padding: 6px 8px;
    text-align: left;
  }
  th {
    background: #f2f4ff;
  }
  #text-total-weight, #text-optimal-weight {
    font-weight: 600;
    margin-top: 8px;
  }
  #text-example-type-algo, #text-example-type-optimal {
    color: var(--muted);
    margin: 6px 0;
  }
  .radio-group { display: flex; gap: 10px; align-items: center; flex-wrap: wrap; }
</style>
</head>
<body>

<div id="app-container">
  <div id="control-panel">
    <div class="section">
      <label>algorithm</label><br/>
      <div class="radio-group">
        <input type="radio" id="algo-cheapest-link" name="algorithm-choice" value="cheapest-link">
        <label for="algo-cheapest-link">cheapest link</label>
        <input type="radio" id="algo-nearest-neighbor" name="algorithm-choice" value="nearest-neighbor" checked>
        <label for="algo-nearest-neighbor">nearest neighbor</label>
      </div>
    </div>

    <button id="btn-start-over">start over</button>

    <div class="section">
      <button id="btn-fixed-example">fixed example</button>
      <button id="btn-random-example">random example</button>
    </div>

    <div id="starting-vertex-selector" class="section">
      <label>starting vertex for nearest neighbor</label>
      <div id="vertex-buttons-container">
        <button id="btn-vertex-A" class="vertex-btn" data-vertex="A">A</button>
        <button id="btn-vertex-B" class="vertex-btn" data-vertex="B">B</button>
        <button id="btn-vertex-C" class="vertex-btn" data-vertex="C">C</button>
        <button id="btn-vertex-D" class="vertex-btn" data-vertex="D">D</button>
        <button id="btn-vertex-E" class="vertex-btn" data-vertex="E">E</button>
        <button id="btn-vertex-F" class="vertex-btn" data-vertex="F">F</button>
        <button id="btn-vertex-G" class="vertex-btn" data-vertex="G">G</button>
      </div>
    </div>

    <div class="section">
      <label>Implement algorithm</label><br/>
      <button id="btn-next-step" class="primary" disabled>next step</button>
    </div>

    <div class="section">
      <label for="checkbox-optimal">show optimal solution</label>
      <input type="checkbox" id="checkbox-optimal">
    </div>
  </div>

  <div id="visualization-panel">
    <div id="visualization-container">
      <div id="algo-result-container">
        <h3 id="title-algo">The nearest neighbor algorithm</h3>
        <div id="canvas-algo-wrapper"></div>
        <p id="text-example-type-algo">fixed example</p>
        <div id="table-container">
          <h4 id="table-title"></h4>
          <table id="table-edge-list"></table>
        </div>
        <p id="text-total-weight"></p>
      </div>

      <div id="optimal-result-container" style="display: none;">
        <h3 id="title-optimal">An optimal solution</h3>
        <div id="canvas-optimal-wrapper"></div>
        <p id="text-example-type-optimal"></p>
        <p id="text-optimal-weight"></p>
      </div>
    </div>
  </div>
</div>

<script>
  // Labels
  const LABELS = ["A","B","C","D","E","F","G"];
  const N = LABELS.length;

  // Global App State
  const AppState = {
    currentAlgorithm: 'nearest-neighbor', // 'nearest-neighbor' | 'cheapest-link'
    graph: null, // {positions:[{x,y}], weights:number[][]}
    exampleType: 'fixed', // 'fixed' | 'random'
    optimal: { path: [], weight: 0 },

    // NN state
    nn: {
      startIndex: null,
      currentIndex: null,
      visited: new Set(),
      selectedEdges: [], // [{u,v,w}]
      totalWeight: 0,
      complete: false
    },

    // CL state
    cl: {
      selectedEdges: [],
      degrees: Array(N).fill(0),
      totalWeight: 0,
      complete: false
    },

    // Edge order for CL
    allEdgesSorted: [],

    // p5 instances
    p5Algo: null,
    p5Optimal: null
  };

  // Utility: Create fixed graph
  function loadFixedGraph() {
    const positions = [
      {x:200, y:60},   // A
      {x:80,  y:120},  // B
      {x:60,  y:240},  // C
      {x:150, y:340},  // D
      {x:250, y:340},  // E
      {x:340, y:240},  // F
      {x:320, y:120}   // G
    ];
    // Adjacency matrix, 0 on diagonal
    const W = Array.from({length:N}, () => Array(N).fill(0));
    function set(i,j,w){ W[i][j]=w; W[j][i]=w; }
    set(0,1,11); set(0,2,16); set(0,3,44); set(0,4,30); set(0,5,44); set(0,6,23);
    set(1,2,12); set(1,3,12); set(1,4,10); set(1,5,30); set(1,6,25);
    set(2,3,43); set(2,4,32); set(2,5,36); set(2,6,44);
    set(3,4,41); set(3,5,30); set(3,6,35);
    set(4,5,31); set(4,6,20);
    set(5,6,18);
    AppState.graph = { positions, weights: W };
    AppState.exampleType = 'fixed';
    computeOptimal();
  }

  // Utility: Create random graph (keep positions on a circle for clarity)
  function loadRandomGraph() {
    const cx = 230, cy = 230, R = 160;
    const positions = Array.from({length:N}, (_,i) => {
      const ang = -Math.PI/2 + (i * 2*Math.PI/N);
      const jitter = 12;
      return {
        x: cx + R*Math.cos(ang) + (Math.random()*jitter - jitter/2),
        y: cy + R*Math.sin(ang) + (Math.random()*jitter - jitter/2)
      };
    });
    const W = Array.from({length:N}, () => Array(N).fill(0));
    for (let i=0;i<N;i++){
      for (let j=i+1;j<N;j++){
        const w = 10 + Math.floor(Math.random()*41); // 10..50
        W[i][j]=w; W[j][i]=w;
      }
    }
    AppState.graph = { positions, weights: W };
    AppState.exampleType = 'random';
    computeOptimal();
  }

  // Compute optimal Hamiltonian circuit via brute force (fix start at index 0)
  function computeOptimal() {
    const W = AppState.graph.weights;
    const vertices = [];
    for (let i=1;i<N;i++) vertices.push(i);

    let bestWeight = Infinity;
    let bestPath = null;

    function permute(arr, l) {
      if (l === arr.length) {
        let weight = 0;
        let prev = 0; // start at 0 (A)
        for (let k=0;k<arr.length;k++){
          weight += W[prev][arr[k]];
          prev = arr[k];
        }
        weight += W[prev][0]; // return to start
        if (weight < bestWeight) {
          bestWeight = weight;
          bestPath = [0, ...arr, 0];
        }
        return;
      }
      for (let i=l;i<arr.length;i++){
        [arr[l], arr[i]] = [arr[i], arr[l]];
        permute(arr, l+1);
        [arr[l], arr[i]] = [arr[i], arr[l]];
      }
    }
    permute(vertices, 0);
    AppState.optimal.path = bestPath;
    AppState.optimal.weight = bestWeight;
  }

  // Prepare CL sorted edge list
  function prepareAllEdgesSorted() {
    const W = AppState.graph.weights;
    const edges = [];
    for (let i=0;i<N;i++){
      for (let j=i+1;j<N;j++){
        edges.push({u:i, v:j, w:W[i][j]});
      }
    }
    edges.sort((a,b) => a.w - b.w || LABELS[a.u].localeCompare(LABELS[b.u]) || LABELS[a.v].localeCompare(LABELS[b.v]));
    AppState.allEdgesSorted = edges;
  }

  // Union-Find for CL
  class DSU {
    constructor(n){
      this.p = Array.from({length:n}, (_,i)=>i);
      this.r = Array(n).fill(0);
    }
    find(x){ return this.p[x]===x ? x : this.p[x]=this.find(this.p[x]); }
    union(a,b){
      a=this.find(a); b=this.find(b);
      if (a===b) return false;
      if (this.r[a]<this.r[b]) [a,b]=[b,a];
      this.p[b]=a;
      if (this.r[a]===this.r[b]) this.r[a]++;
      return true;
    }
  }
  AppState.clDSU = null;

  // Reset algorithm state
  function resetAlgorithmState() {
    // NN
    AppState.nn.startIndex = null;
    AppState.nn.currentIndex = null;
    AppState.nn.visited = new Set();
    AppState.nn.selectedEdges = [];
    AppState.nn.totalWeight = 0;
    AppState.nn.complete = false;

    // CL
    AppState.cl.selectedEdges = [];
    AppState.cl.degrees = Array(N).fill(0);
    AppState.cl.totalWeight = 0;
    AppState.cl.complete = false;
    AppState.clDSU = new DSU(N);

    // Prepare CL edges
    prepareAllEdgesSorted();

    // UI default title
    if (AppState.currentAlgorithm === 'nearest-neighbor') {
      setTitle("Select a starting vertex");
    } else {
      setTitle("The cheapest link algorithm");
    }

    // Table and totals
    clearEdgeTable();
    updateTotalsText();

    // Buttons
    if (AppState.currentAlgorithm === 'nearest-neighbor') {
      setVertexButtonsEnabled(true);
      setNextStepEnabled(false);
    } else {
      setVertexButtonsEnabled(false);
      setNextStepEnabled(true);
    }

    // Redraw canvases
    if (AppState.p5Algo) AppState.p5Algo.redraw();
    if (AppState.p5Optimal) AppState.p5Optimal.redraw();
  }

  function setTitle(text) {
    document.getElementById('title-algo').textContent = text;
  }

  function setVertexButtonsEnabled(enabled) {
    document.querySelectorAll('.vertex-btn').forEach(btn=>{
      btn.disabled = !enabled;
      btn.classList.remove('active');
    });
  }
  function setNextStepEnabled(enabled) {
    document.getElementById('btn-next-step').disabled = !enabled;
  }

  // Table helpers
  function clearEdgeTable() {
    document.getElementById('table-title').textContent = AppState.currentAlgorithm === 'nearest-neighbor' ?
      "Edges selected (nearest neighbor)" : "Edges selected (cheapest link)";
    const tbl = document.getElementById('table-edge-list');
    tbl.innerHTML = "";
    const header = document.createElement('tr');
    ["step","edge","weight"].forEach(h=>{
      const th = document.createElement('th');
      th.textContent = h;
      header.appendChild(th);
    });
    tbl.appendChild(header);
  }
  function appendEdgeRow(step, u, v, w) {
    const tbl = document.getElementById('table-edge-list');
    const tr = document.createElement('tr');
    const td1 = document.createElement('td'); td1.textContent = step;
    const td2 = document.createElement('td'); td2.textContent = `${LABELS[u]}-${LABELS[v]}`;
    const td3 = document.createElement('td'); td3.textContent = w;
    tr.appendChild(td1); tr.appendChild(td2); tr.appendChild(td3);
    tbl.appendChild(tr);
  }
  function updateTotalsText(final=false) {
    const p = document.getElementById('text-total-weight');
    let total = (AppState.currentAlgorithm === 'nearest-neighbor') ? AppState.nn.totalWeight : AppState.cl.totalWeight;
    if ((AppState.currentAlgorithm === 'nearest-neighbor' && AppState.nn.complete) ||
        (AppState.currentAlgorithm === 'cheapest-link' && AppState.cl.complete) || final) {
      p.textContent = `Final circuit weight: ${total}`;
    } else {
      p.textContent = total>0 ? `Total weight so far: ${total}` : "";
    }
  }

  // NN step
  function stepNearestNeighbor() {
    if (AppState.nn.complete) return;

    // On the first step after choosing a start, currentIndex is set already.
    // If not all visited, select nearest unvisited neighbor
    const W = AppState.graph.weights;
    const nVisited = AppState.nn.visited.size;

    if (nVisited < N) {
      // choose nearest unvisited from current
      const u = AppState.nn.currentIndex;
      let bestV = null;
      let bestW = Infinity;
      for (let v=0; v<N; v++){
        if (v===u) continue;
        if (AppState.nn.visited.has(v)) continue;
        const w = W[u][v];
        if (w < bestW || (w === bestW && LABELS[v] < LABELS[bestV||v])) {
          bestW = w; bestV = v;
        }
      }
      if (bestV !== null) {
        AppState.nn.selectedEdges.push({u, v: bestV, w: bestW});
        AppState.nn.totalWeight += bestW;
        AppState.nn.visited.add(bestV);
        AppState.nn.currentIndex = bestV;
        appendEdgeRow(AppState.nn.selectedEdges.length, u, bestV, bestW);
      } else {
        // No unvisited neighbors left (shouldn't happen before visiting all)
      }
    } else if (nVisited === N) {
      // Close the cycle back to start
      const u = AppState.nn.currentIndex;
      const v = AppState.nn.startIndex;
      const w = W[u][v];
      AppState.nn.selectedEdges.push({u, v, w});
      AppState.nn.totalWeight += w;
      appendEdgeRow(AppState.nn.selectedEdges.length, u, v, w);
      AppState.nn.complete = true;
      setNextStepEnabled(false);
    }

    // If we've just visited all N then next click should add return edge.
    if (AppState.nn.visited.size === N && !AppState.nn.complete) {
      // After this step, next step will close the cycle
    }
    if (AppState.nn.selectedEdges.length === N) {
      AppState.nn.complete = true;
      setNextStepEnabled(false);
    }

    updateTotalsText(AppState.nn.complete);
    if (AppState.p5Algo) AppState.p5Algo.redraw();
  }

  // CL step
  function stepCheapestLink() {
    if (AppState.cl.complete) return;
    const W = AppState.graph.weights;
    const degrees = AppState.cl.degrees;
    const dsu = AppState.clDSU;

    // Find the next valid edge (by cheapest first)
    let chosen = null;
    for (const e of AppState.allEdgesSorted) {
      const u = e.u, v = e.v, w = e.w;
      if (degrees[u] >= 2 || degrees[v] >= 2) continue;

      const createsCycle = (dsu.find(u) === dsu.find(v));
      const edgesPicked = AppState.cl.selectedEdges.length;

      if (createsCycle && edgesPicked < N-1) {
        continue; // premature cycle
      }
      if (edgesPicked === N-1) {
        // This should be the last edge closing the cycle; ensure endpoints have degree 1
        if (!(degrees[u] === 1 && degrees[v] === 1)) continue;
        // allowed
      }

      // If not the last edge and createsCycle=false, allowed
      chosen = e;
      break;
    }

    if (chosen) {
      const {u,v,w} = chosen;
      AppState.cl.selectedEdges.push({u,v,w});
      AppState.cl.totalWeight += w;
      degrees[u]++; degrees[v]++;
      // Union if not last edge only, but safe to union anyway
      AppState.clDSU.union(u,v);
      appendEdgeRow(AppState.cl.selectedEdges.length, u, v, w);
    }

    if (AppState.cl.selectedEdges.length === N) {
      AppState.cl.complete = true;
      setNextStepEnabled(false);
    }

    updateTotalsText(AppState.cl.complete);
    if (AppState.p5Algo) AppState.p5Algo.redraw();
  }

  // p5 drawing helpers
  function drawGraphBase(p, positions, weights) {
    p.background(255);
    p.stroke(180);
    p.strokeWeight(1);
    p.fill(255);

    // Draw all edges with weights
    for (let i=0;i<N;i++){
      for (let j=i+1;j<N;j++){
        const a = positions[i], b = positions[j];
        p.stroke(200);
        p.line(a.x, a.y, b.x, b.y);

        // weight label with white background
        const mx = (a.x + b.x)/2;
        const my = (a.y + b.y)/2;
        const label = String(weights[i][j]);
        p.textSize(13);
        p.noStroke();
        const pad = 3;
        const tw = p.textWidth(label);
        const th = 14;
        p.fill(255);
        p.rect(mx - tw/2 - pad, my - th/2 - pad, tw + 2*pad, th + 2*pad);
        p.fill(0);
        p.textAlign(p.CENTER, p.CENTER);
        p.text(label, mx, my);
      }
    }

    // Draw vertices
    for (let i=0;i<N;i++){
      const pos = positions[i];
      p.stroke(0);
      p.strokeWeight(1.5);
      p.fill(255);
      p.circle(pos.x, pos.y, 28);
      p.fill(0);
      p.noStroke();
      p.textAlign(p.CENTER, p.CENTER);
      p.textSize(14);
      p.text(LABELS[i], pos.x, pos.y);
    }
  }

  function drawHighlightedEdges(p, positions, edges, colorStr) {
    if (!edges || edges.length===0) return;
    p.stroke(colorStr);
    p.strokeWeight(4);
    for (const e of edges) {
      const a = positions[e.u];
      const b = positions[e.v];
      p.line(a.x, a.y, b.x, b.y);
    }
  }

  // p5 instances
  function createAlgoSketch() {
    const sketch = (p)=>{
      p.setup = () => {
        const canvas = p.createCanvas(460, 460);
        canvas.parent('canvas-algo-wrapper');
        p.noLoop();
      };
      p.draw = () => {
        if (!AppState.graph) return;
        drawGraphBase(p, AppState.graph.positions, AppState.graph.weights);
        // Highlight algorithm edges
        let edges = [];
        if (AppState.currentAlgorithm === 'nearest-neighbor') {
          edges = AppState.nn.selectedEdges;
        } else {
          edges = AppState.cl.selectedEdges;
        }
        drawHighlightedEdges(p, AppState.graph.positions, edges, '#ff8a00');
      };
    };
    AppState.p5Algo = new p5(sketch);
  }

  function createOptimalSketch() {
    const sketch = (p)=>{
      p.setup = () => {
        const canvas = p.createCanvas(460, 460);
        canvas.parent('canvas-optimal-wrapper');
        p.noLoop();
      };
      p.draw = () => {
        if (!AppState.graph) return;
        drawGraphBase(p, AppState.graph.positions, AppState.graph.weights);
        // Build edges from optimal path
        const path = AppState.optimal.path;
        const edges = [];
        for (let i=0;i<path.length-1;i++){
          edges.push({u: path[i], v: path[i+1]});
        }
        drawHighlightedEdges(p, AppState.graph.positions, edges, '#2b7cff');
      };
    };
    AppState.p5Optimal = new p5(sketch);
  }

  // UI interactions
  function bindUI() {
    const radioCL = document.getElementById('algo-cheapest-link');
    const radioNN = document.getElementById('algo-nearest-neighbor');
    const startOverBtn = document.getElementById('btn-start-over');
    const fixedBtn = document.getElementById('btn-fixed-example');
    const randomBtn = document.getElementById('btn-random-example');
    const nextStepBtn = document.getElementById('btn-next-step');
    const optimalCheckbox = document.getElementById('checkbox-optimal');

    // Algorithm selection
    function onAlgorithmChange() {
      AppState.currentAlgorithm = radioNN.checked ? 'nearest-neighbor' : 'cheapest-link';
      // Show/hide vertex selector
      document.getElementById('starting-vertex-selector').style.display =
        AppState.currentAlgorithm === 'nearest-neighbor' ? 'block' : 'none';
      document.getElementById('table-title').textContent = "";
      setTitle(AppState.currentAlgorithm === 'nearest-neighbor' ?
        "The nearest neighbor algorithm" : "The cheapest link algorithm");
      resetAlgorithmState();
      updateExampleTypeTexts();
    }
    radioCL.addEventListener('change', onAlgorithmChange);
    radioNN.addEventListener('change', onAlgorithmChange);

    // Start over
    startOverBtn.addEventListener('click', () => {
      resetAlgorithmState();
    });

    // Example selection
    fixedBtn.addEventListener('click', () => {
      loadFixedGraph();
      updateExampleTypeTexts();
      resetAlgorithmState();
      if (AppState.p5Algo) AppState.p5Algo.redraw();
      if (AppState.p5Optimal) AppState.p5Optimal.redraw();
    });
    randomBtn.addEventListener('click', () => {
      loadRandomGraph();
      updateExampleTypeTexts();
      resetAlgorithmState();
      if (AppState.p5Algo) AppState.p5Algo.redraw();
      if (AppState.p5Optimal) AppState.p5Optimal.redraw();
    });

    // Vertex selection buttons (NN)
    document.querySelectorAll('.vertex-btn').forEach(btn=>{
      btn.addEventListener('click', () => {
        if (AppState.currentAlgorithm !== 'nearest-neighbor') return;
        const label = btn.dataset.vertex;
        const idx = LABELS.indexOf(label);
        AppState.nn.startIndex = idx;
        AppState.nn.currentIndex = idx;
        AppState.nn.visited = new Set([idx]);
        // UI
        document.querySelectorAll('.vertex-btn').forEach(b=>{ b.classList.remove('active'); b.disabled = true; });
        btn.classList.add('active');
        setNextStepEnabled(true);
        setTitle(`The nearest neighbor algorithm starting at vertex ${label}`);
      });
    });

    // Next step
    nextStepBtn.addEventListener('click', () => {
      if (AppState.currentAlgorithm === 'nearest-neighbor') {
        // If visited size == N, next step closes the cycle
        if (AppState.nn.visited.size < N) {
          stepNearestNeighbor();
          // If after this step, visited size == N, auto close on next click
          if (AppState.nn.visited.size === N && !AppState.nn.complete) {
            // nothing else
          }
        } else {
          // close cycle
          stepNearestNeighbor();
        }
      } else {
        stepCheapestLink();
      }
    });

    // Optimal checkbox
    optimalCheckbox.addEventListener('change', () => {
      const cont = document.getElementById('optimal-result-container');
      if (optimalCheckbox.checked) {
        cont.style.display = 'block';
        if (!AppState.p5Optimal) {
          createOptimalSketch();
        } else {
          AppState.p5Optimal.redraw();
        }
        document.getElementById('text-optimal-weight').textContent = `Optimal circuit weight: ${AppState.optimal.weight}`;
      } else {
        cont.style.display = 'none';
      }
    });
  }

  function updateExampleTypeTexts() {
    const t = AppState.exampleType === 'fixed' ? 'fixed example' : 'random example';
    document.getElementById('text-example-type-algo').textContent = t;
    document.getElementById('text-example-type-optimal').textContent = t;
    // Update optimal weight text if visible
    if (document.getElementById('checkbox-optimal').checked) {
      document.getElementById('text-optimal-weight').textContent = `Optimal circuit weight: ${AppState.optimal.weight}`;
      if (AppState.p5Optimal) AppState.p5Optimal.redraw();
    }
  }

  // Initialization
  function init() {
    // Load fixed graph on start
    loadFixedGraph();

    // Initial UI per plan
    document.getElementById('title-algo').textContent = "The nearest neighbor algorithm";
    document.getElementById('text-example-type-algo').textContent = "fixed example";
    document.getElementById('table-title').textContent = "";
    document.getElementById('text-total-weight').textContent = "";

    // Disable vertex buttons until user starts over or chooses example (per plan)
    setVertexButtonsEnabled(false);
    setNextStepEnabled(false);

    // Create algo canvas
    createAlgoSketch();
    AppState.p5Algo.redraw();

    bindUI();
  }

  // Start
  init();
</script>
