  </div>

  <script>
    // Global state
    let xScale = 80;
    let yScale = 80;

    // Measurement scalars (dot products)
    const meas = {
      au: 0.8,
      bu: -0.5,
      av: 1.2,
      bv: 1.0,
      aw: 1.1,
      bw: -0.2
    };

    // Reconstructed vectors (world coordinates)
    let vec_u = [1, 0]; // row of S for u
    let vec_v = [0, 1]; // row of S for v
    let vec_w = [0.7071, 0.7071]; // row of S for w
    let point_A = [0.5, 0.3];
    let point_B = [-0.3, 0.6];

    let hasSolution = false;
    let lastError = "";

    // Elements
    const sliderIds = [
      { id: "slider-au", span: "value-au", key: "au", type: "dot", fmt: v => v.toFixed(2) },
      { id: "slider-bu", span: "value-bu", key: "bu", type: "dot", fmt: v => v.toFixed(2) },
      { id: "slider-av", span: "value-av", key: "av", type: "dot", fmt: v => v.toFixed(2) },
      { id: "slider-bv", span: "value-bv", key: "bv", type: "dot", fmt: v => v.toFixed(2) },
      { id: "slider-aw", span: "value-aw", key: "aw", type: "dot", fmt: v => v.toFixed(2) },
      { id: "slider-bw", span: "value-bw", key: "bw", type: "dot", fmt: v => v.toFixed(2) },
      { id: "slider-x-scale", span: "value-x-scale", key: "xScale", type: "scale", fmt: v => Math.round(v).toString() },
      { id: "slider-y-scale", span: "value-y-scale", key: "yScale", type: "scale", fmt: v => Math.round(v).toString() }
    ];

    function getEl(id) { return document.getElementById(id); }

    function updateSpan(id, val, fmt) {
      const el = getEl(id);
      if (el) el.textContent = fmt(val);
    }

    function setError(msg) {
      lastError = msg || "";
      hasSolution = false;
      const banner = getEl("error-banner");
      if (banner) {
        if (msg) {
          banner.textContent = "Computation failed: " + msg;
          banner.classList.add("show");
        } else {
          banner.classList.remove("show");
        }
      }
    }

    // Linear algebra helpers using math.js, operating on plain arrays
    function toArray(x) {
      // Convert mathjs Matrix or typed arrays to plain JS arrays
      if (Array.isArray(x)) return x.map(toArray);
      if (x && typeof x.size === "function" && typeof x.get === "function") {
        // mathjs Matrix
        const size = x.size();
        if (size.length === 1) {
          return x.toArray();
        } else if (size.length === 2) {
          return x.toArray();
        }
      }
      return x;
    }

    function transpose(A) { return math.transpose(A); }
    function multiply(A, B) { return math.multiply(A, B); }
    function inv2(A) { return math.inv(A); }

    function svd3x2(M) {
      // Attempt to handle math.svd return variants
      const res = math.svd(M);
      // res could be {U, V, s} or {U, S, V}
      let U = res.U;
      let V = res.V;
      let sVals = null;

      if (res.s) {
        sVals = res.s;
      } else if (res.S) {
        // Extract diagonal elements from S
        const S = res.S;
        const rows = S.length || (S.size && S.size()[0]);
        const cols = S[0]?.length || (S.size && S.size()[1]);
        const n = Math.min(rows, cols);
        sVals = [];
        for (let i = 0; i < n; i++) {
          // Access S[i][i]
          const val = Array.isArray(S) ? S[i][i] : S.get([i, i]);
          sVals.push(val);
        }
      }

      U = toArray(U);
      V = toArray(V);
      sVals = toArray(sVals);

      // Ensure in descending order; math.svd should already be
      // Keep top 2 singular values
      const s1 = sVals[0] || 0;
      const s2 = sVals[1] || 0;
      const D2 = [[s1, 0], [0, s2]]; // 2x2

      return { U, V, D2, s: [s1, s2] };
    }

    function computeReconstruction() {
      // Build measurement matrix M = [[au, bu], [av, bv], [aw, bw]]
      const M = [
        [meas.au, meas.bu],
        [meas.av, meas.bv],
        [meas.aw, meas.bw]
      ];

      let U, V, D2, s;
      try {
        const svd = svd3x2(M);
        U = svd.U; V = svd.V; D2 = svd.D2; s = svd.s;
      } catch (e) {
        setError("SVD failed (" + (e && e.message ? e.message : "unknown") + ")");
        return false;
      }

      // U is 3x3 (full). We need first two columns: S_hat = U(:,1:2) => 3x2
      const U_r = [
        [U[0][0], U[0][1]],
        [U[1][0], U[1][1]],
        [U[2][0], U[2][1]]
      ];

      // Construct K from rows r_i of U_r:
      // For each row r = [r1, r2], equation is:
      // l11 * r1^2 + 2*l12 * r1*r2 + l22 * r2^2 = 1
      // So K rows are [r1^2, 2*r1*r2, r2^2]
      const K = [];
      for (let i = 0; i < 3; i++) {
        const r1 = U_r[i][0], r2 = U_r[i][1];
        K.push([r1 * r1, 2 * r1 * r2, r2 * r2]);
      }
      const b = [1, 1, 1];

      let lvec;
      try {
        // Solve K * l = b
        lvec = toArray(math.lusolve(K, b)).map(row => Array.isArray(row) ? row[0] : row);
      } catch (e) {
        setError("Linear solve failed (K is singular or ill-conditioned)");
        return false;
      }

      const l11 = lvec[0];
      const l12 = lvec[1];
      const l22 = lvec[2];
      // Positive definiteness check
      const det = l11 * l22 - l12 * l12;
      const eps = 1e-9;
      if (!(l11 > eps && det > eps)) {
        setError("Metric L is not positive definite");
        return false;
      }

      // Cholesky decomposition L = Q * Q^T with lower-triangular Q
      // For 2x2:
      // q11 = sqrt(l11)
      // q21 = l12 / q11
      // q22 = sqrt(l22 - q21^2)
      let q11 = Math.sqrt(l11);
      if (!isFinite(q11) || q11 <= 0) {
        setError("Cholesky failed (invalid q11)");
        return false;
      }
      let q21 = l12 / q11;
      let t = l22 - q21 * q21;
      if (!(t > eps)) {
        setError("Cholesky failed (non-positive pivot)");
        return false;
      }
      let q22 = Math.sqrt(t);
      const Q = [
        [q11, 0],
        [q21, q22]
      ];

      // Final motion and shape
      // S = U_r * Q  (3x2)
      let S = toArray(multiply(U_r, Q));

      // P = inv(Q) * D2 * V^T
      let Qinv;
      try {
        Qinv = toArray(inv2(Q));
      } catch (e) {
        setError("inv(Q) failed");
        return false;
      }
      const Vt = toArray(transpose(V)); // 2x2
      const P = toArray(multiply(multiply(Qinv, D2), Vt)); // 2x2

      // Extract vectors
      vec_u = [S[0][0], S[0][1]];
      vec_v = [S[1][0], S[1][1]];
      vec_w = [S[2][0], S[2][1]];

      // Points A and B are columns of P
      point_A = [P[0][0], P[1][0]];
      point_B = [P[0][1], P[1][1]];

      hasSolution = true;
      setError(""); // clear banner
      return true;
    }

    function onDotChanged() {
      // Read dot products
      meas.au = parseFloat(getEl("slider-au").value);
      meas.bu = parseFloat(getEl("slider-bu").value);
      meas.av = parseFloat(getEl("slider-av").value);
      meas.bv = parseFloat(getEl("slider-bv").value);
      meas.aw = parseFloat(getEl("slider-aw").value);
      meas.bw = parseFloat(getEl("slider-bw").value);

      // Update spans
      updateSpan("value-au", meas.au, v => v.toFixed(2));
      updateSpan("value-bu", meas.bu, v => v.toFixed(2));
      updateSpan("value-av", meas.av, v => v.toFixed(2));
      updateSpan("value-bv", meas.bv, v => v.toFixed(2));
      updateSpan("value-aw", meas.aw, v => v.toFixed(2));
      updateSpan("value-bw", meas.bw, v => v.toFixed(2));

      // Recompute and redraw
      computeReconstruction();
      if (typeof redraw === "function") redraw();
    }

    function onScaleChanged() {
      xScale = parseFloat(getEl("slider-x-scale").value);
      yScale = parseFloat(getEl("slider-y-scale").value);
      updateSpan("value-x-scale", xScale, v => Math.round(v).toString());
      updateSpan("value-y-scale", yScale, v => Math.round(v).toString());
      if (typeof redraw === "function") redraw();
    }

    // p5.js sketch
    let cnv;
    function setup() {
      const container = getEl("canvas-container");
      const w = container.clientWidth;
      const h = container.clientHeight;
      cnv = createCanvas(w, h);
      cnv.parent("canvas-container");
      noLoop();
      textFont('Helvetica, Arial, sans-serif');

      // Attach listeners
      sliderIds.forEach(({ id, span, key, type, fmt }) => {
        const el = getEl(id);
        const spanEl = getEl(span);
        if (!el || !spanEl) return;

        // Initialize spans
        let val = parseFloat(el.value);
        spanEl.textContent = fmt(val);

        el.addEventListener("input", () => {
          // Update value display
          spanEl.textContent = fmt(parseFloat(el.value));
          if (type === "dot") {
            onDotChanged();
          } else {
            onScaleChanged();
          }
        });
      });

      // Initial compute and draw
      onDotChanged();
      onScaleChanged();
    }

    function windowResized() {
      const container = getEl("canvas-container");
      if (!container) return;
      resizeCanvas(container.clientWidth, container.clientHeight);
      redraw();
    }

    // Convert world coordinates (x,y) to screen coordinates
    function worldToScreen(pt) {
      // After translate(width/2, height/2) and scale(xScale, -yScale):
      const x = pt[0], y = pt[1];
      const sx = width / 2 + x * xScale;
      const sy = height / 2 - y * yScale;
      return [sx, sy];
    }

    function drawAxesAndLabels() {
      // Long lines along unit vectors vec_u, vec_v, vec_w
      // Compute a length that spans the canvas in world units
      const Lx = (width / xScale) * 0.55;
      const Ly = (height / yScale) * 0.55;
      const L = Math.max(Lx, Ly);

      // Draw line helper
      function drawAxis(n, colorHex, label) {
        stroke(colorHex);
        strokeWeight(3);
        const nx = n[0], ny = n[1];
        line(-nx * L, -ny * L, nx * L, ny * L);

        // Label near + end in screen space for crisp text
        const endWorld = [nx * (L * 1.02), ny * (L * 1.02)];
        const [sx, sy] = worldToScreen(endWorld);
        push();
        resetMatrix();
        noStroke();
        fill(colorHex);
        textSize(13);
        text(label, sx + 4, sy - 4);
        pop();
      }

      drawAxis(vec_u, "#0000CD", "u");
      drawAxis(vec_v, "#6495ED", "v");
      drawAxis(vec_w, "#0000CD", "w");
    }

    function drawPointWithLabel(p, colorHex, label) {
      // Draw in world space (scaled); choose radius in world units
      const r = 0.06; // world radius; will scale with x/y scale
      stroke("#333333");
      strokeWeight(1.5 / Math.max(1, (xScale + yScale) / 160)); // mitigate thickening
      fill(colorHex + "cc");
      circle(p[0], p[1], r * 2);

      // Label in screen space for crispness
      const [sx, sy] = worldToScreen(p);
      push();
      resetMatrix();
      noStroke();
      fill(colorHex);
      textSize(13);
      text(label, sx + 6, sy - 6);
      pop();
    }

    function dot(a, b) { return a[0] * b[0] + a[1] * b[1]; }
    function scaleVec(a, s) { return [a[0] * s, a[1] * s]; }

    function drawProjections() {
      // For each point and axis, projection is (P.n) * n
      const A = point_A, B = point_B;
      const u = vec_u, v = vec_v, w = vec_w;

      const Au = scaleVec(u, meas.au);
      const Av = scaleVec(v, meas.av);
      const Aw = scaleVec(w, meas.aw);

      const Bu = scaleVec(u, meas.bu);
      const Bv = scaleVec(v, meas.bv);
      const Bw = scaleVec(w, meas.bw);

      // Links from A to its projections
      stroke("#808080");
      strokeWeight(1.5 / Math.max(1, (xScale + yScale) / 160));
      line(A[0], A[1], Au[0], Au[1]);
      line(A[0], A[1], Av[0], Av[1]);
      line(A[0], A[1], Aw[0], Aw[1]);

      // Links from B to its projections
      line(B[0], B[1], Bu[0], Bu[1]);
      line(B[0], B[1], Bv[0], Bv[1]);
      line(B[0], B[1], Bw[0], Bw[1]);

      // Draw small green circles at projection points
      fill("#00FF00");
      stroke("#004400");
      const pr = 0.035;
      circle(Au[0], Au[1], pr * 2);
      circle(Av[0], Av[1], pr * 2);
      circle(Aw[0], Aw[1], pr * 2);

      circle(Bu[0], Bu[1], pr * 2);
      circle(Bv[0], Bv[1], pr * 2);
      circle(Bw[0], Bw[1], pr * 2);
    }

    function drawOrigin() {
      const O = [0, 0];
      fill("#FF0000");
      stroke("#333333");
      strokeWeight(1.2 / Math.max(1, (xScale + yScale) / 160));
      const r = 0.045;
      circle(O[0], O[1], r * 2);

      const [sx, sy] = worldToScreen(O);
      push();
      resetMatrix();
      noStroke();
      fill("#FF0000");
      textSize(12);
      text("O", sx + 6, sy - 6);
      pop();
    }

    function draw() {
      background("#F0F0F0");

      // If reconstruction failed, show info and return
      if (!hasSolution) {
        // A faint cross at origin to indicate coordinate frame
        push();
        translate(width / 2, height / 2);
        stroke("#cccccc");
        strokeWeight(1);
        line(-width / 2, 0, width / 2, 0);
        line(0, -height / 2, 0, height / 2);
        pop();

        // Instructions overlay
        push();
        noStroke();
        fill("#a40000");
        textAlign(CENTER, CENTER);
        textSize(14);
        text("Unable to reconstruct with current parameters.\nAdjust sliders to obtain a valid metric.", width / 2, height / 2);
        pop();
        return;
      }

      // World transform: origin at center, y up, scaling
      push();
      translate(width / 2, height / 2);
      scale(xScale, -yScale);

      // Draw axes
      drawAxesAndLabels();

      // Draw projections before points (so points appear on top)
      drawProjections();

      // Draw points A, B and origin
      drawPointWithLabel(point_A, "#FF0000", "A");
      drawPointWithLabel(point_B, "#FFA500", "B");
      drawOrigin();

      pop();
    }
  </script>
</body>
</html>
