<body>
  <div id="main-container">
    <div id="control-panel">
      <div id="controls-2d">
        <h3>2D</h3>
        <div id="buttons-2d-container">
          <button id="btn-2A1">²A₁</button>
          <button id="btn-2A2" class="active">²A₂</button>
          <button id="btn-2B2">²B₂</button>
        </div>
      </div>

      <div id="controls-3d">
        <h3>3D</h3>
        <div id="buttons-3d-container">
          <button id="btn-3A1p">²A₁(+)</button>
          <button id="btn-3A1m">²A₁(-)</button>
          <button id="btn-3A2">²A₂</button>
          <button id="btn-3A3">²A₃</button>
          <button id="btn-3B2p">²B₂(+)</button>
          <button id="btn-3B2m">²B₂(-)</button>
          <button id="btn-3B3">²B₃</button>
          <button id="btn-3C3p">²C₃⁺</button>
          <button id="btn-3C3m">²C₃⁻</button>
        </div>
      </div>

      <div id="time-control-container">
        <label for="time-pad">time</label>
        <div id="time-pad">
          <div id="time-handle"></div>
        </div>
        <div class="note">Drag the handle or click on the pad to set (t1, t2). Left/right = t1 ∈ [-1,1], up/down = t2 ∈ [-1,1].</div>
      </div>
    </div>

    <div id="visualization-container"></div>
  </div>

<script>
(function(){
  // Global state
  let currentType = '2A2';
  let currentDimension = '2D';
  let t1 = 0, t2 = 0;

  // three.js globals
  let renderer, scene, camera, controls = null;
  let currentObject = null; // 2D Line or 3D Mesh group
  let isAnimating = true;

  const container = document.getElementById('visualization-container');

  initRenderer();
  createSceneAndCamera();
  buildVisualization();
  initButtons();
  initTimePad();
  window.addEventListener('resize', onWindowResize);
  animate();

  // Initialize renderer
  function initRenderer() {
    renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
    renderer.setSize(container.clientWidth, container.clientHeight);
    renderer.setClearColor(0x0c1020, 1);
    container.appendChild(renderer.domElement);
  }

  // Create scene and camera based on currentDimension
  function createSceneAndCamera() {
    scene = new THREE.Scene();

    // Lights (used for 3D, harmless in 2D)
    const amb = new THREE.AmbientLight(0xffffff, 0.5);
    const dir = new THREE.DirectionalLight(0xffffff, 0.8);
    dir.position.set(3, 5, 7);
    scene.add(amb, dir);

    if (controls) {
      controls.dispose();
      controls = null;
    }

    if (currentDimension === '2D') {
      // Orthographic camera for 2D
      const w = container.clientWidth;
      const h = container.clientHeight;
      const viewSize = 20; // default; will refit after building geometry
      const aspect = w / h;
      camera = new THREE.OrthographicCamera(
        -viewSize * aspect / 2, viewSize * aspect / 2,
         viewSize / 2, -viewSize / 2,
         0.1, 1000
      );
      camera.position.set(0, 0, 10);
      camera.lookAt(0, 0, 0);
    } else {
      // Perspective camera for 3D with OrbitControls
      const w = container.clientWidth;
      const h = container.clientHeight;
      camera = new THREE.PerspectiveCamera(45, w / h, 0.1, 2000);
      camera.position.set(6, 6, 6);
      camera.lookAt(0, 0, 0);
      controls = new THREE.OrbitControls(camera, renderer.domElement);
      controls.enableDamping = true;
      controls.dampingFactor = 0.07;
    }
  }

  // Build visualization for currentType and t1,t2
  function buildVisualization() {
    // Clear previous visual object
    if (currentObject) {
      scene.remove(currentObject);
      disposeObject(currentObject);
      currentObject = null;
    }

    if (currentDimension === '2D') {
      currentObject = build2DObject(currentType, t1, t2);
      scene.add(currentObject);
      // fit camera to 2D object bounds
      fitOrthoCameraToObject(currentObject);
    } else {
      currentObject = build3DObject(currentType, t1, t2);
      scene.add(currentObject);
      // position camera (or controls target) sensibly for the new geometry
      fitPerspectiveToObject(currentObject);
    }
  }

  // Update geometry only (for time changes)
  function updateVisualizationGeometry() {
    if (!currentObject) return;
    if (currentDimension === '2D') {
      // Rebuild line geometry
      const newGeom = generate2DGeometry(currentType, t1, t2);
      const line = currentObject; // THREE.Line
      line.geometry.dispose();
      line.geometry = newGeom;
    } else {
      // 3D: replace mesh geometry
      const { geom, wireGeom } = generate3DGeometries(currentType, t1, t2);
      const group = currentObject; // THREE.Group
      // first child is solid surface
      const solid = group.children[0];
      solid.geometry.dispose();
      solid.geometry = geom;

      // optional wireframe child for 3A3
      if (group.children.length > 1) {
        const wf = group.children[1];
        if (wireGeom) {
          wf.geometry.dispose();
          wf.geometry = wireGeom;
        }
      }
    }
  }

  function build2DObject(type, t1, t2) {
    const geom = generate2DGeometry(type, t1, t2);
    const mat = new THREE.LineBasicMaterial({ color: 0x4a69bd, linewidth: 2 });
    const line = new THREE.Line(geom, mat);
    return line;
  }

  function generate2DGeometry(type, t1, t2) {
    const positions = [];
    const steps = 300;

    const T1 = t1 * 1.5;
    const T2 = t2 * 1.5;

    let Umin = -2, Umax = 2;
    if (type === '2A2') { Umin = -1.5; Umax =  1.5; }
    if (type === '2B2') { Umin = -2.0; Umax =  2.0; }

    for (let i = 0; i <= steps; i++) {
      const u = i / steps;
      const U = Umin + (Umax - Umin) * u;
      let x=0, y=0;

      if (type === '2A1') { // x=U, y=U^2
        x = U;
        y = U*U;
      } else if (type === '2A2') { // x=2U^3, y=3U^2
        x = 2*Math.pow(U,3);
        y = 3*Math.pow(U,2);
      } else if (type === '2B2') { // x=U^2, y=U^4
        x = Math.pow(U,2);
        y = Math.pow(U,4);
      }

      positions.push(x + T1, y + T2, 0);
    }

    const geom = new THREE.BufferGeometry();
    const arr = new Float32Array(positions);
    geom.setAttribute('position', new THREE.BufferAttribute(arr, 3));
    geom.computeBoundingBox();
    geom.computeBoundingSphere();
    return geom;
  }

  function build3DObject(type, t1, t2) {
    const { geom, wireGeom } = generate3DGeometries(type, t1, t2);

    const mat = new THREE.MeshPhongMaterial({
      color: 0xffc34d,
      side: THREE.DoubleSide,
      shininess: 60,
      specular: 0x555555
    });
    const mesh = new THREE.Mesh(geom, mat);
    mesh.castShadow = false;
    mesh.receiveShadow = false;

    const group = new THREE.Group();
    group.add(mesh);

    if (type === '3A3') {
      // add wireframe overlay
      const lineMat = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 1 });
      const wire = new THREE.LineSegments(wireGeom, lineMat);
      wire.renderOrder = 1;
      group.add(wire);
    }

    return group;
  }

  function generate3DGeometries(type, t1, t2) {
    const slices = 60, stacks = 60;
    const T1 = t1 * 1.5;
    const T2 = t2 * 1.5;
    const scale = 0.35; // global scale to keep surfaces within view

    const paramFunc = (u, v, target) => {
      // Map u,v ∈ [0,1] to U,V in given ranges
      let U=0, V=0;
      let x=0, y=0, z=0;

      const map = (a, b, t) => a + (b - a) * t;

      switch (type) {
        case '3A1p': {
          U = map(-2, 2, u); V = map(-2, 2, v);
          x = U; y = V; z = 0.3*(U*U + V*V) + T1*U + T2*V;
          break;
        }
        case '3A1m': {
          U = map(-2, 2, u); V = map(-2, 2, v);
          x = U; y = V; z = -0.3*(U*U + V*V) + T1*U + T2*V;
          break;
        }
        case '3A2': {
          U = map(-1.5, 1.5, u); V = map(-2, 2, v);
          x = 2*Math.pow(U,3) + T1;
          y = 3*Math.pow(U,2) + T2;
          z = V;
          break;
        }
        case '3A3': { // Swallowtail
          U = map(-1.5, 1.5, u); V = map(-4, 4, v);
          x = 3*Math.pow(U,4) + U*U*V;
          y = -4*Math.pow(U,3) - 2*U*V + T1*2;
          z = V + T2*2;
          break;
        }
        case '3B2p': {
          U = map(-2, 2, u); V = map(-2, 2, v);
          x = U; y = V; z = Math.pow(U*U + T1, 2) + V*V + T2;
          break;
        }
        case '3B2m': {
          U = map(-2, 2, u); V = map(-2, 2, v);
          x = U; y = V; z = -(Math.pow(U,4) + T1*2*U*U + V*V - T2);
          break;
        }
        case '3B3': {
          U = map(-2, 2, u); V = map(-2, 2, v);
          x = U; y = V; z = Math.pow(U,3) + T1*U + V*V + T2;
          break;
        }
        case '3C3p': { // Elliptic Umbilic
          U = map(-2, 2, u); V = map(-2, 2, v);
          x = U; y = V; z = Math.pow(U,3) - 3*U*V*V + T1*2*(U*U + V*V) + T2*4*U;
          break;
        }
        case '3C3m': { // Hyperbolic Umbilic
          U = map(-2, 2, u); V = map(-2, 2, v);
          x = U; y = V; z = Math.pow(U,3) + Math.pow(V,3) + T1*4*U + T2*4*V;
          break;
        }
        default: { // fallback plane
          U = map(-2, 2, u); V = map(-2, 2, v);
          x = U; y = V; z = 0;
        }
      }

      target.set(x*scale, y*scale, z*scale);
    };

    const geom = new THREE.ParametricGeometry(paramFunc, slices, stacks);

    let wireGeom = null;
    if (type === '3A3') {
      // Use WireframeGeometry for overlay
      wireGeom = new THREE.WireframeGeometry(geom);
    }
    return { geom, wireGeom };
  }

  // Fit Orthographic camera to 2D object
  function fitOrthoCameraToObject(obj) {
    if (!(camera instanceof THREE.OrthographicCamera)) return;
    let geom = null;
    if (obj.geometry) {
      geom = obj.geometry;
    }
    if (!geom) return;
    geom.computeBoundingBox();
    const bb = geom.boundingBox;
    const width = bb.max.x - bb.min.x;
    const height = bb.max.y - bb.min.y;
    const centerX = (bb.max.x + bb.min.x) / 2;
    const centerY = (bb.max.y + bb.min.y) / 2;

    // Add margins
    const margin = 0.2;
    const viewWidth = width * (1 + margin);
    const viewHeight = height * (1 + margin);

    const aspect = container.clientWidth / container.clientHeight;
    let halfW = viewWidth / 2;
    let halfH = viewHeight / 2;

    // Adjust to aspect so the curve fits both dimensions
    if (halfW / halfH > aspect) {
      // width is the limiting factor
      halfH = halfW / aspect;
    } else {
      // height is the limiting factor
      halfW = halfH * aspect;
    }

    camera.left = centerX - halfW;
    camera.right = centerX + halfW;
    camera.top = centerY + halfH;
    camera.bottom = centerY - halfH;
    camera.near = 0.1;
    camera.far = 1000;
    camera.updateProjectionMatrix();
    camera.position.set(centerX, centerY, 10);
    camera.lookAt(centerX, centerY, 0);
  }

  // Fit Perspective camera/controls to 3D object bounds (on type change)
  function fitPerspectiveToObject(group) {
    if (!(camera instanceof THREE.PerspectiveCamera)) return;
    // Compute bounding sphere
    const box = new THREE.Box3().setFromObject(group);
    const sphere = box.getBoundingSphere(new THREE.Sphere());
    const center = sphere.center;
    const radius = sphere.radius || 1;

    // Place camera so that the object fits in view
    const fov = camera.fov * (Math.PI / 180);
    const distance = radius / Math.sin(fov / 2);
    const dir = new THREE.Vector3(1, 1, 1).normalize();
    camera.position.copy(center.clone().add(dir.multiplyScalar(distance * 0.8)));
    camera.near = Math.max(0.1, distance * 0.01);
    camera.far = distance * 4 + radius * 2;
    camera.updateProjectionMatrix();
    camera.lookAt(center);

    if (controls) {
      controls.target.copy(center);
      controls.update();
    }
  }

  // Dispose helper
  function disposeObject(obj) {
    obj.traverse((o) => {
      if (o.geometry) { o.geometry.dispose(); }
      if (o.material) {
        if (Array.isArray(o.material)) {
          o.material.forEach(m => m.dispose && m.dispose());
        } else if (o.material.dispose) {
          o.material.dispose();
        }
      }
    });
  }

  function onWindowResize() {
    renderer.setSize(container.clientWidth, container.clientHeight);
    if (camera instanceof THREE.PerspectiveCamera) {
      camera.aspect = container.clientWidth / container.clientHeight;
      camera.updateProjectionMatrix();
    } else if (camera instanceof THREE.OrthographicCamera) {
      // Recompute based on current geometry bounds to maintain fit
      fitOrthoCameraToObject(currentObject || { geometry: null });
    }
  }

  function animate() {
    if (!isAnimating) return;
    requestAnimationFrame(animate);
    if (controls) controls.update();
    renderer.render(scene, camera);
  }

  // Button interactions
  function initButtons() {
    const allButtons = [
      'btn-2A1','btn-2A2','btn-2B2',
      'btn-3A1p','btn-3A1m','btn-3A2','btn-3A3','btn-3B2p','btn-3B2m','btn-3B3','btn-3C3p','btn-3C3m'
    ];
    allButtons.forEach(id => {
      const btn = document.getElementById(id);
      btn.addEventListener('click', () => onTypeButtonClicked(id));
    });
  }

  function onTypeButtonClicked(id) {
    // Update active button styles
    setActiveButton(id);

    // Determine type and dimension
    let newType = id.replace('btn-', '');
    let newDimension = id.startsWith('btn-2') ? '2D' : '3D';

    const dimensionChanged = (newDimension !== currentDimension);
    const typeChanged = (newType !== currentType);

    currentType = newType;
    currentDimension = newDimension;

    // Recreate scene/camera if dimension changed, else reuse
    if (dimensionChanged) {
      createSceneAndCamera();
    }

    // Build fresh visualization for new type
    buildVisualization();
  }

  function setActiveButton(activeId) {
    const btnContainers = [
      document.getElementById('buttons-2d-container'),
      document.getElementById('buttons-3d-container'),
    ];
    btnContainers.forEach(container => {
      Array.from(container.querySelectorAll('button')).forEach(b => b.classList.remove('active'));
    });
    const activeBtn = document.getElementById(activeId);
    if (activeBtn) activeBtn.classList.add('active');
  }

  // Time pad interactions
  function initTimePad() {
    const pad = document.getElementById('time-pad');
    const handle = document.getElementById('time-handle');

    // Place at center initially (t1=0, t2=0)
    setHandleFromT(handle, pad, 0, 0);

    let dragging = false;

    const toPadCoords = (clientX, clientY) => {
      const rect = pad.getBoundingClientRect();
      const x = clientX - rect.left;
      const y = clientY - rect.top;
      return { x, y, rect };
    };

    const clamp = (val, min, max) => Math.max(min, Math.min(max, val));

    const updateFromPadCoords = (x, y, rect) => {
      // constrain to pad bounds
      const cx = clamp(x, 0, rect.width);
      const cy = clamp(y, 0, rect.height);
      // Map to t1,t2 in [-1,1] using handle center
      const t1new = (cx - rect.width/2) / (rect.width/2);
      const t2new = (rect.height/2 - cy) / (rect.height/2);
      // Update handle and visualization
      setHandlePosition(handle, cx, cy);
      setT(t1new, t2new);
    };

    pad.addEventListener('pointerdown', (e) => {
      pad.setPointerCapture(e.pointerId);
      const { x, y, rect } = toPadCoords(e.clientX, e.clientY);
      dragging = true;
      handle.classList.add('dragging');
      updateFromPadCoords(x, y, rect);
    });

    pad.addEventListener('pointermove', (e) => {
      if (!dragging) return;
      const { x, y, rect } = toPadCoords(e.clientX, e.clientY);
      updateFromPadCoords(x, y, rect);
    });

    pad.addEventListener('pointerup', (e) => {
      dragging = false;
      handle.classList.remove('dragging');
      pad.releasePointerCapture(e.pointerId);
    });

    // Also allow dragging from the handle itself
    handle.addEventListener('pointerdown', (e) => {
      e.stopPropagation();
      pad.setPointerCapture(e.pointerId);
      dragging = true;
      handle.classList.add('dragging');
    });
  }

  function setHandleFromT(handle, pad, t1Val, t2Val) {
    const rect = pad.getBoundingClientRect();
    const cx = (t1Val * 0.5 + 0.5) * rect.width; // (t1 in [-1,1]) -> [0,1] -> pixels
    const cy = (0.5 - t2Val * 0.5) * rect.height;
    setHandlePosition(handle, cx, cy);
  }

  function setHandlePosition(handle, cx, cy) {
    handle.style.left = `${cx}px`;
    handle.style.top = `${cy}px`;
  }

  function setT(newT1, newT2) {
    // constrain to [-1,1]
    t1 = Math.max(-1, Math.min(1, newT1));
    t2 = Math.max(-1, Math.min(1, newT2));
    // Update current geometry
    updateVisualizationGeometry();
  }

  // Build initial
  function buildVisualizationInitial() {
    buildVisualization();
  }

  // Initial call made already
})();
</script>
</body>
</html>
