      gap: 6px;
      border: 1px solid #ccc;
      padding: 4px 8px;
      border-radius: 6px;
      background: #fff;
    }
    #feed-cycler button {
      padding: 2px 6px;
      font-size: 12px;
      line-height: 1;
      border: 1px solid #bbb;
      background: #f5f5f5;
      border-radius: 4px;
      cursor: pointer;
    }
    #feed-cycler button:hover {
      background: #eee;
    }
    #cycler-feed-label {
      font-weight: 600;
      min-width: 95px;
      display: inline-block;
      text-align: center;
    }

    /* Right group: sliders */
    #right-controls {
      display: grid;
      grid-template-columns: auto 1fr 80px;
      gap: 6px 10px;
      align-items: center;
      min-width: 420px;
      flex: 1 1 420px;
      max-width: 780px;
    }
    #right-controls label {
      white-space: nowrap;
      font-size: 14px;
    }
    #right-controls input[type="range"] {
      width: 100%;
    }
    #right-controls input[type="number"] {
      width: 100%;
      padding: 4px 6px;
      box-sizing: border-box;
    }

    #plot-area {
      position: relative;
      flex: 1 1 auto;
      min-height: 420px;
      background: #F0F0F0;
      overflow: hidden;
    }
    /* Pre-existing canvas as per plan; will be replaced by p5 canvas with same id */
    #plot-canvas {
      display: block;
      width: 100%;
      height: 100%;
    }

    /* Legend overlay */
    #legend {
      position: absolute;
      top: 8px;
      left: 50%;
      transform: translateX(-50%);
      background: rgba(255,255,255,0.9);
      border: 1px solid #ccc;
      border-radius: 6px;
      padding: 6px 10px;
      display: inline-flex;
      align-items: center;
      gap: 14px;
      pointer-events: none;
      font-size: 14px;
    }
    .legend-item {
      display: inline-flex;
      align-items: center;
      gap: 6px;
      white-space: nowrap;
    }
    .legend-line {
      display: inline-block;
      width: 26px;
      height: 0;
      border-top-width: 3px;
      border-top-style: solid;
    }
    .legend-line.dashed {
      border-top-style: dashed;
    }

    /* Small helper for axis labels in canvas fallbacks */
    .mj-inline {
      display: inline-block;
    }

    /* Responsive tweaks */
    @media (max-width: 720px) {
      #right-controls {
        grid-template-columns: auto 1fr 70px;
        min-width: 0;
      }
      #legend {
        font-size: 12px;
        gap: 8px;
      }
      .legend-line { width: 20px; }
    }
  </style>
</head>
<body>
  <div id="app">
    <div id="control-panel">
      <div id="left-controls">
        <select id="select-plot">
          <option value="semibatch" selected>semibatch</option>
          <option value="batch">batch</option>
          <option value="selectivity">selectivity</option>
        </select>
        <div id="feed-cycler">
          <span id="cycler-feed-label">feed A to B</span>
          <button id="btn-feed-down" title="previous">▼</button>
          <button id="btn-feed-up" title="next">▲</button>
        </div>
      </div>

      <div id="right-controls">
        <label for="slider-feed-rate">feed rate</label>
        <input type="range" id="slider-feed-rate" min="10" max="200" step="1" value="10">
        <input type="number" id="input-feed-rate" min="10" max="200" step="1" value="10">

        <label for="slider-ua">UA</label>
        <input type="range" id="slider-ua" min="5000" max="30000" step="100" value="15000">
        <input type="number" id="input-ua" min="5000" max="30000" step="100" value="15000">

        <label for="slider-dh1">\( \Delta H_1 \)</label>
        <input type="range" id="slider-dh1" min="500" max="5000" step="50" value="1000">
        <input type="number" id="input-dh1" min="500" max="5000" step="50" value="1000">

        <label for="slider-dh2">\( \Delta H_2 \)</label>
        <input type="range" id="slider-dh2" min="500" max="5000" step="50" value="3000">
        <input type="number" id="input-dh2" min="500" max="5000" step="50" value="3000">
      </div>
    </div>

    <div id="plot-area">
      <div id="legend"></div>
      <canvas id="plot-canvas"></canvas>
    </div>
  </div>

  <script>
    // Constants
    const V = 100;                 // L
    const T_initial = 330;         // K
    const T_coolant = 330;         // K
    const R = 8.314;               // J/mol/K
    const Cp_total = 150000;       // J/K
    const k1_0 = 3e4;              // L^2/(mol^2·h)
    const E1 = 30000;              // J/mol
    const k2_0 = 1e5;              // L/(mol·h)
    const E2 = 40000;              // J/mol

    const tMax = 2.0;              // h
    const dt = 0.01;               // h
    const N0 = 100;                // mol initial per reactant

    // State and data
    let plotType = 'semibatch';    // 'semibatch' | 'batch' | 'selectivity'
    const FEEDS = ['feed A to B', 'feed B to A'];
    let feedIndex = 0;             // 0 => A to B, 1 => B to A

    let params = {
      feedRate: 10,  // mol/h
      UA: 15000,     // W/K (J/h/K in model units? We keep as given per instructions)
      dH1: 1000,     // J/mol (magnitude), exothermic handled with negative sign in energy balance formula
      dH2: 3000      // J/mol (magnitude)
    };

    // Simulation results
    let sim = {
      t: [],
      Na: [],
      Nb: [],
      Nd: [],
      Nu: [],
      T: [],
      Sratio: [] // only for selectivity plot
    };

    // Utility: clamp
    function clamp(x, lo, hi) { return Math.max(lo, Math.min(hi, x)); }

    // Link a range and number input pair
    function linkRangeAndNumber(rangeEl, numberEl, onChange) {
      function syncFromRange() {
        numberEl.value = rangeEl.value;
        onChange();
      }
      function syncFromNumber() {
        let v = parseFloat(numberEl.value);
        const min = parseFloat(rangeEl.min);
        const max = parseFloat(rangeEl.max);
        const step = parseFloat(rangeEl.step) || 1;
        if (isNaN(v)) v = parseFloat(rangeEl.value);
        v = clamp(v, min, max);
        // snap to step
        v = Math.round(v / step) * step;
        numberEl.value = v;
        rangeEl.value = v;
        onChange();
      }
      rangeEl.addEventListener('input', syncFromRange);
      numberEl.addEventListener('input', syncFromNumber);
    }

    // Math: reaction rates and derivatives
    function rates(Y) {
      const Na = Y[0], Nb = Y[1], T = Y[4];
      const Ca = Math.max(Na / V, 0);
      const Cb = Math.max(Nb / V, 0);
      const k1 = k1_0 * Math.exp(-E1 / (R * T));
      const k2 = k2_0 * Math.exp(-E2 / (R * T));
      const r1 = k1 * Ca * Cb * Cb; // mol/L/h
      const r2 = k2 * Ca * Cb;      // mol/L/h
      return { r1, r2, k1, k2 };
    }

    function derivs(t, Y, mode, feedRate, tfeed, UA, dH1, dH2) {
      const { r1, r2 } = rates(Y);
      const Vmolh = V; // multiply rates by V to get mol/h
      let dNa = -(r1 + r2) * Vmolh;
      let dNb = -(r1 + r2) * Vmolh;

      if (mode === 'semi-AtoB') {
        const FA0 = (t < tfeed) ? feedRate : 0;
        dNa = FA0 + dNa;
      } else if (mode === 'semi-BtoA') {
        const FB0 = (t < tfeed) ? feedRate : 0;
        dNb = FB0 + dNb;
      }
      const dNd = r1 * Vmolh;
      const dNu = r2 * Vmolh;

      const T = Y[4];
      const Qrem = UA * (T_coolant - T); // J/h (UA uses given units; model balance uses same)
      const Qrxn = (-dH1 * r1 - dH2 * r2) * V; // J/h
      const dT = (Qrem + Qrxn) / Cp_total;

      return [dNa, dNb, dNd, dNu, dT];
    }

    function rk4Step(fun, t, Y, h) {
      const k1 = fun(t, Y);
      const Y2 = Y.map((y, i) => y + 0.5 * h * k1[i]);
      const k2 = fun(t + 0.5 * h, Y2);
      const Y3 = Y.map((y, i) => y + 0.5 * h * k2[i]);
      const k3 = fun(t + 0.5 * h, Y3);
      const Y4 = Y.map((y, i) => y + h * k3[i]);
      const k4 = fun(t + h, Y4);
      return Y.map((y, i) => y + (h / 6) * (k1[i] + 2*k2[i] + 2*k3[i] + k4[i]));
    }

    function simulateBatch(UA, dH1, dH2) {
      const Nsteps = Math.round(tMax / dt);
      const tArr = new Array(Nsteps + 1);
      const Na = new Array(Nsteps + 1);
      const Nb = new Array(Nsteps + 1);
      const Nd = new Array(Nsteps + 1);
      const Nu = new Array(Nsteps + 1);
      const T = new Array(Nsteps + 1);

      let Y = [N0, N0, 0, 0, T_initial];
      tArr[0] = 0; Na[0] = Y[0]; Nb[0] = Y[1]; Nd[0] = Y[2]; Nu[0] = Y[3]; T[0] = Y[4];

      const f = (t, YY) => derivs(t, YY, 'batch', 0, 0, UA, dH1, dH2);
      let t = 0;
      for (let i = 1; i <= Nsteps; i++) {
        Y = rk4Step(f, t, Y, dt);
        t += dt;
        tArr[i] = t;
        Na[i] = Math.max(Y[0], 0);
        Nb[i] = Math.max(Y[1], 0);
        Nd[i] = Math.max(Y[2], 0);
        Nu[i] = Math.max(Y[3], 0);
        T[i] = Y[4];
      }
      return { t: tArr, Na, Nb, Nd, Nu, T };
    }

    function simulateSemibatch(feedMode, feedRate, UA, dH1, dH2) {
      const Nsteps = Math.round(tMax / dt);
      const tArr = new Array(Nsteps + 1);
      const Na = new Array(Nsteps + 1);
      const Nb = new Array(Nsteps + 1);
      const Nd = new Array(Nsteps + 1);
      const Nu = new Array(Nsteps + 1);
      const T = new Array(Nsteps + 1);

      let Y;
      let modeStr;
      if (feedMode === 'feed A to B') {
        Y = [0, N0, 0, 0, T_initial];
        modeStr = 'semi-AtoB';
      } else { // 'feed B to A'
        Y = [N0, 0, 0, 0, T_initial];
        modeStr = 'semi-BtoA';
      }
      const tf = 100 / feedRate;

      tArr[0] = 0; Na[0] = Y[0]; Nb[0] = Y[1]; Nd[0] = Y[2]; Nu[0] = Y[3]; T[0] = Y[4];
      const f = (t, YY) => derivs(t, YY, modeStr, feedRate, tf, UA, dH1, dH2);
      let t = 0;
      for (let i = 1; i <= Nsteps; i++) {
        Y = rk4Step(f, t, Y, dt);
        t += dt;
        tArr[i] = t;
        Na[i] = Math.max(Y[0], 0);
        Nb[i] = Math.max(Y[1], 0);
        Nd[i] = Math.max(Y[2], 0);
        Nu[i] = Math.max(Y[3], 0);
        T[i] = Y[4];
      }
      return { t: tArr, Na, Nb, Nd, Nu, T };
    }

    function computeSelectivityRatio(batchRes, semiRes) {
      const N = batchRes.t.length;
      const Ssemi = new Array(N).fill(NaN);
      const Sbatch = new Array(N).fill(NaN);
      const Sratio = new Array(N).fill(NaN);
      for (let i = 0; i < N; i++) {
        const Nu_b = batchRes.Nu[i];
        const Nu_s = semiRes.Nu[i];
        const Sd_b = (Nu_b > 0) ? (batchRes.Nd[i] / Nu_b) : NaN;
        const Sd_s = (Nu_s > 0) ? (semiRes.Nd[i] / Nu_s) : NaN;
        Sbatch[i] = Sd_b;
        Ssemi[i] = Sd_s;
        Sratio[i] = (isFinite(Sd_b) && Sd_b > 0 && isFinite(Sd_s)) ? (Sd_s / Sd_b) : NaN;
      }
      return Sratio;
    }

    function simulateAll() {
      const UA = params.UA;
      const dH1 = params.dH1;
      const dH2 = params.dH2;
      const feedRate = params.feedRate;
      const feedMode = FEEDS[feedIndex];

      if (plotType === 'batch') {
        const b = simulateBatch(UA, dH1, dH2);
        sim = { ...b, Sratio: [] };
        updateLegend('batch');
      } else if (plotType === 'semibatch') {
        const s = simulateSemibatch(feedMode, feedRate, UA, dH1, dH2);
        sim = { ...s, Sratio: [] };
        updateLegend('semibatch');
      } else {
        // selectivity
        const b = simulateBatch(UA, dH1, dH2);
        const s = simulateSemibatch(feedMode, feedRate, UA, dH1, dH2);
        const Sratio = computeSelectivityRatio(b, s);
        sim = { ...b, Sratio, t: b.t }; // time base the same
        updateLegend('selectivity');
      }
      requestRedraw();
    }

    // Legend handling with MathJax overlay
    function updateLegend(mode) {
      const legend = document.getElementById('legend');
      if (mode === 'selectivity') {
        legend.innerHTML = `
          <div class="legend-item">
            <span class="legend-line" style="border-top-color:#800080;"></span>
            <span>semibatch \\(S_{d/u}\\) / batch \\(S_{d/u}\\)</span>
          </div>
        `;
      } else {
        legend.innerHTML = `
          <div class="legend-item">
            <span class="legend-line dashed" style="border-top-color:#2ca02c;"></span>
            <span>\\(N_a\\)</span>
          </div>
          <div class="legend-item">
            <span class="legend-line dashed" style="border-top-color:#000000;"></span>
            <span>\\(N_b\\)</span>
          </div>
          <div class="legend-item">
            <span class="legend-line" style="border-top-color:#1f77b4;"></span>
            <span>\\(N_d\\)</span>
          </div>
          <div class="legend-item">
            <span class="legend-line" style="border-top-color:#d62728;"></span>
            <span>\\(N_u\\)</span>
          </div>
        `;
      }
      if (window.MathJax && window.MathJax.typesetPromise) {
        MathJax.typesetPromise([legend]).catch(() => {});
      }
    }

    // Controls initialization
    function initControls() {
      const selPlot = document.getElementById('select-plot');
      const feedLabel = document.getElementById('cycler-feed-label');
      const btnUp = document.getElementById('btn-feed-up');
      const btnDown = document.getElementById('btn-feed-down');

      selPlot.value = 'semibatch';
      selPlot.addEventListener('change', () => {
        plotType = selPlot.value;
        // Enable/disable feed cycler visual emphasis (still active even for batch/selectivity)
        document.getElementById('feed-cycler').style.opacity = (plotType === 'batch') ? '0.6' : '1';
        simulateAll();
      });

      function setFeedLabel() {
        feedLabel.textContent = FEEDS[feedIndex];
      }
      btnUp.addEventListener('click', () => {
        feedIndex = (feedIndex + 1) % FEEDS.length;
        setFeedLabel();
        simulateAll();
      });
      btnDown.addEventListener('click', () => {
        feedIndex = (feedIndex - 1 + FEEDS.length) % FEEDS.length;
        setFeedLabel();
        simulateAll();
      });
      setFeedLabel();

      // Sliders and numbers
      linkRangeAndNumber(
        document.getElementById('slider-feed-rate'),
        document.getElementById('input-feed-rate'),
        () => { params.feedRate = parseFloat(document.getElementById('slider-feed-rate').value); simulateAll(); }
      );
      linkRangeAndNumber(
        document.getElementById('slider-ua'),
        document.getElementById('input-ua'),
        () => { params.UA = parseFloat(document.getElementById('slider-ua').value); simulateAll(); }
      );
      linkRangeAndNumber(
        document.getElementById('slider-dh1'),
        document.getElementById('input-dh1'),
        () => { params.dH1 = parseFloat(document.getElementById('slider-dh1').value); simulateAll(); }
      );
      linkRangeAndNumber(
        document.getElementById('slider-dh2'),
        document.getElementById('input-dh2'),
        () => { params.dH2 = parseFloat(document.getElementById('slider-dh2').value); simulateAll(); }
      );
    }

    // p5 sketch (instance mode)
    let p5Instance = null;
    let needRedraw = true;

    function requestRedraw() {
      needRedraw = true;
      if (p5Instance) p5Instance.redraw();
    }

    function createSketch() {
      // Remove pre-existing canvas (as per plan, we initially include one, then replace with p5-managed canvas with same id)
      const pre = document.getElementById('plot-canvas');
      if (pre && pre.parentElement) {
        pre.parentElement.removeChild(pre);
      }

      const sketch = (p) => {
        let canvas, W = 100, H = 100;
        const margins = { left: 64, right: 20, top: 54, bottom: 42 };

        function sizeToContainer() {
          const container = document.getElementById('plot-area');
          const rect = container.getBoundingClientRect();
          const w = Math.max(300, Math.floor(rect.width));
          const h = Math.max(360, Math.floor(rect.height));
          if (canvas) {
            p.resizeCanvas(w, h);
          } else {
            canvas = p.createCanvas(w, h);
            canvas.id('plot-canvas');     // maintain required ID
            canvas.parent('plot-area');
          }
          W = w; H = h;
        }

        function xToPx(x) {
          const xmin = 0, xmax = tMax;
          const plotW = W - margins.left - margins.right;
          return margins.left + plotW * (x - xmin) / (xmax - xmin);
        }

        function yToPx(y, yMin, yMax) {
          const plotH = H - margins.top - margins.bottom;
          return margins.top + plotH * (1 - (y - yMin) / (yMax - yMin));
        }

        function drawGridAndAxes(yMin, yMax, yTicks, yLabel) {
          p.push();
          p.background('#F0F0F0');

          // Plot area boundary
          p.noFill();
          p.stroke(200);
          p.rect(margins.left, margins.top, W - margins.left - margins.right, H - margins.top - margins.bottom);

          // Grid lines
          p.stroke(230);
          p.strokeWeight(1);

          // X grid and ticks at 0,0.5,1.0,1.5,2.0
          for (let t = 0; t <= tMax + 1e-9; t += 0.5) {
            const x = Math.round(xToPx(t)) + 0.5;
            p.line(x, margins.top, x, H - margins.bottom);
          }
          // Y grid lines
          for (const yval of yTicks) {
            const y = Math.round(yToPx(yval, yMin, yMax)) + 0.5;
            p.line(margins.left, y, W - margins.right, y);
          }

          // Axes ticks and labels
          p.stroke(80);
          p.fill(40);
          p.noStroke();
          p.textSize(12);
          p.textAlign(p.CENTER, p.TOP);
          for (let t = 0; t <= tMax + 1e-9; t += 0.5) {
            const x = xToPx(t);
            p.fill(60);
            p.text(t.toFixed(1), x, H - margins.bottom + 6);
          }
          p.textAlign(p.CENTER, p.TOP);
          p.text('time (h)', margins.left + (W - margins.left - margins.right) / 2, H - margins.bottom + 22);

          // Y labels
          p.textAlign(p.RIGHT, p.CENTER);
          for (const yval of yTicks) {
            const y = yToPx(yval, yMin, yMax);
            p.fill(60);
            const txt = (Math.abs(yval) >= 1000 || Math.abs(yval) < 0.01) ? yval.toExponential(1) : yval.toFixed(2).replace(/\.00$/, '');
            p.text(txt, margins.left - 8, y);
          }

          // Y axis title (rotated)
          p.push();
          p.translate(18, margins.top + (H - margins.top - margins.bottom) / 2);
          p.rotate(-Math.PI / 2);
          p.textAlign(p.CENTER, p.CENTER);
          p.fill(30);
          p.text(yLabel, 0, 0);
          p.pop();

          p.pop();
        }

        function getYTicksFixed(min, max, step) {
          const arr = [];
          for (let y = min; y <= max + 1e-9; y += step) arr.push(y);
          return arr;
        }
        function getYTicksDynamic(min, max, nticks = 5) {
          const arr = [];
          if (max <= min) {
            const mid = min;
            for (let i = 0; i <= nticks; i++) arr.push(mid);
            return arr;
          }
          for (let i = 0; i <= nticks; i++) arr.push(min + (max - min) * i / nticks);
          return arr;
        }

        function strokeDash(dashArray = []) {
          p.drawingContext.setLineDash(dashArray);
        }

        function drawSeries(xs, ys, color, dashed, yMin, yMax) {
          p.push();
          p.noFill();
          p.stroke(color);
          p.strokeWeight(2);
          strokeDash(dashed ? [8, 6] : []);
          p.beginShape();
          const N = xs.length;
          for (let i = 0; i < N; i++) {
            const yv = ys[i];
            if (!isFinite(yv)) {
              // Break the polyline on NaN to avoid long jumps
              p.endShape();
              p.beginShape();
              continue;
            }
            const x = xToPx(xs[i]);
            const y = yToPx(yv, yMin, yMax);
            p.vertex(x, y);
          }
          p.endShape();
          strokeDash([]);
          p.pop();
        }

        p.setup = () => {
          sizeToContainer();
          p.noLoop();
          // Initial draw happens after simulateAll triggers redraw
        };

        p.windowResized = () => {
          sizeToContainer();
          requestRedraw();
        };

        p.draw = () => {
          if (!needRedraw) return;
          needRedraw = false;

          // Determine y-range and ticks
          let yMin, yMax, yTicks, yLabel;
          if (plotType === 'selectivity') {
            const vals = sim.Sratio.filter(v => isFinite(v));
            if (vals.length === 0) {
              yMin = 0; yMax = 1;
            } else {
              let vmin = Math.min(...vals);
              let vmax = Math.max(...vals);
              if (vmin === vmax) {
                const pad = Math.max(0.1, Math.abs(vmax) * 0.1);
                vmin -= pad; vmax += pad;
              } else {
                const pad = 0.1 * (vmax - vmin);
                vmin -= pad; vmax += pad;
              }
              yMin = vmin; yMax = vmax;
              // Avoid zero-height range
              if (yMax - yMin < 1e-6) { yMin -= 0.5; yMax += 0.5; }
            }
            yTicks = getYTicksDynamic(yMin, yMax, 5);
            yLabel = 'selectivity ratio';
          } else {
            yMin = 0; yMax = 100;
            yTicks = getYTicksFixed(0, 100, 20);
            yLabel = 'moles (mol)';
          }

          drawGridAndAxes(yMin, yMax, yTicks, yLabel);

          // Draw data
          if (plotType === 'selectivity') {
            drawSeries(sim.t, sim.Sratio, '#800080', false, yMin, yMax);
          } else if (plotType === 'batch' || plotType === 'semibatch') {
            drawSeries(sim.t, sim.Na, '#2ca02c', true, yMin, yMax);
            drawSeries(sim.t, sim.Nb, '#000000', true, yMin, yMax);
            drawSeries(sim.t, sim.Nd, '#1f77b4', false, yMin, yMax);
            drawSeries(sim.t, sim.Nu, '#d62728', false, yMin, yMax);
          }
        };
      };

      p5Instance = new p5(sketch);
    }

    // Initialize everything after DOM is ready and MathJax typesets labels
    window.addEventListener('DOMContentLoaded', () => {
      // Ensure MathJax typesets control panel labels
      if (window.MathJax && window.MathJax.typesetPromise) {
        MathJax.typesetPromise().catch(() => {});
      }

      initControls();
      createSketch();
      simulateAll(); // first run
    });
  </script>
</body>
