    <div id="canvas-container">
      <p id="status-text"></p>
      <button id="btn-reset">+</button>
      <!-- p5.js will insert a canvas here -->
    </div>
  </div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.min.js"></script>
  <script>
    // Global state
    const W = 500, H = 500;
    const margin = 12; // to avoid drawing through the outer stroke
    const stepSize = 18;
    const neighborRadius = 40;

    let algo = 'RT'; // 'RT' | 'RRT' | 'RRT*'
    let obstacleType = 'narrow-passage';

    let startDefault = { x: 50, y: 250 };
    let goalDefault = { x: 450, y: 450 };

    let start = { x: startDefault.x, y: startDefault.y };
    let goal  = { x: goalDefault.x, y: goalDefault.y };

    let goalRadius = 1;
    let bias = 0.0;

    let nodes = []; // {x, y, parent, cost}
    let goalReached = false;
    let bestPath = null; // array of indices representing the path (end -> ... -> start) or array of points
    let bestLength = Infinity;

    // Obstacles are represented as array of rectangles: {x, y, w, h}
    let obstacles = [];

    // p5 canvas reference
    let p5Canvas;
    let draggingGoal = false;

    // DOM Elements
    const elBtnRT = document.getElementById('btn-rt');
    const elBtnRRT = document.getElementById('btn-rrt');
    const elBtnRRTStar = document.getElementById('btn-rrt-star');
    const elObstacleSelect = document.getElementById('select-obstacle');
    const elAdd1 = document.getElementById('btn-add-1');
    const elAdd10 = document.getElementById('btn-add-10');
    const elAdd100 = document.getElementById('btn-add-100');
    const elAdd200 = document.getElementById('btn-add-200');
    const elAdd500 = document.getElementById('btn-add-500');
    const elBias = document.getElementById('slider-bias');
    const elBiasLabel = document.getElementById('bias-value-label');
    const elRadius = document.getElementById('slider-radius');
    const elRadiusLabel = document.getElementById('radius-value-label');
    const elStatus = document.getElementById('status-text');
    const elReset = document.getElementById('btn-reset');

    // Utility math
    function dist2(a, b) {
      const dx = a.x - b.x, dy = a.y - b.y;
      return dx*dx + dy*dy;
    }
    function distance(a, b) {
      return Math.sqrt(dist2(a, b));
    }
    function clamp(v, a, b) {
      return Math.max(a, Math.min(b, v));
    }

    // Geometry helpers for collision detection
    function pointInRect(px, py, r) {
      return px >= r.x && px <= r.x + r.w && py >= r.y && py <= r.y + r.h;
    }
    function ccw(ax, ay, bx, by, cx, cy) {
      return (cy - ay) * (bx - ax) > (by - ay) * (cx - ax);
    }
    function segmentsIntersect(a, b, c, d) {
      // a,b,c,d are {x,y}
      return ccw(a.x, a.y, c.x, c.y, d.x, d.y) !== ccw(b.x, b.y, c.x, c.y, d.x, d.y) &&
             ccw(a.x, a.y, b.x, b.y, c.x, c.y) !== ccw(a.x, a.y, b.x, b.y, d.x, d.y);
    }
    function segmentIntersectsRect(ax, ay, bx, by, r) {
      // If either endpoint is inside the rect, consider it intersecting
      if (pointInRect(ax, ay, r) || pointInRect(bx, by, r)) return true;
      // Check intersection with each side of the rectangle
      const p1 = {x: r.x, y: r.y};
      const p2 = {x: r.x + r.w, y: r.y};
      const p3 = {x: r.x + r.w, y: r.y + r.h};
      const p4 = {x: r.x, y: r.y + r.h};
      const A = {x: ax, y: ay}, B = {x: bx, y: by};
      if (segmentsIntersect(A, B, p1, p2)) return true;
      if (segmentsIntersect(A, B, p2, p3)) return true;
      if (segmentsIntersect(A, B, p3, p4)) return true;
      if (segmentsIntersect(A, B, p4, p1)) return true;
      return false;
    }
    function collidesPoint(x, y) {
      for (const r of obstacles) {
        if (pointInRect(x, y, r)) return true;
      }
      return false;
    }
    function collidesSegment(ax, ay, bx, by) {
      for (const r of obstacles) {
        if (segmentIntersectsRect(ax, ay, bx, by, r)) return true;
      }
      return false;
    }

    // Obstacle generation and drawing
    function generateObstacles(type) {
      const walls = [];
      // We approximate stroke-thick blue rectangles as filled rectangles to test collisions.
      const strokeW = 8;
      const half = strokeW / 2;

      if (type === 'empty') {
        // No obstacles, but keep a visual border (just for drawing, not collision)
        // For collision, we do not add borders so tree can grow up to margins; we limit sampling domain instead.
        return walls;
      }
      if (type === 'narrow-passage') {
        // Border walls as four rectangles (collision)
        walls.push({x: 5 - half, y: 5 - half, w: 490 + strokeW, h: strokeW}); // top
        walls.push({x: 5 - half, y: 495 - half, w: 490 + strokeW, h: strokeW}); // bottom
        walls.push({x: 5 - half, y: 5 - half, w: strokeW, h: 490 + strokeW}); // left
        walls.push({x: 495 - half, y: 5 - half, w: strokeW, h: 490 + strokeW}); // right

        // Two vertical walls with openings
        // Wall 1 at x ~ 200
        const w1x = 200;
        const gap1Start = 220, gap1End = 280; // opening
        // upper segment
        walls.push({x: w1x - half, y: 5, w: strokeW, h: gap1Start - 5});
        // lower segment
        walls.push({x: w1x - half, y: gap1End, w: strokeW, h: 500 - gap1End - 5});

        // Wall 2 at x ~ 300
        const w2x = 300;
        const gap2Start = 240, gap2End = 260; // narrow opening
        walls.push({x: w2x - half, y: 5, w: strokeW, h: gap2Start - 5});
        walls.push({x: w2x - half, y: gap2End, w: strokeW, h: 500 - gap2End - 5});
        return walls;
      }
      if (type === 'random-rects') {
        // Generate a few random rectangles
        const rngRects = [];
        const count = 6;
        for (let i = 0; i < count; i++) {
          const rw = 60 + Math.random() * 120;
          const rh = 40 + Math.random() * 120;
          const rx = 30 + Math.random() * (W - 60 - rw);
          const ry = 30 + Math.random() * (H - 60 - rh);
          // Represent as a thin-walled rectangle with thick stroke: for collision, approximate as a filled ring by taking stroke as solid band
          // Simplify: treat as filled rectangle expanded by strokeW/2 (it makes them solid blocks)
          rngRects.push({x: rx - half, y: ry - half, w: rw + strokeW, h: rh + strokeW});
        }
        return rngRects;
      }
      return walls;
    }

    function drawObstacles(p) {
      p.push();
      p.noFill();
      p.stroke('blue');
      p.strokeWeight(8);

      if (obstacleType === 'narrow-passage') {
        // Draw outer border
        p.rect(5, 5, 490, 490);
        // Two vertical walls as long thin rectangles (stroke outlines)
        // For drawing, show walls as long rects centered on the x positions
        p.rect(200 - 4, 5, 8, 490);
        p.rect(300 - 4, 5, 8, 490);
        // But the "openings" will visually appear as continuous outlines (since we use stroke/noFill).
        // The collision model above already introduced gaps; visually, to hint gaps, overlay white rectangles over the gaps:
        p.pop();
        p.push();
        p.noStroke();
        p.fill(255);
        // carve openings
        p.rect(200 - 6, 220, 12, 60);
        p.rect(300 - 6, 240, 12, 20);
      } else if (obstacleType === 'random-rects') {
        p.pop();
        p.push();
        p.noFill();
        p.stroke('blue');
        p.strokeWeight(8);
        for (const r of obstacles) {
          // For drawing, shrink by stroke/2 to mimic stroke outline around a hollow rect
          const drawX = r.x + 4;
          const drawY = r.y + 4;
          const drawW = Math.max(0, r.w - 8);
          const drawH = Math.max(0, r.h - 8);
          p.rect(drawX, drawY, drawW, drawH);
        }
      } else {
        p.pop();
        // empty -> nothing except background
      }
    }

    // Tree operations
    function resetSimulation(fullResetPositions = false) {
      if (fullResetPositions) {
        start = { x: startDefault.x, y: startDefault.y };
        goal  = { x: goalDefault.x,  y: goalDefault.y  };
        // Also reset UI to initial defaults? Spec: only positions reset. Keep algorithm and obstacles as selected.
      }
      nodes = [{ x: start.x, y: start.y, parent: -1, cost: 0 }];
      goalReached = false;
      bestPath = null;
      bestLength = Infinity;
      updateStatusText();
      if (window._p5) window._p5.redraw();
    }

    function initializeObstacles() {
      obstacles = generateObstacles(obstacleType);
    }

    function randomSample() {
      return {
        x: margin + Math.random() * (W - 2*margin),
        y: margin + Math.random() * (H - 2*margin)
      };
    }

    function sampleTarget() {
      if (algo === 'RRT' || algo === 'RRT*') {
        if (Math.random() < bias) {
          return { x: goal.x, y: goal.y };
        }
      }
      return randomSample();
    }

    function nearestNodeIndex(pt) {
      let bestI = 0;
      let bestD2 = Infinity;
      for (let i = 0; i < nodes.length; i++) {
        const d2 = (nodes[i].x - pt.x) ** 2 + (nodes[i].y - pt.y) ** 2;
        if (d2 < bestD2) {
          bestD2 = d2; bestI = i;
        }
      }
      return bestI;
    }

    function steer(from, to, step = stepSize) {
      const dx = to.x - from.x, dy = to.y - from.y;
      const d = Math.hypot(dx, dy);
      if (d <= step) return { x: to.x, y: to.y };
      const ux = dx / d, uy = dy / d;
      return { x: from.x + ux * step, y: from.y + uy * step };
    }

    function neighborsWithinRadius(p, r) {
      const idxs = [];
      const r2 = r*r;
      for (let i = 0; i < nodes.length; i++) {
        const d2 = (nodes[i].x - p.x) ** 2 + (nodes[i].y - p.y) ** 2;
        if (d2 <= r2) idxs.push(i);
      }
      return idxs;
    }

    function pathFromNodeIndex(i) {
      const indices = [];
      let cur = i;
      while (cur !== -1) {
        indices.push(cur);
        cur = nodes[cur].parent;
      }
      return indices; // from end to start
    }

    function pathLengthFromNodeIndex(i) {
      let len = 0;
      let cur = i;
      while (nodes[cur].parent !== -1) {
        const p = nodes[cur];
        const pp = nodes[p.parent];
        len += Math.hypot(p.x - pp.x, p.y - pp.y);
        cur = p.parent;
      }
      return len;
    }

    function tryAddOneNode() {
      const target = sampleTarget();

      let parentIdx;
      let newPoint;

      if (algo === 'RT') {
        parentIdx = Math.floor(Math.random() * nodes.length);
        const parent = nodes[parentIdx];
        newPoint = steer(parent, target, stepSize);
      } else {
        const nearIdx = nearestNodeIndex(target);
        const nearNode = nodes[nearIdx];
        newPoint = steer(nearNode, target, stepSize);
        parentIdx = nearIdx;
      }

      // clamp to domain
      newPoint.x = clamp(newPoint.x, margin, W - margin);
      newPoint.y = clamp(newPoint.y, margin, H - margin);

      // collision checks
      if (collidesPoint(newPoint.x, newPoint.y)) return false;
      const parentNode = nodes[parentIdx];
      if (collidesSegment(parentNode.x, parentNode.y, newPoint.x, newPoint.y)) return false;

      // For RRT*, choose optimal parent among neighbors
      let cost = nodes[parentIdx].cost + Math.hypot(newPoint.x - parentNode.x, newPoint.y - parentNode.y);

      if (algo === 'RRT*') {
        const neigh = neighborsWithinRadius(newPoint, neighborRadius);
        let bestIdx = parentIdx;
        let bestCost = cost;
        for (const i of neigh) {
          const cand = nodes[i];
          if (collidesSegment(cand.x, cand.y, newPoint.x, newPoint.y)) continue;
          const c = cand.cost + Math.hypot(newPoint.x - cand.x, newPoint.y - cand.y);
          if (c < bestCost) {
            bestCost = c;
            bestIdx = i;
          }
        }
        parentIdx = bestIdx;
        cost = bestCost;
      }

      const newIdx = nodes.length;
      nodes.push({ x: newPoint.x, y: newPoint.y, parent: parentIdx, cost });

      // RRT* rewiring
      if (algo === 'RRT*') {
        const neigh = neighborsWithinRadius(newPoint, neighborRadius);
        for (const i of neigh) {
          if (i === parentIdx || i === newIdx) continue;
          const cand = nodes[i];
          const newCost = cost + Math.hypot(cand.x - newPoint.x, cand.y - newPoint.y);
          // If connecting through new point is cheaper and collision-free, rewire
          if (newCost + 1e-9 < cand.cost) {
            if (!collidesSegment(cand.x, cand.y, newPoint.x, newPoint.y)) {
              cand.parent = newIdx;
              cand.cost = newCost;
            }
          }
        }
      }

      // Goal checking
      const gdx = nodes[newIdx].x - goal.x;
      const gdy = nodes[newIdx].y - goal.y;
      const within = (gdx*gdx + gdy*gdy) <= goalRadius*goalRadius;

      if (within) {
        if (!goalReached) {
          goalReached = true;
          bestPath = pathFromNodeIndex(newIdx);
          bestLength = pathLengthFromNodeIndex(newIdx);
        } else if (algo === 'RRT*') {
          const len = pathLengthFromNodeIndex(newIdx);
          if (len + 1e-9 < bestLength) {
            bestLength = len;
            bestPath = pathFromNodeIndex(newIdx);
          }
        }
      } else if (algo === 'RRT*' && goalReached) {
        // After rewiring, check if any node inside goal got improved
        // This is a small scan; nodes are modest in size here.
        let improvedIdx = -1;
        let improvedLen = bestLength;
        for (let i = 0; i < nodes.length; i++) {
          const nd = nodes[i];
          const dx = nd.x - goal.x, dy = nd.y - goal.y;
          if ((dx*dx + dy*dy) <= goalRadius*goalRadius) {
            const len = pathLengthFromNodeIndex(i);
            if (len + 1e-9 < improvedLen) {
              improvedLen = len;
              improvedIdx = i;
            }
          }
        }
        if (improvedIdx >= 0) {
          bestLength = improvedLen;
          bestPath = pathFromNodeIndex(improvedIdx);
        }
      }

      return true;
    }

    function addNodesLoop(n) {
      // Try up to attempts per intended node to avoid getting stuck on collisions
      let added = 0;
      let attempts = 0;
      const maxAttempts = n * 50;
      while (added < n && attempts < maxAttempts) {
        attempts++;
        const ok = tryAddOneNode();
        if (ok) added++;
      }
      updateStatusText();
      window._p5.redraw();
    }

    function updateStatusText() {
      const count = nodes.length;
      if (goalReached && bestPath && isFinite(bestLength)) {
        elStatus.textContent = `${count} ${count === 1 ? 'node' : 'nodes'}, path length ${bestLength.toFixed(2)}`;
      } else {
        elStatus.textContent = `${count} ${count === 1 ? 'node' : 'nodes'}, goal not yet reached`;
      }
    }

    function setActiveAlgoButton(btn) {
      for (const b of [elBtnRT, elBtnRRT, elBtnRRTStar]) b.classList.remove('active');
      btn.classList.add('active');
    }

    function setBiasControlEnabled(enabled) {
      elBias.disabled = !enabled;
      if (enabled) {
        elBias.parentElement.classList.remove('disabled');
      } else {
        elBias.parentElement.classList.add('disabled');
      }
    }

    function checkGoalAgainstExistingNodes() {
      // After changing radius, see if current nodes now reach the goal
      let foundIdx = -1;
      let bestLen = Infinity;
      for (let i = 0; i < nodes.length; i++) {
        const nd = nodes[i];
        const dx = nd.x - goal.x, dy = nd.y - goal.y;
        if ((dx*dx + dy*dy) <= goalRadius*goalRadius) {
          if (algo === 'RRT*') {
            const len = pathLengthFromNodeIndex(i);
            if (len < bestLen) {
              bestLen = len;
              foundIdx = i;
            }
          } else {
            // For RT/RRT, accept the first found only if not already reached
            if (!goalReached) {
              foundIdx = i;
              bestLen = pathLengthFromNodeIndex(i);
              break;
            }
          }
        }
      }
      if (foundIdx >= 0) {
        goalReached = true;
        bestPath = pathFromNodeIndex(foundIdx);
        bestLength = bestLen;
      }
      updateStatusText();
      window._p5.redraw();
    }

    // p5 Sketch
    const sketch = (p) => {
      window._p5 = p;
      p.setup = function() {
        p.pixelDensity(1);
        p5Canvas = p.createCanvas(W, H);
        p5Canvas.parent('canvas-container');
        p.noLoop();

        // Initial state setup
        initializeObstacles();
        resetSimulation(false);

        // Status initial
        updateStatusText();
        p.redraw();
      };

      p.draw = function() {
        p.background(255);

        // Obstacles
        drawObstacles(p);

        // Goal area (yellow translucent) + crosshair
        p.push();
        p.noStroke();
        p.fill(255, 255, 0, 100);
        p.circle(goal.x, goal.y, goalRadius * 2);
        p.pop();

        // Crosshair
        p.push();
        p.stroke(0);
        p.strokeWeight(1);
        p.line(goal.x - 8, goal.y, goal.x + 8, goal.y);
        p.line(goal.x, goal.y - 8, goal.x, goal.y + 8);
        p.pop();

        // Draw tree (nodes for RT/RRT, nodes+edges for RRT*)
        if (algo === 'RRT*') {
          p.push();
          p.stroke(0);
          p.strokeWeight(0.5);
          for (let i = 1; i < nodes.length; i++) {
            const n = nodes[i];
            const pn = nodes[n.parent];
            p.line(n.x, n.y, pn.x, pn.y);
          }
          p.pop();
        }

        // Nodes
        p.push();
        p.noStroke();
        p.fill('red');
        for (let i = 0; i < nodes.length; i++) {
          const r = 3;
          p.circle(nodes[i].x, nodes[i].y, r*2);
        }
        p.pop();

        // Final path if reached
        if (goalReached && bestPath && bestPath.length > 0) {
          p.push();
          p.stroke('green');
          p.strokeWeight(3);
          for (let i = 0; i < bestPath.length - 1; i++) {
            const a = nodes[bestPath[i]];
            const b = nodes[bestPath[i + 1]];
            p.line(a.x, a.y, b.x, b.y);
          }
          p.pop();
        }

        // Start node (green if simulation started, red if only the start exists)
        p.push();
        p.noStroke();
        if (nodes.length <= 1) {
          p.fill('red');
        } else {
          p.fill('green');
        }
        p.circle(start.x, start.y, 8);
        p.pop();
      };

      p.mousePressed = function() {
        const mx = p.mouseX, my = p.mouseY;
        if (mx < 0 || my < 0 || mx > W || my > H) return;
        // Begin dragging goal if within some tolerance around goal circle
        const d = Math.hypot(mx - goal.x, my - goal.y);
        if (d <= goalRadius + 10) {
          draggingGoal = true;
          // Dragging triggers a full reset of the simulation (tree, path, node count)
          // but keep current start position and obstacle/algorithm settings.
          resetSimulation(false);
          p.redraw();
        }
      };
      p.mouseDragged = function() {
        if (!draggingGoal) return;
        const mx = clamp(p.mouseX, margin, W - margin);
        const my = clamp(p.mouseY, margin, H - margin);
        goal.x = mx;
        goal.y = my;
        // Keep reset state (only start node). Reflect movement visually.
        updateStatusText();
        p.redraw();
      };
      p.mouseReleased = function() {
        if (draggingGoal) {
          draggingGoal = false;
        }
      };
    };
    new p5(sketch);

    // Event listeners
    elBtnRT.addEventListener('click', () => {
      algo = 'RT';
      setActiveAlgoButton(elBtnRT);
      setBiasControlEnabled(false);
      // Full reset of the simulation (tree/path) but keep positions
      resetSimulation(false);
    });
    elBtnRRT.addEventListener('click', () => {
      algo = 'RRT';
      setActiveAlgoButton(elBtnRRT);
      setBiasControlEnabled(true);
      resetSimulation(false);
    });
    elBtnRRTStar.addEventListener('click', () => {
      algo = 'RRT*';
      setActiveAlgoButton(elBtnRRTStar);
      setBiasControlEnabled(true);
      resetSimulation(false);
    });

    elObstacleSelect.addEventListener('change', (e) => {
      obstacleType = e.target.value;
      initializeObstacles();
      resetSimulation(false);
    });

    elAdd1.addEventListener('click', () => addNodesLoop(1));
    elAdd10.addEventListener('click', () => addNodesLoop(10));
    elAdd100.addEventListener('click', () => addNodesLoop(100));
    elAdd200.addEventListener('click', () => addNodesLoop(200));
    elAdd500.addEventListener('click', () => addNodesLoop(500));

    elBias.addEventListener('input', (e) => {
      bias = parseFloat(e.target.value);
      elBiasLabel.textContent = bias.toFixed(2).replace(/\.00$/, '.0').replace(/0$/, '');
      // does not reset simulation
    });

    elRadius.addEventListener('input', (e) => {
      goalRadius = parseFloat(e.target.value);
      elRadiusLabel.textContent = goalRadius.toFixed(1);
      // update visualization of goal radius and check if any node now within radius
      checkGoalAgainstExistingNodes();
    });

    elReset.addEventListener('click', () => {
      // Reset to initial state including positions
      // Keep current algorithm and obstacle selection
      initializeObstacles();
      resetSimulation(true);
      // Reset UI labels to reflect internal values (bias and radius unchanged per spec)
      updateStatusText();
    });

    // Initialize slider labels
    elBiasLabel.textContent = parseFloat(elBias.value).toString();
    elRadiusLabel.textContent = parseFloat(elRadius.value).toString();
    setBiasControlEnabled(false); // default RT

  </script>
</body>
</html>
