      const container = document.getElementById('visualization-container');
      const { width, height } = container.getBoundingClientRect();
      camera = new THREE.PerspectiveCamera(60, width / height, 0.1, 100);
      camera.position.set(4, 4, 4);
      camera.lookAt(0, 1, 0);

      controls = new THREE.OrbitControls(camera, renderer.domElement);
      controls.target.set(0, 1, 0);
      controls.update();

      // Lights
      const ambient = new THREE.AmbientLight(0x666666);
      scene.add(ambient);
      const dir = new THREE.DirectionalLight(0xffffff, 1.0);
      dir.position.set(5, 5, 5);
      scene.add(dir);

      // Axes Helper (X: red, Y: green, Z: blue)
      const axes = new THREE.AxesHelper(4);
      scene.add(axes);

      // Potential Surface (Parametric)
      const surfFunc = function (u, v, target) {
        const rho = 0.4 + u * 2.1;
        const theta = v * Math.PI * 2.0;
        const x = rho * Math.cos(theta);
        const y_surf = rho * rho + 1 / (rho * rho); // vertical in three.js
        const z_surf = -rho * Math.sin(theta);      // lateral
        target.set(x, y_surf, z_surf);
      };
      const surfaceGeom = new THREE.ParametricGeometry(surfFunc, 200, 100);
      const surfaceMat = new THREE.MeshStandardMaterial({
        color: 0xE69943,
        metalness: 0.4,
        roughness: 0.5,
        side: THREE.DoubleSide
      });
      const surfaceMesh = new THREE.Mesh(surfaceGeom, surfaceMat);
      scene.add(surfaceMesh);

      // Central Cylinder
      const cylGeom = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
      const cylMat = new THREE.MeshStandardMaterial({
        color: 0xcccccc,
        metalness: 0.1,
        roughness: 0.9
      });
      const cylinder = new THREE.Mesh(cylGeom, cylMat);
      cylinder.position.set(0, 1.25, 0);
