    }

    function onWindowResize() {
      const container = document.getElementById('visualization-container');
      const rect = container.getBoundingClientRect();
      const width = rect.width;
      const height = rect.height;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height, false);
    }

    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }

    function initControls() {
      const pairs = [
        { range: 'slider-t', number: 'input-t' },
        { range: 'slider-rho0', number: 'input-rho0' },
        { range: 'slider-zeta0', number: 'input-zeta0' },
        { range: 'slider-e0', number: 'input-e0' },
        { range: 'slider-v', number: 'input-v' },
        { range: 'slider-lambda', number: 'input-lambda' }
      ];

      for (const p of pairs) {
        const r = document.getElementById(p.range);
        const n = document.getElementById(p.number);

        // Ensure number reflects range defaults on load
        n.value = r.value;

        r.addEventListener('input', () => {
          n.value = r.value;
          updateVisualization();
        });

        n.addEventListener('input', () => {
          const min = parseFloat(r.min);
          const max = parseFloat(r.max);
          let val = parseFloat(n.value);
          if (Number.isNaN(val)) val = parseFloat(r.value);
          val = Math.min(max, Math.max(min, val));
          // Round to nearest step to avoid tiny floating diffs
          const step = parseFloat(r.step) || 1;
          val = Math.round(val / step) * step;
          n.value = val;
          r.value = val;
          updateVisualization();
        });
      }
    }

    function updateVisualization() {
      const t = parseFloat(document.getElementById('input-t').value);
      const rho0 = parseFloat(document.getElementById('input-rho0').value);
      const zeta0 = parseFloat(document.getElementById('input-zeta0').value);
      const e0 = parseFloat(document.getElementById('input-e0').value);
      const nu = parseFloat(document.getElementById('input-v').value);
      const lambda = parseFloat(document.getElementById('input-lambda').value);

      const pointsXYZ = computeIonPathPoints(t, rho0, zeta0, e0, nu, lambda);
      if (pointsXYZ.length < 2) return;

      // Remove existing path if present
      const old = scene.getObjectByName('ionPath');
      if (old) {
        if (old.geometry) old.geometry.dispose();
        if (old.material) old.material.dispose();
        scene.remove(old);
      }

      // Map to three.js orientation: (x, y, z) -> (x, z, -y)
      const orientedPoints = pointsXYZ.map(p => new THREE.Vector3(p.x, p.z, -p.y));

      const curve = new THREE.CatmullRomCurve3(orientedPoints, false);
      const tubeGeom = new THREE.TubeGeometry(curve, 512, 0.04, 8, false);
      const mat = new THREE.MeshBasicMaterial({ color: 0x61AEEF });
      const mesh = new THREE.Mesh(tubeGeom, mat);
      mesh.name = 'ionPath';
      scene.add(mesh);
    }

    function computeIonPathPoints(t, rho0, zeta0, e0, nu, lambda) {
      const ds = 0.05;
      const steps = Math.max(1, Math.floor(t / ds));
      let s = 0;

      // State Y = [rho, drho/ds, phi]
      let Y = [rho0, 0.0, 0.0];

      const points = [];

      const clampRho = (rho) => Math.max(0.05, rho); // prevent division by zero

      const derivs = (state) => {
        const rho = clampRho(state[0]);
        const drho = state[1];
        const phi = state[2];
        const invR2 = 1.0 / (rho * rho);
        const invR3 = invR2 / rho;

        const d_rho = drho;
        const d2_rho = -(2 * rho - 2 * invR3) - 4 * lambda * lambda * rho + 4 * e0 * invR3;
        const d_phi = 2 * (e0 * invR2 - lambda);

        return [d_rho, d2_rho, d_phi];
      };

      function rk4Step(Y, h) {
        const k1 = derivs(Y);
        const Y2 = [Y[0] + 0.5 * h * k1[0], Y[1] + 0.5 * h * k1[1], Y[2] + 0.5 * h * k1[2]];
        const k2 = derivs(Y2);
        const Y3 = [Y[0] + 0.5 * h * k2[0], Y[1] + 0.5 * h * k2[1], Y[2] + 0.5 * h * k2[2]];
        const k3 = derivs(Y3);
        const Y4 = [Y[0] + h * k3[0], Y[1] + h * k3[1], Y[2] + h * k3[2]];
        const k4 = derivs(Y4);

        return [
          Y[0] + (h / 6) * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]),
          Y[1] + (h / 6) * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]),
          Y[2] + (h / 6) * (k1[2] + 2 * k2[2] + 2 * k3[2] + k4[2])
        ];
      }

      // Generate points
      for (let i = 0; i <= steps; i++) {
        const rho = clampRho(Y[0]);
        const phi = Y[2];

        const u_rho = rho * rho + 1.0 / (rho * rho);
        const sqrt_arg = e0 - u_rho;
        const z = Math.sqrt(Math.max(0, sqrt_arg)) * Math.sin(Math.SQRT2 * nu * s + zeta0);
        const x = rho * Math.cos(phi);
        const y = rho * Math.sin(phi);

        points.push(new THREE.Vector3(x, y, z));

        // Advance integration
        Y = rk4Step(Y, ds);
        s += ds;
      }

      return points;
    }

    // Initialize on load
    window.addEventListener('DOMContentLoaded', () => {
      initThree();
      initControls();
      // Initial render of labels with MathJax
      if (window.MathJax && window.MathJax.typesetPromise) {
        MathJax.typesetPromise();
      }
      updateVisualization();
    });
  </script>
