<body>
  <div id="app-container">
    <!-- Control Panel -->
    <div id="control-panel">
      <label for="slider-r">R (Å)</label>
      <input id="slider-r" type="range" min="0.001" max="4.0" step="0.001" value="0.001" />
      <output id="slider-r-value">0.001</output>
      <span class="sr-note">Drag to change internuclear distance</span>
    </div>

    <!-- Potential Energy Plot -->
    <div id="potential-energy-plot-container"></div>

    <!-- Electron Density Plots -->
    <div id="density-plots-container">
      <div id="bonding-plot-container" class="plot-wrapper">
        <div class="plot-title">bonding density</div>
      </div>
      <div id="antibonding-plot-container" class="plot-wrapper">
        <div class="plot-title">antibonding density</div>
      </div>
    </div>
  </div>

  <script>
    // Constants
    const ANGSTROM_PER_BOHR = 0.529177; // 1 bohr = 0.529177 Å
    const BOHR_PER_ANGSTROM = 1.0 / ANGSTROM_PER_BOHR;

    // Utility: generate linearly spaced array
    function linspace(a, b, n) {
      const arr = new Array(n);
      const step = (b - a) / (n - 1);
      for (let i = 0; i < n; i++) arr[i] = a + step * i;
      return arr;
    }

    // Overlap integral S_ab as a function of R (in a.u.)
    function overlapSab(R) {
      // S_ab = (1 + R + R^2/3) * exp(-R)
      return (1 + R + (R * R) / 3) * Math.exp(-R);
    }

    // Coulomb integral J and exchange integral K for H2+ (in a.u.), R in a.u.
    function coulombJ(R) {
      // J = (1/R) - (1 + 1/R) * exp(-2R)
      return (1 / R) - (1 + 1 / R) * Math.exp(-2 * R);
    }
    function exchangeK(R) {
      // K = (1 + R) * exp(-R)
      return (1 + R) * Math.exp(-R);
    }

    // Potential energy data generation (static)
    function makePotentialEnergyData() {
      const Rmin = 0.2, Rmax = 15.0, n = 900;
      const R_au = linspace(Rmin, Rmax, n);
      const R_A = R_au.map(r => r * ANGSTROM_PER_BOHR);
      const Eb = [];
      const Ea = [];
      for (let i = 0; i < R_au.length; i++) {
        const R = R_au[i];
        const Sab = overlapSab(R);
        const J = coulombJ(R);
        const K = exchangeK(R);
        // Safeguards against pathological tiny denominators
        const denom_b = 1 + Sab;
        const denom_a_raw = 1 - Sab;
        const denom_a = Math.abs(denom_a_raw) < 1e-10 ? (denom_a_raw < 0 ? -1e-10 : 1e-10) : denom_a_raw;

        Eb.push((J + K) / denom_b);
        Ea.push((J - K) / denom_a);
      }
      return {
        xA: R_A,
        Eb: Eb,
        Ea: Ea
      };
    }

    // Build 2D grid for electron density calculations
    const gridN = 61; // 50-60 points as required; choose 61
    const gridMin = -3, gridMax = 3;
    const xGrid = linspace(gridMin, gridMax, gridN);
    const yGrid = linspace(gridMin, gridMax, gridN);

    // Compute bonding and antibonding densities for a given R (in a.u.)
    function computeDensities(R_au) {
      const halfR = R_au / 2;

      // Overlap and normalization constants
      const Sab = overlapSab(R_au);
      const denom_b = Math.max(1 + Sab, 1e-10);
      const denom_a = Math.max(1 - Sab, 1e-10);
      const Nb = 1 / Math.sqrt(2 * denom_b);
      const Na = 1 / Math.sqrt(2 * denom_a);

      const zBond = new Array(yGrid.length);
      const zAnti = new Array(yGrid.length);

      for (let i = 0; i < yGrid.length; i++) {
        const y = yGrid[i];
        const rowB = new Array(xGrid.length);
        const rowA = new Array(xGrid.length);

        for (let j = 0; j < xGrid.length; j++) {
          const x = xGrid[j];
          // Distances to nuclei A(-R/2,0,0), B(+R/2,0,0)
          const distA = Math.hypot(x + halfR, y);
          const distB = Math.hypot(x - halfR, y);

          // 1s AO values (ignore 1/sqrt(pi) prefactor)
          const phiA = Math.exp(-distA);
          const phiB = Math.exp(-distB);

          // Molecular orbitals
          const psi_b = Nb * (phiA + phiB);
          const psi_a = Na * (phiA - phiB);

          // Prob. densities
          rowB[j] = psi_b * psi_b;
          rowA[j] = psi_a * psi_a;
        }
        zBond[i] = rowB;
        zAnti[i] = rowA;
      }
      return { zBond, zAnti };
    }

    // Initialize Potential Energy plot (static)
    function initPotentialEnergyPlot() {
      const pe = makePotentialEnergyData();
      const traceB = {
        x: pe.xA,
        y: pe.Eb,
        type: 'scatter',
        mode: 'lines',
        line: { color: 'royalblue', width: 3 },
        name: 'bonding PE',
        hovertemplate: 'R: %{x:.2f} Å<br>E: %{y:.3f} Ha<extra></extra>'
      };
      const traceA = {
        x: pe.xA,
        y: pe.Ea,
        type: 'scatter',
        mode: 'lines',
        line: { color: 'firebrick', width: 3 },
        name: 'antibonding PE',
        hovertemplate: 'R: %{x:.2f} Å<br>E: %{y:.3f} Ha<extra></extra>'
      };
      const layout = {
        title: { text: 'potential energy', font: { color: '#e8edf3' } },
        paper_bgcolor: 'rgba(0,0,0,0)',
        plot_bgcolor: 'rgba(0,0,0,0)',
        margin: { l: 60, r: 20, t: 50, b: 45 },
        xaxis: {
          title: { text: 'R', font: { color: '#e8edf3' } },
          range: [0, 8],
          gridcolor: '#25304e',
          zerolinecolor: '#25304e',
          color: '#cfd9e6'
        },
        yaxis: {
          title: { text: 'E (Hartree)', font: { color: '#e8edf3' } },
          range: [-0.6, 0.4],
          gridcolor: '#25304e',
          zerolinecolor: '#25304e',
          color: '#cfd9e6'
        },
        legend: {
          orientation: 'h',
          yanchor: 'bottom',
          y: 1.02,
          xanchor: 'right',
          x: 1,
          font: { color: '#e8edf3' }
        }
      };
      const config = { responsive: true, displayModeBar: false };
      Plotly.newPlot('potential-energy-plot-container', [traceB, traceA], layout, config);
    }

    // Initialize 3D surface plots
    function initDensityPlots(initialR_au) {
      const { zBond, zAnti } = computeDensities(initialR_au);

      const baseSurfaceProps = {
        type: 'surface',
        x: xGrid,
        y: yGrid,
        contours: {
          z: { show: false }
        },
        hovertemplate: 'x:%{x:.2f}<br>y:%{y:.2f}<br>P:%{z:.3f}<extra></extra>',
        colorscale: 'YlOrBr',
        cmin: 0,
        cmax: 0.5,
        showscale: false,
        lighting: { ambient: 0.9, diffuse: 0.6, specular: 0.2, roughness: 0.9 },
        opacity: 1.0
      };

      const bondTrace = Object.assign({}, baseSurfaceProps, { z: zBond });
      const antiTrace = Object.assign({}, baseSurfaceProps, { z: zAnti });

      const sceneLayout = {
        showlegend: false,
        autosize: true,
        margin: { l: 0, r: 0, t: 0, b: 0 },
        paper_bgcolor: 'rgba(0,0,0,0)',
        scene: {
          xaxis: {
            range: [gridMin, gridMax],
            showgrid: false,
            zeroline: false,
            showticklabels: false,
            title: ''
          },
          yaxis: {
            range: [gridMin, gridMax],
            showgrid: false,
            zeroline: false,
            showticklabels: false,
            title: ''
          },
          zaxis: {
            range: [0, 0.5],
            showgrid: false,
            zeroline: false,
            showticklabels: false,
            title: ''
          },
          aspectmode: 'cube',
          camera: {
            eye: { x: 1.45, y: 1.45, z: 0.9 },
            projection: { type: 'orthographic' }
          },
          bgcolor: 'rgba(0,0,0,0)'
        }
      };
      const config = { responsive: true, displayModeBar: false };

      Plotly.newPlot('bonding-plot-container', [bondTrace], sceneLayout, config);
      Plotly.newPlot('antibonding-plot-container', [antiTrace], sceneLayout, config);
    }

    // Update 3D plots when R changes
    function updateDensityPlots(R_angstroms) {
      const R_au = R_angstroms * BOHR_PER_ANGSTROM;
      const { zBond, zAnti } = computeDensities(R_au);

      // Update only the z data; x and y stay constant
      Plotly.restyle('bonding-plot-container', { z: [zBond] });
      Plotly.restyle('antibonding-plot-container', { z: [zAnti] });
    }

    // Initialize everything
    window.addEventListener('DOMContentLoaded', () => {
      const slider = document.getElementById('slider-r');
      const out = document.getElementById('slider-r-value');

      // Set initial output display formatted to 3 decimals
      const initR_A = parseFloat(slider.value);
      out.textContent = initR_A.toFixed(3);

      // Initialize plots
      initPotentialEnergyPlot();
      initDensityPlots(initR_A * BOHR_PER_ANGSTROM);

      // Slider interaction
      slider.addEventListener('input', (e) => {
        const R_A = parseFloat(e.target.value);
        out.textContent = R_A.toFixed(3);
        // Convert to atomic units and update densities
        updateDensityPlots(R_A);
      });

      // Handle window resize for responsive Plotly layouts
      window.addEventListener('resize', () => {
        Plotly.Plots.resize('potential-energy-plot-container');
        Plotly.Plots.resize('bonding-plot-container');
        Plotly.Plots.resize('antibonding-plot-container');
      });
    });
  </script>
</body>
</html>
