        <button id="btn-reset">⥀</button>
        <button id="btn-step-forward">»</button>
      </div>
      <p id="display-time">0 d 0 h</p>

      <hr>

      <h4>Moon's rotational period related to the current value</h4>
      <div id="rotation-selector" class="btn-row">
        <button id="btn-rot-half">half</button>
        <button id="btn-rot-normal" class="active">normal</button>
        <button id="btn-rot-double">double</button>
        <button id="btn-rot-none">no rotation</button>
      </div>

      <hr>

      <div>
        <input type="checkbox" id="check-day-night">
        <label for="check-day-night">day/night</label>
      </div>
      <div>
        <input type="checkbox" id="check-near-side" checked>
        <label for="check-near-side">near side of the Moon</label>
      </div>
      <div>
        <input type="checkbox" id="check-earth-moon-line" checked>
        <label for="check-earth-moon-line">Earth-Moon line</label>
      </div>
      <div>
        <input type="checkbox" id="check-moon-only">
        <label for="check-moon-only">the Moon only</label>
      </div>
      <p class="hint">Tip: Use Play to animate, or step by 1 hour with « and »</p>
    </div>

    <div id="visualization-area">
      <canvas id="main-canvas"></canvas>
    </div>
  </div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.min.js"></script>
  <script>
    // ======== Simulation State and Constants ========
    const ORBITAL_PERIOD_HOURS = 672; // 28 days
    const EARTH_ROTATION_HOURS = 24;
    const EARTH_R = 80;
    const MOON_R = 25;
    const ORBIT_R = 250;
    const HOURS_PER_SECOND = 6; // animation speed

    let timeHours = 0; // global time in hours (can be fractional)
    let playing = false;

    // Rotation mode: 'half' (faster), 'normal', 'double' (slower), 'none'
    let rotationMode = 'normal';

    // Display toggles
    let showDayNight = false;
    let showNearSide = true;
    let showEarthMoonLine = true;
    let moonOnly = false;

    // DOM elements
    const sliderTime = document.getElementById('slider-time');
    const btnPlayPause = document.getElementById('btn-play-pause');
    const btnStepBack = document.getElementById('btn-step-back');
    const btnReset = document.getElementById('btn-reset');
    const btnStepForward = document.getElementById('btn-step-forward');
    const displayTime = document.getElementById('display-time');

    const btnRotHalf = document.getElementById('btn-rot-half');
    const btnRotNormal = document.getElementById('btn-rot-normal');
    const btnRotDouble = document.getElementById('btn-rot-double');
    const btnRotNone = document.getElementById('btn-rot-none');

    const checkDayNight = document.getElementById('check-day-night');
    const checkNearSide = document.getElementById('check-near-side');
    const checkEarthMoonLine = document.getElementById('check-earth-moon-line');
    const checkMoonOnly = document.getElementById('check-moon-only');

    // Base orientation so that at t=0 the "near side" marker points toward Earth (downward from the Moon at top)
    const BASE_NEAR_SIDE_ANGLE = Math.PI / 2;

    // ======== Helpers ========
    function updateDisplayTime() {
      const t = (timeHours % ORBITAL_PERIOD_HOURS + ORBITAL_PERIOD_HOURS) % ORBITAL_PERIOD_HOURS;
      const days = Math.floor(t / 24);
      const hours = Math.floor(t % 24);
      displayTime.textContent = days + " d " + hours + " h";
    }

    function syncSlider() {
      // Slider expects integer hours 0..672; we wrap to 0..671 (modulo)
      let t = (timeHours % ORBITAL_PERIOD_HOURS + ORBITAL_PERIOD_HOURS) % ORBITAL_PERIOD_HOURS;
      sliderTime.value = Math.floor(t);
    }

    function setPlaying(state) {
      playing = state;
      if (playing) {
        btnPlayPause.textContent = '❚❚';
        loop();
      } else {
        btnPlayPause.textContent = '▶';
        noLoop();
      }
    }

    function wrapHours(h) {
      // Wrap into [0, ORBITAL_PERIOD_HOURS)
      let t = h % ORBITAL_PERIOD_HOURS;
      if (t < 0) t += ORBITAL_PERIOD_HOURS;
      return t;
    }

    function setRotationMode(mode) {
      rotationMode = mode;
      // Update active CSS class
      [btnRotHalf, btnRotNormal, btnRotDouble, btnRotNone].forEach(b => b.classList.remove('active'));
      if (mode === 'half') btnRotHalf.classList.add('active');
      if (mode === 'normal') btnRotNormal.classList.add('active');
      if (mode === 'double') btnRotDouble.classList.add('active');
      if (mode === 'none') btnRotNone.classList.add('active');
      redraw();
    }

    // ======== Event Listeners ========
    sliderTime.addEventListener('input', () => {
      timeHours = Number(sliderTime.value);
      updateDisplayTime();
      redraw();
    });

    btnPlayPause.addEventListener('click', () => {
      setPlaying(!playing);
    });

    btnStepBack.addEventListener('click', () => {
      timeHours = wrapHours(timeHours - 1);
      syncSlider();
      updateDisplayTime();
      redraw();
    });

    btnStepForward.addEventListener('click', () => {
      timeHours = wrapHours(timeHours + 1);
      syncSlider();
      updateDisplayTime();
      redraw();
    });

    btnReset.addEventListener('click', () => {
      timeHours = 0;
      syncSlider();
      updateDisplayTime();
      redraw();
    });

    btnRotHalf.addEventListener('click', () => setRotationMode('half'));
    btnRotNormal.addEventListener('click', () => setRotationMode('normal'));
    btnRotDouble.addEventListener('click', () => setRotationMode('double'));
    btnRotNone.addEventListener('click', () => setRotationMode('none'));

    checkDayNight.addEventListener('change', () => {
      showDayNight = checkDayNight.checked;
      redraw();
    });
    checkNearSide.addEventListener('change', () => {
      showNearSide = checkNearSide.checked;
      redraw();
    });
    checkEarthMoonLine.addEventListener('change', () => {
      showEarthMoonLine = checkEarthMoonLine.checked;
      redraw();
    });
    checkMoonOnly.addEventListener('change', () => {
      moonOnly = checkMoonOnly.checked;
      redraw();
    });

    // ======== p5.js Sketch ========
    function sizeToContainer(cnv) {
      const area = document.getElementById('visualization-area');
      const w = Math.max(100, area.clientWidth);
      const h = Math.max(100, area.clientHeight);
      resizeCanvas(w, h);
      // ensure 1:1 CSS pixels (p5 handles pixelDensity)
      cnv.style('width', w + 'px');
      cnv.style('height', h + 'px');
    }

    function setup() {
      // Remove placeholder canvas to ensure a single canvas element is present
      const placeholder = document.getElementById('main-canvas');
      if (placeholder && placeholder.parentElement) {
        placeholder.parentElement.removeChild(placeholder);
      }
      const area = document.getElementById('visualization-area');
      const w = Math.max(100, area.clientWidth);
      const h = Math.max(100, area.clientHeight);
      const cnv = createCanvas(w, h);
      cnv.parent('visualization-area');
      cnv.id('main-canvas');
      pixelDensity(window.devicePixelRatio || 1);

      // Defaults already set in HTML; mirror in JS state
      showDayNight = checkDayNight.checked;
      showNearSide = checkNearSide.checked;
      showEarthMoonLine = checkEarthMoonLine.checked;
      moonOnly = checkMoonOnly.checked;

      // Start paused
      setPlaying(false);

      updateDisplayTime();
      noLoop();
    }

    function windowResized() {
      const area = document.getElementById('visualization-area');
      const w = Math.max(100, area.clientWidth);
      const h = Math.max(100, area.clientHeight);
      resizeCanvas(w, h);
      redraw();
    }

    function drawSpaceBackdrop() {
      // Subtle starry background
      background(8, 10, 20);
      // Optional faint vignette
      noStroke();
      for (let i = 0; i < 2; i++) {
        const alpha = i === 0 ? 40 : 20;
        const r = i === 0 ? max(width, height) * 1.2 : max(width, height) * 1.6;
        fill(0, 0, 0, alpha);
        circle(width/2, height/2, r);
      }
    }

    function drawDashedLine(x1, y1, x2, y2, dash = 10, gap = 6) {
      const dx = x2 - x1;
      const dy = y2 - y1;
      const dist = Math.hypot(dx, dy);
      const angle = Math.atan2(dy, dx);
      let traveled = 0;
      let cx = x1, cy = y1;
      stroke(173, 216, 230, 180);
      strokeWeight(1.5);
      while (traveled < dist) {
        const seg = Math.min(dash, dist - traveled);
        const nx = cx + Math.cos(angle) * seg;
        const ny = cy + Math.sin(angle) * seg;
        line(cx, cy, nx, ny);
        cx = nx + Math.cos(angle) * gap;
        cy = ny + Math.sin(angle) * gap;
        traveled += seg + gap;
      }
    }

    function drawEarth(earthRotation) {
      // Draw Earth base (not rotating)
      noStroke();
      fill('#3B82F6');
      circle(0, 0, EARTH_R * 2);

      // North pole marker
      fill(255);
      circle(0, -EARTH_R * 0.9, 12);

      // Simplified landmasses (static)
      fill('#16A34A');
      beginShape();
      vertex(-40, -10);
      vertex(-20, -40);
      vertex(12, -30);
      vertex(28, -8);
      vertex(0, 12);
      vertex(-32, 4);
      endShape(CLOSE);

      fill('#16A34A');
      beginShape();
      vertex(10, 25);
      vertex(35, 15);
      vertex(42, 28);
      vertex(28, 42);
      vertex(12, 36);
      endShape(CLOSE);

      // Rotate the coordinate system for the stick figure
      push();
      rotate(earthRotation);

      // Red stick figure at the "top" of Earth (surface)
      stroke('#EF4444');
      strokeWeight(3);
      noFill();
      const topX = 0;
      const topY = -EARTH_R;
      // Head slightly above the surface
      circle(topX, topY - 10, 10);
      // Body
      line(topX, topY - 5, topX, topY + 14);
      // Arms
      line(topX - 10, topY + 2, topX + 10, topY + 2);
      // Legs
      line(topX, topY + 14, topX - 8, topY + 26);
      line(topX, topY + 14, topX + 8, topY + 26);

      pop();

      // Day/Night terminator (left half shaded), independent of Earth's rotation
      if (showDayNight) {
        noStroke();
        fill(0, 0, 0, 128);
        arc(0, 0, EARTH_R * 2, EARTH_R * 2, PI / 2, 3 * PI / 2, PIE);
      }
    }

    function drawMoon(moonRotation) {
      // Draw Moon disk
      noStroke();
      fill('#808080');
      circle(0, 0, MOON_R * 2);

      if (showNearSide) {
        // Rotate to set "near side" indicator orientation
        push();
        rotate(BASE_NEAR_SIDE_ANGLE + moonRotation);

        // Green arc showing the near-side hemisphere (facing to +X in local coords)
        noFill();
        stroke('#22C55E');
        strokeWeight(3);
        arc(0, 0, MOON_R * 2, MOON_R * 2, -PI / 2, PI / 2);

        // Magenta stick figure on this near-side marker
        stroke('#D946EF');
        strokeWeight(2);
        const px = MOON_R * 0.6;
        const py = 0;
        // head
        noFill();
        circle(px, py - 5, 8);
        // body
        line(px, py - 1, px, py + 12);
        // arms
        line(px - 7, py + 3, px + 7, py + 3);
        // legs
        line(px, py + 12, px - 6, py + 22);
        line(px, py + 12, px + 6, py + 22);

        pop();
      }

      // Day/Night terminator on Moon (left half shaded), independent of Moon's rotation mode
      if (showDayNight) {
        noStroke();
        fill(0, 0, 0, 128);
        arc(0, 0, MOON_R * 2, MOON_R * 2, PI / 2, 3 * PI / 2, PIE);
      }
    }

    function draw() {
      // Update time if playing
      if (playing) {
        const dt = deltaTime / 1000; // seconds
        timeHours = wrapHours(timeHours + dt * HOURS_PER_SECOND);
        syncSlider();
        updateDisplayTime();
      }

      drawSpaceBackdrop();

      push();
      translate(width / 2, height / 2);

      // Angles
      const earthRotation = (timeHours / EARTH_ROTATION_HOURS) * TWO_PI;
      const moonOrbit = (timeHours / ORBITAL_PERIOD_HOURS) * TWO_PI;

      let moonRotation;
      switch (rotationMode) {
        case 'half':   moonRotation = 2 * moonOrbit; break;   // half period => 2x angular speed
        case 'double': moonRotation = 0.5 * moonOrbit; break; // double period => 0.5x angular speed
        case 'none':   moonRotation = 0; break;
        default:       moonRotation = moonOrbit; // normal (synchronous)
      }

      // Positions
      const theta = moonOrbit - PI / 2; // start at "top"
      let mx = ORBIT_R * Math.cos(theta);
      let my = ORBIT_R * Math.sin(theta);

      if (moonOnly) {
        mx = 0;
        my = 0;
      }

      // Draw Earth (unless Moon-only)
      if (!moonOnly) {
        push();
        drawEarth(earthRotation);
        pop();
      }

      // Draw Earth-Moon line if enabled and not Moon-only
      if (showEarthMoonLine && !moonOnly) {
        drawDashedLine(0, 0, mx, my, 10, 6);
      }

      // Draw Moon
      push();
      translate(mx, my);
      drawMoon(moonRotation);
      pop();

      pop();
    }

    // Initialize the UI state and ensure redraw on first load
    document.addEventListener('DOMContentLoaded', () => {
      // Ensure correct active rotation button
      setRotationMode('normal');
      // Set initial time display
      updateDisplayTime();
    });
  </script>
</body>
</html>
