            <input type="radio" name="k-selector" id="radio-k-1" value="1"><label for="radio-k-1">1</label>
            <input type="radio" name="k-selector" id="radio-k-2" value="2"><label for="radio-k-2">2</label>
            <input type="radio" name="k-selector" id="radio-k-3" value="3"><label for="radio-k-3">3</label>
            <input type="radio" name="k-selector" id="radio-k-4" value="4"><label for="radio-k-4">4</label>
            <input type="radio" name="k-selector" id="radio-k-5" value="5"><label for="radio-k-5">5</label>
        </div>

        <!-- Sub-band Type Selector -->
        <div id="subband-type-group">
            <label>select +/- sub-band</label>
            <input type="radio" name="subband-type" id="radio-subband-full" value="full" checked><label for="radio-subband-full">full sub-band</label>
            <input type="radio" name="subband-type" id="radio-subband-positive" value="positive"><label for="radio-subband-positive">positive sub-band</label>
            <input type="radio" name="subband-type" id="radio-subband-negative" value="negative"><label for="radio-subband-negative">negative sub-band</label>
        </div>

        <!-- Branch Selector -->
        <div id="branch-selector-group">
            <label>select branch</label>
            <input type="radio" name="branch-selector" id="radio-branch-all" value="all" checked><label for="radio-branch-all">all branches</label>
            <input type="radio" name="branch-selector" id="radio-branch-p" value="P"><label for="radio-branch-p">P branch</label>
            <input type="radio" name="branch-selector" id="radio-branch-q" value="Q"><label for="radio-branch-q">Q branch</label>
            <input type="radio" name="branch-selector" id="radio-branch-r" value="R"><label for="radio-branch-r">R branch</label>
        </div>
    </div>

    <!-- Visualization Area -->
    <div id="plot-div" style="height: 600px;"></div>

    <!-- Zoom Control Panel -->
    <div id="zoom-controls">
        <div>
            <label for="slider-x-lower">x axis lower boundary</label>
            <input type="range" id="slider-x-lower" min="900" max="1100" step="1" value="902">
            <span id="span-x-lower-value">902.</span>
        </div>
        <div>
            <label for="slider-x-upper">x axis upper boundary</label>
            <input type="range" id="slider-x-upper" min="900" max="1100" step="1" value="1091">
            <span id="span-x-upper-value">1091.</span>
        </div>
    </div>

    <script>
        // Physical constants and settings
        const nu0 = 1000.0; // cm^-1
        const Bu = 0.98;    // upper state B'
        const Au = 1.95;    // upper state A'
        const Bl = 1.00;    // lower state B"
        const Al = 2.00;    // lower state A"
        const zeta = 0.2;   // Coriolis coupling (simplified)
        const T = 300.0;    // Kelvin
        const kBoltz = 0.695; // cm^-1/K
        const J_max = 60;   // maximum J to consider

        // Cached DOM references
        const plotDiv = document.getElementById('plot-div');

        const radioViewFull = document.getElementById('radio-view-full');
        const radioViewSub  = document.getElementById('radio-view-sub');

        const kSelectorGroup = document.getElementById('k-selector-group');
        const subbandTypeGroup = document.getElementById('subband-type-group');
        const branchSelectorGroup = document.getElementById('branch-selector-group');

        const radioSubbandFull = document.getElementById('radio-subband-full');
        const radioSubbandPos  = document.getElementById('radio-subband-positive');
        const radioSubbandNeg  = document.getElementById('radio-subband-negative');

        const sliderLower = document.getElementById('slider-x-lower');
        const sliderUpper = document.getElementById('slider-x-upper');
        const spanLower = document.getElementById('span-x-lower-value');
        const spanUpper = document.getElementById('span-x-upper-value');

        // Utility: set group disabled/enabled
        function setGroupDisabled(groupEl, disabled) {
            if (disabled) {
                groupEl.classList.add('disabled-group');
            } else {
                groupEl.classList.remove('disabled-group');
            }
            // Also toggle input disabled for accessibility
            groupEl.querySelectorAll('input').forEach(inp => inp.disabled = disabled);
        }

        function setBranchGroupVisible(visible) {
            if (visible) {
                branchSelectorGroup.classList.remove('hidden');
                setGroupDisabled(branchSelectorGroup, false);
            } else {
                branchSelectorGroup.classList.add('hidden');
                setGroupDisabled(branchSelectorGroup, true);
            }
        }

        // Energy of symmetric top level
        function energySymTop(B, A, J, K) {
            return B * J * (J + 1) + (A - B) * K * K;
        }

        // Hönl-London-like factors (simplified) for a perpendicular band
        function HL_perpendicular(branch, J, K) {
            if (branch === 'P') {
                if (J <= 0) return 0;
                const val = (J*J - K*K) / J;
                return Math.max(0, val);
            } else if (branch === 'Q') {
                if (J <= 0) return 0;
                const denom = J * (J + 1);
                const val = 2 * K * K / (denom + 1e-12);
                return Math.max(0, val);
            } else if (branch === 'R') {
                const val = ((J + 1) * (J + 1) - K*K) / (J + 1);
                return Math.max(0, val);
            }
            return 0;
        }

        // Compute lines given mode and filters
        // mode: 'full' or 'sub'
        // For 'sub', provide Ksel, subType ('full'|'positive'|'negative'), branchSel ('all'|'P'|'Q'|'R')
        function computeLines(mode, Ksel = null, subType = 'full', branchSel = 'all') {
            const lines = []; // each item: {x, I, sign, branch, J, K}
            const Ks = mode === 'full' ? [0,1,2,3,4,5] : [Ksel];

            Ks.forEach(K => {
                ['positive', 'negative'].forEach(signType => {
                    if (signType === 'negative' && K === 0) return; // invalid negative ΔK for K=0

                    // If in 'sub' mode, respect subType filter
                    if (mode === 'sub') {
                        if (subType === 'positive' && signType !== 'positive') return;
                        if (subType === 'negative' && signType !== 'negative') return;
                        // if subType === 'full' include both where valid
                    }

                    ['P','Q','R'].forEach(branch => {
                        // In 'sub' mode, if branchSel is specific, filter
                        if (mode === 'sub' && (branchSel !== 'all') && (branch !== branchSel)) return;

                        const signDelta = (signType === 'positive') ? +1 : -1;
                        const K_upper = K + signDelta; // K' = K'' ± 1

                        // J loops for lower state J''
                        for (let J = Math.max(K, 0); J <= J_max; J++) {
                            let J_upper;
                            if (branch === 'P') {
                                J_upper = J - 1;
                                if (J_upper < 0) continue;
                            } else if (branch === 'Q') {
                                J_upper = J;
                            } else { // 'R'
                                J_upper = J + 1;
                            }

                            // Physical constraints: K' ≤ J' and K'' ≤ J''
                            if (J < K) continue;
                            if (J_upper < K_upper) continue;
                            if (J_upper < 0) continue;

                            const E_lower = energySymTop(Bl, Al, J, K);
                            // Add a small Coriolis-like term to upper energy to mimic sub-band splitting
                            const E_upper = energySymTop(Bu, Au, J_upper, K_upper) + signDelta * zeta * (K_upper + 0.5);

                            const nu = nu0 + (E_upper - E_lower);

                            // Boltzmann population of lower level
                            const population = (2 * J + 1) * Math.exp(-E_lower / (kBoltz * T));

                            // Hönl-London-like factor
                            const HL = HL_perpendicular(branch, J, K);

                            const intensity = population * HL;

                            if (intensity > 0) {
                                lines.push({
                                    x: nu,
                                    I: intensity,
                                    sign: signType,
                                    branch: branch,
                                    J: J,
                                    K: K
                                });
                            }
                        }
                    });
                });
            });

            return lines;
        }

        function buildTracesFullSpectrum() {
            const lines = computeLines('full');
            if (lines.length === 0) return [];

            // Normalize intensities
            let Imax = 0;
            for (const l of lines) if (l.I > Imax) Imax = l.I;
            const x = [];
            const y = [];
            const text = [];

            for (const l of lines) {
                x.push(l.x);
                y.push(l.I / Imax);
                text.push(`ν = ${l.x.toFixed(3)} cm⁻¹<br>K = ${l.K}, ΔK = ${l.sign === 'positive' ? '+1' : '-1'}<br>Branch ${l.branch}, J'' = ${l.J}`);
            }

            return [{
                type: 'bar',
                x: x,
                y: y,
                width: 0.1,
                marker: { color: 'black' },
                hovertemplate: '%{text}<extra></extra>',
                text: text,
                name: 'full'
            }];
        }

        function buildTracesSubband(K, subType, branchSel) {
            const lines = computeLines('sub', K, subType, branchSel);
            if (lines.length === 0) return [];

            // Normalize within current view
            let Imax = 0;
            for (const l of lines) if (l.I > Imax) Imax = l.I;

            // If showing "full sub-band", combine by sign (two traces: positive red, negative blue)
            if (subType === 'full') {
                const pos = lines.filter(l => l.sign === 'positive');
                const neg = lines.filter(l => l.sign === 'negative');

                const traces = [];
                if (pos.length > 0) {
                    const x = pos.map(l => l.x);
                    const y = pos.map(l => l.I / Imax);
                    const text = pos.map(l => `ν = ${l.x.toFixed(3)} cm⁻¹<br>K = ${l.K}, ΔK = +1<br>Branch ${l.branch}, J'' = ${l.J}`);
                    traces.push({
                        type: 'bar',
                        x: x, y: y, width: 0.1,
                        marker: { color: 'red' },
                        hovertemplate: '%{text}<extra></extra>',
                        text: text,
                        name: 'positive'
                    });
                }
                if (neg.length > 0) {
                    const x = neg.map(l => l.x);
                    const y = neg.map(l => l.I / Imax);
                    const text = neg.map(l => `ν = ${l.x.toFixed(3)} cm⁻¹<br>K = ${l.K}, ΔK = -1<br>Branch ${l.branch}, J'' = ${l.J}`);
                    traces.push({
                        type: 'bar',
                        x: x, y: y, width: 0.1,
                        marker: { color: 'blue' },
                        hovertemplate: '%{text}<extra></extra>',
                        text: text,
                        name: 'negative'
                    });
                }
                return traces;
            } else {
                // Showing only positive or negative; if branchSel == 'all' combine all branches into one trace
                const color = (subType === 'positive') ? 'red' : 'blue';
                if (branchSel === 'all') {
                    const x = lines.map(l => l.x);
                    const y = lines.map(l => l.I / Imax);
                    const text = lines.map(l => `ν = ${l.x.toFixed(3)} cm⁻¹<br>K = ${l.K}, ΔK = ${l.sign === 'positive' ? '+1' : '-1'}<br>Branch ${l.branch}, J'' = ${l.J}`);
                    return [{
                        type: 'bar',
                        x: x, y: y, width: 0.1,
                        marker: { color },
                        hovertemplate: '%{text}<extra></extra>',
                        text: text,
                        name: `${subType}-all`
                    }];
                } else {
                    // Separate trace with that branch only (already filtered in computeLines)
                    const x = lines.map(l => l.x);
                    const y = lines.map(l => l.I / Imax);
                    const text = lines.map(l => `ν = ${l.x.toFixed(3)} cm⁻¹<br>K = ${l.K}, ΔK = ${l.sign === 'positive' ? '+1' : '-1'}<br>Branch ${l.branch}, J'' = ${l.J}`);
                    return [{
                        type: 'bar',
                        x: x, y: y, width: 0.1,
                        marker: { color },
                        hovertemplate: '%{text}<extra></extra>',
                        text: text,
                        name: `${subType}-${branchSel}`
                    }];
                }
            }
        }

        function getSelectedK() {
            const checked = document.querySelector('input[name="k-selector"]:checked');
            return checked ? parseInt(checked.value, 10) : 0;
        }
        function getSelectedSubbandType() {
            const checked = document.querySelector('input[name="subband-type"]:checked');
            return checked ? checked.value : 'full';
        }
        function getSelectedBranch() {
            const checked = document.querySelector('input[name="branch-selector"]:checked');
            return checked ? checked.value : 'all';
        }
        function isSubView() {
            return document.getElementById('radio-view-sub').checked;
        }

        function updateControlsState() {
            const subView = isSubView();

            // Enable/disable K selector and subband type based on view
            setGroupDisabled(kSelectorGroup, !subView);
            setGroupDisabled(subbandTypeGroup, !subView);

            // Manage negative subband availability depending on K
            const K = getSelectedK();
            if (K === 0) {
                radioSubbandNeg.disabled = true;
                // If currently selected negative, switch to 'full'
                if (radioSubbandNeg.checked) {
                    radioSubbandFull.checked = true;
                }
            } else {
                radioSubbandNeg.disabled = false;
            }

            // Branch selector: visible only in sub-view AND when subband type is positive or negative
            const subType = getSelectedSubbandType();
            if (subView && (subType === 'positive' || subType === 'negative')) {
                setBranchGroupVisible(true);
            } else {
                setBranchGroupVisible(false);
                // Reset to "all branches" when hidden
                document.getElementById('radio-branch-all').checked = true;
            }
        }

        function plotTitle() {
            if (radioViewFull.checked) {
                return 'Rovibronic Spectrum';
            } else {
                const K = getSelectedK();
                const subType = getSelectedSubbandType();
                const branchSel = getSelectedBranch();

                let title = `K = ${K} sub-band`;
                if (subType === 'full') {
                    return title;
                } else if (subType === 'positive') {
                    title += '<br>positive (ΔK = +1)';
                } else if (subType === 'negative') {
                    title += '<br>negative (ΔK = -1)';
                }
                if (subType !== 'full' && branchSel !== 'all') {
                    title += ` - ${branchSel} branch`;
                }
                return title;
            }
        }

        function drawPlot() {
            const layout = {
                title: { text: plotTitle() },
                xaxis: { title: 'wavenumber (cm⁻¹)', range: [parseFloat(sliderLower.value), parseFloat(sliderUpper.value)] },
                yaxis: { title: 'absorbance', range: [0, 1.2] },
                showlegend: false,
                bargap: 0
            };

            let traces = [];
            if (radioViewFull.checked) {
                traces = buildTracesFullSpectrum();
            } else {
                const K = getSelectedK();
                const subType = getSelectedSubbandType();
                const branchSel = getSelectedBranch();
                traces = buildTracesSubband(K, subType, branchSel);
            }

            Plotly.react(plotDiv, traces, layout, {responsive: true});
        }

        function updateRangeDisplaysAndRelayout() {
            let lower = parseInt(sliderLower.value, 10);
            let upper = parseInt(sliderUpper.value, 10);
            if (lower >= upper) {
                upper = lower + 1;
                sliderUpper.value = upper;
            }
            if (upper <= lower) {
                lower = upper - 1;
                sliderLower.value = lower;
            }
            spanLower.textContent = sliderLower.value + '.';
            spanUpper.textContent = sliderUpper.value + '.';
            Plotly.relayout(plotDiv, { 'xaxis.range': [parseFloat(sliderLower.value), parseFloat(sliderUpper.value)] });
        }

        // Event listeners
        function attachListeners() {
            // View toggles
            [radioViewFull, radioViewSub].forEach(el => el.addEventListener('change', () => {
                updateControlsState();
                drawPlot();
            }));

            // K selection
            document.querySelectorAll('input[name="k-selector"]').forEach(el => {
                el.addEventListener('change', () => {
                    updateControlsState();
                    drawPlot();
                });
            });

            // Sub-band type selection
            document.querySelectorAll('input[name="subband-type"]').forEach(el => {
                el.addEventListener('change', () => {
                    updateControlsState();
                    drawPlot();
                });
            });

            // Branch selection
            document.querySelectorAll('input[name="branch-selector"]').forEach(el => {
                el.addEventListener('change', () => {
                    drawPlot();
                });
            });

            // Sliders for zoom
            sliderLower.addEventListener('input', updateRangeDisplaysAndRelayout);
            sliderUpper.addEventListener('input', updateRangeDisplaysAndRelayout);
        }

        // Initialization
        function init() {
            // Initial UI state
            // In initial state, full spectrum view is selected, K selector and subband type disabled, branch selector hidden
            setGroupDisabled(kSelectorGroup, true);
            setGroupDisabled(subbandTypeGroup, true);
            setBranchGroupVisible(false);

            // Initialize slider displays
            spanLower.textContent = sliderLower.value + '.';
            spanUpper.textContent = sliderUpper.value + '.';

            // Attach events
            attachListeners();

            // Initial plot
            drawPlot();
        }

        // Start
        window.addEventListener('DOMContentLoaded', init);
    </script>
</body>
</html>
