<script type="module">
import * as THREE from 'three';
import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
import { ParametricGeometry } from 'three/addons/geometries/ParametricGeometry.js';

const container = document.getElementById('canvas-container');

// UI elements
const sliderStrike = document.getElementById('slider-strike');
const sliderDip = document.getElementById('slider-dip');
const sliderRake = document.getElementById('slider-rake');
const strikeValue = document.getElementById('strike-value');
const dipValue = document.getElementById('dip-value');
const rakeValue = document.getElementById('rake-value');
const btnP = document.getElementById('btn-p');
const btnSV = document.getElementById('btn-sv');
const btnSH = document.getElementById('btn-sh');
const checkboxBox = document.getElementById('checkbox-box');

// State
let currentWaveType = 'P';

// Scene, Camera, Renderer
const scene = new THREE.Scene();
scene.background = new THREE.Color('#f0f0f0');

const camera = new THREE.PerspectiveCamera(50, container.clientWidth / container.clientHeight, 0.1, 100);
camera.position.set(4, 3.5, 4);
camera.lookAt(0, 0, 0);

const renderer = new THREE.WebGLRenderer({ antialias: true });
renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
renderer.setSize(container.clientWidth, container.clientHeight);
container.appendChild(renderer.domElement);

// Controls
const controls = new OrbitControls(camera, renderer.domElement);
controls.enableDamping = true;
controls.dampingFactor = 0.07;
controls.target.set(0, 0, 0);

// Lights
scene.add(new THREE.AmbientLight(0xffffff, 0.6));
const dir1 = new THREE.DirectionalLight(0xffffff, 0.8);
dir1.position.set(5, 6, 8);
scene.add(dir1);
const dir2 = new THREE.DirectionalLight(0xffffff, 0.5);
dir2.position.set(-6, -4, 5);
scene.add(dir2);

// Group to hold the radiation pattern and to apply rotations
const patternGroup = new THREE.Object3D();
scene.add(patternGroup);

// Bounding box helper attached to the parent Object3D
const boundingBoxHelper = new THREE.BoxHelper(patternGroup, 0x333333);
boundingBoxHelper.visible = false;
scene.add(boundingBoxHelper);

// Material for patterns
const patternMaterial = new THREE.MeshStandardMaterial({
    color: 0xD2B48C, // tan
    roughness: 0.65,
    metalness: 0.1,
    side: THREE.DoubleSide
});

// Radiation mesh reference
let patternMesh = null;

// Helpers
const deg2rad = (d) => d * Math.PI / 180;

// Create radiation pattern geometry via ParametricGeometry
function createRadiationGeometry(type, uSegments = 180, vSegments = 90) {
    const fn = (u, v, target) => {
        const phi = u * 2 * Math.PI;   // azimuth (0..2π)
        const theta = v * Math.PI;     // polar (0..π)
        let r;
        switch (type) {
            case 'P':
                r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                break;
            case 'SV':
                r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                break;
            case 'SH':
                r = Math.abs(Math.cos(theta) * Math.cos(phi));
                break;
            default:
                r = 0.0;
        }
        const scale = 1.5; // size scale for visibility
        r *= scale;

        const sinT = Math.sin(theta);
        const x = r * sinT * Math.cos(phi);
        const y = r * sinT * Math.sin(phi);
        const z = r * Math.cos(theta);
        target.set(x, y, z);
    };
    return new ParametricGeometry(fn, uSegments, vSegments);
}

// Create or replace the mesh
function buildPatternMesh(type) {
    // Remove existing mesh
    if (patternMesh) {
        patternGroup.remove(patternMesh);
        patternMesh.geometry.dispose();
        // Do not dispose material; reused
        patternMesh = null;
    }
    const geom = createRadiationGeometry(type);
    patternMesh = new THREE.Mesh(geom, patternMaterial);
    patternMesh.castShadow = false;
    patternMesh.receiveShadow = false;
    patternGroup.add(patternMesh);

    // Update bounding box helper for new geometry
    boundingBoxHelper.update();
}

// Apply orientation with Z (world) -> X (local) -> Y (local)
function applyOrientation() {
    const strike = deg2rad(parseFloat(sliderStrike.value));
    const dip = deg2rad(parseFloat(sliderDip.value));
    const rake = deg2rad(parseFloat(sliderRake.value));

    // Reset orientation
    patternGroup.quaternion.identity();
    patternGroup.rotation.set(0, 0, 0);
    patternGroup.updateMatrixWorld(true);

    // 1) Strike: world Z-axis
    patternGroup.rotateOnWorldAxis(new THREE.Vector3(0, 0, 1), strike);
    // 2) Dip: local X-axis
    patternGroup.rotateX(dip);
    // 3) Rake: local Y-axis
    patternGroup.rotateY(rake);

    // keep helper updated
    boundingBoxHelper.update();
}

// "U" arrow pointing Up (+Z) with label
function addUpArrow() {
    const length = 2.2;
    const dir = new THREE.Vector3(0, 0, 1);
    const origin = new THREE.Vector3(0, 0, 0);
    const arrow = new THREE.ArrowHelper(dir, origin, length, 0x000000, 0.25, 0.15);
    scene.add(arrow);

    const label = createTextSprite('U', { fontSize: 64, color: '#000000', padding: 8, bg: 'rgba(255,255,255,0)' });
    label.position.set(0, 0, length + 0.15);
    // scale sprite to readable size (world units)
    scaleSpriteToHeight(label, 0.4);
    scene.add(label);
}

// Compass overlay (inside 3D scene)
function addCompass() {
    const compassGroup = new THREE.Object3D();
    scene.add(compassGroup);

    const radius = 2.2;

    // Dashed circle
    const segments = 256;
    const circlePositions = [];
    for (let i = 0; i <= segments; i++) {
        const t = (i / segments) * Math.PI * 2;
        circlePositions.push(
            Math.cos(t) * radius,
            Math.sin(t) * radius,
            0
        );
    }
    const circleGeom = new THREE.BufferGeometry();
    circleGeom.setAttribute('position', new THREE.Float32BufferAttribute(circlePositions, 3));
    const circleMat = new THREE.LineDashedMaterial({
        color: 0x444444,
        dashSize: 0.15,
        gapSize: 0.08
    });
    const circle = new THREE.Line(circleGeom, circleMat);
    circle.computeLineDistances();
    compassGroup.add(circle);

    // Tick marks every 30°
    const tickGeom = new THREE.BufferGeometry();
    const tickPositions = [];
    for (let deg = 0; deg < 360; deg += 30) {
        const a = deg2rad(deg);
        const r1 = radius * 0.96;
        const r2 = radius * 1.04;
        const x1 = Math.cos(a) * r1, y1 = Math.sin(a) * r1;
        const x2 = Math.cos(a) * r2, y2 = Math.sin(a) * r2;
        tickPositions.push(x1, y1, 0, x2, y2, 0);
    }
    tickGeom.setAttribute('position', new THREE.Float32BufferAttribute(tickPositions, 3));
    const tickMat = new THREE.LineBasicMaterial({ color: 0x666666 });
    const ticks = new THREE.LineSegments(tickGeom, tickMat);
    compassGroup.add(ticks);

    // Cardinal sprites N/E/S/W (N at +Y)
    const cardinals = [
        { text: 'N', x: 0, y: radius * 1.10 },
        { text: 'E', x: radius * 1.10, y: 0 },
        { text: 'S', x: 0, y: -radius * 1.10 },
        { text: 'W', x: -radius * 1.10, y: 0 }
    ];
    cardinals.forEach(c => {
        const sprite = createTextSprite(c.text, { fontSize: 96, color: '#111', padding: 12, bold: true });
        scaleSpriteToHeight(sprite, 0.5);
        sprite.position.set(c.x, c.y, 0.0);
        sprite.renderOrder = 2;
        sprite.material.depthTest = false; // keep visible
        compassGroup.add(sprite);
    });

    // Degree labels at 30°, 60°, 120°, 150°, ... (avoid overlap with cardinals)
    const degreeMarks = [30, 60, 120, 150, 210, 240, 300, 330];
    degreeMarks.forEach(deg => {
        const a = deg2rad(deg);
        const r = radius * 1.14;
        const x = Math.cos(a) * r;
        const y = Math.sin(a) * r;
        const sprite = createTextSprite(`${deg}°`, { fontSize: 60, color: '#333', padding: 8 });
        scaleSpriteToHeight(sprite, 0.32);
        sprite.position.set(x, y, 0);
        sprite.renderOrder = 2;
        sprite.material.depthTest = false;
        compassGroup.add(sprite);
    });
}

// Utility: create a text sprite using canvas
function createTextSprite(text, options = {}) {
    const {
        fontFace = 'Segoe UI, Roboto, Helvetica, Arial, sans-serif',
        fontSize = 64,
        color = '#000',
        bg = 'rgba(255,255,255,0)',
        padding = 6,
        bold = false
    } = options;

    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    const font = `${bold ? 'bold ' : ''}${fontSize}px ${fontFace}`;
    ctx.font = font;

    const metrics = ctx.measureText(text);
    const textWidth = Math.ceil(metrics.width);
    const textHeight = Math.ceil(fontSize * 1.35);

    canvas.width = textWidth + padding * 2;
    canvas.height = textHeight + padding * 2;

    // Redraw with proper size
    const ctx2 = canvas.getContext('2d');
    ctx2.font = font;
    ctx2.fillStyle = bg;
    ctx2.fillRect(0, 0, canvas.width, canvas.height);
    ctx2.fillStyle = color;
    ctx2.textAlign = 'left';
    ctx2.textBaseline = 'top';
    ctx2.fillText(text, padding, padding);

    const texture = new THREE.CanvasTexture(canvas);
    texture.anisotropy = 4;
    texture.minFilter = THREE.LinearFilter;
    texture.needsUpdate = true;

    const material = new THREE.SpriteMaterial({ map: texture, transparent: true, depthWrite: false });
    const sprite = new THREE.Sprite(material);

    // Default scale to world units ~ 1 (will adjust later)
    sprite.scale.set(canvas.width / canvas.height, 1, 1);

    return sprite;
}

// Utility: scale a sprite to a specific height in world units
function scaleSpriteToHeight(sprite, height = 0.5) {
    const aspect = sprite.scale.x / sprite.scale.y;
    sprite.scale.set(height * aspect, height, 1);
}

// Initialize
buildPatternMesh(currentWaveType);
applyOrientation();
addUpArrow();
addCompass();

// UI bindings
function updateStrike() {
    strikeValue.textContent = sliderStrike.value;
    applyOrientation();
}
function updateDip() {
    dipValue.textContent = sliderDip.value;
    applyOrientation();
}
function updateRake() {
    rakeValue.textContent = sliderRake.value;
    applyOrientation();
}
sliderStrike.addEventListener('input', updateStrike);
sliderDip.addEventListener('input', updateDip);
sliderRake.addEventListener('input', updateRake);

function setActiveButton(type) {
    [btnP, btnSV, btnSH].forEach(btn => btn.classList.remove('active'));
    if (type === 'P') btnP.classList.add('active');
    if (type === 'SV') btnSV.classList.add('active');
    if (type === 'SH') btnSH.classList.add('active');
}
function setWaveType(type) {
    if (currentWaveType === type) return;
    currentWaveType = type;
    setActiveButton(type);
    buildPatternMesh(currentWaveType);
    applyOrientation();
}
btnP.addEventListener('click', () => setWaveType('P'));
btnSV.addEventListener('click', () => setWaveType('SV'));
btnSH.addEventListener('click', () => setWaveType('SH'));

checkboxBox.addEventListener('change', () => {
    boundingBoxHelper.visible = checkboxBox.checked;
    boundingBoxHelper.update();
});

// Resize handler
function onWindowResize() {
    const w = container.clientWidth;
    const h = container.clientHeight;
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
    renderer.setSize(w, h);
}
window.addEventListener('resize', onWindowResize);

// Animation loop
renderer.setAnimationLoop(() => {
    controls.update();
    // keep box helper updated with transforms
    boundingBoxHelper.update();
    renderer.render(scene, camera);
});

// Render math labels
if (window.MathJax && MathJax.typesetPromise) {
    MathJax.typesetPromise();
}
</script>
</body>
</html>
