<!-- CDN for p5.js library -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<script>
  // -------------------------
  // Joystick state and logic
  // -------------------------
  const handle1 = document.getElementById('joystick-handle1');
  const handle2 = document.getElementById('joystick-handle2');
  const container1 = document.getElementById('joystick-share1');
  const container2 = document.getElementById('joystick-share2');
  const resetBtn = document.getElementById('btn-reset');

  // Internal normalized state (0..1) for left (x), top (y)
  const joystickState = {
    1: { x: 0.75, y: 0.25 }, // share 1 default: left:75%, top:25%
    2: { x: 0.25, y: 0.75 }  // share 2 default: left:25%, top:75%
  };

  let active = null; // { idx: 1|2, container, handle }

  // Helper to set handle position from normalized state
  function applyHandlePosition(idx) {
    const st = joystickState[idx];
    const handle = idx === 1 ? handle1 : handle2;
    handle.style.left = (st.x * 100).toFixed(3) + '%';
    handle.style.top  = (st.y * 100).toFixed(3) + '%';
  }

  // Initialize handle positions
  applyHandlePosition(1);
  applyHandlePosition(2);

  // Convert pointer position to normalized (0..1) inside container using handle center
  function updateFromPointer(e, container, handle, idx) {
    const rect = container.getBoundingClientRect();
    const hs = handle.offsetWidth; // handle size in px
    const cx = ('touches' in e && e.touches.length) ? e.touches[0].clientX : e.clientX;
    const cy = ('touches' in e && e.touches.length) ? e.touches[0].clientY : e.clientY;

    // Position relative to container
    let x = cx - rect.left;
    let y = cy - rect.top;

    // Clamp (0..container size) because we position by center using translate(-50%, -50%)
    x = Math.max(0, Math.min(x, rect.width));
    y = Math.max(0, Math.min(y, rect.height));

    const nx = x / rect.width;
    const ny = y / rect.height;

    joystickState[idx].x = nx;
    joystickState[idx].y = ny;

    // Apply to handle CSS
    handle.style.left = (nx * 100).toFixed(3) + '%';
    handle.style.top  = (ny * 100).toFixed(3) + '%';

    // Notify drawing code (positions depend on joystickState)
    if (typeof window.updateSharePositions === 'function') {
      window.updateSharePositions();
    }
  }

  function startDrag(e, idx) {
    e.preventDefault();
    active = {
      idx,
      container: idx === 1 ? container1 : container2,
      handle: idx === 1 ? handle1 : handle2
    };
    document.addEventListener('mousemove', onMove);
    document.addEventListener('mouseup', endDrag);
    document.addEventListener('touchmove', onMove, {passive:false});
    document.addEventListener('touchend', endDrag);
  }

  function onMove(e) {
    if (!active) return;
    e.preventDefault();
    updateFromPointer(e, active.container, active.handle, active.idx);
  }

  function endDrag() {
    document.removeEventListener('mousemove', onMove);
    document.removeEventListener('mouseup', endDrag);
    document.removeEventListener('touchmove', onMove);
    document.removeEventListener('touchend', endDrag);
    active = null;
  }

  handle1.addEventListener('mousedown', (e)=> startDrag(e, 1));
  handle2.addEventListener('mousedown', (e)=> startDrag(e, 2));
  handle1.addEventListener('touchstart', (e)=> startDrag(e, 1), {passive:false});
  handle2.addEventListener('touchstart', (e)=> startDrag(e, 2), {passive:false});

  // Reset button logic
  function resetState() {
    joystickState[1].x = 0.75;
    joystickState[1].y = 0.25;
    joystickState[2].x = 0.25;
    joystickState[2].y = 0.75;
    applyHandlePosition(1);
    applyHandlePosition(2);
    if (typeof window.updateSharePositions === 'function') {
      window.updateSharePositions();
    }
  }
  resetBtn.addEventListener('click', resetState);

  // -------------------------
  // p5.js Visualization Logic
  // -------------------------
  let secretSize = 64;            // 64x64 secret
  let shareW = secretSize * 2;    // 2x2 subpixel blocks
  let shareH = secretSize * 2;

  let share1Graphic, share2Graphic;
  let pos1 = { x: 0, y: 0 };
  let pos2 = { x: 0, y: 0 };

  // Expose a function to update positions from joystick state
  window.updateSharePositions = function() {
    if (window._p5instance) {
      const p = window._p5instance;
      // Map 0..1 -> [0 .. canvasSize - shareSize]
      pos1.x = joystickState[1].x * Math.max(0, (p.width  - shareW));
      pos1.y = joystickState[1].y * Math.max(0, (p.height - shareH));
      pos2.x = joystickState[2].x * Math.max(0, (p.width  - shareW));
      pos2.y = joystickState[2].y * Math.max(0, (p.height - shareH));
    }
  };

  function buildSecretArray(size) {
    // 2D array of 0 (white) and 1 (black)
    const arr = new Array(size);
    for (let y = 0; y < size; y++) {
      arr[y] = new Array(size).fill(0);
    }
    const cx = size / 2;
    const cy = size / 2;

    // Face outline (ring)
    const r = size * 0.42;
    for (let y = 0; y < size; y++) {
      for (let x = 0; x < size; x++) {
        const dx = x - cx + 0.5;
        const dy = y - cy + 0.5;
        const d = Math.sqrt(dx*dx + dy*dy);
        if (Math.abs(d - r) < 1.2) {
          arr[y][x] = 1;
        }
      }
    }

    // Eyes (filled)
    const eyeR = Math.max(2, Math.round(size * 0.05));
    const leftEye  = { x: Math.round(cx - size*0.16), y: Math.round(cy - size*0.18) };
    const rightEye = { x: Math.round(cx + size*0.16), y: Math.round(cy - size*0.18) };
    for (let y = 0; y < size; y++) {
      for (let x = 0; x < size; x++) {
        const dL = Math.hypot(x - leftEye.x,  y - leftEye.y);
        const dR = Math.hypot(x - rightEye.x, y - rightEye.y);
        if (dL <= eyeR || dR <= eyeR) {
          arr[y][x] = 1;
        }
      }
    }

    // Mouth: smile curve (parabola-like), thicker line
    // y_line = a*(x - cx)^2 + offset
    const a = 0.018; // curvature
    const yOffset = cy + size * 0.14;
    const thickness = 1.3;
    for (let x = 0; x < size; x++) {
      const yLine = a * Math.pow(x - cx, 2) + yOffset;
      for (let y = 0; y < size; y++) {
        if (y >= cy + size*0.03 && Math.abs(y - yLine) < thickness) {
          arr[y][x] = 1;
        }
      }
    }

    return arr;
  }

  function generateShares(p, secret) {
    const w = secret[0].length;
    const h = secret.length;

    share1Graphic = p.createGraphics(w*2, h*2);
    share2Graphic = p.createGraphics(w*2, h*2);

    // Ensure sharp 1x1 drawing regardless of pixel density
    share1Graphic.pixelDensity(1);
    share2Graphic.pixelDensity(1);

    share1Graphic.clear();
    share2Graphic.clear();

    share1Graphic.noStroke();
    share2Graphic.noStroke();

    share1Graphic.fill(0, 255);
    share2Graphic.fill(0, 255);

    // Two diagonal patterns
    // A: [ [1,0], [0,1] ], B: [ [0,1], [1,0] ]
    function drawBlock(gfx, gx, gy, pattern) {
      // gx, gy are in units of block top-left (pixel coordinates)
      // pattern is 2x2 array with 1=black, 0=transparent
      for (let j = 0; j < 2; j++) {
        for (let i = 0; i < 2; i++) {
          if (pattern[j][i]) {
            gfx.rect(gx + i, gy + j, 1, 1);
          }
        }
      }
    }

    for (let y = 0; y < h; y++) {
      for (let x = 0; x < w; x++) {
        const val = secret[y][x]; // 0:white, 1:black
        const A = [[1,0],[0,1]];
        const B = [[0,1],[1,0]];
        const chooseA = p.random() < 0.5;

        if (val === 0) {
          // White pixel: same pattern on both shares
          const pat = chooseA ? A : B;
          drawBlock(share1Graphic, x*2, y*2, pat);
          drawBlock(share2Graphic, x*2, y*2, pat);
        } else {
          // Black pixel: complementary patterns
          const pat1 = chooseA ? A : B;
          const pat2 = chooseA ? B : A;
          drawBlock(share1Graphic, x*2, y*2, pat1);
          drawBlock(share2Graphic, x*2, y*2, pat2);
        }
      }
    }
  }

  // p5 sketch (global mode, but keep a reference)
  new p5((p) => {
    window._p5instance = p;

    let secret = null;

    function canvasSizeFromContainer() {
      const viz = document.getElementById('visualization-area');
      const pad = 24; // padding around canvas for aesthetics
      const w = Math.max(shareW + 20, viz.clientWidth - pad*2);
      const h = Math.max(shareH + 20, viz.clientHeight - pad*2);
      return { w, h };
    }

    p.setup = function() {
      p.pixelDensity(1);
      const { w, h } = canvasSizeFromContainer();
      const cnv = p.createCanvas(w, h);
      cnv.parent('visualization-area');

      // Build secret and shares
      secret = buildSecretArray(secretSize);
      generateShares(p, secret);

      // Initial mapping of joystick to canvas positions
      window.updateSharePositions();
    };

    p.windowResized = function() {
      const { w, h } = canvasSizeFromContainer();
      p.resizeCanvas(w, h);
      // Re-map positions to new canvas size
      window.updateSharePositions();
    };

    p.draw = function() {
      p.background(255);

      // Subtle checker background to better perceive transparency
      p.push();
      p.noStroke();
      const grid = 20;
      for (let y = 0; y < p.height; y += grid) {
        for (let x = 0; x < p.width; x += grid) {
          const on = ((x / grid) + (y / grid)) % 2 === 0;
          p.fill(on ? 248 : 242);
          p.rect(x, y, grid, grid);
        }
      }
      p.pop();

      // Draw shares
      p.image(share1Graphic, pos1.x, pos1.y);
      p.image(share2Graphic, pos2.x, pos2.y);

      // Optional frame around the canvas content area for clarity
      p.noFill();
      p.stroke(220);
      p.rect(0.5, 0.5, p.width-1, p.height-1);
    };
  });

  // Ensure initial positions are set when DOM is ready
  document.addEventListener('DOMContentLoaded', () => {
    window.updateSharePositions();
  });
</script>

</body>
</html>
