<body>
  <div id="main-container">
    <div id="control-panel">
      <div id="rings-control-group">
        <label for="rings-radios">rings</label>
        <div id="rings-radios">
          <input type="radio" name="rings" value="1" id="rings-1" checked>
          <label for="rings-1">1</label>

          <input type="radio" name="rings" value="2" id="rings-2">
          <label for="rings-2">2</label>

          <input type="radio" name="rings" value="3" id="rings-3">
          <label for="rings-3">3</label>

          <input type="radio" name="rings" value="4" id="rings-4">
          <label for="rings-4">4</label>

          <input type="radio" name="rings" value="5" id="rings-5">
          <label for="rings-5">5</label>

          <input type="radio" name="rings" value="6" id="rings-6">
          <label for="rings-6">6</label>

          <input type="radio" name="rings" value="7" id="rings-7">
          <label for="rings-7">7</label>
        </div>
      </div>

      <div id="circles-per-ring-control-group">
        <label for="cpr-radios">circles per ring</label>
        <div id="cpr-radios">
          <input type="radio" name="cpr" value="4" id="cpr-4">
          <label for="cpr-4">4</label>

          <input type="radio" name="cpr" value="6" id="cpr-6">
          <label for="cpr-6">6</label>

          <input type="radio" name="cpr" value="8" id="cpr-8" checked>
          <label for="cpr-8">8</label>

          <input type="radio" name="cpr" value="10" id="cpr-10">
          <label for="cpr-10">10</label>

          <input type="radio" name="cpr" value="12" id="cpr-12">
          <label for="cpr-12">12</label>

          <input type="radio" name="cpr" value="14" id="cpr-14">
          <label for="cpr-14">14</label>
        </div>
      </div>

      <div id="inverse-control-group">
        <label for="checkbox-inverse">inverse</label>
        <input type="checkbox" id="checkbox-inverse">
        <div class="hint">Drag the two red points in inverse mode.</div>
      </div>
    </div>

    <div id="canvas-container">
      <canvas id="p5-canvas"></canvas>
    </div>
  </div>

  <script>
    // Internal state variables
    let locators = null; // array of two p5.Vector objects (relative to canvas center)
    let draggedLocator = -1; // index of dragged locator, -1 if none
    const CANVAS_SIZE = 550;

    // Utility: get UI state
    function getSelectedRings() {
      const el = document.querySelector('input[name="rings"]:checked');
      return el ? parseInt(el.value, 10) : 1;
    }
    function getSelectedCPR() {
      const el = document.querySelector('input[name="cpr"]:checked');
      return el ? parseInt(el.value, 10) : 8;
    }
    function isInverseMode() {
      const el = document.getElementById('checkbox-inverse');
      return !!(el && el.checked);
    }

    // p5.js sketch
    function setup() {
      // Remove placeholder canvas so we can create and assign the required id
      const placeholder = document.getElementById('p5-canvas');
      if (placeholder && placeholder.parentElement) {
        placeholder.parentElement.removeChild(placeholder);
      }

      const cnv = createCanvas(CANVAS_SIZE, CANVAS_SIZE);
      cnv.parent('canvas-container');
      cnv.id('p5-canvas');

      angleMode(RADIANS);

      // Initialize locators relative to center
      locators = [createVector(-50, 0), createVector(50, 0)];
      draggedLocator = -1;

      // Redraw when controls change
      const inputs = document.querySelectorAll('input[name="rings"], input[name="cpr"], #checkbox-inverse');
      inputs.forEach(inp => {
        inp.addEventListener('change', () => {
          redraw();
        });
      });

      // We'll keep looping for responsive dragging/interaction
      // but call redraw here for consistency on initial load
      redraw();
    }

    function draw() {
      background('#C1D4E6');

      // Work in a centered coordinate system
      push();
      translate(width / 2, height / 2);

      const N = getSelectedCPR();
      const M = getSelectedRings();

      if (!isInverseMode()) {
        drawNormalMode(N, M);
      } else {
        drawInverseMode(N, M);
      }

      pop();
    }

    function drawNormalMode(N, M) {
      stroke(0);
      strokeWeight(1);
      fill('#8C929D');

      const pattern = computePattern(N, M);

      // Draw circles
      for (let i = 0; i < pattern.circles.length; i++) {
        const c = pattern.circles[i];
        ellipse(c.center.x, c.center.y, 2 * c.r, 2 * c.r);
      }

      // Guide lines to outermost ring centers
      stroke('#A0A0A0');
      strokeWeight(1);
      for (let j = 0; j < pattern.outerCenters.length; j++) {
        const p = pattern.outerCenters[j];
        line(0, 0, p.x, p.y);
      }
    }

    function drawInverseMode(N, M) {
      // Draw locators and inversion circle
      const L0 = locators[0];
      const L1 = locators[1];

      // Connecting line
      stroke('#FF0000');
      strokeWeight(1);
      line(L0.x, L0.y, L1.x, L1.y);

      // Locator points
      noStroke();
      fill('#FF0000');
      const locatorDiameter = 8;
      ellipse(L0.x, L0.y, locatorDiameter, locatorDiameter);
      ellipse(L1.x, L1.y, locatorDiameter, locatorDiameter);

      // Inversion circle
      const C_inv = p5.Vector.add(L0, L1).mult(0.5);
      const R_inv = p5.Vector.dist(L0, L1) * 0.5;

      stroke(0);
      fill(255, 0, 0, 76);
      ellipse(C_inv.x, C_inv.y, 2 * R_inv, 2 * R_inv);

      if (R_inv <= 0.0001) {
        // Degenerate inversion circle — nothing to invert
        return;
      }

      // Compute original pattern and invert
      const pattern = computePattern(N, M);

      // Draw inverted shapes
      stroke(0);
      fill(220, 100, 220, 128); // semi-transparent magenta for circles

      const EPS = Math.max(0.5, 0.0001 * (width + height)); // tolerance for line-case
      for (let i = 0; i < pattern.circles.length; i++) {
        const c = pattern.circles[i];
        const C_orig = c.center;
        const r_orig = c.r;

        const d = p5.Vector.dist(C_inv, C_orig);

        if (Math.abs(d - r_orig) < EPS) {
          // Inversion is a line: perpendicular to C_inv -> C_orig
          // Distance from C_inv to the line is R_inv^2 / (2*d)
          if (d > 0) {
            const n = p5.Vector.sub(C_orig, C_inv).normalize(); // normal direction
            const t = (R_inv * R_inv) / (2 * d);
            const P_line = p5.Vector.add(C_inv, p5.Vector.mult(n, t));
            // Direction vector for the line (perpendicular to n)
            const v = createVector(-n.y, n.x);
            const L = Math.max(width, height); // long enough to cover canvas
            noFill();
            stroke(0);
            line(P_line.x - v.x * L, P_line.y - v.y * L, P_line.x + v.x * L, P_line.y + v.y * L);
          }
        } else {
          // Inverted circle
          const denom = (d * d - r_orig * r_orig);
          if (Math.abs(denom) < 1e-8) continue; // numerical safety
          const factor = (R_inv * R_inv) / denom;
          const dir = p5.Vector.sub(C_orig, C_inv);
          const C_new = p5.Vector.add(C_inv, p5.Vector.mult(dir, factor));
          const r_new = Math.abs(factor) * r_orig;

          // Draw the inverted circle
          stroke(0);
          fill(220, 100, 220, 128);
          ellipse(C_new.x, C_new.y, 2 * r_new, 2 * r_new);
        }
      }
    }

    function computePattern(N, M) {
      // Based on provided geometry
      const R_max = (width / 2) * 0.9;
      const s = Math.sin(Math.PI / N);
      const k = (1 - s) / (1 + s);
      const R_c = R_max / (1 + s);
      const r = R_c * s;

      const circles = [];
      const outerCenters = [];

      for (let i = 0; i < M; i++) {
        const R_ci = R_c * Math.pow(k, i);
        const r_i = r * Math.pow(k, i);

        for (let j = 0; j < N; j++) {
          const angle = j * (2 * Math.PI / N);
          const x = R_ci * Math.cos(angle);
          const y = R_ci * Math.sin(angle);

          const center = createVector(x, y);
          circles.push({ center, r: r_i });

          if (i === 0) {
            outerCenters.push(center.copy());
          }
        }
      }
      return { circles, outerCenters };
    }

    // Interaction: Draggable locators in inverse mode
    function mousePressed() {
      if (!isInverseMode()) return;

      // Check if within canvas bounds
      if (mouseX < 0 || mouseX > width || mouseY < 0 || mouseY > height) return;

      const mx = mouseX - width / 2;
      const my = mouseY - height / 2;

      const pickRadius = 8; // same as visual diameter, forgiving pick
      const m = createVector(mx, my);
      const d0 = p5.Vector.dist(m, locators[0]);
      const d1 = p5.Vector.dist(m, locators[1]);

      if (d0 <= pickRadius) {
        draggedLocator = 0;
      } else if (d1 <= pickRadius) {
        draggedLocator = 1;
      } else {
        draggedLocator = -1;
      }
    }

    function mouseDragged() {
      if (!isInverseMode()) return;
      if (draggedLocator === -1) return;

      // Only update if dragging within canvas bounds; otherwise allow free drag
      const mx = mouseX - width / 2;
      const my = mouseY - height / 2;

      locators[draggedLocator].x = mx;
      locators[draggedLocator].y = my;
    }

    function mouseReleased() {
      draggedLocator = -1;
    }
  </script>
</body>
</html>
