</head>
<body>
  <div id="app">
    <!-- Control Panel -->
    <div id="controls">
      <div class="control-group">
        <label for="slider-logCa">\(C_a\)</label>
        <input id="slider-logCa" type="range" min="-4" max="0" step="0.01" value="-1" />
      </div>
      <div class="control-group">
        <label for="slider-pka">\(pK_a\)</label>
        <input id="slider-pka" type="range" min="-4" max="7" step="0.1" value="1.2" />
      </div>
    </div>

    <!-- Visualization Area -->
    <div id="viz-container">
      <!-- p5.js canvas will be injected here -->
      <div id="formula-strong">\( \color{maroon}{pH = - \log C_a} \)</div>
      <div id="formula-weak">\( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)</div>
    </div>
  </div>

  <script>
    // Wait for p5 to be loaded (defer ensures load order), then build the sketch.
    (function() {
      const sliderLogCaEl = document.getElementById('slider-logCa');
      const sliderPkaEl = document.getElementById('slider-pka');
      const vizContainer = document.getElementById('viz-container');
      const formulaStrong = document.getElementById('formula-strong');
      const formulaWeak = document.getElementById('formula-weak');

      const sketch = (p) => {
        const xMin = -4, xMax = 7;    // pKa range
        const yMin = 0,  yMax = 7;    // pH range
        const pad = 50;

        let curvePoints = [];

        p.setup = function() {
          const cnv = p.createCanvas(vizContainer.clientWidth, vizContainer.clientHeight);
          cnv.parent(vizContainer);
          // Give the canvas an id per the plan
          cnv.id('plot-canvas');

          p.textFont('sans-serif');
          p.textSize(12);
          p.noLoop();

          recomputeExactCurve();
          drawAll();
          updateFormulaPositions();
        };

        p.windowResized = function() {
          p.resizeCanvas(vizContainer.clientWidth, vizContainer.clientHeight);
          drawAll();
          updateFormulaPositions();
        };

        function mapX(x) {
          return p.map(x, xMin, xMax, pad, p.width - pad);
        }
        function mapY(y) {
          return p.map(y, yMin, yMax, p.height - pad, pad);
        }

        function recomputeExactCurve() {
          curvePoints = [];
          const logCa = parseFloat(sliderLogCaEl.value);
          const Ca = Math.pow(10, logCa);

          const steps = 700;
          for (let i = 0; i <= steps; i++) {
            const x = xMin + (xMax - xMin) * (i / steps); // pKa
            const Ka = Math.pow(10, -x);
            const rad = Math.sqrt(Ka*Ka + 4 * Ka * Ca);
            const H = (-Ka + rad) / 2;
            let pH = -Math.log10(H);
            if (!isFinite(pH)) pH = yMax;
            curvePoints.push({ x, y: pH });
          }
        }

        function drawAxes() {
          p.background(255);
          p.stroke('#444');
          p.strokeWeight(1);
          p.noFill();

          // Axes lines
          p.line(pad, pad, pad, p.height - pad);
          p.line(pad, p.height - pad, p.width - pad, p.height - pad);

          // X ticks
          for (let xv = Math.ceil(xMin); xv <= Math.floor(xMax); xv++) {
            const px = mapX(xv);
            p.stroke('#777');
            p.line(px, p.height - pad, px, p.height - pad + 6);
            p.noStroke();
            p.fill('#333');
            p.textAlign(p.CENTER, p.TOP);
            p.text(xv.toString(), px, p.height - pad + 9);
          }

          // Y ticks
          for (let yv = yMin; yv <= yMax; yv += 1) {
            const py = mapY(yv);
            p.stroke('#777');
            p.line(pad - 6, py, pad, py);
            p.noStroke();
            p.fill('#333');
            p.textAlign(p.RIGHT, p.CENTER);
            p.text(yv.toString(), pad - 9, py);
          }

          // Axis labels
          p.noStroke();
          p.fill('#222');
          p.textAlign(p.CENTER, p.TOP);
          p.text('pK_a', (pad + (p.width - pad)) / 2, p.height - pad + 30);

          p.push();
          p.translate(pad - 35, (pad + (p.height - pad)) / 2);
          p.rotate(-Math.PI / 2);
          p.textAlign(p.CENTER, p.TOP);
          p.text('pH', 0, 0);
          p.pop();
        }

        function drawStrongLine() {
          const logCa = parseFloat(sliderLogCaEl.value);
          const yStrong = -logCa;
          p.stroke('maroon');
          p.strokeWeight(2);
          p.line(mapX(xMin), mapY(yStrong), mapX(xMax), mapY(yStrong));
        }

        function drawWeakLine() {
          const logCa = parseFloat(sliderLogCaEl.value);
          const x0 = xMin, y0 = 0.5 * (x0 - logCa);
          const x1 = xMax, y1 = 0.5 * (x1 - logCa);
          p.stroke('#1e8e1e');
          p.strokeWeight(2);
          p.line(mapX(x0), mapY(y0), mapX(x1), mapY(y1));
        }

        function drawExactCurve() {
          p.noFill();
          p.stroke('#1f56d6');
          p.strokeWeight(3);
          p.beginShape();
          for (let i = 0; i < curvePoints.length; i++) {
            const pt = curvePoints[i];
            p.vertex(mapX(pt.x), mapY(pt.y));
          }
          p.endShape();
        }

        function drawIndicator() {
          const pKa = parseFloat(sliderPkaEl.value);
          const logCa = parseFloat(sliderLogCaEl.value);
          const Ca = Math.pow(10, logCa);
          const Ka = Math.pow(10, -pKa);
          const H = (-Ka + Math.sqrt(Ka*Ka + 4 * Ka * Ca)) / 2;
          let pH = -Math.log10(H);
          if (!isFinite(pH)) pH = yMax;

          const px = mapX(pKa);
          const py = mapY(pH);

          p.stroke('#222');
          p.fill('#ff9500');
          p.strokeWeight(2);
          p.circle(px, py, 10);
        }

        function drawAll() {
          drawAxes();
          drawStrongLine();
          drawWeakLine();
          drawExactCurve();
          drawIndicator();
          updateFormulaPositions();
        }

        function placeOverlay(el, xPx, yPx) {
          // keep the element within the visible area
          const padding = 6;
          const clampedX = Math.min(p.width - padding, Math.max(padding, xPx));
          const clampedY = Math.min(p.height - padding, Math.max(padding, yPx));
          el.style.left = clampedX + 'px';
          el.style.top = clampedY + 'px';
        }

        function updateFormulaPositions() {
          const logCa = parseFloat(sliderLogCaEl.value);

          // Strong acid formula: left side near the horizontal red line
          const xLeft = xMin + 0.6; // a little to the right of the y-axis
          const yStrong = -logCa;
          const strongXpx = mapX(xLeft);
          const strongYpx = mapY(yStrong) - 14; // position above the line
          placeOverlay(formulaStrong, strongXpx, strongYpx);

          // Weak acid formula: right side, along the green line
          const xRight = xMax - 1.0;
          const yWeak = 0.5 * (xRight - logCa);
          const weakXpx = mapX(xRight);
          const weakYpx = mapY(yWeak) - 14;
          placeOverlay(formulaWeak, weakXpx, weakYpx);
        }

        // Slider interactions
        sliderLogCaEl.addEventListener('input', () => {
          // Recompute everything when C_a changes
          recomputeExactCurve();
          drawAll();
        });

        sliderPkaEl.addEventListener('input', () => {
          // Only the indicator point needs to move; redraw for simplicity
          drawAll();
        });
      };

      // Initialize the p5 sketch
      new p5(sketch);
    })();
  </script>
</body>
</html>
