<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

<script>
/* ---------- Global State and Utilities ---------- */
const rows = 50;
const cols = 50;
const totalNodes = rows * cols;

const COLORS = {
    empty: "#FFFFFF",
    obstacle: "#000000",
    open: "#BDE0FE",
    closed: "#0000CD",
    startEnd: "#34A853",
    path: "#FF0000",
    grid: "#e5e7eb"
};

const slider = document.getElementById('slider-step');
const sliderLabel = document.getElementById('label-step');
const selectObstacles = document.getElementById('select-obstacles');
const radioDijkstra = document.getElementById('radio-dijkstra');
const radioAStar = document.getElementById('radio-a-star');

let startNorm = { x: 0.15, y: 0.85 }; // normalized 0..1
let endNorm   = { x: 0.85, y: 0.15 };

let obstaclesSet = new Set(); // of node id
let history = []; // frames
// Each frame: { open: number[], closed: number[], current: number|null, path?: {r,c}[] }

/* Convert (r,c) to id and back */
const idFromRC = (r, c) => r * cols + c;
const rcFromId = (id) => ({ r: Math.floor(id / cols), c: id % cols });

/* Clamp utility */
const clamp = (v, min, max) => Math.max(min, Math.min(max, v));

/* Convert normalized 0..1 to grid index 0..(maxIndex) using rounding */
const normToIndex = (n, maxIndex) => clamp(Math.round(n * maxIndex), 0, maxIndex);

/* Heuristic functions */
const manhattan = (r1, c1, r2, c2) => Math.abs(r1 - r2) + Math.abs(c1 - c2);

/* 8-direction moves */
const DIRS = [
    {dr: -1, dc:  0, cost: 1},
    {dr:  1, dc:  0, cost: 1},
    {dr:  0, dc: -1, cost: 1},
    {dr:  0, dc:  1, cost: 1},
    {dr: -1, dc: -1, cost: Math.SQRT2},
    {dr: -1, dc:  1, cost: Math.SQRT2},
    {dr:  1, dc: -1, cost: Math.SQRT2},
    {dr:  1, dc:  1, cost: Math.SQRT2}
];

/* ---------- Priority Queue (Min-Heap) ---------- */
class MinHeap {
    constructor(compare) {
        this.data = [];
        this.compare = compare || ((a, b) => a - b);
    }
    size() { return this.data.length; }
    peek() { return this.data[0]; }
    push(value) {
        this.data.push(value);
        this._siftUp();
    }
    pop() {
        const top = this.data[0];
        const bottom = this.data.pop();
        if (this.data.length > 0) {
            this.data[0] = bottom;
            this._siftDown();
        }
        return top;
    }
    _siftUp() {
        let nodeIdx = this.data.length - 1;
        while (nodeIdx > 0) {
            let parentIdx = (nodeIdx - 1) >> 1;
            if (this.compare(this.data[nodeIdx], this.data[parentIdx]) < 0) {
                [this.data[nodeIdx], this.data[parentIdx]] = [this.data[parentIdx], this.data[nodeIdx]];
                nodeIdx = parentIdx;
            } else break;
        }
    }
    _siftDown() {
        let idx = 0;
        const length = this.data.length;
        while (true) {
            let left = (idx << 1) + 1;
            let right = left + 1;
            let smallest = idx;
            if (left < length && this.compare(this.data[left], this.data[smallest]) < 0) smallest = left;
            if (right < length && this.compare(this.data[right], this.data[smallest]) < 0) smallest = right;
            if (smallest !== idx) {
                [this.data[idx], this.data[smallest]] = [this.data[smallest], this.data[idx]];
                idx = smallest;
            } else break;
        }
    }
}

/* ---------- Obstacles Layouts ---------- */
function buildObstacles(option, startId, goalId) {
    const set = new Set();
    const centerR = Math.floor(rows / 2);
    const centerC = Math.floor(cols / 2);

    if (option === '1-square') {
        const half = 4; // 8x8 block centered
        for (let r = centerR - half; r <= centerR + half - 1; r++) {
            for (let c = centerC - half; c <= centerC + half - 1; c++) {
                if (r >= 0 && r < rows && c >= 0 && c < cols) set.add(idFromRC(r, c));
            }
        }
    } else if (option === '2-squares') {
        // Top-left block
        for (let r = 5; r < 13; r++) {
            for (let c = 5; c < 13; c++) set.add(idFromRC(r, c));
        }
        // Bottom-right block
        for (let r = rows - 13; r < rows - 5; r++) {
            for (let c = cols - 13; c < cols - 5; c++) set.add(idFromRC(r, c));
        }
    } else if (option === 'l-shaped-wall') {
        // Vertical bar
        const wallC = Math.floor(cols * 0.35);
        for (let r = Math.floor(rows * 0.2); r < Math.floor(rows * 0.8); r++) {
            set.add(idFromRC(r, wallC));
        }
        // Horizontal bar forming 'L'
        const wallR = Math.floor(rows * 0.8) - 1;
        for (let c = wallC; c < Math.floor(cols * 0.8); c++) {
            set.add(idFromRC(wallR, c));
        }
    }
    // Ensure start and end are free
    set.delete(startId);
    set.delete(goalId);
    return set;
}

/* ---------- Pathfinding (Dijkstra / A*) with History Recording ---------- */
function runPathfinding(algorithm, startRC, goalRC, obstacles) {
    const startId = idFromRC(startRC.r, startRC.c);
    const goalId  = idFromRC(goalRC.r, goalRC.c);

    const gScore = new Float32Array(totalNodes);
    const parent = new Int32Array(totalNodes);
    for (let i = 0; i < totalNodes; i++) {
        gScore[i] = Infinity;
        parent[i] = -1;
    }

    const closed = new Set();
    const openSetIds = new Set();

    const useAStar = (algorithm === 'a-star');

    const heap = new MinHeap((a, b) => {
        // Tie-break by lower g then by id to stabilize
        if (a.f !== b.f) return a.f - b.f;
        if (a.g !== b.g) return a.g - b.g;
        return a.id - b.id;
    });

    gScore[startId] = 0;
    const startH = useAStar ? manhattan(startRC.r, startRC.c, goalRC.r, goalRC.c) : 0;
    heap.push({ id: startId, g: 0, f: 0 + startH });
    openSetIds.add(startId);

    const frames = [];

    while (heap.size() > 0) {
        const current = heap.pop();
        const currentId = current.id;

        // Skip if stale entry in heap
        if (current.g > gScore[currentId]) continue;

        // Remove from open set (validly)
        if (openSetIds.has(currentId)) openSetIds.delete(currentId);

        // Add to closed set
        closed.add(currentId);

        const { r: cr, c: cc } = rcFromId(currentId);

        // Goal check
        if (currentId === goalId) {
            // Reconstruct path
            const path = [];
            let trace = currentId;
            while (trace !== -1 && trace !== undefined) {
                const { r, c } = rcFromId(trace);
                path.push({ r, c });
                trace = parent[trace];
            }
            path.reverse();

            // Final frame with path
            frames.push({
                open: Array.from(openSetIds),
                closed: Array.from(closed),
                current: currentId,
                path: path
            });
            break;
        }

        // Explore neighbors
        for (const d of DIRS) {
            const nr = cr + d.dr;
            const nc = cc + d.dc;
            if (nr < 0 || nr >= rows || nc < 0 || nc >= cols) continue;
            const nid = idFromRC(nr, nc);
            // Treat start/end as walkable regardless, but other obstacles block
            if (nid !== startId && nid !== goalId && obstacles.has(nid)) continue;
            if (closed.has(nid)) continue;

            const tentativeG = gScore[currentId] + d.cost;
            if (tentativeG < gScore[nid]) {
                gScore[nid] = tentativeG;
                parent[nid] = currentId;
                const h = useAStar ? manhattan(nr, nc, goalRC.r, goalRC.c) : 0;
                const f = tentativeG + h;
                heap.push({ id: nid, g: tentativeG, f });
                openSetIds.add(nid);
            }
        }

        // Record frame after expansion
        frames.push({
            open: Array.from(openSetIds),
            closed: Array.from(closed),
            current: currentId
        });
    }

    // If no path found and heap exhausted, ensure at least one frame exists
    if (frames.length === 0) {
        frames.push({
            open: [],
            closed: Array.from(closed),
            current: null
        });
    }

    return frames;
}

/* ---------- General Workflow ---------- */
function recompute() {
    const algorithm = radioAStar.checked ? 'a-star' : 'dijkstra';

    // Convert normalized positions to grid indices
    const startRC = {
        c: normToIndex(startNorm.x, cols - 1),
        r: normToIndex(startNorm.y, rows - 1)
    };
    const goalRC = {
        c: normToIndex(endNorm.x, cols - 1),
        r: normToIndex(endNorm.y, rows - 1)
    };

    const startId = idFromRC(startRC.r, startRC.c);
    const goalId  = idFromRC(goalRC.r, goalRC.c);

    obstaclesSet = buildObstacles(selectObstacles.value, startId, goalId);

    history = runPathfinding(algorithm, startRC, goalRC, obstaclesSet);

    slider.max = String(history.length);
    slider.value = "1";
    sliderLabel.textContent = `+ ${slider.value}`;

    if (window._p5) window._p5.redraw();
}

/* ---------- Joystick Controls ---------- */
function setupJoystick(areaId, handleId, initialNorm, onRelease) {
    const area = document.getElementById(areaId);
    const handle = document.getElementById(handleId);

    function updateHandlePositionFromNorm(norm) {
        const rect = area.getBoundingClientRect();
        const x = norm.x * rect.width;
        const y = norm.y * rect.height;
        handle.style.left = `${x}px`;
        handle.style.top = `${y}px`;
    }

    function posToNorm(clientX, clientY) {
        const rect = area.getBoundingClientRect();
        const x = clamp(clientX - rect.left, 0, rect.width);
        const y = clamp(clientY - rect.top, 0, rect.height);
        return { x: rect.width ? x / rect.width : 0, y: rect.height ? y / rect.height : 0 };
    }

    let dragging = false;

    function pointerDown(e) {
        dragging = true;
        area.setPointerCapture?.(e.pointerId);
        const norm = posToNorm(e.clientX, e.clientY);
        initialNorm.x = norm.x;
        initialNorm.y = norm.y;
        updateHandlePositionFromNorm(initialNorm);
        e.preventDefault();
    }
    function pointerMove(e) {
        if (!dragging) return;
        const norm = posToNorm(e.clientX, e.clientY);
        initialNorm.x = norm.x;
        initialNorm.y = norm.y;
        updateHandlePositionFromNorm(initialNorm);
        e.preventDefault();
    }
    function pointerUp(e) {
        if (!dragging) return;
        dragging = false;
        area.releasePointerCapture?.(e.pointerId);
        if (typeof onRelease === 'function') onRelease();
        e.preventDefault();
    }

    // Initialize
    updateHandlePositionFromNorm(initialNorm);

    // Use Pointer Events for both mouse and touch
    area.addEventListener('pointerdown', pointerDown);
    window.addEventListener('pointermove', pointerMove);
    window.addEventListener('pointerup', pointerUp);

    // Return a helper to update position on resize
    return () => updateHandlePositionFromNorm(initialNorm);
}

/* ---------- UI Event Wiring ---------- */
slider.addEventListener('input', () => {
    sliderLabel.textContent = `+ ${slider.value}`;
    if (window._p5) window._p5.redraw();
});
radioDijkstra.addEventListener('change', recompute);
radioAStar.addEventListener('change', recompute);
selectObstacles.addEventListener('change', recompute);

/* ---------- p5.js Sketch ---------- */
let sketch = (p) => {
    let cellW, cellH;

    p.setup = function() {
        const container = document.getElementById('canvas-container');
        const { width, height } = container.getBoundingClientRect();
        const cnv = p.createCanvas(Math.floor(width), Math.floor(height));
        cnv.parent('canvas-container');
        p.noLoop();
        p.pixelDensity(1);
        computeCellSize();

        // Initial compute
        recompute();
    };

    function computeCellSize() {
        cellW = p.width / cols;
        cellH = p.height / rows;
    }

    p.windowResized = function() {
        const container = document.getElementById('canvas-container');
        const { width, height } = container.getBoundingClientRect();
        p.resizeCanvas(Math.floor(width), Math.floor(height), true);
        computeCellSize();
        p.redraw();
        // Update joystick handle positions (since they depend on element size)
        if (updateStartHandlePos) updateStartHandlePos();
        if (updateEndHandlePos) updateEndHandlePos();
    };

    p.draw = function() {
        p.background(255);
        if (!history || history.length === 0) return;

        const currentStep = clamp(parseInt(slider.value, 10) || 1, 1, history.length);
        const frame = history[currentStep - 1];

        const openSet = new Set(frame.open);
        const closedSet = new Set(frame.closed);

        // Draw grid cells
        p.noStroke();
        for (let r = 0; r < rows; r++) {
            for (let c = 0; c < cols; c++) {
                const id = idFromRC(r, c);
                let fillColor = COLORS.empty;
                if (obstaclesSet.has(id)) {
                    fillColor = COLORS.obstacle;
                } else if (closedSet.has(id)) {
                    fillColor = COLORS.closed;
                } else if (openSet.has(id)) {
                    fillColor = COLORS.open;
                }
                p.fill(fillColor);
                p.rect(c * cellW, r * cellH, cellW, cellH);
            }
        }

        // Optional grid lines
        p.stroke(COLORS.grid);
        p.strokeWeight(1);
        for (let r = 0; r <= rows; r++) {
            p.line(0, r * cellH, p.width, r * cellH);
        }
        for (let c = 0; c <= cols; c++) {
            p.line(c * cellW, 0, c * cellW, p.height);
        }

        // Draw start/end cells on top (green)
        const sRC = {
            c: normToIndex(startNorm.x, cols - 1),
            r: normToIndex(startNorm.y, rows - 1)
        };
        const eRC = {
            c: normToIndex(endNorm.x, cols - 1),
            r: normToIndex(endNorm.y, rows - 1)
        };

        p.noStroke();
        p.fill(COLORS.startEnd);
        p.rect(sRC.c * cellW, sRC.r * cellH, cellW, cellH);
        p.rect(eRC.c * cellW, eRC.r * cellH, cellW, cellH);

        // If last step and a path exists, draw it
        if (currentStep === history.length && frame.path && frame.path.length > 0) {
            p.stroke(COLORS.path);
            p.strokeWeight(3);
            p.noFill();
            p.beginShape();
            for (const pt of frame.path) {
                const cx = pt.c * cellW + cellW / 2;
                const cy = pt.r * cellH + cellH / 2;
                p.vertex(cx, cy);
            }
            p.endShape();
        }
    };
};

window._p5 = new p5(sketch);

/* ---------- Initialize Joysticks ---------- */
let updateStartHandlePos = setupJoystick('joystick-start', 'handle-start', startNorm, () => {
    recompute();
});
let updateEndHandlePos = setupJoystick('joystick-end', 'handle-end', endNorm, () => {
    recompute();
});

// Ensure initial slider label
sliderLabel.textContent = `+ ${slider.value}`;
</script>
</body>
</html>
