      </div>
      <div class="slider-group">
        <label for="slider-test-charge">charge</label>
        <input type="range" id="slider-test-charge" min="-0.2" max="0.2" step="0.0005" value="-0.0845">
        <span id="label-test-charge">-0.0845</span>
      </div>
    </div>

    <!-- Main Content Section -->
    <div id="main-content">
      <div id="canvas-container"></div>
      <div id="info-panel">
        <p id="display-E-mag">|E| = 0.153 N/C</p>
        <p id="display-Ex">E<sub>x</sub> component = 0.085 N/C</p>
        <p id="display-Ey">E<sub>y</sub> component = 0.127 N/C</p>
        <p id="display-x-pos">x position = 2.00 m</p>
        <p id="display-y-pos">y position = 3.00 m</p>
        <p id="display-force">Electric force = 0.013 N</p>
        <div class="legend">
          Notes: Red arrow = Electric Field E, Blue arrow = Electric Force F. Drag the black point to move the test charge.
        </div>
      </div>
    </div>
  </div>

  <!-- Scripts -->
  <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.js"></script>
  <script>
    // Simulation constants and state
    const k = 1.0; // Coulomb constant (scaled)
    const ppm = 40; // pixels per meter
    const canvasW = 600;
    const canvasH = 500;
    const worldLimit = { x: canvasW / (2*ppm), y: canvasH / (2*ppm) }; // half-span in meters
    const vectorScalePx = 600; // px per (N/C) and px per (N); same for E and F
    const vectorScaleWorld = vectorScalePx / ppm; // world meters per (N/C) or (N)
    const locatorRadiusPx = 10;
    const locatorRadiusWorld = locatorRadiusPx / ppm;
    const epsilon = 1e-6;

    let Q = 2.0;        // source charge
    let q = -0.0845;    // test charge
    let testPos;        // p5.Vector (meters)
    let dragging = false;

    // DOM references
    const sliderQ = document.getElementById('slider-source-charge');
    const sliderq = document.getElementById('slider-test-charge');
    const labelQ  = document.getElementById('label-source-charge');
    const labelq  = document.getElementById('label-test-charge');

    const displayEmag = document.getElementById('display-E-mag');
    const displayEx   = document.getElementById('display-Ex');
    const displayEy   = document.getElementById('display-Ey');
    const displayX    = document.getElementById('display-x-pos');
    const displayY    = document.getElementById('display-y-pos');
    const displayF    = document.getElementById('display-force');

    // p5 sketch
    let sketch = (p) => {

      p.setup = () => {
        let cnv = p.createCanvas(canvasW, canvasH);
        cnv.parent('canvas-container');
        p.pixelDensity(1);
        p.noLoop(); // We'll manually loop with redraw() after state changes, but allow continuous updates on drag
        testPos = p.createVector(2, 3);

        // Initialize slider label displays
        labelQ.textContent = Number(Q).toFixed(1);
        labelq.textContent = Number(q).toFixed(4);

        // Attach event listeners
        sliderQ.addEventListener('input', () => {
          Q = parseFloat(sliderQ.value);
          labelQ.textContent = Q.toFixed(1);
          p.redraw();
        });
        sliderq.addEventListener('input', () => {
          q = parseFloat(sliderq.value);
          labelq.textContent = q.toFixed(4);
          p.redraw();
        });

        // For smooth dragging updates, we'll loop continuously while dragging
        p.frameRate(60);
        p.loop();
      };

      p.draw = () => {
        p.background(255);
        p.push();
        // World transform: origin at center, +x right, +y up; scale meters to pixels
        p.translate(p.width/2, p.height/2);
        p.scale(ppm, -ppm);

        drawGrid(p);

        // Draw source charge at origin
        p.push();
        p.stroke(0);
        p.fill('#2563eb');
        p.strokeWeight(1/ppm);
        p.circle(0, 0, 12/ppm); // ~12 px diameter
        p.pop();

        // Draw test charge locator
        p.push();
        p.noStroke();
        p.fill(0);
        p.circle(testPos.x, testPos.y, 2*locatorRadiusWorld);
        p.pop();

        // Compute field and force
        const rVec = p.createVector(testPos.x, testPos.y);
        const r = rVec.mag();
        let EVec = p.createVector(0, 0);
        let FVec = p.createVector(0, 0);
        if (r > epsilon) {
          const r3 = r*r*r;
          EVec = p5.Vector.mult(rVec, (k * Q) / r3);
          FVec = p5.Vector.mult(EVec, q);
        }

        // Draw Electric Field vector (red), scaled to pixels via vectorScaleWorld
        if (r > epsilon) {
          const EDraw = p5.Vector.mult(EVec, vectorScaleWorld);
          drawArrowWorld(p, testPos, EDraw, '#d64545');
        }

        // Draw Electric Force vector (blue), only if q != 0
        if (r > epsilon && Math.abs(q) > 0) {
          const FDraw = p5.Vector.mult(FVec, vectorScaleWorld);
          drawArrowWorld(p, testPos, FDraw, '#2563eb');
        }

        p.pop(); // end world transform

        // Update info panel from current state
        const Emag = r > epsilon ? Math.abs(k*Q)/(r*r) : 0; // |E|
        const Ex = EVec.x;
        const Ey = EVec.y;
        const Fmag = Math.abs(FVec.mag());

        // Format values
        // Using toFixed(3) for E components and |E|; positions to 2 decimals; force to 3 decimals
        displayEmag.textContent = `|E| = ${Emag.toFixed(3)} N/C`;
        displayEx.innerHTML     = `E<sub>x</sub> component = ${Ex.toFixed(3)} N/C`;
        displayEy.innerHTML     = `E<sub>y</sub> component = ${Ey.toFixed(3)} N/C`;
        displayX.textContent    = `x position = ${testPos.x.toFixed(2)} m`;
        displayY.textContent    = `y position = ${testPos.y.toFixed(2)} m`;
        displayF.textContent    = `Electric force = ${Fmag.toFixed(3)} N`;
      };

      // Mouse interaction for dragging the test charge
      p.mousePressed = () => {
        if (!isMouseInCanvas(p)) return;
        const locScreen = worldToScreen(testPos.x, testPos.y, p);
        const d = Math.hypot(p.mouseX - locScreen.x, p.mouseY - locScreen.y);
        if (d <= locatorRadiusPx + 2) {
          dragging = true;
        }
      };

      p.mouseDragged = () => {
        if (!dragging) return;
        if (!isMouseInCanvas(p)) return;
        const w = screenToWorld(p.mouseX, p.mouseY, p);
        // Clamp to visible world bounds (slightly inset to keep it visible)
        const margin = 0.05;
        testPos.x = clamp(w.x, -worldLimit.x + margin, worldLimit.x - margin);
        testPos.y = clamp(w.y, -worldLimit.y + margin, worldLimit.y - margin);
        // While dragging, continuously update
        // p.redraw(); // draw() already looping at 60 fps
      };

      p.mouseReleased = () => {
        dragging = false;
      };
    };

    // Helper: clamp
    function clamp(v, lo, hi) { return Math.max(lo, Math.min(hi, v)); }

    // Convert world (meters) to screen (pixels)
    function worldToScreen(wx, wy, p) {
      return { x: p.width/2 + wx*ppm, y: p.height/2 - wy*ppm };
    }

    // Convert screen (pixels) to world (meters)
    function screenToWorld(sx, sy, p) {
      return { x: (sx - p.width/2)/ppm, y: (p.height/2 - sy)/ppm };
    }

    // Check if mouse is over the canvas area
    function isMouseInCanvas(p) {
      return p.mouseX >= 0 && p.mouseX <= p.width && p.mouseY >= 0 && p.mouseY <= p.height;
    }

    // Draw a grid with major lines every 2 units, plus axes with labels
    function drawGrid(p) {
      p.push();
      const halfX = p.width/(2*ppm);
      const halfY = p.height/(2*ppm);

      // Minor grid every 1 unit
      p.strokeWeight(1/ppm);
      p.stroke('#e5e7eb');
      for (let x = Math.ceil(-halfX); x <= Math.floor(halfX); x += 1) {
        p.line(x, -halfY, x, halfY);
      }
      for (let y = Math.ceil(-halfY); y <= Math.floor(halfY); y += 1) {
        p.line(-halfX, y, halfX, y);
      }

      // Major grid every 2 units
      p.stroke('#cbd5e1');
      p.strokeWeight(1.5/ppm);
      for (let x = Math.ceil(-halfX/2)*2; x <= Math.floor(halfX/2)*2; x += 2) {
        p.line(x, -halfY, x, halfY);
      }
      for (let y = Math.ceil(-halfY/2)*2; y <= Math.floor(halfY/2)*2; y += 2) {
        p.line(-halfX, y, halfX, y);
      }

      // Axes
      p.stroke(0);
      p.strokeWeight(2/ppm);
      p.line(-halfX, 0, halfX, 0);
      p.line(0, -halfY, 0, halfY);
      p.pop();

      // Numerical labels (drawn in screen space to keep upright)
      const txtOffPx = 16;
      p.push();
      p.textAlign(p.CENTER, p.CENTER);
      p.textSize(12);
      p.noStroke();
      p.fill(80);

      // X-axis labels every 2 units (except 0)
      for (let x = Math.ceil(-halfX/2)*2; x <= Math.floor(halfX/2)*2; x += 2) {
        if (x === 0) continue;
        const s = worldToScreen(x, 0, p);
        p.text(x.toString(), s.x, s.y + txtOffPx);
      }
      // Y-axis labels every 2 units (except 0)
      for (let y = Math.ceil(-halfY/2)*2; y <= Math.floor(halfY/2)*2; y += 2) {
        if (y === 0) continue;
        const s = worldToScreen(0, y, p);
        p.text(y.toString(), s.x - txtOffPx, s.y);
      }
      p.pop();
    }

    // Draw an arrow in WORLD units (with world transform active): base at 'base', vector 'vec'
    function drawArrowWorld(p, base, vec, color) {
      const end = p5.Vector.add(base, vec);
      const v = vec.copy();
      const mag = v.mag();
      p.push();
      p.stroke(color);
      p.fill(color);
      p.strokeWeight(2/ppm);

      // Shaft
      p.line(base.x, base.y, end.x, end.y);

      // Arrowhead
      if (mag > 1e-4) {
        const headSizeWorld = (8 / ppm); // ~8px
        const headWidthWorld = (6 / ppm); // ~6px
        const dir = v.copy().normalize();
        const left = p.createVector(-dir.y, dir.x).mult(headWidthWorld*0.5);
        const tip = end.copy();
        const back = end.copy().sub(dir.mult(headSizeWorld));
        const leftPt = back.copy().add(left);
        const rightPt = back.copy().sub(left);
        p.triangle(tip.x, tip.y, leftPt.x, leftPt.y, rightPt.x, rightPt.y);
      }
      p.pop();
    }

    new p5(sketch);
  </script>
</body>
</html>
