    </div>

    <div id="sliders-container">
      <div id="slider-r-control">
        <label for="slider-r">radius r</label>
        <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5" />
        <span id="span-r-value">2.5</span>
      </div>
      <div id="slider-k-control">
        <label for="slider-k">wave number k</label>
        <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50" />
        <span id="span-k-value">50</span>
      </div>
    </div>
  </div>

  <p id="hover-info">(Hover over the curves to see math labels.)</p>

  <div id="plots-container">
    <div id="plot-left"></div>
    <div id="plot-right"></div>
  </div>

  <script>
    // Colors for consistency
    const COLORS = {
      hertz: '#ff0000',
      overlap: '#000000',
      gaussian: '#0000ff'
    };

    // Utility: generate linearly spaced array from a to b with n points
    function linspace(a, b, n) {
      const arr = new Array(n);
      const step = (b - a) / (n - 1);
      for (let i = 0; i < n; i++) {
        arr[i] = a + i * step;
      }
      return arr;
    }

    // Potential functions in real space
    function fHertz(r) {
      if (r <= 1) {
        return Math.pow(1 - r * r, 2.5);
      }
      return 0;
    }
    function fOverlap(r) {
      if (r <= 1) {
        const d = 1 - r;
        return d * d;
      }
      return 0;
    }
    function fGaussian(r) {
      return Math.exp(-Math.PI * r * r);
    }

    // 3D Fourier transforms (as provided in the plan)
    function FHertz(k) {
      if (k === 0) {
        // Using series expansion limit -> π
        return Math.PI;
      }
      const k2 = k * k;
      const num = (3 - k2) * Math.sin(k) - 3 * k * Math.cos(k);
      return 15 * Math.PI * (num) / Math.pow(k, 5);
    }
    function FOverlap(k) {
      if (k === 0) {
        // Finite limit via radial integral: 4π ∫0^1 r^2 (1 - r)^2 dr = 2π/15
        return 2 * Math.PI / 15;
      }
      const k2 = k * k;
      const num = 2 - k2 - 2 * Math.cos(k) - 2 * k * Math.sin(k);
      return 8 * Math.PI * (num) / Math.pow(k, 4);
    }
    function FGaussian(k) {
      // always positive
      return (1 / Math.sqrt(Math.PI)) * Math.exp(- (k * k) / (4 * Math.PI));
    }

    function init() {
      // Prepare initial empty plots to reserve layout, then update
      const leftLayout = {
        title: { text: 'function', font: { color: '#aaa' } },
        xaxis: { title: 'r', rangemode: 'tozero', zeroline: true },
        yaxis: { title: 'f(r)', rangemode: 'tozero', zeroline: true },
        margin: { l: 50, r: 20, t: 35, b: 45 },
        showlegend: true,
        legend: { orientation: 'h', x: 0, y: 1.1 }
      };
      const rightLayout = {
        title: { text: '3D Fourier transform (real part)', font: { color: '#aaa' } },
        xaxis: { title: 'k', rangemode: 'tozero', zeroline: true },
        yaxis: { title: 'F(k)', type: 'linear', rangemode: 'tozero', zeroline: true },
        margin: { l: 60, r: 20, t: 35, b: 45 },
        showlegend: true,
        legend: { orientation: 'h', x: 0, y: 1.1 }
      };
      const config = { responsive: true, displaylogo: false };

      // Create initial empty traces for stable ordering (H, O, G)
      const emptyLeft = [
        { x: [], y: [], mode: 'lines', name: 'Hertz', line: { color: COLORS.hertz, width: 2 }, hovertemplate: '' },
        { x: [], y: [], mode: 'lines', name: 'overlap', line: { color: COLORS.overlap, width: 2 }, hovertemplate: '' },
        { x: [], y: [], mode: 'lines', name: 'Gaussian', line: { color: COLORS.gaussian, width: 2 }, hovertemplate: '' }
      ];
      const emptyRight = [
        { x: [], y: [], mode: 'lines', name: 'Hertz', line: { color: COLORS.hertz, width: 2 }, hovertemplate: '' },
        { x: [], y: [], mode: 'lines', name: 'overlap', line: { color: COLORS.overlap, width: 2 }, hovertemplate: '' },
        { x: [], y: [], mode: 'lines', name: 'Gaussian', line: { color: COLORS.gaussian, width: 2 }, hovertemplate: '' }
      ];
      Plotly.newPlot('plot-left', emptyLeft, leftLayout, config);
      Plotly.newPlot('plot-right', emptyRight, rightLayout, config);

      // Event listeners
      document.getElementById('checkbox-hertz').addEventListener('change', updatePlots);
      document.getElementById('checkbox-overlap').addEventListener('change', updatePlots);
      document.getElementById('checkbox-gaussian').addEventListener('change', updatePlots);

      document.getElementById('btn-linear-linear').addEventListener('click', () => setScaleActive('btn-linear-linear'));
      document.getElementById('btn-log-linear').addEventListener('click', () => setScaleActive('btn-log-linear'));
      document.getElementById('btn-log-log').addEventListener('click', () => setScaleActive('btn-log-log'));

      document.getElementById('slider-r').addEventListener('input', updatePlots);
      document.getElementById('slider-k').addEventListener('input', updatePlots);

      // Initial plots
      updatePlots();
    }

    function setScaleActive(activeId) {
      const ids = ['btn-linear-linear', 'btn-log-linear', 'btn-log-log'];
      ids.forEach(id => {
        const btn = document.getElementById(id);
        if (id === activeId) btn.classList.add('active');
        else btn.classList.remove('active');
      });
      updatePlots();
    }

    function updatePlots() {
      // 1. Read checkboxes
      const showHertz   = document.getElementById('checkbox-hertz').checked;
      const showOverlap = document.getElementById('checkbox-overlap').checked;
      const showGaussian= document.getElementById('checkbox-gaussian').checked;

      // 2. Read active scale button
      const isLinearLinear = document.getElementById('btn-linear-linear').classList.contains('active');
      const isLogLinear    = document.getElementById('btn-log-linear').classList.contains('active');
      const isLogLog       = document.getElementById('btn-log-log').classList.contains('active');

      // 3. Read sliders
      const maxR = parseFloat(document.getElementById('slider-r').value);
      const maxK = parseFloat(document.getElementById('slider-k').value);

      // 4. Update displayed slider values
      document.getElementById('span-r-value').textContent = maxR.toFixed(1);
      document.getElementById('span-k-value').textContent = (Math.round(maxK * 100) / 100).toString();

      // 5. Prepare data for plots
      // Left plot data
      const N = 200;
      const r = linspace(0, maxR, N);

      const yH = showHertz ? r.map(rv => fHertz(rv)) : [];
      const yO = showOverlap ? r.map(rv => fOverlap(rv)) : [];
      const yG = showGaussian ? r.map(rv => fGaussian(rv)) : [];

      const leftTraces = [
        {
          x: showHertz ? r : [],
          y: yH,
          mode: 'lines',
          name: 'Hertz',
          line: { color: COLORS.hertz, width: 2 },
          hovertemplate: 'Hertz<br>$f(r) = (1 - r^2)^{5/2},\\ r \\le 1$<br>r=%{x:.3f}<br>f=%{y:.3e}<extra></extra>'
        },
        {
          x: showOverlap ? r : [],
          y: yO,
          mode: 'lines',
          name: 'overlap',
          line: { color: COLORS.overlap, width: 2, dash: 'dot' },
          hovertemplate: 'overlap<br>$f(r) = (1 - r)^2,\\ r \\le 1$<br>r=%{x:.3f}<br>f=%{y:.3e}<extra></extra>'
        },
        {
          x: showGaussian ? r : [],
          y: yG,
          mode: 'lines',
          name: 'Gaussian',
          line: { color: COLORS.gaussian, width: 2, dash: 'dash' },
          hovertemplate: 'Gaussian<br>$f(r) = e^{-\\pi r^2}$<br>r=%{x:.3f}<br>f=%{y:.3e}<extra></extra>'
        }
      ];

      // Right plot data
      const k = linspace(0, maxK, N);

      let yFH = [];
      let yFO = [];
      let yFG = [];
      if (showHertz) {
        yFH = k.map(kv => FHertz(kv));
      }
      if (showOverlap) {
        yFO = k.map(kv => FOverlap(kv));
      }
      if (showGaussian) {
        yFG = k.map(kv => FGaussian(kv));
      }

      // Detect selected y-axis type ('linear' or 'log') for right plot
      const yTypeRight = (isLinearLinear) ? 'linear' : 'log';

      // If log axis is selected, Plotly hides non-positive values automatically.
      // We keep the series as-is; this also demonstrates sign changes.

      const rightTraces = [
        {
          x: showHertz ? k : [],
          y: yFH,
          mode: 'lines',
          name: 'Hertz',
          line: { color: COLORS.hertz, width: 2 },
          hovertemplate: 'Hertz<br>$F_H(k) = 15\\pi\\, \\dfrac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}$<br>k=%{x:.3f}<br>F=%{y:.3e}<extra></extra>'
        },
        {
          x: showOverlap ? k : [],
          y: yFO,
          mode: 'lines',
          name: 'overlap',
          line: { color: COLORS.overlap, width: 2, dash: 'dot' },
          hovertemplate: 'overlap<br>$F_O(k) = 8\\pi\\, \\dfrac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}$<br>k=%{x:.3f}<br>F=%{y:.3e}<extra></extra>'
        },
