        }
      ];

      // 6. Update plots with Plotly.react
      const leftLayoutUpdate = {
        xaxis: { title: 'r', range: [0, maxR], type: 'linear' }
      };
      const rightLayoutUpdate = {
        xaxis: { title: 'k', range: [0, maxK], type: 'linear' },
        yaxis: { title: 'F(k)', type: yTypeRight }
      };

      Plotly.react('plot-left', leftTraces, Object.assign({}, Plotly.__plots__ && Plotly.__plots__['plot-left'] ? Plotly.__plots__['plot-left'].layout : {}, leftLayoutUpdate));
      Plotly.react('plot-right', rightTraces, Object.assign({}, Plotly.__plots__ && Plotly.__plots__['plot-right'] ? Plotly.__plots__['plot-right'].layout : {}, rightLayoutUpdate));

      // 7. Ask MathJax to typeset any (new) LaTeX in hover labels and elsewhere
      if (window.MathJax && MathJax.typeset) {
        MathJax.typeset();
      }
    }

    // Start
    window.addEventListener('DOMContentLoaded', init);
  </script>
