<script>
(function(){
  // DOM elements
  const el = {
    scalePopulation: document.getElementById('scale-population'),
    scaleIndividual: document.getElementById('scale-individual'),
    sliderPopulation: document.getElementById('slider-population'),
    valuePopulation: document.getElementById('value-population'),

    typeEmissions: document.getElementById('type-emissions'),
