    sliderCompCost: document.getElementById('slider-compensation-cost'),
    valueCompCost: document.getElementById('value-compensation-cost'),

    sliderFracComp: document.getElementById('slider-fractional-compensation'),
    valueFracComp: document.getElementById('value-fractional-compensation'),

    sliderConstComp: document.getElementById('slider-constant-compensation'),
    valueConstComp: document.getElementById('value-constant-compensation'),

    groupPopulation: document.getElementById('group-population'),

    btnNow: document.getElementById('btn-neutral-now'),
    btn2030: document.getElementById('btn-neutral-2030'),
    btn2040: document.getElementById('btn-neutral-2040'),
    btn2050: document.getElementById('btn-neutral-2050'),
    btnNever: document.getElementById('btn-neutral-never'),

    mainPlotContainer: document.getElementById('main-plot-container'),
    budgetPlotContainer: document.getElementById('budget-plot-container'),
  };

  // App state
  const state = {
    neutral_year: 2040, // default active button
    scale: 'individual',
    type: 'emissions',
    // derived arrays for plotting
    years: [],
    real_person: [],
    comp_person: [],
    eff_person: [],
    cum_eff_person: [],

    // scaled versions for plotting (population scaling and unit conversions)
    real_scaled: [],
    eff_scaled: [],
    comp_scaled: [],
    annual_costs_scaled: [],
    cum_eff_scaled: [],

    total_costs_scaled: 0,
    avg_annual_cost_scaled: 0,

    // axis labels
    yAxisLabel: '',
    budgetTitle: '',
  };

  function numberFormatShort(n, unit=''){
    const abs = Math.abs(n);
    let val = n, suffix = '';
    if (abs >= 1e9) { val = n/1e9; suffix='B'; }
    else if (abs >= 1e6) { val = n/1e6; suffix='M'; }
    else if (abs >= 1e3) { val = n/1e3; suffix='k'; }
    const s = (Math.abs(val) >= 100 ? val.toFixed(0) : Math.abs(val) >= 10 ? val.toFixed(1) : val.toFixed(2));
    return s + (suffix ? suffix : '') + (unit ? ' ' + unit : '');
  }

  function clamp(v, a, b){ return Math.max(a, Math.min(b, v)); }

  function getInputs(){
    return {
      scale: el.scalePopulation.checked ? 'population' : 'individual',
      population_k: parseFloat(el.sliderPopulation.value),
      type: el.typeCosts.checked ? 'costs' : 'emissions',
      initial_emissions_person: parseFloat(el.sliderAnnualEmissions.value),
      reduction_half_time: parseFloat(el.sliderReductionTime.value),
      annual_budget_person: parseFloat(el.sliderAnnualBudget.value),
      start_year: parseInt(el.sliderStartYear.value),
      cost_per_ton: parseFloat(el.sliderCompCost.value),
      fractional_comp_pct: parseFloat(el.sliderFracComp.value),
      constant_comp_pct: parseFloat(el.sliderConstComp.value),
      neutral_year: state.neutral_year, // may be null for 'never'
    };
  }

  function updateValueDisplays(){
    el.valuePopulation.textContent = el.sliderPopulation.value;
    el.valueAnnualEmissions.textContent = parseFloat(el.sliderAnnualEmissions.value).toFixed(1);
    el.valueReductionTime.textContent = el.sliderReductionTime.value;
    el.valueAnnualBudget.textContent = parseFloat(el.sliderAnnualBudget.value).toFixed(1);
    el.valueStartYear.textContent = el.sliderStartYear.value;
    el.valueCompCost.textContent = '$' + el.sliderCompCost.value;
    el.valueFracComp.textContent = el.sliderFracComp.value + '%';
    el.valueConstComp.textContent = el.sliderConstComp.value + '%';
  }

  function setScaleUI(scale){
    const isPop = scale === 'population';
    el.sliderPopulation.disabled = !isPop;
    el.groupPopulation.classList.toggle('disabled', !isPop);
  }

  // Button active state handling
  function setNeutralButtonsActive(id){
    const buttons = [el.btnNow, el.btn2030, el.btn2040, el.btn2050, el.btnNever];
    buttons.forEach(b => b.classList.remove('active'));
    const btn = document.getElementById(id);
    if (btn) btn.classList.add('active');
  }

  function computeModel(){
    const inp = getInputs();
    state.scale = inp.scale;
    state.type = inp.type;

    const years = [];
    const real = [];
    const comp = [];
    const eff = [];
    const cumEff = [];
    const t0 = 2020, t1 = 2050;
    let running = 0;

    for (let t=t0; t<=t1; t++){
      years.push(t);
      let real_t;
      if (t < inp.start_year){
        real_t = inp.initial_emissions_person;
      } else {
        real_t = inp.initial_emissions_person * Math.pow(0.5, (t - inp.start_year)/inp.reduction_half_time);
      }
      // compensated at year t
      const fractional = real_t * (inp.fractional_comp_pct/100);
      const constant = inp.initial_emissions_person * (inp.constant_comp_pct/100);
      const comp_t = fractional + constant;

      const eff_t = real_t - comp_t;

      real.push(real_t);
      comp.push(comp_t);
      eff.push(eff_t);

      running += eff_t;
      cumEff.push(running);
    }

    state.years = years;
    state.real_person = real;
    state.comp_person = comp;
    state.eff_person = eff;
    state.cum_eff_person = cumEff;

    // Scale to population if needed
    const factorPop = (inp.scale === 'population') ? (inp.population_k * 1000.0) : 1.0;

    // Scaled emissions and costs; additionally handle "display units"
    const real_scaled = real.map(v => v * factorPop);
    const comp_scaled = comp.map(v => v * factorPop);
    const eff_scaled = eff.map(v => v * factorPop);

    const annual_costs_scaled = comp_scaled.map(v => v * inp.cost_per_ton);
    const total_costs_scaled = annual_costs_scaled.reduce((a,b)=>a+b,0);
    const avg_annual_cost_scaled = total_costs_scaled / (t1 - t0 + 1);

    const cum_eff_scaled = cumEff.map(v => v * factorPop);

    state.real_scaled = real_scaled;
    state.comp_scaled = comp_scaled;
    state.eff_scaled = eff_scaled;
    state.annual_costs_scaled = annual_costs_scaled;
    state.total_costs_scaled = total_costs_scaled;
    state.avg_annual_cost_scaled = avg_annual_cost_scaled;
    state.cum_eff_scaled = cum_eff_scaled;

    // Labels
    if (state.type === 'emissions'){
      if (inp.scale === 'individual'){
        state.yAxisLabel = 'annual CO₂ emissions per person (tons)';
        state.budgetTitle = 'individual climate budget';
      } else {
        state.yAxisLabel = 'annual city emission of CO₂ (mil tons)';
        state.budgetTitle = 'population climate budget';
      }
    } else {
      if (inp.scale === 'individual'){
        state.yAxisLabel = 'annual personal compensation costs ($)';
        state.budgetTitle = 'individual climate budget';
      } else {
        state.yAxisLabel = 'annual city compensation costs ($M)';
        state.budgetTitle = 'population climate budget';
      }
    }
  }

  // p5 sketch
  let sketch = function(p){
    let canvas;
    const margins = {left:64, right:24, top:24, bottom:20};
    let mainRect = {x:0,y:0,w:0,h:0};
    let budgetRect = {x:0,y:0,w:0,h:0};

    p.setup = function(){
      const d = getCanvasSize();
      canvas = p.createCanvas(d.w, d.h);
      canvas.parent(el.mainPlotContainer);
      p.noLoop();
      p.pixelDensity(1);
      updateRects();
      update(); // initial draw
    };

    function getCanvasSize(){
      // Make a single canvas large enough for both main plot and budget row
      // Use container width, and set height proportionally
      const containerW = el.mainPlotContainer.clientWidth || 900;
      const containerH = Math.max(640, Math.min(window.innerHeight-180, 820));
      // We'll dedicate ~70% to main, ~30% to budget
      return {w: containerW, h: containerH};
    }

    function updateRects(){
      const w = p.width, h = p.height;
      const mainH = Math.floor(h * 0.7);
      mainRect = {x:0, y:0, w:w, h:mainH};
      budgetRect = {x:0, y:mainH, w:w, h:h - mainH};
    }

    p.windowResized = function(){
      const d = getCanvasSize();
      p.resizeCanvas(d.w, d.h);
      updateRects();
      p.redraw();
    };

    function mapX(year){
      const xs = 2020, xe = 2050;
      const x0 = margins.left;
      const x1 = mainRect.w - margins.right;
      const t = (year - xs) / (xe - xs);
      return x0 + t * (x1 - x0);
    }
    function mapY(val, yMin, yMax){
      const y0 = mainRect.y + margins.top;
      const y1 = mainRect.y + mainRect.h - margins.bottom;
      const t = (val - yMin)/(yMax - yMin);
      return p.lerp(y1, y0, t);
    }

    function drawAxes(xMinYear, xMaxYear, yMin, yMax, yLabel, unitsHint){
      // background
      p.push();
      p.noStroke();
      p.fill(255);
      p.rect(mainRect.x, mainRect.y, mainRect.w, mainRect.h);
      p.pop();

      const x0 = margins.left;
      const x1 = mainRect.w - margins.right;
      const y0 = mainRect.y + margins.top;
      const y1 = mainRect.y + mainRect.h - margins.bottom;

      // Gridlines Y
      p.push();
      p.stroke(230);
      p.strokeWeight(1);
      p.textAlign(p.RIGHT, p.CENTER);
      p.fill(120);
      const ticks = 5;
      for (let i=0;i<=ticks;i++){
        const tv = p.lerp(yMin, yMax, i/ticks);
        const yy = mapY(tv, yMin, yMax);
        p.stroke(235);
        p.line(x0, yy, x1, yy);
        p.noStroke();
        p.fill(120);
        p.textSize(11);
        const unitTxt = (unitsHint && unitsHint.length && i===0) ? (' ' + unitsHint) : '';
        p.text(Number.isFinite(tv)? tv.toFixed( (Math.abs(yMax - yMin) >= 10 ? 1 : 2) ) : '', x0 - 8, yy);
      }
      p.pop();

      // Axis lines
      p.push();
      p.stroke(180);
      p.line(x0, y1, x1, y1);
      p.line(x0, y0, x0, y1);
      // X axis ticks
      p.textAlign(p.CENTER, p.TOP);
      p.fill(90);
      p.textSize(11);
      for(let yr = 2020; yr<=2050; yr+=5){
        const xx = mapX(yr);
        p.stroke(200);
        p.line(xx, y1, xx, y1+4);
        p.noStroke();
        p.text(yr.toString(), xx, y1+6);
      }
      // Y label
      p.push();
      p.textAlign(p.CENTER, p.CENTER);
      p.translate(18, (y0+y1)/2);
      p.rotate(-Math.PI/2);
      p.fill(70);
      p.textSize(12);
      p.text(yLabel, 0, 0);
      p.pop();
      p.pop();
    }

    function drawEmissionsPlot(){
      const inp = getInputs();
      // Prepare data series to draw, including population unit conversion for y-axis
      const popMode = inp.scale === 'population';
      const unitDiv = popMode ? 1e6 : 1; // million tons for population y-axis
      const yUnits = popMode ? 'mil tons' : 'tons';

      const yrs = state.years;
      const real = state.real_scaled.map(v=>v/unitDiv);
      const eff  = state.eff_scaled.map(v=>v/unitDiv);
      const comp = state.comp_scaled.map(v=>v/unitDiv);
      const cum = state.cum_eff_scaled.map(v=>v/unitDiv);

      // y range
      let yMin = Math.min(0, ...eff, ...(inp.neutral_year? [cum[yrs.indexOf(inp.neutral_year)]]:[]), cum[cum.length-1]);
      let yMax = Math.max(...real, ...eff, ...(inp.neutral_year? [cum[yrs.indexOf(inp.neutral_year)]]:[]), cum[cum.length-1]);
      if (yMax === yMin){ yMax += 1; yMin -= 1; }
      const pad = (yMax - yMin)*0.1;
      yMin -= pad; yMax += pad;

      drawAxes(2020,2050,yMin,yMax, state.yAxisLabel, yUnits);

      // compensated area (between real and effective) in blue
      p.push();
      p.noStroke();
      p.fill(52,152,219,120); // semi-transparent blue
      for (let i=0; i<yrs.length-1; i++){
        const xA = mapX(yrs[i]);
        const xB = mapX(yrs[i+1]);
        const yRealA = mapY(real[i], yMin, yMax);
        const yRealB = mapY(real[i+1], yMin, yMax);
        const yEffA = mapY(eff[i], yMin, yMax);
        const yEffB = mapY(eff[i+1], yMin, yMax);
        p.quad(xA,yRealA, xB,yRealB, xB,yEffB, xA,yEffA);
      }
      p.pop();

      // fill between effective and y=0 (red for >0, green for <0)
      p.push();
      p.noStroke();
      for (let i=0; i<yrs.length-1; i++){
        const xA = mapX(yrs[i]);
        const xB = mapX(yrs[i+1]);
        const yEffA = mapY(eff[i], yMin, yMax);
        const yEffB = mapY(eff[i+1], yMin, yMax);
        const yZeroA = mapY(0, yMin, yMax);
        const yZeroB = yZeroA;

        const avg = (eff[i] + eff[i+1]) / 2;
        if (avg >= 0){
          p.fill(231,76,60,90); // red
        } else {
          p.fill(39,174,96,90); // green
        }
        p.quad(xA,yEffA, xB,yEffB, xB,yZeroB, xA,yZeroA);
      }
      p.pop();

      // real emissions line (red)
      p.push();
      p.stroke(231,76,60);
      p.strokeWeight(2);
      p.noFill();
      p.beginShape();
      for (let i=0;i<yrs.length;i++){
        p.vertex(mapX(yrs[i]), mapY(real[i], yMin, yMax));
      }
      p.endShape();
      p.pop();

      // effective emissions line (black)
      p.push();
      p.stroke(34);
      p.strokeWeight(2.2);
      p.noFill();
      p.beginShape();
      for (let i=0;i<yrs.length;i++){
        p.vertex(mapX(yrs[i]), mapY(eff[i], yMin, yMax));
      }
      p.endShape();
      p.pop();

      // Annotations: neutral vertical + dashed horizontals
      const x0 = margins.left;
      const x1 = mainRect.w - margins.right;

      // dashed style helper
      function dashedLine(xa, ya, xb, yb){
        p.push();
        p.stroke(80);
        p.strokeWeight(1);
        p.drawingContext.setLineDash([6,4]);
        p.line(xa, ya, xb, yb);
        p.pop();
      }

      // Horizontal dashed for 2050 cumulative
      const cum2050 = cum[cum.length-1];
      const yCum2050 = mapY(cum2050, yMin, yMax);
      dashedLine(x0, yCum2050, x1, yCum2050);
      p.push();
      p.noStroke();
      p.fill(60);
      p.textAlign(p.RIGHT, p.BOTTOM);
      p.textSize(12);
      p.text(`CO₂ emissions until 2050 = ${numberFormatShort(cum2050, yUnits)} CO₂`, x1, yCum2050 - 4);
      p.pop();

      // Neutral year vertical + its cumulative dashed line (if set)
      if (inp.neutral_year !== null){
        const idx = yrs.indexOf(inp.neutral_year);
        if (idx !== -1){
          const xNeutral = mapX(inp.neutral_year);
          // vertical
          p.push();
          p.stroke(80);
          p.strokeWeight(1.2);
          p.drawingContext.setLineDash([4,4]);
          p.line(xNeutral, mainRect.y + margins.top, xNeutral, mainRect.y + mainRect.h - margins.bottom);
          p.pop();

          // label
          p.push();
          p.noStroke();
          p.fill(60);
          p.textAlign(p.CENTER, p.TOP);
          p.textSize(12);
          p.text(`climate neutral ${inp.neutral_year}`, xNeutral, mainRect.y + margins.top + 4);
          p.pop();

          // cumulative horizontal at neutral year
          const cumNeu = cum[idx];
          const yCumNeu = mapY(cumNeu, yMin, yMax);
          dashedLine(x0, yCumNeu, x1, yCumNeu);
          p.push();
          p.noStroke();
          p.fill(60);
          p.textAlign(p.RIGHT, p.BOTTOM);
          p.textSize(12);
          p.text(`CO₂ emissions until ${inp.neutral_year} = ${numberFormatShort(cumNeu, yUnits)} CO₂`, x1, yCumNeu - 4);
          p.pop();
        }
      }

      // simple legend
      p.push();
      const lx = x1 - 180, ly = mainRect.y + margins.top + 14;
      const rowH = 16;
      p.textSize(12);
      p.noStroke();
      p.fill(60);
      p.textAlign(p.LEFT, p.CENTER);

      p.fill(231,76,60);
      p.rect(lx, ly-6, 18, 3, 2);
      p.fill(60); p.text('real emissions', lx+26, ly);

      p.fill(0); p.rect(lx, ly-6+rowH, 18, 3, 2);
      p.fill(60); p.text('effective emissions', lx+26, ly+rowH);

      p.fill(52,152,219,140); p.rect(lx, ly-6+2*rowH, 18, 10, 2);
      p.fill(60); p.text('compensated CO₂', lx+26, ly+2*rowH);
      p.pop();
    }

    function drawCostsPlot(){
      const inp = getInputs();
      const popMode = inp.scale === 'population';
      const costDiv = popMode ? 1e6 : 1; // show $M for population
      const yUnits = popMode ? '$M' : '$';

      const yrs = state.years;
      const costs = state.annual_costs_scaled.map(v => v / costDiv);
      const avg = state.avg_annual_cost_scaled / costDiv;
      const total = state.total_costs_scaled / costDiv; // note: if population, this is in $M units already

      let yMin = 0;
      let yMax = Math.max(...costs, avg) * 1.1;
      if (yMax === 0) yMax = 1;

      drawAxes(2020,2050, yMin, yMax, state.yAxisLabel, yUnits);

      // area under costs
      p.push();
      p.noStroke();
      p.fill(52,152,219,120);
      for (let i=0;i<yrs.length-1;i++){
        const xA = mapX(yrs[i]);
        const xB = mapX(yrs[i+1]);
        const yA = mapY(costs[i], yMin, yMax);
        const yB = mapY(costs[i+1], yMin, yMax);
        const y0 = mapY(0, yMin, yMax);
        p.quad(xA,yA, xB,yB, xB,y0, xA,y0);
      }
      p.pop();

      // line
      p.push();
      p.stroke(52,152,219);
      p.strokeWeight(2);
      p.noFill();
      p.beginShape();
      for (let i=0;i<yrs.length;i++){
        p.vertex(mapX(yrs[i]), mapY(costs[i], yMin, yMax));
      }
      p.endShape();
      p.pop();

      // avg dashed
      p.push();
      p.stroke(80);
      p.strokeWeight(1);
      p.drawingContext.setLineDash([6,4]);
      const yAvg = mapY(avg, yMin, yMax);
      p.line(margins.left, yAvg, mainRect.w - margins.right, yAvg);
      p.pop();

      p.push();
      p.noStroke();
      p.fill(60);
      p.textSize(12);
      p.textAlign(p.RIGHT, p.BOTTOM);
      p.text(`annual personal compensation costs (average) = ${numberFormatShort(avg, yUnits)} per year`, mainRect.w - margins.right, yAvg - 4);
      p.textAlign(p.RIGHT, p.TOP);
      const topY = mainRect.y + margins.top + 4;
      p.text(`total personal compensation costs (until 2050) = ${numberFormatShort(total, yUnits)}`, mainRect.w - margins.right, topY);
      p.pop();
    }

    function drawBudget(){
      // background band
      p.push();
      p.noStroke();
      p.fill(250);
      p.rect(budgetRect.x, budgetRect.y, budgetRect.w, budgetRect.h);
      p.pop();

      // title
      p.push();
      p.fill(60);
      p.textAlign(p.LEFT, p.TOP);
      p.textSize(13);
      p.text(state.budgetTitle, budgetRect.x + 16, budgetRect.y + 8);
      p.pop();

      // compute 4 circles
      const inp = getInputs();
      const yrs = [2020, 2030, 2040, 2050];
      const idxs = yrs.map(y => state.years.indexOf(y));
      // scaling for population totals
      const factorPop = (inp.scale === 'population') ? (inp.population_k*1000.0) : 1.0;

      const total_budget = 30 * inp.annual_budget_person * factorPop; // 30-year total budget
      const values = idxs.map(i => total_budget - state.cum_eff_scaled[i]); // remaining budget

      // circle style depends on per-person effective emissions at T
      const effAtT_person = idxs.map(i => state.eff_person[i]);

      // radius scaling
      const absVals = values.map(v => Math.abs(v));
      const maxAbs = Math.max(...absVals, 1);
      const cols = 4;
      const cellW = budgetRect.w / cols;
      const maxRadius = Math.min( (budgetRect.h - 36) * 0.42, cellW * 0.32 );
      const C = maxRadius / Math.sqrt(maxAbs);

      // draw zero line baseline label
      p.push();
      p.stroke(220);
      p.line(budgetRect.x + 12, budgetRect.y + budgetRect.h - 10, budgetRect.x + budgetRect.w - 12, budgetRect.y + budgetRect.h - 10);
      p.pop();

      for (let k=0;k<4;k++){
        const cx = budgetRect.x + cellW * (k + 0.5);
        const cy = budgetRect.y + budgetRect.h * 0.58;
        const r = Math.max(0, C * Math.sqrt(Math.abs(values[k] || 0)));

        // circle
        p.push();
        p.stroke(40);
        p.strokeWeight(2);
        if ((effAtT_person[k] || 0) <= 0){
          // solid green
          p.fill(39,174,96,180);
          p.drawingContext.setLineDash([]);
        } else {
          // dashed outline
          p.noFill();
          p.drawingContext.setLineDash([8,6]);
        }
        p.circle(cx, cy, 2*r);
        p.pop();

        // value label inside
        p.push();
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(12);
        p.fill(30);
        const unitTxt = 'tons CO₂';
        p.text(numberFormatShort(values[k] || 0, unitTxt), cx, cy);
        p.pop();

        // year label
        p.push();
        p.textAlign(p.CENTER, p.TOP);
        p.textSize(12);
        p.fill(80);
        p.text(yrs[k].toString(), cx, budgetRect.y + 22);
        p.pop();
      }
    }

    p.draw = function(){
      p.background(255);
      // Main plot
      if (state.type === 'emissions'){
        drawEmissionsPlot();
      } else {
        drawCostsPlot();
      }
      // Budget row
      drawBudget();
    };

    // expose for calling from outside
    p._forceRedraw = function(){
      p.redraw();
    };
  };

  let p5instance = new p5(sketch);

  // Central update function
  function update(){
    updateValueDisplays();
    setScaleUI(getInputs().scale);
    computeModel();
    // Ask p5 to redraw
    if (p5instance && typeof p5instance._forceRedraw === 'function'){
      p5instance._forceRedraw();
    }
  }

  // Event listeners
  function attachEvents(){
    // scale radios
    el.scalePopulation.addEventListener('change', () => { update(); });
    el.scaleIndividual.addEventListener('change', () => { update(); });

    // type radios
    el.typeEmissions.addEventListener('change', () => { update(); });
    el.typeCosts.addEventListener('change', () => { update(); });

    // sliders
    [
      el.sliderPopulation,
      el.sliderAnnualEmissions,
      el.sliderReductionTime,
      el.sliderAnnualBudget,
      el.sliderStartYear,
      el.sliderCompCost,
      el.sliderFracComp,
      el.sliderConstComp,
    ].forEach(s => {
      s.addEventListener('input', update);
    });

    // neutral buttons
    el.btnNow.addEventListener('click', () => {
      state.neutral_year = 2020;
      setNeutralButtonsActive('btn-neutral-now');
      update();
    });
    el.btn2030.addEventListener('click', () => {
      state.neutral_year = 2030;
      setNeutralButtonsActive('btn-neutral-2030');
      update();
    });
    el.btn2040.addEventListener('click', () => {
      state.neutral_year = 2040;
      setNeutralButtonsActive('btn-neutral-2040');
      update();
    });
    el.btn2050.addEventListener('click', () => {
      state.neutral_year = 2050;
      setNeutralButtonsActive('btn-neutral-2050');
      update();
    });
    el.btnNever.addEventListener('click', () => {
      state.neutral_year = null;
      setNeutralButtonsActive('btn-neutral-never');
      update();
    });

    // initial UI reflect scale
    setScaleUI('individual');
  }

  // Initialize
  attachEvents();
  // First update happens in p5 setup after canvas creation, but ensure spans now too:
  updateValueDisplays();
})();
</script>
